'use client';


import { Shirt, Save, Edit2 } from "lucide-react";
import React, { useState, useEffect } from 'react';
import { createClient } from '@/lib/supabase/client';
import { Button, UnifiedInput, Select, Card, CardHeader, CardContent } from '@ghxstship/ui';
import { useToast } from '@ghxstship/ui';

interface UniformSizing {
  id: string;
  shirt_size?: string;
  pants_size?: string;
  jacket_size?: string;
  shoe_size?: string;
  hat_size?: string;
  glove_size?: string;
  gender_fit?: string;
  special_requirements?: string;
  measurements?: {
    chest?: string;
    waist?: string;
    hips?: string;
    inseam?: string;
    sleeve?: string;
    neck?: string;
  };
}

export default function UniformSizingClient() {
  const [sizing, setSizing] = useState<UniformSizing | null>(null);
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState<Partial<UniformSizing>>({});
  const { addToast } = useToast();
  const supabase = createClient();

  useEffect(() => {
    fetchSizing();
  }, []);

  const fetchSizing = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const { data, error } = await supabase
        .from('uniform_sizing')
        .select('*')
        .eq('user_id', user.id)
        .single();

      if (error && error.code !== 'PGRST116') throw error;

      if (data) {
        setSizing(data);
        setFormData(data);
      } else {
        setFormData({
          measurements: {}
        });
        setIsEditing(true);
      }
    } catch (error) {
      console.error('Error fetching uniform sizing:', error);
      addToast({
        type: 'error',
        title: 'Error',
        description: 'Failed to load uniform sizing',
      });
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const dataToSave = {
        ...formData,
        user_id: user.id,
      };

      if (sizing?.id) {
        const { error } = await supabase
          .from('uniform_sizing')
          .update(dataToSave)
          .eq('id', sizing.id)
          .eq('user_id', user.id);

        if (error) throw error;
      } else {
        const { error } = await supabase
          .from('uniform_sizing')
          .insert(dataToSave);

        if (error) throw error;
      }

      addToast({
        type: 'success',
        title: 'Success',
        description: 'Uniform sizing saved successfully',
      });

      setIsEditing(false);
      fetchSizing();
    } catch (error) {
      console.error('Error saving uniform sizing:', error);
      addToast({
        type: 'error',
        title: 'Error',
        description: 'Failed to save uniform sizing',
      });
    }
  };

  const updateMeasurement = (key: string, value: string) => {
    setFormData({
      ...formData,
      measurements: {
        ...(formData.measurements || {}),
        [key]: value,
      },
    });
  };

  if (loading) {
    return (
      <div className="stack-md">
        {[1, 2, 3].map(i => (
          <Card key={i} className="animate-pulse">
            <CardContent className="h-32" />
          </Card>
        ))}
      </div>
    );
  }

  return (
    <div className="stack-lg">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-heading-3">Uniform Sizing</h2>
          <p className="color-muted">Manage your uniform and clothing sizes</p>
        </div>
        {!isEditing ? (
          <Button onClick={() => setIsEditing(true)}>
            <Edit2 className="w-4 h-4 mr-sm" />
            Edit Sizing
          </Button>
        ) : (
          <div className="cluster">
            <Button variant="outline" onClick={() => {
              setIsEditing(false);
              setFormData(sizing || {});
            }}>
              Cancel
            </Button>
            <Button onClick={handleSave}>
              <Save className="w-4 h-4 mr-sm" />
              Save
            </Button>
          </div>
        )}
      </div>

      {/* Standard Sizes */}
      <Card>
        <CardHeader>
          <h3 className="text-heading-4 flex items-center">
            <Shirt className="w-5 h-5 mr-sm color-accent" />
            Standard Sizes
          </h3>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
            <label className="text-body-sm form-label">Shirt Size</label>
            <Select
              value={formData.shirt_size || ''}
              onValueChange={(value: string) => setFormData({ ...formData, shirt_size: value })}
              disabled={!isEditing}
            >
              <option value="">Select...</option>
              <option value="XS">XS</option>
              <option value="S">S</option>
              <option value="M">M</option>
              <option value="L">L</option>
              <option value="XL">XL</option>
              <option value="2XL">2XL</option>
              <option value="3XL">3XL</option>
              <option value="4XL">4XL</option>
            </Select>
            <UnifiedInput               label="Pants Size"
              value={formData.pants_size || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, pants_size: e.target.value })}
              placeholder="e.g., 32x30"
              disabled={!isEditing}
            />
            <label className="text-body-sm form-label">Jacket Size</label>
            <Select
              value={formData.jacket_size || ''}
              onValueChange={(value: string) => setFormData({ ...formData, jacket_size: value })}
              disabled={!isEditing}
            >
              <option value="">Select...</option>
              <option value="XS">XS</option>
              <option value="S">S</option>
              <option value="M">M</option>
              <option value="L">L</option>
              <option value="XL">XL</option>
              <option value="2XL">2XL</option>
              <option value="3XL">3XL</option>
              <option value="4XL">4XL</option>
            </Select>
            <UnifiedInput               label="Shoe Size"
              value={formData.shoe_size || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, shoe_size: e.target.value })}
              placeholder="e.g., 10.5"
              disabled={!isEditing}
            />
            <UnifiedInput               label="Hat Size"
              value={formData.hat_size || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, hat_size: e.target.value })}
              placeholder="e.g., 7 1/4"
              disabled={!isEditing}
            />
            <label className="text-body-sm form-label">Glove Size</label>
            <Select
              value={formData.glove_size || ''}
              onValueChange={(value: string) => setFormData({ ...formData, glove_size: value })}
              disabled={!isEditing}
            >
              <option value="">Select...</option>
              <option value="XS">XS</option>
              <option value="S">S</option>
              <option value="M">M</option>
              <option value="L">L</option>
              <option value="XL">XL</option>
              <option value="2XL">2XL</option>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Fit Preference */}
      <Card>
        <CardHeader>
          <h3 className="text-body text-heading-4">Fit Preference</h3>
        </CardHeader>
        <CardContent>
          <label className="text-body-sm form-label">Gender Fit</label>
          <Select
            value={formData.gender_fit || ''}
            onValueChange={(value: string) => setFormData({ ...formData, gender_fit: value })}
            disabled={!isEditing}
          >
            <option value="">Select...</option>
            <option value="mens">Men&apos;s</option>
            <option value="womens">Women&apos;s</option>
            <option value="unisex">Unisex</option>
          </Select>
        </CardContent>
      </Card>

      {/* Detailed Measurements */}
      <Card>
        <CardHeader>
          <h3 className="text-body text-heading-4">Detailed Measurements</h3>
          <p className="text-body-sm color-muted">Optional measurements for custom fitting</p>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
            <UnifiedInput               label="Chest"
              value={formData.measurements?.chest || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateMeasurement('chest', e.target.value)}
              placeholder="e.g., 42 inches"
              disabled={!isEditing}
            />
            <UnifiedInput               label="Waist"
              value={formData.measurements?.waist || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateMeasurement('waist', e.target.value)}
              placeholder="e.g., 34 inches"
              disabled={!isEditing}
            />
            <UnifiedInput               label="Hips"
              value={formData.measurements?.hips || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateMeasurement('hips', e.target.value)}
              placeholder="e.g., 40 inches"
              disabled={!isEditing}
            />
            <UnifiedInput               label="Inseam"
              value={formData.measurements?.inseam || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateMeasurement('inseam', e.target.value)}
              placeholder="e.g., 30 inches"
              disabled={!isEditing}
            />
            <UnifiedInput               label="Sleeve Length"
              value={formData.measurements?.sleeve || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateMeasurement('sleeve', e.target.value)}
              placeholder="e.g., 33 inches"
              disabled={!isEditing}
            />
            <UnifiedInput               label="Neck"
              value={formData.measurements?.neck || ''}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateMeasurement('neck', e.target.value)}
              placeholder="e.g., 16 inches"
              disabled={!isEditing}
            />
          </div>
        </CardContent>
      </Card>

      {/* Special Requirements */}
      <Card>
        <CardHeader>
          <h3 className="text-body text-heading-4">Special Requirements</h3>
        </CardHeader>
        <CardContent>
          <UnifiedInput             value={formData.special_requirements || ''}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, special_requirements: e.target.value })}
            placeholder="Any special uniform requirements or notes..."
            disabled={!isEditing}
          />
        </CardContent>
      </Card>
    </div>
  );
}

'use client';


import { useState, useEffect } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Plus,
  Download,
  Calendar,
  Clock,
  FileText,
  Database,
  Filter,
  Search,
  Play,
  Pause,
  Trash2,
  Edit3,
  Eye,
  Share2,
  Settings,
  CheckCircle,
  XCircle,
  AlertCircle,
  FileSpreadsheet,
  FileJson,
  Files,
  Archive,
  RefreshCw
} from 'lucide-react';

interface ExportJob {
  id: string;
  organizationId: string;
  name: string;
  description?: string;
  dataSource: 'projects' | 'people' | 'finance' | 'events' | 'custom_query';
  format: 'csv' | 'xlsx' | 'json' | 'pdf';
  filters: Record<string, any>;
  schedule?: {
    enabled: boolean;
    frequency: 'daily' | 'weekly' | 'monthly' | 'quarterly';
    time: string;
    dayOfWeek?: number;
    dayOfMonth?: number;
  };
  status: 'active' | 'paused' | 'failed' | 'completed';
  lastRun?: string;
  nextRun?: string;
  fileUrl?: string;
  fileSize?: number;
  recordCount?: number;
  createdAt: string;
  updatedAt: string;
  createdBy: string;
}

interface ExportHistory {
  id: string;
  exportJobId: string;
  status: 'running' | 'completed' | 'failed';
  startedAt: string;
  completedAt?: string;
  fileUrl?: string;
  fileSize?: number;
  recordCount?: number;
  errorMessage?: string;
}

interface ExportsClientProps {
  organizationId: string;
  translations: Record<string, string>;
}

export default function ExportsClient({ organizationId, translations }: ExportsClientProps) {
  const [exportJobs, setExportJobs] = useState<ExportJob[]>([]);
  const [exportHistory, setExportHistory] = useState<ExportHistory[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [activeTab, setActiveTab] = useState<'jobs' | 'history'>('jobs');
  const [searchTerm, setSearchTerm] = useState('');

  const supabase = createBrowserClient();

  useEffect(() => {
    loadExportData();
  }, [organizationId]);

  const loadExportData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Mock export jobs for demonstration
      const mockJobs: ExportJob[] = [
        {
          id: '1',
          organizationId,
          name: 'Monthly Financial Report',
          description: 'Complete financial transactions and revenue data',
          dataSource: 'finance',
          format: 'xlsx',
          filters: {
            dateRange: 'last_30_days',
            transactionType: 'all'
          },
          schedule: {
            enabled: true,
            frequency: 'monthly',
            time: '09:00',
            dayOfMonth: 1
          },
          status: 'active',
          lastRun: new Date(Date.now() - 1000 * 60 * 60 * 24).toISOString(),
          nextRun: new Date(Date.now() + 1000 * 60 * 60 * 24 * 30).toISOString(),
          fileUrl: '/exports/financial-report-2024-01.xlsx',
          fileSize: 2048576,
          recordCount: 1250,
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString(),
          createdBy: 'user-1'
        },
        {
          id: '2',
          organizationId,
          name: 'Team Directory Export',
          description: 'Complete team member contact information',
          dataSource: 'people',
          format: 'csv',
          filters: {
            status: 'active',
            includeContacts: true
          },
          schedule: {
            enabled: true,
            frequency: 'weekly',
            time: '08:00',
            dayOfWeek: 1
          },
          status: 'active',
          lastRun: new Date(Date.now() - 1000 * 60 * 60 * 24 * 7).toISOString(),
          nextRun: new Date(Date.now() + 1000 * 60 * 60 * 24 * 7).toISOString(),
          fileUrl: '/exports/team-directory-2024-01-15.csv',
          fileSize: 512000,
          recordCount: 85,
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString(),
          createdBy: 'user-2'
        },
        {
          id: '3',
          organizationId,
          name: 'Project Status Report',
          description: 'All active projects with current status and metrics',
          dataSource: 'projects',
          format: 'json',
          filters: {
            status: ['active', 'in_progress'],
            includeMetrics: true
          },
          status: 'paused',
          lastRun: new Date(Date.now() - 1000 * 60 * 60 * 24 * 14).toISOString(),
          fileUrl: '/exports/project-status-2024-01-01.json',
          fileSize: 1024000,
          recordCount: 24,
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString(),
          createdBy: 'user-1'
        }
      ];

      // Mock export history
      const mockHistory: ExportHistory[] = [
        {
          id: 'h1',
          exportJobId: '1',
          status: 'completed',
          startedAt: new Date(Date.now() - 1000 * 60 * 60 * 2).toISOString(),
          completedAt: new Date(Date.now() - 1000 * 60 * 60 * 2 + 1000 * 60 * 5).toISOString(),
          fileUrl: '/exports/financial-report-2024-01-15.xlsx',
          fileSize: 2048576,
          recordCount: 1250
        },
        {
          id: 'h2',
          exportJobId: '2',
          status: 'completed',
          startedAt: new Date(Date.now() - 1000 * 60 * 60 * 24).toISOString(),
          completedAt: new Date(Date.now() - 1000 * 60 * 60 * 24 + 1000 * 60 * 2).toISOString(),
          fileUrl: '/exports/team-directory-2024-01-14.csv',
          fileSize: 512000,
          recordCount: 85
        },
        {
          id: 'h3',
          exportJobId: '1',
          status: 'failed',
          startedAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2).toISOString(),
          completedAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2 + 1000 * 60 * 3).toISOString(),
          errorMessage: 'Database connection timeout'
        }
      ];

      setExportJobs(mockJobs);
      setExportHistory(mockHistory);

    } catch (err) {
      console.error('Error loading export data:', err);
      setError('Failed to load export data');
    } finally {
      setLoading(false);
    }
  };

  const createExportJob = async (jobData: Partial<ExportJob>) => {
    try {
      const newJob: ExportJob = {
        id: crypto.randomUUID(),
        organizationId,
        name: jobData.name || '',
        description: jobData.description,
        dataSource: jobData.dataSource || 'projects',
        format: jobData.format || 'csv',
        filters: jobData.filters || {},
        schedule: jobData.schedule,
        status: 'active',
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
        createdBy: 'current-user'
      };

      setExportJobs(prev => [...prev, newJob]);
      setShowCreateForm(false);
    } catch (err) {
      console.error('Error creating export job:', err);
      setError('Failed to create export job');
    }
  };

  const runExportJob = async (jobId: string) => {
    try {
      // Update job status and add to history
      setExportJobs(prev => prev.map(job => 
        job.id === jobId 
          ? { ...job, lastRun: new Date().toISOString(), status: 'active' as const }
          : job
      ));

      // Add running history entry
      const historyEntry: ExportHistory = {
        id: crypto.randomUUID(),
        exportJobId: jobId,
        status: 'running',
        startedAt: new Date().toISOString()
      };

      setExportHistory(prev => [historyEntry, ...prev]);

      // Simulate completion after 3 seconds
      setTimeout(() => {
        setExportHistory(prev => prev.map(entry => 
          entry.id === historyEntry.id 
            ? { 
                ...entry, 
                status: 'completed' as const,
                completedAt: new Date().toISOString(),
                fileUrl: `/exports/export-${jobId}-${Date.now()}.csv`,
                fileSize: Math.floor(Math.random() * 2000000) + 100000,
                recordCount: Math.floor(Math.random() * 1000) + 50
              }
            : entry
        ));
      }, 3000);

    } catch (err) {
      console.error('Error running export job:', err);
      setError('Failed to run export job');
    }
  };

  const toggleJobStatus = async (jobId: string) => {
    try {
      setExportJobs(prev => prev.map(job => 
        job.id === jobId 
          ? { ...job, status: job.status === 'active' ? 'paused' : 'active' }
          : job
      ));
    } catch (err) {
      console.error('Error updating job status:', err);
      setError('Failed to update job status');
    }
  };

  const deleteExportJob = async (jobId: string) => {
    try {
      setExportJobs(prev => prev.filter(job => job.id !== jobId));
      setExportHistory(prev => prev.filter(entry => entry.exportJobId !== jobId));
    } catch (err) {
      console.error('Error deleting export job:', err);
      setError('Failed to delete export job');
    }
  };

  const downloadFile = (fileUrl: string, fileName: string) => {
    // In a real implementation, this would handle the actual file download
    console.log(`Downloading file: ${fileUrl}`);
    alert(`Download started: ${fileName}`);
  };

  const getFormatIcon = (format: ExportJob['format']) => {
    switch (format) {
      case 'csv': return Files;
      case 'xlsx': return FileSpreadsheet;
      case 'json': return FileJson;
      case 'pdf': return FileText;
      default: return FileText;
    }
  };

  const getStatusIcon = (status: ExportJob['status'] | ExportHistory['status']) => {
    switch (status) {
      case 'active':
      case 'completed': return CheckCircle;
      case 'failed': return XCircle;
      case 'paused': return Pause;
      case 'running': return RefreshCw;
      default: return AlertCircle;
    }
  };

  const getStatusColor = (status: ExportJob['status'] | ExportHistory['status']) => {
    switch (status) {
      case 'active': return 'color-success';
      case 'completed': return 'color-success';
      case 'failed': return 'color-destructive';
      case 'paused': return 'color-warning';
      case 'running': return 'color-accent';
      default: return 'color-muted';
    }
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const filteredJobs = exportJobs.filter(job =>
    job.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    job.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse">
          <div className="h-8 bg-secondary rounded w-1/4 mb-md"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
            {[1, 2, 3].map((i: any) => (
              <div key={i} className="h-48 bg-secondary rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <Card title="Error">
        <div className="text-body-sm color-destructive">{error}</div>
        <Button onClick={loadExportData} className="mt-md">
          Retry
        </Button>
      </Card>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">Data Exports</h1>
          <p className="text-body-sm color-muted">Schedule and manage data exports</p>
        </div>
        <Button onClick={() => setShowCreateForm(true)}>
          <Plus className="h-4 w-4 mr-sm" />
          New Export Job
        </Button>
      </div>

      {/* Tabs */}
      <div className="border-b border-border">
        <nav className="-mb-px flex cluster-xl">
          <button
            onClick={() => setActiveTab('jobs')}
            className={`py-sm px-xs border-b-2 form-label text-body-sm ${
              activeTab === 'jobs'
                ? 'border-primary color-accent'
                : 'border-transparent color-muted hover:color-foreground hover:border-muted'
            }`}
          >
            Export Jobs ({exportJobs.length})
          </button>
          <button
            onClick={() => setActiveTab('history')}
            className={`py-sm px-xs border-b-2 form-label text-body-sm ${
              activeTab === 'history'
                ? 'border-primary color-accent'
                : 'border-transparent color-muted hover:color-foreground hover:border-muted'
            }`}
          >
            Export History ({exportHistory.length})
          </button>
        </nav>
      </div>

      {activeTab === 'jobs' && (
        <>
          {/* Search */}
          <div className="relative">
            <Search className="h-4 w-4 absolute left-3 top-1/2 transform -translate-y-1/2 color-muted" />
            <input
              type="text"
              placeholder="Search export jobs..."
              value={searchTerm}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
              className="w-full pl-2xl pr-md py-sm border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
            />
          </div>

          {/* Export Jobs Grid */}
          {filteredJobs.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-lg">
              {filteredJobs.map((job: any) => {
                const FormatIcon = getFormatIcon(job.format);
                const StatusIcon = getStatusIcon(job.status);

                return (
                  <Card key={job.id} className="p-lg">
                    <div className="flex items-start justify-between mb-md">
                      <div className="flex items-center cluster-sm">
                        <div className="p-sm bg-accent/10 rounded-lg">
                          <FormatIcon className="h-5 w-5 color-accent" />
                        </div>
                        <div>
                          <h3 className="text-heading-4 color-foreground">{job.name}</h3>
                          <div className="flex items-center cluster-sm mt-xs">
                            <StatusIcon className={`h-4 w-4 ${getStatusColor(job.status)}`} />
                            <Badge className={`text-body-sm ${
                              job.status === 'active' ? 'bg-success/10 color-success' :
