      )}

      {/* Create Export Job Form */}
      {showCreateForm && (
        <div className="fixed inset-0 bg-foreground/30 backdrop-blur-sm flex items-center justify-center z-50">
          <Card className="w-full max-w-2xl p-lg max-h-[90vh] overflow-y-auto">
