            </form>
          </Card>
        </div>
      )}
    </div>
  );
}

'use client';

import { FileText, Download, Calendar, Clock, AlertCircle, CheckCircle, XCircle, Loader, User, Database, Filter, Mail, Settings, RotateCcw, Pause, Copy } from "lucide-react";
import React from 'react';
import { Badge } from '@ghxstship/ui/components/Badge';
import { Button } from '@ghxstship/ui/components/Button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@ghxstship/ui/components/Card';
import { Progress } from '@ghxstship/ui/components/Progress';
import type { ExportJob } from '../types';

interface ExportViewDrawerProps {
  exportJob: ExportJob;
  onDownload: () => void;
  onCancel: () => void;
  onRetry: () => void;
  onDuplicate: () => void;
  onClose: () => void;
}

export default function ExportViewDrawer({
  exportJob,
  onDownload,
  onCancel,
  onRetry,
  onDuplicate,
  onClose
}: ExportViewDrawerProps) {
  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'processing':
        return <Loader className="h-5 w-5 text-blue-500 animate-spin" />;
      case 'failed':
        return <XCircle className="h-5 w-5 text-red-500" />;
      case 'cancelled':
        return <XCircle className="h-5 w-5 text-gray-500" />;
      default:
        return <AlertCircle className="h-5 w-5 text-yellow-500" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'processing':
        return 'bg-blue-100 text-blue-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      case 'cancelled':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-yellow-100 text-yellow-800';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'csv':
        return 'ðŸ“Š';
      case 'excel':
        return 'ðŸ“ˆ';
      case 'pdf':
        return 'ðŸ“„';
      case 'json':
        return 'ðŸ”§';
      default:
        return 'ðŸ“';
    }
  };

  const formatFileSize = (bytes?: number) => {
    if (!bytes) return 'Unknown';
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return Math.round(bytes / Math.pow(1024, i) * 100) / 100 + ' ' + sizes[i];
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-start justify-between">
        <div className="flex items-center space-x-3">
          <div className="text-3xl">{getTypeIcon(exportJob.type)}</div>
          <div>
            <h2 className="text-xl font-semibold text-foreground">{exportJob.name}</h2>
            <p className="text-muted-foreground">{exportJob.description}</p>
            <div className="flex items-center space-x-2 mt-2">
              {getStatusIcon(exportJob.status)}
              <Badge className={getStatusColor(exportJob.status)}>
                {exportJob.status}
              </Badge>
              <Badge variant="outline" className="uppercase">
                {exportJob.type}
              </Badge>
            </div>
          </div>
        </div>
        <div className="flex items-center space-x-2">
          {exportJob.status === 'completed' && exportJob.downloadUrl && (
            <Button onClick={onDownload} className="bg-green-600 hover:bg-green-700">
              <Download className="h-4 w-4 mr-2" />
              Download
            </Button>
          )}
          {exportJob.status === 'processing' && (
            <Button variant="outline" onClick={onCancel}>
              <Pause className="h-4 w-4 mr-2" />
              Cancel
            </Button>
          )}
          {exportJob.status === 'failed' && (
            <Button variant="outline" onClick={onRetry}>
              <RotateCcw className="h-4 w-4 mr-2" />
              Retry
            </Button>
          )}
          <Button variant="outline" onClick={onDuplicate}>
            <Copy className="h-4 w-4 mr-2" />
            Duplicate
          </Button>
        </div>
      </div>

      {/* Progress Bar for Processing Jobs */}
      {exportJob.status === 'processing' && exportJob.progress && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">Export Progress</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Processing...</span>
                <span>{exportJob.progress}%</span>
              </div>
              <Progress value={exportJob.progress} className="h-3" />
            </div>
          </CardContent>
        </Card>
      )}

      {/* Export Details */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="text-base">Export Information</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-muted-foreground">Data Source:</span>
                <p className="font-medium capitalize flex items-center">
