              </div>
              <div>
                <span className="text-muted-foreground">File Type:</span>
                <p className="font-medium uppercase">{exportJob.type}</p>
              </div>
              {exportJob.fileSize && (
                <div>
                  <span className="text-muted-foreground">File Size:</span>
                  <p className="font-medium">{formatFileSize(exportJob.fileSize)}</p>
                </div>
              )}
              <div>
                <span className="text-muted-foreground">Filters:</span>
                <p className="font-medium">{exportJob.filters.length} applied</p>
              </div>
            </div>
          </CardContent>
