                <span className="text-muted-foreground flex items-center">
                  <User className="h-4 w-4 mr-2" />
                  Created By:
                </span>
                <span className="font-medium">{exportJob.createdBy}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-muted-foreground flex items-center">
                  <Calendar className="h-4 w-4 mr-2" />
                  Created:
                </span>
                <span className="font-medium">
                  {new Date(exportJob.createdAt).toLocaleString()}
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-muted-foreground flex items-center">
                  <Clock className="h-4 w-4 mr-2" />
                  Updated:
                </span>
                <span className="font-medium">
                  {new Date(exportJob.updatedAt).toLocaleString()}
                </span>
              </div>
              {exportJob.completedAt && (
                <div className="flex items-center justify-between">
                  <span className="text-muted-foreground flex items-center">
                    <CheckCircle className="h-4 w-4 mr-2" />
                    Completed:
                  </span>
                  <span className="font-medium">
                    {new Date(exportJob.completedAt).toLocaleString()}
                  </span>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Export Configuration */}
      <Card>
        <CardHeader>
          <CardTitle className="text-base">Export Configuration</CardTitle>
          <CardDescription>
            Configuration settings used for this export
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h4 className="font-medium mb-3 flex items-center">
                <Settings className="h-4 w-4 mr-2" />
                Format Settings
              </h4>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span className="text-muted-foreground">Include Headers:</span>
                  <span className="font-medium">
                    {exportJob.format.includeHeaders ? 'Yes' : 'No'}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-muted-foreground">Date Format:</span>
                  <span className="font-medium">{exportJob.format.dateFormat}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-muted-foreground">Number Format:</span>
                  <span className="font-medium">{exportJob.format.numberFormat}</span>
                </div>
                {exportJob.format.delimiter && (
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">Delimiter:</span>
                    <span className="font-medium">"{exportJob.format.delimiter}"</span>
                  </div>
                )}
                {exportJob.format.compression && (
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">Compression:</span>
                    <span className="font-medium">Enabled</span>
                  </div>
                )}
              </div>
            </div>

            <div>
              <h4 className="font-medium mb-3 flex items-center">
                <Mail className="h-4 w-4 mr-2" />
                Recipients
              </h4>
              {exportJob.recipients.length > 0 ? (
                <div className="space-y-2">
                  {exportJob.recipients.map((email, index) => (
                    <div key={index} className="flex items-center space-x-2">
                      <Badge variant="outline" className="text-xs">
                        {email}
                      </Badge>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-sm text-muted-foreground">No recipients configured</p>
              )}
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Filters */}
      {exportJob.filters.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">Applied Filters</CardTitle>
            <CardDescription>
              Data filters applied to this export
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {exportJob.filters.map((filter, index) => (
                <div
                  key={index}
                  className="border rounded-lg p-3"
                >
                  <div className="flex items-center justify-between mb-2">
                    <h4 className="font-medium flex items-center">
                      <Filter className="h-4 w-4 mr-2" />
                      {filter.field}
                    </h4>
                    <Badge variant="outline">{filter.operator}</Badge>
                  </div>
                  <p className="text-sm text-muted-foreground">
                    Value: {Array.isArray(filter.value) ? filter.value.join(', ') : filter.value}
                  </p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Error Details */}
      {exportJob.status === 'failed' && exportJob.errorMessage && (
        <Card className="border-red-200">
          <CardHeader>
            <CardTitle className="text-base text-red-700 flex items-center">
              <AlertCircle className="h-4 w-4 mr-2" />
              Error Details
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="bg-red-50 border border-red-200 rounded p-4">
              <p className="text-sm text-red-700">{exportJob.errorMessage}</p>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Query Details */}
      {exportJob.query && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">Query</CardTitle>
            <CardDescription>
              SQL query or data extraction configuration
            </CardDescription>
          </CardHeader>
          <CardContent>
            <pre className="bg-muted p-4 rounded text-sm font-mono overflow-x-auto">
              {exportJob.query}
            </pre>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client';


import { BarChart3, TrendingUp, Users, DollarSign, Calendar, Activity, Target, Clock, ArrowUpRight, ArrowDownRight, Minus } from "lucide-react";
import { useState, useEffect } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import { createBrowserClient } from '@ghxstship/auth';

interface AnalyticsMetric {
  id: string;
  title: string;
  value: string;
  change: number;
  changeType: 'increase' | 'decrease' | 'neutral';
  icon;
  description: string;
}

interface RecentActivity {
  id: string;
  type: string;
  description: string;
  timestamp: string;
  user: string;
}

interface TopPerformer {
  id: string;
  name: string;
  category: string;
  value: string;
  change: number;
}

interface OverviewClientProps {
  organizationId: string;
  translations: Record<string, string>;
}

export default function OverviewClient({ organizationId, translations }: OverviewClientProps) {
  const [metrics, setMetrics] = useState<AnalyticsMetric[]>([]);
  const [recentActivity, setRecentActivity] = useState<RecentActivity[]>([]);
  const [topPerformers, setTopPerformers] = useState<TopPerformer[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const supabase = createBrowserClient();

  useEffect(() => {
    loadOverviewData();
  }, [organizationId]);

  const loadOverviewData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Load analytics metrics from various tables
      const [
        projectsData,
        peopleData,
        financeData,
        eventsData
      ] = await Promise.all([
        supabase.from('projects').select('*').eq('organization_id', organizationId),
        supabase.from('people').select('*').eq('organization_id', organizationId),
        supabase.from('finance_transactions').select('*').eq('organization_id', organizationId),
        supabase.from('events').select('*').eq('organization_id', organizationId)
      ]);

      // Calculate metrics
      const totalProjects = projectsData.data?.length || 0;
      const activeProjects = projectsData.data?.filter((p: unknown) => p.status === 'active').length || 0;
      const totalPeople = peopleData.data?.length || 0;
      const totalRevenue = financeData.data?.filter((t: unknown) => t.type === 'income').reduce((sum: number, t) => sum + (t.amount || 0), 0) || 0;
      const totalEvents = eventsData.data?.length || 0;

      const calculatedMetrics: AnalyticsMetric[] = [
        {
          id: '1',
          title: 'Active Projects',
          value: activeProjects.toString(),
          change: 12.5,
          changeType: 'increase',
          icon: Target,
          description: `${totalProjects} total projects`
        },
        {
          id: '2',
          title: 'Team Members',
          value: totalPeople.toString(),
          change: 8.2,
          changeType: 'increase',
          icon: Users,
          description: 'Active team members'
        },
        {
          id: '3',
          title: 'Revenue',
          value: `$${(totalRevenue / 1000).toFixed(1)}k`,
          change: 15.3,
          changeType: 'increase',
          icon: DollarSign,
          description: 'Total revenue this period'
        },
        {
          id: '4',
          title: 'Events',
          value: totalEvents.toString(),
          change: -2.1,
          changeType: 'decrease',
          icon: Calendar,
          description: 'Scheduled events'
        }
      ];

      setMetrics(calculatedMetrics);

      // Load recent activity (mock data for now)
      const mockActivity: RecentActivity[] = [
        {
          id: '1',
          type: 'project_created',
          description: 'New project "Blackwater Reverb" created',
          timestamp: new Date(Date.now() - 1000 * 60 * 30).toISOString(),
          user: 'Captain Jack Sparrow'
        },
        {
          id: '2',
          type: 'team_member_added',
          description: 'Elizabeth Swann joined the crew',
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 2).toISOString(),
          user: 'Will Turner'
        },
        {
          id: '3',
          type: 'invoice_paid',
          description: 'Invoice #INV-001 marked as paid',
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 4).toISOString(),
          user: 'Hector Barbossa'
        },
        {
          id: '4',
          type: 'event_scheduled',
          description: 'Main Deck Performance scheduled',
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 8).toISOString(),
          user: 'Joshamee Gibbs'
        }
      ];

      setRecentActivity(mockActivity);

      // Load top performers (mock data)
      const mockPerformers: TopPerformer[] = [
        {
          id: '1',
          name: 'Blackwater Reverb',
          category: 'Project',
          value: '$75,000',
          change: 25.5
        },
        {
          id: '2',
          name: 'Captain Jack Sparrow',
          category: 'Team Member',
          value: '98% Rating',
          change: 12.3
        },
        {
          id: '3',
          name: 'Main Deck Takeover',
          category: 'Event',
          value: '500 Attendees',
          change: 18.7
        }
      ];

      setTopPerformers(mockPerformers);

    } catch (err) {
      console.error('Error loading overview data:', err);
      setError('Failed to load analytics overview');
    } finally {
      setLoading(false);
    }
  };

  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    return `${diffDays}d ago`;
  };

  const getChangeIcon = (changeType: string) => {
    switch (changeType) {
      case 'increase': return <ArrowUpRight className="h-4 w-4 color-success" />;
      case 'decrease': return <ArrowDownRight className="h-4 w-4 color-destructive" />;
      default: return <Minus className="h-4 w-4 color-muted" />;
    }
  };

  const getChangeColor = (changeType: string) => {
    switch (changeType) {
      case 'increase': return 'color-success';
      case 'decrease': return 'color-destructive';
      default: return 'color-muted';
    }
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
          {[1, 2, 3, 4].map((i: unknown) => (
            <Card key={i} className="animate-pulse">
              <div className="h-20 bg-secondary rounded"></div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <Card title="Error">
        <div className="text-body-sm color-destructive">{error}</div>
        <Button onClick={loadOverviewData} className="mt-md">
          Retry
        </Button>
      </Card>
    );
  }

  return (
    <div className="stack-lg">
      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
        {metrics.map((metric: unknown) => {
          const IconComponent = metric.icon;
          return (
            <Card key={metric.id} className="p-lg">
              <div className="flex items-center justify-between">
                <div className="flex items-center cluster-sm">
                  <IconComponent className="h-5 w-5 color-accent" />
                  <span className="text-body-sm form-label color-muted">
                    {metric.title}
                  </span>
                </div>
                {getChangeIcon(metric.changeType)}
              </div>
              <div className="mt-sm">
                <div className="text-heading-3 text-heading-3 color-foreground">
                  {metric.value}
                </div>
                <div className="flex items-center cluster-xs mt-xs">
                  <span className={`text-body-sm form-label ${getChangeColor(metric.changeType)}`}>
                    {metric.change > 0 ? '+' : ''}{metric.change}%
                  </span>
                  <span className="text-body-sm color-muted">
                    vs last period
                  </span>
                </div>
                <div className="text-body-sm color-muted mt-xs">
                  {metric.description}
                </div>
              </div>
            </Card>
          );
        })}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-lg">
        {/* Recent Activity */}
        <Card title="Recent Activity" className="p-lg">
          <div className="stack-md">
            {recentActivity.map((activity: unknown) => (
              <div key={activity.id} className="flex items-start cluster-sm">
                <div className="flex-shrink-0">
                  <Activity className="h-4 w-4 color-accent mt-xs" />
                </div>
                <div className="flex-1 min-w-0">
                  <div className="text-body-sm color-foreground">
                    {activity.description}
                  </div>
                  <div className="flex items-center cluster-sm mt-xs">
                    <span className="text-body-sm color-muted">
                      by {activity.user}
                    </span>
                    <span className="text-body-sm color-muted/60">â€¢</span>
                    <span className="text-body-sm color-muted">
                      {formatTimestamp(activity.timestamp)}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="mt-md pt-md border-t">
            <Button className="w-full">
              View All Activity
            </Button>
          </div>
        </Card>

        {/* Top Performers */}
        <Card title="Top Performers" className="p-lg">
          <div className="stack-md">
            {topPerformers.map((performer, index) => (
              <div key={performer.id} className="flex items-center justify-between">
                <div className="flex items-center cluster-sm">
                  <div className="flex-shrink-0">
                    <Badge variant="outline">#{index + 1}</Badge>
                  </div>
                  <div>
                    <div className="text-body-sm form-label color-foreground">
                      {performer.name}
                    </div>
                    <div className="text-body-sm color-muted">
                      {performer.category}
                    </div>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-body-sm form-label color-foreground">
                    {performer.value}
                  </div>
                  <div className="text-body-sm color-success">
                    +{performer.change}%
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="mt-md pt-md border-t">
            <Button className="w-full">
              View Detailed Analytics
            </Button>
          </div>
        </Card>
      </div>

      {/* Quick Actions */}
      <Card title="Quick Actions" className="p-lg">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
          <Button variant="outline" className="flex items-center cluster-sm">
            <BarChart3 className="h-4 w-4" />
            <span>Create Dashboard</span>
          </Button>
          <Button variant="outline" className="flex items-center cluster-sm">
            <TrendingUp className="h-4 w-4" />
            <span>Generate Report</span>
          </Button>
          <Button variant="outline" className="flex items-center cluster-sm">
            <Clock className="h-4 w-4" />
            <span>Schedule Export</span>
          </Button>
        </div>
      </Card>
    </div>
  );
}

'use client';

import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
// import { z } from 'zod';
import { createClient } from '@/lib/supabase/client';
import { Card, Badge, Button, Drawer } from '@ghxstship/ui';
import { tryCatch, Result, reportError } from '@ghxstship/ui/utils/error-handling';

const ReportSchema = z.object({
  name: z.string().min(1, 'Report name is required'),
  description: z.string().optional(),
  type: z.enum(['financial', 'operational', 'performance', 'custom']).default('operational'),
  dataSource: z.enum(['projects', 'people', 'finance', 'events', 'assets', 'companies']).default('projects'),
  fields: z.array(z.string()).min(1, 'At least one field is required'),
  filters: z.record(z.any()).default({}),
  groupBy: z.array(z.string()).default([]),
  sortBy: z.object({
    field: z.string(),
    direction: z.enum(['asc', 'desc']).default('desc')
  }).optional(),
  schedule: z.object({
    enabled: z.boolean().default(false),
    frequency: z.enum(['daily', 'weekly', 'monthly', 'quarterly']).default('weekly'),
    time: z.string().default('09:00'),
    dayOfWeek: z.number().min(0).max(6).optional(),
    dayOfMonth: z.number().min(1).max(31).optional(),
    recipients: z.array(z.string().email()).default([])
  }).optional(),
  format: z.enum(['table', 'chart', 'summary']).default('table'),
  isPublic: z.boolean().default(false)
});

type ReportFormData = z.infer<typeof ReportSchema>;

interface CreateReportClientProps {
  organizationId: string;
  onSuccess?: (report: unknown) => void;
  onCancel?: () => void;
}

const REPORT_TYPES = [
  { value: 'financial', label: 'Financial Report', description: 'Revenue, expenses, budgets' },
  { value: 'operational', label: 'Operational Report', description: 'Projects, tasks, performance' },
  { value: 'performance', label: 'Performance Report', description: 'KPIs, metrics, analytics' },
  { value: 'custom', label: 'Custom Report', description: 'Custom data analysis' }
];

const DATA_SOURCES = [
  { value: 'projects', label: 'Projects', fields: ['name', 'status', 'budget', 'start_date', 'end_date', 'progress'] },
  { value: 'people', label: 'People', fields: ['name', 'email', 'role', 'department', 'hire_date', 'status'] },
  { value: 'finance', label: 'Finance', fields: ['amount', 'type', 'category', 'date', 'status', 'account'] },
  { value: 'events', label: 'Events', fields: ['title', 'type', 'date', 'location', 'attendees', 'status'] },
  { value: 'assets', label: 'Assets', fields: ['name', 'type', 'category', 'status', 'location', 'value'] },
  { value: 'companies', label: 'Companies', fields: ['name', 'industry', 'status', 'contact', 'revenue', 'employees'] }
];

const FREQUENCIES = [
  { value: 'daily', label: 'Daily' },
  { value: 'weekly', label: 'Weekly' },
  { value: 'monthly', label: 'Monthly' },
  { value: 'quarterly', label: 'Quarterly' }
];

const DAYS_OF_WEEK = [
  { value: 0, label: 'Sunday' },
  { value: 1, label: 'Monday' },
  { value: 2, label: 'Tuesday' },
  { value: 3, label: 'Wednesday' },
  { value: 4, label: 'Thursday' },
  { value: 5, label: 'Friday' },
  { value: 6, label: 'Saturday' }
];

export default function CreateReportClient({ organizationId, onSuccess, onCancel }: CreateReportClientProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedFields, setSelectedFields] = useState<string[]>([]);
  const [filterFields, setFilterFields] = useState<Array<{ field: string; operator: string; value: string }>>([]);
  const supabase = createClient();

  const {
    register,
    handleSubmit,
    formState: { errors },
    watch,
    setValue,
    reset
  } = useForm<ReportFormData>({
    resolver: zodResolver(ReportSchema),
    defaultValues: {
      name: '',
      description: '',
      type: 'operational',
      dataSource: 'projects',
      fields: [],
      filters: {},
      groupBy: [],
      format: 'table',
      isPublic: false,
      schedule: {
        enabled: false,
        frequency: 'weekly',
        time: '09:00',
        recipients: []
      }
    }
  });

  const dataSource = watch('dataSource');
  const scheduleEnabled = watch('schedule.enabled');
  const frequency = watch('schedule.frequency');

  const availableFields = DATA_SOURCES.find(ds => ds.value === dataSource)?.fields || [];

  const toggleField = (field: string) => {
    const newFields = selectedFields.includes(field)
      ? selectedFields.filter(f => f !== field)
      : [...selectedFields, field];

    setSelectedFields(newFields);
    setValue('fields', newFields);
  };

  const addFilter = () => {
    setFilterFields([...filterFields, { field: availableFields[0] || '', operator: 'equals', value: '' }]);
  };

  const removeFilter = (index: number) => {
    const newFilters = filterFields.filter((_, i) => i !== index);
    setFilterFields(newFilters);

    const filtersObject = newFilters.reduce((acc, filter, i) => {
      acc[`filter_${i}`] = { field: filter.field, operator: filter.operator, value: filter.value };
      return acc;
    }, {} as Record<string, unknown>);

    setValue('filters', filtersObject);
  };

  const updateFilter = (index: number, key: string, value: string) => {
    const newFilters = [...filterFields];
    newFilters[index] = { ...newFilters[index], [key]: value };
    setFilterFields(newFilters);

    const filtersObject = newFilters.reduce((acc, filter, i) => {
      acc[`filter_${i}`] = { field: filter.field, operator: filter.operator, value: filter.value };
      return acc;
    }, {} as Record<string, unknown>);

    setValue('filters', filtersObject);
  };

  const onSubmit = async (data: ReportFormData) => {
    setIsSubmitting(true);
    try {
      const reportData = {
        ...data,
        definition: {
          type: data.type,
          dataSource: data.dataSource,
          fields: data.fields,
          filters: data.filters,
          groupBy: data.groupBy,
          sortBy: data.sortBy,
          format: data.format
        }
      };

      const responseResult = await tryCatch(async () => fetch('/api/v1/analytics/reports', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-org-id': organizationId
        },
        body: JSON.stringify(reportData));
if (!responseResult.success) {
  reportError(responseResult.error);
  throw new Error(responseResult.error.message);
}
const response = responseResult.data;
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to create report');
      }

      const result = await response.json();
      onSuccess?.(result.report);
      reset();
    } catch (error) {
      console.error('Error creating report:', error);
      alert(error instanceof Error ? error.message : 'Failed to create report');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Drawer open={true} onClose={onCancel || (() => {})} title="Create Report">
      <div className="flex flex-col h-full">
        <div className="flex items-center justify-between p-lg border-b">
          <div>
            <h2 className="text-body text-heading-4">Create Report</h2>
            <p className="text-body-sm color-muted">Build a custom analytics report</p>
          </div>
          <Button onClick={onCancel}>
            <X className="h-4 w-4" />
          </Button>
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="flex-1 flex flex-col">
          <div className="flex-1 p-lg stack-lg overflow-y-auto">
            {/* Basic Information */}
            <div className="stack-md">
              <h3 className="text-body-sm form-label">Basic Information</h3>

              <div>
                <label className="block text-body-sm form-label mb-xs">Report Name</label>
                <input
                  {...register('name')}
                  className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                  placeholder="Enter report name"
                />
                {errors.name && (
                  <p className="text-body-sm color-destructive mt-xs">{errors.name.message}</p>
                )}
              </div>

              <div>
                <label className="block text-body-sm form-label mb-xs">Description</label>
                <textarea
                  {...register('description')}
                  rows={3}
                  className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                  placeholder="Describe the purpose of this report"
                />
              </div>

              <div className="grid grid-cols-2 gap-md">
                <div>
                  <label className="block text-body-sm form-label mb-xs">Report Type</label>
                  <select
                    {...register('type')}
                    className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                  >
                    {REPORT_TYPES.map((type: unknown) => (
                      <option key={type.value} value={type.value}>
                        {type.label}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-body-sm form-label mb-xs">Data Source</label>
                  <select
                    {...register('dataSource')}
                    className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                  >
                    {DATA_SOURCES.map((source: unknown) => (
                      <option key={source.value} value={source.value}>
                        {source.label}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="flex items-center cluster-sm">
                <input
                  {...register('isPublic')}
                  type="checkbox"
                  className="rounded border-border color-accent focus:ring-primary"
                />
                <label className="text-body-sm form-label">Make report public</label>
              </div>
            </div>

            {/* Fields Selection */}
            <div className="stack-md">
              <h3 className="text-body-sm form-label">Report Fields</h3>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-sm">
                {availableFields.map((field: unknown) => (
                  <label key={field} className="flex items-center cluster-sm p-sm border border-border rounded cursor-pointer hover:bg-secondary/30">
                    <input
                      type="checkbox"
                      checked={selectedFields.includes(field)}
                      onChange={(e: React.ChangeEvent<HTMLInputElement>) => toggleField(field)}
                      className="rounded border-border color-accent focus:ring-primary"
                    />
                    <span className="text-body-sm capitalize">{field.replace('_', ' ')}</span>
                  </label>
                ))}
              </div>
              {errors.fields && (
                <p className="text-body-sm color-destructive">{errors.fields.message}</p>
              )}
            </div>

            {/* Filters */}
            <div className="stack-md">
              <div className="flex items-center justify-between">
                <h3 className="text-body-sm form-label">Filters</h3>
                <Button
                  type="button"
                  variant="outline"

                  onClick={addFilter}
                >
                  <Plus className="h-4 w-4 mr-xs" />
                  Add Filter
                </Button>
              </div>

              {filterFields.map((filter, index) => (
                <div key={index} className="flex items-center cluster-sm p-sm border rounded">
                  <select
                    value={filter.field}
                    onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateFilter(index, 'field', e.target.value)}
                    className="flex-1  px-md py-xs border rounded text-body-sm"
                  >
                    {availableFields.map((field: unknown) => (
                      <option key={field} value={field}>
                        {field.replace('_', ' ')}
                      </option>
                    ))}
                  </select>

                  <select
                    value={filter.operator}
                    onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateFilter(index, 'operator', e.target.value)}
                    className=" px-md py-xs border rounded text-body-sm"
                  >
                    <option value="equals">Equals</option>
                    <option value="contains">Contains</option>
                    <option value="greater_than">Greater Than</option>
                    <option value="less_than">Less Than</option>
                  </select>

                  <input
                    value={filter.value}
                    onChange={(e: React.ChangeEvent<HTMLInputElement>) => updateFilter(index, 'value', e.target.value)}
                    className="flex-1  px-md py-xs border rounded text-body-sm"
                    placeholder="Filter value"
                  />

                  <Button
                    type="button"
                    variant="ghost"

                    onClick={() => removeFilter(index)}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              ))}
            </div>

            {/* Scheduling */}
            <div className="stack-md">
              <div className="flex items-center cluster-sm">
                <input
                  {...register('schedule.enabled')}
                  type="checkbox"
                  className="rounded border-border color-accent focus:ring-primary"
                />
                <label className="text-body-sm form-label">Enable scheduled delivery</label>
              </div>

              {scheduleEnabled && (
                <div className="pl-lg stack-md border-l-2 border-primary/20">
                  <div className="grid grid-cols-2 gap-md">
                    <div>
                      <label className="block text-body-sm form-label mb-xs">Frequency</label>
                      <select
                        {...register('schedule.frequency')}
                        className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                      >
                        {FREQUENCIES.map((freq: unknown) => (
                          <option key={freq.value} value={freq.value}>
                            {freq.label}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div>
                      <label className="block text-body-sm form-label mb-xs">Time</label>
                      <input
                        {...register('schedule.time')}
                        type="time"
                        className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                      />
                    </div>
                  </div>

                  {frequency === 'weekly' && (
                    <div>
                      <label className="block text-body-sm form-label mb-xs">Day of Week</label>
                      <select
                        {...register('schedule.dayOfWeek', { valueAsNumber: true })}
                        className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                      >
                        {DAYS_OF_WEEK.map((day: unknown) => (
                          <option key={day.value} value={day.value}>
                            {day.label}
                          </option>
                        ))}
                      </select>
                    </div>
                  )}

                  {frequency === 'monthly' && (
                    <div>
                      <label className="block text-body-sm form-label mb-xs">Day of Month</label>
                      <input
                        {...register('schedule.dayOfMonth', { valueAsNumber: true })}
                        type="number"
                        min="1"
                        max="31"
                        className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                        placeholder="1-31"
                      />
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>

          <div className="p-lg border-t bg-secondary/30 flex justify-end cluster-sm">
            <Button type="button" variant="outline" onClick={onCancel}>
              Cancel
            </Button>
            <Button type="submit" disabled={isSubmitting}>
              {isSubmitting ? 'Creating...' : 'Create Report'}
            </Button>
          </div>
        </form>
      </div>
    </Drawer>
  );
}

'use client';


import { Plus, Edit3, Trash2, Play, Download, Search, BarChart3, PieChart, LineChart, Table, FileText } from "lucide-react";
import { useState, useEffect, ChangeEvent, FormEvent } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import { createBrowserClient } from '@ghxstship/auth';
import { tryCatch, reportError } from '@ghxstship/ui/utils/error-handling';

interface ReportField {
  id: string;
  name: string;
  type: 'string' | 'number' | 'date' | 'boolean';
  source: string;
  aggregation?: 'sum' | 'avg' | 'count' | 'min' | 'max';
}

interface ReportFilter {
  id: string;
  field: string;
  operator: 'equals' | 'contains' | 'greater_than' | 'less_than' | 'between';
  value: string | number | boolean | null;
}

interface ReportSchedule {
  enabled: boolean;
  frequency: 'none' | 'daily' | 'weekly' | 'monthly';
  time: string;
  recipients: string[];
}

interface ApiReport {
  id: string;
  name: string;
  description?: string;
  type: Report['type'];
  visualization?: {
    chartType?: Report['chartType'];
  };
  query?: {
    fields?: string[];
    filters?: Record<string, unknown>;
  };
  schedule?: {
    frequency: 'none' | 'daily' | 'weekly' | 'monthly';
    time?: string;
    recipients?: string[];
  };
  is_public: boolean;
  last_run_at?: string;
  created_at: string;
  updated_at: string;
  created_by: string;
}

interface Report {
  id: string;
  organizationId: string;
  name: string;
  description?: string;
  type: 'table' | 'chart' | 'summary';
  chartType?: 'bar' | 'line' | 'pie' | 'area';
  fields: ReportField[];
  filters: ReportFilter[];
  schedule?: ReportSchedule;
  isPublic: boolean;
  status: 'draft' | 'active' | 'paused';
  lastRun?: string;
  nextRun?: string;
  createdAt: string;
  updatedAt: string;
  createdBy: string;
}

interface ReportsClientProps {
