  const createReport = async (reportData: Partial<Report>) => {
    try {
      const responseResult = await tryCatch(async () => {
        const res = await fetch('/api/v1/analytics/reports', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'x-org-id': organizationId,
          },
          body: JSON.stringify({
            name: reportData.name,
            description: reportData.description,
            type: reportData.type || 'table',
            dataSource: 'projects', // Default data source
            query: {
              fields: ['name', 'status', 'created_at'],
              filters: {},
              orderBy: [{ field: 'created_at', direction: 'desc' }],
            },
            visualization: reportData.chartType
              ? {
                  chartType: reportData.chartType,
                  xAxis: 'name',
                  yAxis: 'status',
                }
              : undefined,
            schedule: {
              frequency: 'none',
            },
            isPublic: false,
          }),
        });
        return res;
      });
if (!responseResult.success) {
  reportError(responseResult.error);
  throw new Error(responseResult.error.message);
}
const response = responseResult.data;

      if (!response.ok) {
        throw new Error('Failed to create report');
      }

      const data = (await response.json()) as { report: ApiReport };

      // Add to local state
      const newReport: Report = {
        id: data.report.id,
        organizationId,
        name: data.report.name,
        description: data.report.description,
        type: data.report.type || 'table',
        chartType: data.report.visualization?.chartType,
        fields: data.report.query?.fields?.map((field: string, index: number) => ({
          id: `field_${index}`,
