                  <div>
                    <h3 className="text-heading-4 color-foreground">{report.name}</h3>
                    <p className="text-body-sm color-muted mt-xs">{report.description}</p>
                  </div>
                </div>
                <Badge variant="outline" className="uppercase">
                  {report.status}
                </Badge>
              </div>

              <div className="stack-sm mb-md">
                <div className="flex items-center justify-between text-body-sm">
                  <span className="color-muted">Type:</span>
                  <span className="form-label capitalize">{report.type}</span>
                </div>

                <div className="flex items-center justify-between text-body-sm">
                  <span className="color-muted">Fields:</span>
                  <span className="form-label">{report.fields.length}</span>
                </div>

                <div className="flex items-center justify-between text-body-sm">
                  <span className="color-muted">Filters:</span>
                  <span className="form-label">{report.filters.length}</span>
                </div>

                {report.schedule?.enabled && (
                  <div className="flex items-center justify-between text-body-sm">
                    <span className="color-muted">Schedule:</span>
                      <Badge variant="outline">
                        {report.schedule.frequency}
                      </Badge>
                    </div>
                  )}

                {report.lastRun && (
                  <div className="flex items-center justify-between text-body-sm">
                    <span className="color-muted">Last Run:</span>
                    <span className="text-body-sm color-muted">
                      {new Date(report.lastRun).toLocaleDateString()}
                    </span>
                  </div>
                )}

                <p className="text-body-sm color-muted mt-sm">
                  Created {new Date(report.createdAt).toLocaleDateString()}
                </p>
              </div>

              <div className="flex items-center justify-between mt-md pt-md border-t border-border">
                <div className="flex items-center cluster-sm">
                  <Button variant="ghost" size="sm" onClick={() => runReport(report.id)}>
                    <Play className="h-3 w-3 mr-xs" />
                    Run
                  </Button>
                  <Button variant="ghost" size="sm">
                    <Edit3 className="h-3 w-3 mr-xs" />
                    Edit
                  </Button>
                  <Button variant="ghost" size="sm">
                    <Download className="h-3 w-3 mr-xs" />
                    Export
                  </Button>
                </div>

                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => deleteReport(report.id)}
                >
                  <Trash2 className="h-3 w-3" />
                </Button>
              </div>
            </Card>
            );
          })}
        </div>
      ) : (
        <Card className="p-xl text-center">
            <FileText className="h-12 w-12 color-muted mx-auto mb-md" />
            <h3 className="text-body form-label color-foreground mb-sm">No reports found</h3>
          <p className="color-muted mb-md">Create your first report to get started with analytics.</p>
          <Button onClick={() => setShowCreateForm(true)}>
            <Plus className="h-4 w-4 mr-sm" />
            Create Report
          </Button>
        </Card>
      )}

      {/* Create Report Form */}
      {showCreateForm && (
        <div className="fixed inset-0 bg-foreground/30 backdrop-blur-sm flex items-center justify-center z-50">
          <Card className="w-full max-w-2xl p-lg max-h-[90vh] overflow-y-auto">
            <h3 className="text-body text-heading-4 mb-md">Create New Report</h3>
            <form
              onSubmit={(e: FormEvent<HTMLFormElement>) => {
                e.preventDefault();
                const formData = new FormData(e.currentTarget);
                const reportData = {
                  name: safeString(formData.get('name')),
                  description: safeString(formData.get('description')),
                  type: safeString(formData.get('type')) as Report['type'],
                  chartType: safeString(formData.get('chartType')) as Report['chartType']
                };
                createReport({
                  ...reportData,
                  chartType: reportData.chartType || undefined,
                  description: reportData.description || undefined
                });
              }}
            >
              <div className="stack-md">
                <div>
                  <label className="block text-body-sm form-label color-foreground mb-xs">
                    Report Name
                  </label>
                  <input
                    name="name"
                    type="text"
                    required
                    className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                    placeholder="e.g., Monthly Revenue Report"
                  />
                </div>

                <div>
                  <label className="block text-body-sm form-label color-foreground mb-xs">
                    Description
                  </label>
                  <textarea
                    name="description"
                    rows={3}
                    className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                    placeholder="Brief description of this report..."
                  />
                </div>

                <div className="grid grid-cols-2 gap-md">
                  <div>
                    <label className="block text-body-sm form-label color-foreground mb-xs">
                      Report Type
                    </label>
                    <select
                      name="type"
                      required
                      className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                    >
                      <option value="table">Table</option>
                      <option value="chart">Chart</option>
                      <option value="summary">Summary</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-body-sm form-label color-foreground mb-xs">
                      Chart Type (if applicable)
                    </label>
                    <select
                      name="chartType"
                      className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                    >
                      <option value="" className="color-muted">All Types</option>
                      <option value="bar">Bar Chart</option>
                      <option value="line">Line Chart</option>
                      <option value="pie">Pie Chart</option>
                      <option value="area">Area Chart</option>
                    </select>
                  </div>
                </div>
              </div>

              <div className="flex items-center justify-end cluster-sm mt-lg">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setShowCreateForm(false)}
                >
                  Cancel
                </Button>
                <Button type="submit">
                  Create Report
                </Button>
              </div>
            </form>
          </Card>
        </div>
      )}
    </div>
  );
}

