  };

  const handleBulkDelete = async () => {
    try {
      await Promise.all(selectedAssets.map(id => assetsService.deleteAsset(id)));
      await loadAssets();
      setSelectedAssets([]);
    } catch (error) {
      console.error('Error bulk deleting assets:', error);
    }
  };

  const handleExport = async (format: 'csv' | 'json' | 'excel') => {
    try {
      const data = selectedAssets.length > 0 
        ? assets.filter(asset => selectedAssets.includes(asset.id))
        : assets;

      const exportData = data.map(asset => ({
        Name: asset.name,
        Description: asset.description,
        Category: asset.category,
        Type: asset.type,
        Status: asset.status,
        SKU: asset.sku,
        Location: asset.location,
        'Current Value': asset.current_value,
        'Created At': new Date(asset.created_at).toLocaleDateString()
      }));

      if (format === 'csv') {
        const csv = [
          Object.keys(exportData[0]).join(','),
          ...exportData.map(row => Object.values(row).join(','))
        ].join('\n');

        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `assets-${new Date().toISOString().split('T')[0]}.csv`;
        a.click();
        URL.revokeObjectURL(url);
      } else if (format === 'json') {
        const json = JSON.stringify(exportData, null, 2);
        const blob = new Blob([json], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `assets-${new Date().toISOString().split('T')[0]}.json`;
        a.click();
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Error exporting assets:', error);
    }
  };

  const getStatusBadge = (status: Asset['status']) => {
    switch (status) {
      case 'available':
        return <Badge variant="success">Available</Badge>;
      case 'in_use':
        return <Badge variant="warning">In Use</Badge>;
      case 'under_maintenance':
        return <Badge variant="secondary">Maintenance</Badge>;
      case 'damaged':
        return <Badge variant="destructive">Damaged</Badge>;
      case 'missing':
        return <Badge variant="destructive">Missing</Badge>;
      case 'retired':
        return <Badge variant="outline">Retired</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const formatCurrency = (value?: number) => {
    if (!value) return 'N/A';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(value);
  };

  const filteredAssets = assets.filter(asset => {
    const matchesSearch = !searchQuery || 
      asset.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      asset.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      asset.sku?.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesCategory = !filters.category || asset.category === filters.category;
    const matchesType = !filters.type || asset.type === filters.type;
    const matchesStatus = !filters.status || asset.status === filters.status;
    const matchesLocation = !filters.location || asset.location?.toLowerCase().includes(filters.location.toLowerCase());

    return matchesSearch && matchesCategory && matchesType && matchesStatus && matchesLocation;
  });

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-heading-3 font-anton uppercase">Assets Management</h1>
            <p className="text-body-sm color-muted">Loading asset data...</p>
          </div>
        </div>
        <Card>
          <div className="p-xl text-center color-muted">Loading assets data...</div>
        </Card>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 font-anton uppercase">Assets Management</h1>
          <p className="text-body-sm color-muted">Comprehensive asset inventory, tracking, and management system</p>
        </div>
        <div className="flex items-center gap-sm">
          {selectedAssets.length > 0 && (
            <>
              <Button variant="outline" onClick={handleBulkDelete}>
                <Trash2 className="w-4 h-4 mr-2" />
                Delete ({selectedAssets.length})
              </Button>
              <Button variant="outline" onClick={() => handleExport('csv')}>
                <Download className="w-4 h-4 mr-2" />
                Export Selected
              </Button>
            </>
          )}
          <Button onClick={() => setShowCreateDrawer(true)}>
            <Plus className="w-4 h-4 mr-2" />
            Add Asset
          </Button>
        </div>
      </div>

      {/* Statistics Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-md">
        <Card>
          <div className="p-md">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm form-label color-muted">Total Assets</p>
                <p className="text-heading-3">{stats.total}</p>
              </div>
              <Package className="w-8 h-8 color-accent" />
            </div>
          </div>
        </Card>
        <Card>
          <div className="p-md">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm form-label color-muted">Available</p>
                <p className="text-heading-3">{stats.available}</p>
              </div>
              <TrendingUp className="w-8 h-8 color-success" />
            </div>
          </div>
        </Card>
        <Card>
          <div className="p-md">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm form-label color-muted">In Use</p>
                <p className="text-heading-3">{stats.in_use}</p>
              </div>
              <Settings className="w-8 h-8 color-warning" />
            </div>
          </div>
        </Card>
        <Card>
          <div className="p-md">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm form-label color-muted">Maintenance</p>
                <p className="text-heading-3">{stats.under_maintenance}</p>
              </div>
              <AlertTriangle className="w-8 h-8 color-warning" />
            </div>
          </div>
        </Card>
        <Card>
          <div className="p-md">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm form-label color-muted">Total Value</p>
                <p className="text-heading-3">${stats.total_value.toLocaleString()}</p>
              </div>
              <BarChart3 className="w-8 h-8 color-secondary" />
            </div>
          </div>
        </Card>
      </div>

      {/* Controls */}
      <Card>
        <div className="p-md">
          <div className="flex items-center justify-between mb-md">
            <h3 className="text-heading-4">Asset Inventory ({filteredAssets.length})</h3>
            <div className="flex items-center gap-sm">
              <Button 
                variant={viewMode === 'grid' ? 'default' : 'outline'} 
                size="sm"
                onClick={() => setViewMode('grid')}
              >
                Grid
              </Button>
              <Button 
                variant={viewMode === 'list' ? 'default' : 'outline'} 
                size="sm"
                onClick={() => setViewMode('list')}
              >
                List
              </Button>
              <Button 
                variant={viewMode === 'table' ? 'default' : 'outline'} 
                size="sm"
                onClick={() => setViewMode('table')}
              >
                Table
              </Button>
              <Button variant="outline" size="sm" onClick={() => handleExport('csv')}>
                <Download className="w-4 h-4 mr-2" />
                Export
              </Button>
            </div>
          </div>

          {/* Search and Filters */}
          <div className="flex items-center gap-sm mb-md">
            <div className="flex-1">
              <input
                type="text"
                placeholder="Search assets..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full px-sm py-xs border rounded"
              />
            </div>
            <select
              value={filters.category || ''}
              onChange={(e) => setFilters(prev => ({ ...prev, category: e.target.value || undefined }))}
              className="px-sm py-xs border rounded"
            >
              <option value="">All Categories</option>
              <option value="site_infrastructure">Site Infrastructure</option>
              <option value="artist_technical">Artist Technical</option>
              <option value="artist_hospitality">Artist Hospitality</option>
              <option value="site_services">Site Services</option>
            </select>
            <select
              value={filters.status || ''}
              onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value || undefined }))}
              className="px-sm py-xs border rounded"
            >
              <option value="">All Statuses</option>
              <option value="available">Available</option>
              <option value="in_use">In Use</option>
              <option value="under_maintenance">Maintenance</option>
              <option value="damaged">Damaged</option>
            </select>
          </div>

          {/* Assets Display */}
          {viewMode === 'grid' && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
              {filteredAssets.map(asset => (
                <Card key={asset.id} className="p-md">
                  <div className="stack-sm">
                    <div className="flex items-start justify-between">
                      <div className="flex items-start gap-sm">
                        <input
                          type="checkbox"
                          checked={selectedAssets.includes(asset.id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedAssets(prev => [...prev, asset.id]);
                            } else {
                              setSelectedAssets(prev => prev.filter(id => id !== asset.id));
                            }
                          }}
                        />
                        <div className="flex-1">
                          <h4 className="form-label">{asset.name}</h4>
                          <p className="text-body-sm color-muted">{asset.description}</p>
                        </div>
                      </div>
                      {getStatusBadge(asset.status)}
                    </div>

                    <div className="grid grid-cols-2 gap-sm text-body-sm">
                      <div>
                        <span className="color-muted">SKU:</span> {asset.sku || 'N/A'}
                      </div>
                      <div>
                        <span className="color-muted">Type:</span> {asset.type}
                      </div>
                      <div>
                        <span className="color-muted">Location:</span> {asset.location || 'N/A'}
                      </div>
                      <div>
                        <span className="color-muted">Value:</span> {formatCurrency(asset.current_value)}
                      </div>
                    </div>

                    <div className="flex items-center justify-between pt-sm border-t">
                      <span className="text-body-sm color-muted">
                        Created {new Date(asset.created_at).toLocaleDateString()}
                      </span>
                      <div className="flex items-center gap-xs">
                        <Button variant="ghost" size="sm">
                          <Eye className="w-4 h-4" />
                        </Button>
                        <Button variant="ghost" size="sm" onClick={() => setEditingAsset(asset)}>
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button variant="ghost" size="sm" onClick={() => handleDeleteAsset(asset.id)}>
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}

          {viewMode === 'list' && (
            <div className="stack-xs">
              {filteredAssets.map(asset => (
                <div key={asset.id} className="flex items-center justify-between p-sm border rounded hover:bg-muted">
                  <div className="flex items-center gap-sm">
                    <input
                      type="checkbox"
                      checked={selectedAssets.includes(asset.id)}
                      onChange={(e) => {
                        if (e.target.checked) {
                          setSelectedAssets(prev => [...prev, asset.id]);
                        } else {
                          setSelectedAssets(prev => prev.filter(id => id !== asset.id));
                        }
                      }}
                    />
                    <Package className="w-8 h-8 color-muted" />
                    <div>
                      <h4 className="form-label">{asset.name}</h4>
                      <p className="text-body-sm color-muted">{asset.category} â€¢ {asset.location}</p>
                    </div>
                  </div>
                  <div className="flex items-center gap-sm">
                    {getStatusBadge(asset.status)}
                    <span className="text-body-sm font-medium">{formatCurrency(asset.current_value)}</span>
                    <div className="flex items-center gap-xs">
                      <Button variant="ghost" size="sm">
                        <Eye className="w-4 h-4" />
                      </Button>
                      <Button variant="ghost" size="sm" onClick={() => setEditingAsset(asset)}>
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button variant="ghost" size="sm" onClick={() => handleDeleteAsset(asset.id)}>
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {viewMode === 'table' && (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b">
                    <th className="text-left p-sm">
                      <input
                        type="checkbox"
                        checked={selectedAssets.length === filteredAssets.length && filteredAssets.length > 0}
                        onChange={(e) => {
                          if (e.target.checked) {
                            setSelectedAssets(filteredAssets.map(asset => asset.id));
                          } else {
                            setSelectedAssets([]);
                          }
                        }}
                      />
                    </th>
                    <th className="text-left p-sm">Name</th>
                    <th className="text-left p-sm">Category</th>
                    <th className="text-left p-sm">Type</th>
                    <th className="text-left p-sm">Status</th>
                    <th className="text-left p-sm">Location</th>
                    <th className="text-left p-sm">Value</th>
                    <th className="text-left p-sm">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAssets.map(asset => (
                    <tr key={asset.id} className="border-b hover:bg-muted">
                      <td className="p-sm">
                        <input
                          type="checkbox"
                          checked={selectedAssets.includes(asset.id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedAssets(prev => [...prev, asset.id]);
                            } else {
                              setSelectedAssets(prev => prev.filter(id => id !== asset.id));
                            }
                          }}
                        />
                      </td>
                      <td className="p-sm">
                        <div>
                          <div className="form-label">{asset.name}</div>
                          <div className="text-body-sm color-muted">{asset.sku}</div>
                        </div>
                      </td>
                      <td className="p-sm">{asset.category}</td>
                      <td className="p-sm">{asset.type}</td>
                      <td className="p-sm">{getStatusBadge(asset.status)}</td>
                      <td className="p-sm">{asset.location || 'N/A'}</td>
                      <td className="p-sm">{formatCurrency(asset.current_value)}</td>
                      <td className="p-sm">
                        <div className="flex items-center gap-xs">
                          <Button variant="ghost" size="sm">
                            <Eye className="w-4 h-4" />
                          </Button>
                          <Button variant="ghost" size="sm" onClick={() => setEditingAsset(asset)}>
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button variant="ghost" size="sm" onClick={() => handleDeleteAsset(asset.id)}>
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {filteredAssets.length === 0 && !loading && (
            <div className="text-center py-xl color-muted">
              <Package className="w-12 h-12 mx-auto mb-md opacity-50" />
              <h3 className="text-heading-4 mb-sm">No Assets Found</h3>
              <p className="text-body-sm mb-md">
                {searchQuery || Object.keys(filters).length > 0 
                  ? 'No assets match your current filters.' 
                  : 'Start by adding your first asset to the inventory.'
                }
              </p>
              <Button onClick={() => setShowCreateDrawer(true)}>
                <Plus className="w-4 h-4 mr-2" />
                Add Asset
              </Button>
            </div>
          )}
        </div>
      </Card>
    </div>
  );
}

'use client';

import { Plus, Package, TrendingUp, AlertTriangle, Settings, BarChart3, Shield, Search, RotateCcw, QrCode, FileCheck, Download, Upload, Eye, Edit, Trash2 } from "lucide-react";
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Button, Badge } from '@ghxstship/ui';
import { AssetsService } from './lib/service';
import type { Asset, AssetFilters, AssetFormData } from './types';

interface AssetsClientProps {
  orgId: string;
  userId?: string;
  userEmail?: string;
}

type AssetRecord = Asset & {
  id: string;
  [key: string]: any;
};

export default function AssetsClient({ orgId, userId, userEmail }: AssetsClientProps) {
  const t = useTranslations('assets');
  const supabase = useMemo(() => createBrowserClient(), []);
  const assetsService = useMemo(() => new AssetsService(), []);

  const [assets, setAssets] = useState<AssetRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedAssets, setSelectedAssets] = useState<string[]>([]);
  const [viewMode, setViewMode] = useState<'grid' | 'list' | 'table'>('grid');
  const [searchQuery, setSearchQuery] = useState('');
  const [filters, setFilters] = useState<AssetFilters>({});
  const [showCreateDrawer, setShowCreateDrawer] = useState(false);
  const [editingAsset, setEditingAsset] = useState<AssetRecord | null>(null);

  const [stats, setStats] = useState({
    total: 0,
    available: 0,
    in_use: 0,
    under_maintenance: 0,
    total_value: 0
  });

  const loadAssets = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const [assetsData, statsData] = await Promise.all([
        assetsService.getAssets(orgId, { ...filters, search: searchQuery }),
        assetsService.getAssetStats(orgId)
      ]);

      setAssets(assetsData as AssetRecord[]);
      setStats(statsData);
    } catch (err) {
      console.error('Error loading assets:', err);
      setError('Failed to load assets');
      // Use demo data as fallback
      setAssets(generateDemoAssets());
      setStats({
        total: 5,
        available: 2,
        in_use: 2,
        under_maintenance: 1,
        total_value: 180000
      });
    } finally {
      setLoading(false);
    }
  }, [orgId, assetsService, filters, searchQuery]);

  useEffect(() => {
    loadAssets();
  }, [loadAssets]);

  const generateDemoAssets = (): AssetRecord[] => [
    {
      id: '1',
      organization_id: orgId,
      name: 'Main Stage Rigging System',
      description: 'Professional stage rigging and truss system for main performance area',
      category: 'site_infrastructure',
      type: 'fixed',
      status: 'in_use',
      sku: 'STAGE-RIG-001',
      current_value: 45000,
      location: 'Main Deck',
      created_at: '2024-01-01T10:00:00Z',
      updated_at: '2024-01-01T10:00:00Z'
    },
    {
      id: '2',
      organization_id: orgId,
      name: 'Professional Sound System',
      description: 'High-end PA system with mixing console and monitors',
      category: 'artist_technical',
      type: 'rentable',
      status: 'available',
      sku: 'AUDIO-PA-002',
      current_value: 25000,
      location: 'Equipment Storage',
      created_at: '2024-01-02T14:30:00Z',
      updated_at: '2024-01-02T14:30:00Z'
    },
    {
      id: '3',
      organization_id: orgId,
      name: 'LED Video Wall Panels',
      description: 'Modular LED display panels for backdrop and visual effects',
      category: 'artist_technical',
      type: 'rentable',
      status: 'under_maintenance',
      sku: 'LED-WALL-003',
      current_value: 35000,
      location: 'Tech Workshop',
      created_at: '2024-01-03T09:15:00Z',
      updated_at: '2024-01-03T09:15:00Z'
    },
    {
      id: '4',
      organization_id: orgId,
      name: 'Catering Services',
      description: 'Full-service catering for crew and artist hospitality',
      category: 'artist_hospitality',
      type: 'service',
      status: 'available',
      sku: 'CATER-SRV-004',
      location: 'Galley',
      created_at: '2024-01-04T16:45:00Z',
      updated_at: '2024-01-04T16:45:00Z'
    },
    {
      id: '5',
      organization_id: orgId,
      name: 'Generator - 500kW',
      description: 'Primary power generation unit for event infrastructure',
      category: 'site_services',
      type: 'fixed',
      status: 'in_use',
      sku: 'GEN-500-005',
      current_value: 75000,
      location: 'Power Station',
      created_at: '2024-01-05T11:20:00Z',
      updated_at: '2024-01-05T11:20:00Z'
    }
  ];

  const handleCreateAsset = async (data: AssetFormData) => {
    try {
      const newAsset = await assetsService.createAsset(data, orgId);
      await loadAssets();
      setShowCreateDrawer(false);
    } catch (error) {
      console.error('Error creating asset:', error);
    }
  };

  const handleUpdateAsset = async (id: string, data: Partial<AssetFormData>) => {
    try {
      await assetsService.updateAsset(id, data);
      await loadAssets();
      setEditingAsset(null);
    } catch (error) {
      console.error('Error updating asset:', error);
    }
  };

  const handleDeleteAsset = async (id: string) => {
    try {
      await assetsService.deleteAsset(id);
      await loadAssets();
    } catch (error) {
      console.error('Error deleting asset:', error);
    }
  };

  const handleBulkDelete = async () => {
    try {
      await Promise.all(selectedAssets.map(id => assetsService.deleteAsset(id)));
      await loadAssets();
      setSelectedAssets([]);
    } catch (error) {
      console.error('Error bulk deleting assets:', error);
    }
  };

  const handleExport = async (format: 'csv' | 'json' | 'excel') => {
    try {
      const data = selectedAssets.length > 0 
        ? assets.filter(asset => selectedAssets.includes(asset.id))
        : assets;

      const exportData = data.map(asset => ({
        Name: asset.name,
        Description: asset.description,
        Category: asset.category,
        Type: asset.type,
        Status: asset.status,
        SKU: asset.sku,
        Location: asset.location,
        'Current Value': asset.current_value,
        'Created At': new Date(asset.created_at).toLocaleDateString()
      }));

      if (format === 'csv') {
        const csv = [
          Object.keys(exportData[0]).join(','),
          ...exportData.map(row => Object.values(row).join(','))
        ].join('\n');

        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `assets-${new Date().toISOString().split('T')[0]}.csv`;
        a.click();
        URL.revokeObjectURL(url);
      } else if (format === 'json') {
        const json = JSON.stringify(exportData, null, 2);
        const blob = new Blob([json], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `assets-${new Date().toISOString().split('T')[0]}.json`;
        a.click();
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Error exporting assets:', error);
    }
  };

  const getStatusBadge = (status: Asset['status']) => {
    switch (status) {
      case 'available':
        return <Badge variant="success">Available</Badge>;
      case 'in_use':
        return <Badge variant="warning">In Use</Badge>;
      case 'under_maintenance':
        return <Badge variant="secondary">Maintenance</Badge>;
      case 'damaged':
        return <Badge variant="destructive">Damaged</Badge>;
      case 'missing':
        return <Badge variant="destructive">Missing</Badge>;
      case 'retired':
        return <Badge variant="outline">Retired</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
