      case 'approved':
        return <Badge variant="success">Approved</Badge>;
      case 'ordered':
        return <Badge variant="warning">Ordered</Badge>;
      case 'delivered':
        return <Badge variant="success">Delivered</Badge>;
      case 'cancelled':
