  };

  const getPriorityBadge = (priority: AdvancingItem['priority']) => {
    switch (priority) {
      case 'urgent':
        return <Badge variant="destructive">Urgent</Badge>;
      case 'high':
        return <Badge variant="warning">High</Badge>;
      case 'medium':
        return <Badge variant="secondary">Medium</Badge>;
      case 'low':
        return <Badge variant="outline">Low</Badge>;
      default:
        return <Badge variant="outline">{priority}</Badge>;
    }
  };

  const getTypeBadge = (type: AdvancingItem['type']) => {
    switch (type) {
      case 'purchase':
        return <Badge variant="default">Purchase</Badge>;
      case 'rental':
        return <Badge variant="secondary">Rental</Badge>;
      case 'service':
        return <Badge variant="outline">Service</Badge>;
      default:
        return <Badge variant="outline">{type}</Badge>;
    }
  };

  const getCategoryColor = (category: string) => {
    const categoryInfo = ASSET_CATEGORIES.find(cat => cat.id === category);
    return categoryInfo?.color || 'bg-secondary';
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(value);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 font-anton uppercase">Asset Advancing</h1>
          <p className="text-body-sm color-muted">Request and manage asset procurement</p>
        </div>
        <div className="flex items-center gap-sm">
          <Button variant="outline" className="flex items-center gap-sm">
            <Download className="w-4 h-4" />
            Export
          </Button>
          <Button onClick={handleCreateItem} className="flex items-center gap-sm">
            <Plus className="w-4 h-4" />
            New Request
          </Button>
        </div>
      </div>

      {/* Filters */}
      <Card>
        <div className="p-md">
          <div className="flex flex-wrap items-center gap-md">
            <div className="flex-1 min-w-64">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 color-muted w-4 h-4" />
                <UnifiedInput                   placeholder="Search requests..."
                  value={searchQuery}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchQuery(e.target.value)}
                  className="pl-2xl"
                />
              </div>
            </div>
            <div className="flex items-center gap-sm">
              <select
                value={selectedCategory}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSelectedCategory(e.target.value)}
                className=" px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="all">All Categories</option>
                {ASSET_CATEGORIES.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
              <select
                value={selectedType}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSelectedType(e.target.value)}
                className=" px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="all">All Types</option>
                <option value="purchase">Purchase</option>
                <option value="rental">Rental</option>
                <option value="service">Service</option>
              </select>
              <select
                value={selectedStatus}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSelectedStatus(e.target.value)}
                className=" px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="all">All Status</option>
                <option value="requested">Requested</option>
                <option value="approved">Approved</option>
                <option value="ordered">Ordered</option>
                <option value="delivered">Delivered</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>
          </div>
        </div>
      </Card>

      {/* Items List */}
      {loading ? (
        <Card>
          <div className="p-xl text-center color-muted">Loading advancing requests...</div>
        </Card>
      ) : filteredItems.length === 0 ? (
        <Card>
          <div className="p-xl text-center color-muted">
            No advancing requests found matching your criteria.
          </div>
        </Card>
      ) : (
        <div className="stack-md">
          {filteredItems.map(item => (
            <Card key={item.id} className="hover:shadow-elevated transition-shadow">
              <div className="p-md">
                <div className="flex items-start justify-between mb-sm">
                  <div className="flex items-center gap-sm">
                    <div className={`w-3 h-3 rounded-full ${getCategoryColor(item.category)}`} />
                    <div>
                      <h3 className="text-body text-heading-4">{item.name}</h3>
                      <p className="text-body-sm color-muted">
                        Requested by {item.requestedBy} â€¢ {formatDate(item.requestedDate)}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-sm">
                    {getPriorityBadge(item.priority)}
                    {getStatusBadge(item.status)}
                    <div className="flex items-center gap-xs ml-sm">
                      <Button

                        variant="ghost"
                        onClick={() => handleEditItem(item)}
                      >
                        <Edit className="w-3 h-3" />
                      </Button>
                      <Button

                        variant="ghost"
                        onClick={() => handleDuplicateItem(item)}
                      >
                        <Copy className="w-3 h-3" />
                      </Button>
                      <Button

                        variant="ghost"
                        onClick={() => handleDeleteItem(item.id)}
                      >
                        <Trash2 className="w-3 h-3" />
                      </Button>
                    </div>
                  </div>
                </div>

                {item.description && (
                  <p className="text-body-sm color-muted mb-sm">
                    {item.description}
                  </p>
                )}

                <div className="grid grid-cols-2 md:grid-cols-4 gap-md mb-md">
                  <div>
                    <span className="text-body-sm color-muted block">Type</span>
                    {getTypeBadge(item.type)}
                  </div>
                  <div>
                    <span className="text-body-sm color-muted block">Quantity</span>
                    <span className="form-label">{item.quantity} {item.unit}</span>
                  </div>
                  <div>
                    <span className="text-body-sm color-muted block">Estimated Cost</span>
                    <span className="form-label">
                      {item.estimatedCost ? formatCurrency(item.estimatedCost) : 'TBD'}
                    </span>
                  </div>
                  <div>
                    <span className="text-body-sm color-muted block">Needed By</span>
                    <span className="form-label">
                      {item.neededBy ? formatDate(item.neededBy) : 'TBD'}
                    </span>
                  </div>
                </div>

                {item.vendor && (
                  <div className="flex items-center gap-md text-body-sm">
                    <span className="color-muted">Vendor:</span>
                    <span className="form-label">{item.vendor}</span>
                    {item.location && (
                      <>
                        <span className="color-muted">Location:</span>
                        <span>{item.location}</span>
                      </>
                    )}
                  </div>
                )}

                {item.notes && (
                  <div className="mt-sm p-sm bg-secondary rounded-md">
                    <h3 className="text-body text-heading-4">Notes</h3>
                    <p className="text-body-sm">{item.notes}</p>
                  </div>
                )}
              </div>
            </Card>
          ))}
        </div>
      )}

      {/* Request Form Drawer */}
      <Drawer
        open={showDrawer}
        onClose={() => setShowDrawer(false)}
        title={selectedItem ? 'Edit Advancing Request' : 'New Advancing Request'}

      >
        <div className="p-lg stack-md">
          <div>
            <label className="block text-body-sm form-label mb-xs">Request Name</label>
            <UnifiedInput               placeholder="Enter request name"
              defaultValue={selectedItem?.name}
            />
          </div>
          <div>
            <label className="block text-body-sm form-label mb-xs">Description</label>
            <UnifiedInput               placeholder="Enter description"
              defaultValue={selectedItem?.description}
            />
          </div>
          <div className="grid grid-cols-2 gap-md">
            <div>
              <label className="block text-body-sm form-label mb-xs">Category</label>
              <select
                defaultValue={selectedItem?.category}
                className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
              >
                {ASSET_CATEGORIES.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-body-sm form-label mb-xs">Type</label>
              <select
                defaultValue={selectedItem?.type}
                className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="purchase">Purchase</option>
                <option value="rental">Rental</option>
                <option value="service">Service</option>
              </select>
            </div>
          </div>
          <div className="grid grid-cols-3 gap-md">
            <div>
              <label className="block text-body-sm form-label mb-xs">Quantity</label>
              <UnifiedInput                 type="number"
                placeholder="1"
                defaultValue={selectedItem?.quantity}
              />
            </div>
            <div>
              <label className="block text-body-sm form-label mb-xs">Unit</label>
              <UnifiedInput                 placeholder="units"
                defaultValue={selectedItem?.unit}
              />
            </div>
            <div>
              <label className="block text-body-sm form-label mb-xs">Priority</label>
              <select
                defaultValue={selectedItem?.priority}
                className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
                <option value="urgent">Urgent</option>
              </select>
            </div>
          </div>
          <div className="grid grid-cols-2 gap-md">
            <div>
              <label className="block text-body-sm form-label mb-xs">Estimated Cost</label>
              <UnifiedInput                 type="number"
                placeholder="0.00"
                defaultValue={selectedItem?.estimatedCost}
              />
            </div>
            <div>
              <label className="block text-body-sm form-label mb-xs">Needed By</label>
              <UnifiedInput                 type="date"
                defaultValue={selectedItem?.neededBy?.split('T')[0]}
              />
            </div>
          </div>
          <div>
            <label className="block text-body-sm form-label mb-xs">Notes</label>
            <textarea
              className="w-full  px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
              rows={3}
              placeholder="Additional notes or requirements"
              defaultValue={selectedItem?.notes}
            />
          </div>
          <div className="flex gap-sm pt-md">
            <Button className="flex-1">
              {selectedItem ? 'Update Request' : 'Submit Request'}
            </Button>
            <Button variant="outline" onClick={() => setShowDrawer(false)}>
              Cancel
            </Button>
          </div>
        </div>
      </Drawer>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Badge, Button, Card } from '@ghxstship/ui';
import {
  DataActions,
  DataGrid,
  DataViewProvider,
  DashboardView,
  KanbanBoard,
  ListView,
  TimelineView,
  UniversalDrawer,
  ViewSwitcher
} from '@ghxstship/ui/components/DataViews';
import type {
  ActionConfig,
  DashboardWidget,
  DataRecord,
  DataViewConfig,
  ExportConfig,
  FieldConfig,
  FilterConfig,
  ImportConfig,
  SortConfig
} from '@ghxstship/ui/components/DataViews/types';
import type { FieldConfig as DrawerFieldConfig } from '@ghxstship/ui/components/DataViews/UniversalDrawer';
import { assetsService } from '../lib/service';
import type { AssetAssignment } from '../types';

interface AssignmentsClientProps {
  orgId: string;
}

type AssignmentRecord = DataRecord & {
  id: string;
  organization_id: string;
  asset_id: string;
  asset_name: string;
  assignee_type: AssetAssignment['assignee_type'];
  assignee_id: string;
  assignee_name: string;
  status: AssetAssignment['status'];
  assigned_by: string;
  assigned_date: string;
  expected_return_date?: string | null;
  actual_return_date?: string | null;
  location?: string | null;
  purpose?: string | null;
  condition: AssetAssignment['condition'];
  notes?: string | null;
  created_at: string;
  updated_at: string;
};

const STATUS_OPTIONS = [
  { value: 'assigned', label: 'Assigned' },
  { value: 'in_use', label: 'In Use' },
  { value: 'returned', label: 'Returned' },
  { value: 'overdue', label: 'Overdue' },
  { value: 'damaged', label: 'Damaged' }
] as const;

const ASSIGNEE_TYPES = [
  { value: 'project', label: 'Project' },
  { value: 'crew_member', label: 'Crew Member' },
  { value: 'vendor', label: 'Vendor' },
  { value: 'partner', label: 'Partner' }
] as const;

const CONDITION_OPTIONS = [
  { value: 'excellent', label: 'Excellent' },
  { value: 'good', label: 'Good' },
  { value: 'fair', label: 'Fair' },
  { value: 'poor', label: 'Poor' },
  { value: 'damaged', label: 'Damaged' }
] as const;

const toIsoString = (value: string | null | undefined): string | null => {
  if (!value) {
    return null;
  }
  if (value.length > 10) {
    return value;
  }
  const date = new Date(value);
  return Number.isNaN(date.getTime()) ? value : date.toISOString();
};

export default function AssignmentsClient({ orgId }: AssignmentsClientProps) {
  const t = useTranslations('assets.assignments');
  const realtimeClient = useMemo(() => createBrowserClient(), []);

  const [records, setRecords] = useState<AssignmentRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [formError, setFormError] = useState<string | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');
  const [selectedRecord, setSelectedRecord] = useState<AssignmentRecord | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [assigneeTypeFilter, setAssigneeTypeFilter] = useState<string>('all');
  const [sorts, setSorts] = useState<SortConfig[] | null>(null);
  const [busy, setBusy] = useState(false);

  const fieldConfig: FieldConfig[] = useMemo(() => [
    {
      key: 'asset_name',
      label: 'Asset',
      type: 'text',
      required: true,
      searchable: true,
      sortable: true
    },
    {
      key: 'assignee_name',
      label: 'Assignee',
      type: 'text',
      required: true,
      searchable: true,
      sortable: true
    },
    {
      key: 'assignee_type',
      label: 'Assignee Type',
      type: 'select',
      options: ASSIGNEE_TYPES.map(({ value, label }) => ({ value, label })),
      filterable: true,
      sortable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: STATUS_OPTIONS.map(({ value, label }) => ({ value, label })),
      filterable: true,
      sortable: true,
      render: (value: string) => {
        const variants = {
          assigned: 'secondary',
          in_use: 'success',
          returned: 'outline',
          overdue: 'destructive',
          damaged: 'destructive'
        } as const;
        const variant = variants[value as keyof typeof variants] ?? 'outline';
        return <Badge variant={variant}>{value.replace('_', ' ')}</Badge>;
      }
    },
    {
      key: 'condition',
      label: 'Condition',
      type: 'select',
      options: CONDITION_OPTIONS.map(({ value, label }) => ({ value, label })),
      filterable: true,
      sortable: true
    },
    {
      key: 'assigned_date',
      label: 'Assigned Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'expected_return_date',
      label: 'Expected Return',
      type: 'date',
      sortable: true
    },
    {
      key: 'actual_return_date',
      label: 'Actual Return',
      type: 'date',
      sortable: true
    },
    {
      key: 'location',
      label: 'Location',
      type: 'text',
      searchable: true
    },
    {
      key: 'purpose',
      label: 'Purpose',
      type: 'textarea',
      searchable: true
    },
    {
      key: 'notes',
      label: 'Notes',
      type: 'textarea'
    },
    {
      key: 'assigned_by',
      label: 'Assigned By',
      type: 'text',
      searchable: true
    }
  ], []);

  const drawerFields: DrawerFieldConfig[] = useMemo(
    () =>
      fieldConfig.map((field) => {
        const drawerType: DrawerFieldConfig['type'] =
          field.type === 'multiselect' || field.type === 'array' || field.type === 'object'
            ? 'text'
            : (field.type as DrawerFieldConfig['type']);
        return {
          key: field.key,
          label: field.label,
          type: drawerType,
          options: field.options,
          required: field.required
        } as DrawerFieldConfig;
      }),
    [fieldConfig]
  );

  const fetchAssignments = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const assignments = await assetsService.getAssetAssignments(orgId);
      const normalized = assignments.map((assignment) => ({
        id: assignment.id,
        organization_id: assignment.organization_id,
        asset_id: assignment.asset_id,
        asset_name: (assignment as unknown).assets?.name ?? assignment.asset_id,
        assignee_type: assignment.assignee_type,
        assignee_id: assignment.assignee_id,
        assignee_name: assignment.assignee_name,
        status: assignment.status,
        assigned_by: assignment.assigned_by,
        assigned_date: assignment.assigned_date,
        expected_return_date: assignment.expected_return_date ?? null,
        actual_return_date: assignment.actual_return_date ?? null,
        location: assignment.location ?? null,
        purpose: assignment.purpose ?? null,
        condition: assignment.condition,
        notes: assignment.notes ?? null,
        created_at: assignment.created_at,
        updated_at: assignment.updated_at
      }));
      setRecords(normalized);
    } catch (err) {
      console.error('Failed to load assignments', err);
      setError(t('errors.load'));
    } finally {
      setLoading(false);
    }
  }, [orgId, t]);

  useEffect(() => {
    fetchAssignments();
  }, [fetchAssignments]);

  useEffect(() => {
    const channel = realtimeClient
      .channel(`public:asset_assignments:${orgId}`)
      .on(
        'postgres_changes',
        { event: '*', schema: 'public', table: 'asset_assignments', filter: `organization_id=eq.${orgId}` },
        () => fetchAssignments()
      )
      .subscribe();

    return () => {
      channel.unsubscribe();
    };
  }, [realtimeClient, orgId, fetchAssignments]);

  const derivedRecords = useMemo(() => {
    let result = records.filter((record) => {
      const matchesSearch = searchQuery
        ? [
            record.asset_name,
            record.assignee_name,
            record.assigned_by,
            record.location ?? '',
            record.purpose ?? '',
            record.notes ?? ''
          ]
            .join(' ')
            .toLowerCase()
            .includes(searchQuery.toLowerCase())
        : true;
      const matchesStatus = statusFilter === 'all' || record.status === statusFilter;
      const matchesType = assigneeTypeFilter === 'all' || record.assignee_type === assigneeTypeFilter;
      return matchesSearch && matchesStatus && matchesType;
    });

    if (sorts && sorts.length > 0) {
      const { field, direction } = sorts[0];
      result = [...result].sort((a, b) => {
        const aValue = (a as unknown)[field];
        const bValue = (b as unknown)[field];
        if (aValue === bValue) return 0;
        if (aValue == null) return direction === 'asc' ? -1 : 1;
        if (bValue == null) return direction === 'asc' ? 1 : -1;
        if (aValue > bValue) return direction === 'asc' ? 1 : -1;
        return direction === 'asc' ? -1 : 1;
      });
    }

    return result;
  }, [records, searchQuery, statusFilter, assigneeTypeFilter, sorts]);

  const dataForViews = useMemo(
    () =>
      derivedRecords.map((record) => ({
        ...record,
        // expose normalized fields the views expect
        title: record.asset_name,
        subtitle: record.assignee_name,
        description: record.purpose,
        assignee: record.assignee_name,
        dueDate: record.expected_return_date || undefined,
        status: record.status,
        priority: record.condition,
        tags: record.location ? [record.location] : [],
        createdAt: record.created_at,
        updatedAt: record.updated_at
      })),
    [derivedRecords]
  );

  const stats = useMemo(() => {
    const total = derivedRecords.length;
    const inUse = derivedRecords.filter((record) => record.status === 'in_use').length;
    const overdue = derivedRecords.filter((record) => record.status === 'overdue').length;
    const returned = derivedRecords.filter((record) => record.status === 'returned').length;
    return { total, inUse, overdue, returned };
  }, [derivedRecords]);

  const openDrawer = useCallback((mode: 'create' | 'view' | 'edit', record?: AssignmentRecord | null) => {
    setDrawerMode(mode);
    setSelectedRecord(record ?? null);
    setDrawerOpen(true);
    setFormError(null);
  }, []);

  const closeDrawer = useCallback(() => {
    setDrawerOpen(false);
    setSelectedRecord(null);
  }, []);

  const persistAssignment = useCallback(
    async (formData: Record<string, unknown>) => {
      setBusy(true);
      setFormError(null);
      try {
        const payload: Partial<AssetAssignment> = {
          asset_id: formData.asset_id || selectedRecord?.asset_id,
          assignee_type: formData.assignee_type,
          assignee_id: formData.assignee_id || selectedRecord?.assignee_id,
          assignee_name: formData.assignee_name,
          status: formData.status,
          assigned_by: formData.assigned_by,
          assigned_date: toIsoString(formData.assigned_date) ?? new Date().toISOString(),
          expected_return_date: toIsoString(formData.expected_return_date) ?? undefined,
          actual_return_date: toIsoString(formData.actual_return_date) ?? undefined,
          location: formData.location || null,
          purpose: formData.purpose || null,
          condition: formData.condition,
          notes: formData.notes || null
        };

        if (!payload.asset_id) {
          throw new Error(t('errors.assetRequired'));
        }

        if (drawerMode === 'create') {
          await assetsService.createAssetAssignment(payload, orgId);
        } else if (drawerMode === 'edit' && selectedRecord) {
          await assetsService.updateAssetAssignment(selectedRecord.id, payload);
        }

        await fetchAssignments();
        closeDrawer();
      } catch (err: unknown) {
        console.error('Failed to save assignment', err);
        setFormError(err?.message ?? t('errors.save'));
      } finally {
        setBusy(false);
      }
    },
    [drawerMode, selectedRecord, orgId, fetchAssignments, closeDrawer, t]
  );

  const handleDelete = useCallback(
    async (ids: string[]) => {
      if (!ids.length) return;
      setBusy(true);
      setError(null);
      try {
        await Promise.all(ids.map((id) => assetsService.deleteAssetAssignment(id)));
        await fetchAssignments();
      } catch (err) {
        console.error('Failed to delete assignments', err);
        setError(t('errors.delete'));
      } finally {
        setBusy(false);
      }
    },
    [fetchAssignments, t]
  );

  const handleExport = useCallback(
    (data: DataRecord[], format: string) => {
      if (!data.length) return;
      const headers = fieldConfig.map((field) => field.label);
      const keys = fieldConfig.map((field) => field.key);

      if (format === 'csv') {
        const rows = [headers.join(',')];
        data.forEach((record) => {
          const row = keys.map((key) => {
            const value = (record as unknown)[key];
            if (value == null) return '';
            if (Array.isArray(value)) return `"${value.join(';')}"`;
            if (typeof value === 'string') return `"${value.replace(/"/g, '""')}"`;
            return value;
          });
          rows.push(row.join(','));
        });
        const blob = new Blob([rows.join('\n')], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = 'asset-assignments.csv';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
        return;
      }

      const payload = JSON.stringify(data, null, 2);
      const blob = new Blob([payload], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'asset-assignments.json';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    },
    [fieldConfig]
  );

