    const totalBarcodes = data.length;
    const activeBarcodes = data.filter(item => item.status === 'active').length;
    const totalScans = data.reduce((sum, item) => sum + item.scan_count, 0);
    const mobileCompatible = data.filter(item => item.mobile_app_compatible).length;
    const needReplacement = data.filter(item => item.status === 'damaged').length;
    const avgScansPerBarcode = totalBarcodes > 0 ? Math.round(totalScans / totalBarcodes) : 0;

    return { 
      totalBarcodes, 
      activeBarcodes, 
      totalScans, 
      mobileCompatible, 
      needReplacement, 
      avgScansPerBarcode 
    };
  }, [data]);

  // Event handlers
  const handleCreate = () => {
    setSelectedRecord(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEdit = (record: AssetBarcode) => {
    setSelectedRecord(record);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleView = (record: AssetBarcode) => {
    setSelectedRecord(record);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSave = async (formData: unknown) => {
    try {
      if (drawerMode === 'create') {
        const { error } = await supabase
          .from('asset_barcodes')
          .insert([{ ...formData, organization_id: orgId }]);

        if (error) throw error;
      } else if (drawerMode === 'edit' && selectedRecord) {
        const { error } = await supabase
          .from('asset_barcodes')
          .update(formData)
          .eq('id', selectedRecord.id);

        if (error) throw error;
      }

      await loadData();
      setDrawerOpen(false);
    } catch (error) {
      console.error('Error saving barcode record:', error);
    }
  };

  const handleDelete = async (record: AssetBarcode) => {
    try {
      const { error } = await supabase
        .from('asset_barcodes')
        .delete()
        .eq('id', record.id);

      if (error) throw error;
      await loadData();
