'use client';

import { X, Save, QrCode, Printer } from "lucide-react";
import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
// import { z } from 'zod';
import { createBrowserClient } from '@ghxstship/auth';
import { Button, Card, Badge } from '@ghxstship/ui';
import { UnifiedInput } from '@ghxstship/ui';

const BarcodeFormSchema = z.object({
  asset_id: z.string().min(1, 'Asset is required'),
  barcode_type: z.enum(['code128', 'code39', 'ean13', 'upc_a', 'qr_code', 'data_matrix', 'pdf417']),
  barcode_value: z.string().min(1, 'Barcode value is required'),
  qr_code_value: z.string().optional(),
  status: z.enum(['active', 'inactive', 'damaged', 'replaced']),
  print_date: z.string().min(1, 'Print date is required'),
  printed_by: z.string().optional(),
  mobile_app_compatible: z.boolean().optional(),
  encoding_format: z.enum(['utf8', 'ascii', 'iso8859_1', 'binary']).optional(),
  label_size: z.enum(['1x1', '1x2', '2x1', '2x2', '3x1', '4x2', 'custom']).optional(),
  label_material: z.enum(['paper', 'vinyl', 'polyester', 'aluminum', 'ceramic', 'tamper_evident']).optional(),
  replacement_reason: z.string().optional(),
  notes: z.string().optional()
});

type BarcodeFormData = z.infer<typeof BarcodeFormSchema>;

interface CreateBarcodeClientProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
  orgId: string;
  editData?: any;
}

export default function CreateBarcodeClient({
  isOpen,
  onClose,
