    resolver: zodResolver(BarcodeFormSchema),
    defaultValues: editData || {
      barcode_type: 'code128',
      status: 'active',
      mobile_app_compatible: true,
      encoding_format: 'utf8',
      label_size: '2x1',
      label_material: 'vinyl'
    }
