  const selectedBarcodeType = watch('barcode_type');

  const onSubmit = async (data: BarcodeFormData) => {
    setLoading(true);
    try {
      const response = await fetch('/api/v1/assets/barcodes', {
        method: editData ? 'PATCH' : 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-organization-id': orgId
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        throw new Error('Failed to save barcode record');
      }

      reset();
      onSuccess();
      onClose();
    } catch (error) {
      console.error('Error saving barcode record:', error);
    } finally {
      setLoading(false);
    }
  };

  const generateBarcodeValue = () => {
    const prefix = selectedBarcodeType.toUpperCase().substring(0, 3);
    const timestamp = Date.now().toString().slice(-6);
    const random = Math.random().toString(36).substring(2, 5).toUpperCase();
    setValue('barcode_value', `${prefix}-${timestamp}-${random}`);
  };

  if (!isOpen) return null;

  return (
