                    <option value="pdf417">PDF417</option>
                  </select>
                  {errors.barcode_type && (
                    <p className="text-body-xs color-destructive mt-xs">
                      {errors.barcode_type.message}
                    </p>
                  )}
                </div>

                {/* Barcode Value */}
