                  >
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                    <option value="damaged">Damaged</option>
                    <option value="replaced">Replaced</option>
                  </select>
                  {errors.status && (
                    <p className="text-body-xs color-destructive mt-xs">
                      {errors.status.message}
                    </p>
                  )}
                </div>

                {/* Print Date */}
                <div>
                  <label className="block text-body-sm font-medium mb-sm">
                    Print Date <span className="color-destructive">*</span>
                  </label>
                  <UnifiedInput
                    {...register('print_date')}
                    type="date"
                    error={errors.print_date?.message}
                  />
                </div>

                {/* Printed By */}
                <div>
                  <label className="block text-body-sm font-medium mb-sm">
                    Printed By
                  </label>
                  <UnifiedInput
                    {...register('printed_by')}
                    placeholder="Enter name of person who printed..."
                    error={errors.printed_by?.message}
                  />
                </div>

                {/* Mobile App Compatible */}
                <div>
                  <label className="flex items-center space-x-sm">
                    <input
                      {...register('mobile_app_compatible')}
                      type="checkbox"
                      className="rounded border-border"
                    />
                    <span className="text-body-sm font-medium">
                      Mobile App Compatible
                    </span>
                  </label>
                </div>
              </div>
            )}

            {activeTab === 'label' && (
              <div className="space-y-lg">
                {/* Encoding Format */}
                <div>
                  <label className="block text-body-sm font-medium mb-sm">
                    Encoding Format
                  </label>
                  <select
                    {...register('encoding_format')}
                    className="w-full px-md py-sm border border-border rounded-md bg-background"
                  >
                    <option value="utf8">UTF-8</option>
                    <option value="ascii">ASCII</option>
                    <option value="iso8859_1">ISO-8859-1</option>
                    <option value="binary">Binary</option>
                  </select>
                  {errors.encoding_format && (
                    <p className="text-body-xs color-destructive mt-xs">
                      {errors.encoding_format.message}
                    </p>
                  )}
                </div>

                {/* Label Size */}
                <div>
                  <label className="block text-body-sm font-medium mb-sm">
                    Label Size
                  </label>
                  <select
                    {...register('label_size')}
                    className="w-full px-md py-sm border border-border rounded-md bg-background"
                  >
                    <option value="1x1">1" x 1"</option>
                    <option value="1x2">1" x 2"</option>
                    <option value="2x1">2" x 1"</option>
                    <option value="2x2">2" x 2"</option>
                    <option value="3x1">3" x 1"</option>
                    <option value="4x2">4" x 2"</option>
                    <option value="custom">Custom</option>
                  </select>
                  {errors.label_size && (
                    <p className="text-body-xs color-destructive mt-xs">
                      {errors.label_size.message}
                    </p>
                  )}
                </div>

                {/* Label Material */}
                <div>
                  <label className="block text-body-sm font-medium mb-sm">
                    Label Material
                  </label>
                  <select
                    {...register('label_material')}
                    className="w-full px-md py-sm border border-border rounded-md bg-background"
                  >
                    <option value="paper">Paper</option>
                    <option value="vinyl">Vinyl</option>
                    <option value="polyester">Polyester</option>
                    <option value="aluminum">Aluminum</option>
                    <option value="ceramic">Ceramic</option>
                    <option value="tamper_evident">Tamper Evident</option>
                  </select>
                  {errors.label_material && (
                    <p className="text-body-xs color-destructive mt-xs">
                      {errors.label_material.message}
                    </p>
                  )}
                </div>

                {/* Replacement Reason */}
                <div>
                  <label className="block text-body-sm font-medium mb-sm">
                    Replacement Reason
                  </label>
                  <textarea
                    {...register('replacement_reason')}
                    rows={3}
                    className="w-full px-md py-sm border border-border rounded-md bg-background resize-none"
                    placeholder="Enter reason for replacement (if applicable)..."
                  />
                  {errors.replacement_reason && (
                    <p className="text-body-xs color-destructive mt-xs">
                      {errors.replacement_reason.message}
                    </p>
                  )}
                </div>

                {/* Label Preview */}
                <Card>
                  <div className="p-md">
                    <h4 className="text-body font-medium mb-sm flex items-center">
                      <Printer className="w-4 h-4 mr-sm" />
                      Label Preview
                    </h4>
