                        Label preview will be generated based on selected settings
                      </p>
                    </div>
                  </div>
                </Card>
              </div>
            )}

            {activeTab === 'qr' && (
              <div className="space-y-lg">
                {/* QR Code Value */}
                <div>
                  <label className="block text-body-sm font-medium mb-sm">
                    QR Code Data
                  </label>
                  <textarea
                    {...register('qr_code_value')}
                    rows={6}
