'use client';

import { Shield, AlertTriangle, CheckCircle, Clock, FileText, Plus } from "lucide-react";
import React, { useState, useEffect, useCallback } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Badge, Button } from '@ghxstship/ui';

interface ComplianceClientProps {
  orgId: string;
}

interface AssetCompliance {
  id: string;
  asset_id: string;
  asset_name: string;
  compliance_type: string;
  status: 'pending' | 'in_progress' | 'compliant' | 'non_compliant' | 'expired';
  requirement: string;
  description: string;
  due_date: string;
  completed_date?: string;
  assigned_to?: string;
  inspector?: string;
  certificate_url?: string;
  notes?: string;
  created_at: string;
  updated_at: string;
}

export default function ComplianceClient({ orgId }: ComplianceClientProps) {
  const t = useTranslations();
  const supabase = createBrowserClient();

  const [data, setData] = useState<AssetCompliance[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedRecord, setSelectedRecord] = useState<AssetCompliance | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');

  // Field configuration for compliance data
  const fieldConfig: FieldConfig[] = useMemo(() => [
    {
      key: 'asset_name',
      label: 'Asset Name',
      type: 'text',
      required: true,
      sortable: true,
      searchable: true
    },
    {
      key: 'compliance_type',
      label: 'Compliance Type',
      type: 'select',
      options: [
        { value: 'safety', label: 'Safety Inspection' },
        { value: 'regulatory', label: 'Regulatory Compliance' },
        { value: 'certification', label: 'Certification' },
        { value: 'audit', label: 'Audit' },
        { value: 'insurance', label: 'Insurance' },
        { value: 'environmental', label: 'Environmental' },
        { value: 'quality', label: 'Quality Assurance' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'requirement',
      label: 'Requirement',
      type: 'text',
      required: true,
      searchable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'pending', label: 'Pending' },
        { value: 'in_progress', label: 'In Progress' },
        { value: 'compliant', label: 'Compliant' },
        { value: 'non_compliant', label: 'Non-Compliant' },
        { value: 'expired', label: 'Expired' }
      ],
      required: true,
      filterable: true,
      sortable: true,
      render: (value: string) => {
        const variants = {
          pending: 'secondary',
          in_progress: 'warning',
          compliant: 'success',
          non_compliant: 'destructive',
          expired: 'destructive'
        };
        return <Badge variant={variants[value as keyof typeof variants] as unknown}>{value.replace('_', ' ')}</Badge>;
      }
    },
    {
      key: 'due_date',
      label: 'Due Date',
      type: 'date',
      required: true,
      sortable: true
    },
    {
      key: 'completed_date',
      label: 'Completed Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'assigned_to',
      label: 'Assigned To',
      type: 'text',
      searchable: true
    },
    {
      key: 'inspector',
      label: 'Inspector',
      type: 'text',
      searchable: true
    },
    {
      key: 'description',
      label: 'Description',
      type: 'textarea',
      searchable: true
    },
    {
      key: 'certificate_url',
      label: 'Certificate',
      type: 'url'
    },
    {
      key: 'notes',
      label: 'Notes',
      type: 'textarea'
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'datetime',
      sortable: true
    },
    {
      key: 'updated_at',
      label: 'Updated',
      type: 'datetime',
      sortable: true
    }
  ], []);

  // Load compliance data
  const loadData = useCallback(async () => {
    setLoading(true);
    try {
      const { data: complianceData, error } = await supabase
        .from('asset_compliance')
        .select(`
          *,
          assets!inner(name)
        `)
        .eq('organization_id', orgId)
        .order('due_date', { ascending: true });

      if (error) {
        console.error('Error loading compliance data:', error);
        // Use demo data as fallback
        setData(generateDemoData());
      } else {
        const formattedData = (complianceData || []).map(item => ({
          ...item,
          asset_name: item.assets?.name || 'Unknown Asset'
        }));
        setData(formattedData);
      }
    } catch (error) {
      console.error('Error loading compliance data:', error);
      setData(generateDemoData());
    } finally {
      setLoading(false);
    }
  }, [supabase, orgId]);

  // Generate demo data
  const generateDemoData = (): AssetCompliance[] => [
    {
      id: 'demo-1',
      asset_id: 'asset-1',
      asset_name: 'Main Stage Rigging System',
      compliance_type: 'safety',
      status: 'compliant',
      requirement: 'Annual Safety Inspection',
      description: 'Comprehensive safety inspection of rigging system including load testing',
      due_date: '2024-03-15',
      completed_date: '2024-02-28',
      assigned_to: 'Jack Sparrow',
      inspector: 'Certified Safety Inspector',
      certificate_url: 'https://example.com/cert1.pdf',
      notes: 'All safety checks passed. Next inspection due March 2025.',
      created_at: '2024-01-01T10:00:00Z',
      updated_at: '2024-02-28T14:30:00Z'
    },
    {
      id: 'demo-2',
      asset_id: 'asset-2',
      asset_name: 'Professional Sound System',
      compliance_type: 'regulatory',
      status: 'pending',
      requirement: 'FCC Equipment Authorization',
      description: 'Federal Communications Commission equipment authorization renewal',
      due_date: '2024-04-30',
      assigned_to: 'Will Turner',
      notes: 'Documentation being prepared for submission.',
      created_at: '2024-01-15T09:00:00Z',
      updated_at: '2024-01-15T09:00:00Z'
    },
    {
      id: 'demo-3',
      asset_id: 'asset-3',
      asset_name: 'LED Video Wall Panels',
      compliance_type: 'certification',
      status: 'expired',
      requirement: 'CE Marking Certification',
      description: 'European Conformity marking certification for LED panels',
      due_date: '2024-01-31',
      assigned_to: 'Elizabeth Swann',
      notes: 'Certification expired. Renewal process initiated.',
      created_at: '2023-12-01T08:00:00Z',
      updated_at: '2024-02-01T10:00:00Z'
    },
    {
      id: 'demo-4',
      asset_id: 'asset-4',
      asset_name: 'Generator - 500kW',
      compliance_type: 'environmental',
      status: 'in_progress',
      requirement: 'Emissions Testing',
      description: 'Annual emissions testing and environmental compliance check',
      due_date: '2024-05-15',
      assigned_to: 'Hector Barbossa',
      inspector: 'Environmental Compliance Officer',
      notes: 'Testing scheduled for next week.',
      created_at: '2024-02-01T11:00:00Z',
      updated_at: '2024-02-15T16:00:00Z'
    }
  ];

  // Statistics calculation
  const stats = useMemo(() => {
    const total = data.length;
    const compliant = data.filter(item => item.status === 'compliant').length;
    const pending = data.filter(item => item.status === 'pending').length;
    const expired = data.filter(item => item.status === 'expired').length;
    const dueThisMonth = data.filter(item => {
      const dueDate = new Date(item.due_date);
      const now = new Date();
      return dueDate.getMonth() === now.getMonth() && dueDate.getFullYear() === now.getFullYear();
    }).length;

    return { total, compliant, pending, expired, dueThisMonth };
  }, [data]);

  // Event handlers
  const handleCreate = () => {
    setSelectedRecord(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEdit = (record: AssetCompliance) => {
    setSelectedRecord(record);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleView = (record: AssetCompliance) => {
    setSelectedRecord(record);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSave = async (formData: unknown) => {
    try {
      if (drawerMode === 'create') {
        const { error } = await supabase
          .from('asset_compliance')
          .insert([{ ...formData, organization_id: orgId }]);

        if (error) throw error;
      } else if (drawerMode === 'edit' && selectedRecord) {
        const { error } = await supabase
          .from('asset_compliance')
          .update(formData)
          .eq('id', selectedRecord.id);

        if (error) throw error;
      }

      await loadData();
      setDrawerOpen(false);
    } catch (error) {
      console.error('Error saving compliance record:', error);
    }
  };

  const handleDelete = async (record: AssetCompliance) => {
    try {
      const { error } = await supabase
        .from('asset_compliance')
        .delete()
        .eq('id', record.id);

      if (error) throw error;
      await loadData();
    } catch (error) {
      console.error('Error deleting compliance record:', error);
    }
  };

  // DataView configuration
  const dataViewConfig: DataViewConfig = useMemo(() => ({
    id: 'asset-compliance',
    name: 'Asset Compliance',
    description: 'Manage asset compliance, audits, and regulatory requirements',
    viewType: 'grid',
    defaultView: 'grid',
    fields: fieldConfig,
    data: data,
    loading: loading,
    onRefresh: loadData,
    onSearch: (query: string) => {
      // Implement search logic
      console.log('Search:', query);
    },
    onFilter: (filters: FilterConfig[]) => {
      // Implement filter logic
      console.log('Filter:', filters);
    },
    onSort: (sorts: SortConfig[]) => {
      // Implement sort logic
      console.log('Sort:', sorts);
    },
    onExport: (data: DataRecord[], format: string) => {
      console.log('Export:', data, format);
    },
    onImport: (data: unknown[]) => {
      console.log('Import:', data);
    },
    actions: {
      canCreate: true,
      canEdit: true,
      canDelete: true,
      canView: true,
      onCreate: handleCreate,
      onEdit: handleEdit,
      onView: handleView,
      onDelete: handleDelete
    }
  }), [fieldConfig, data, loading, loadData]);

  useEffect(() => {
    loadData();
  }, [loadData]);

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 font-anton uppercase">Asset Compliance</h1>
          <p className="text-body-sm color-muted">Manage compliance requirements, audits, and certifications</p>
        </div>
        <Button onClick={handleCreate} className="flex items-center gap-sm">
          <Plus className="w-4 h-4" />
          Add Compliance Record
        </Button>
      </div>

      {/* Statistics Dashboard */}
      <DashboardView>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-md">
          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Total Records</p>
                  <p className="text-heading-3">{stats.total}</p>
                </div>
                <Shield className="w-8 h-8 color-accent" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Compliant</p>
                  <p className="text-heading-3">{stats.compliant}</p>
                </div>
                <CheckCircle className="w-8 h-8 color-success" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Pending</p>
                  <p className="text-heading-3">{stats.pending}</p>
                </div>
                <Clock className="w-8 h-8 color-warning" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Expired</p>
                  <p className="text-heading-3">{stats.expired}</p>
                </div>
                <AlertTriangle className="w-8 h-8 color-destructive" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Due This Month</p>
                  <p className="text-heading-3">{stats.dueThisMonth}</p>
                </div>
                <FileText className="w-8 h-8 color-secondary" />
              </div>
            </div>
          </Card>
        </div>
      </DashboardView>

      {/* Data Views */}
      <DataViewProvider config={dataViewConfig}>
        <div className="stack-md">
          <DataActions />
          <ViewSwitcher />
          <DataGrid />
          <KanbanBoard 
            columns={[
              { id: 'pending', title: 'Pending' },
              { id: 'in_progress', title: 'In Progress' },
              { id: 'compliant', title: 'Compliant' },
              { id: 'non_compliant', title: 'Non-Compliant' },
              { id: 'expired', title: 'Expired' }
            ]}
            statusField="status"
            titleField="requirement"
          />
          <CalendarView 
            startDateField="due_date"
            titleField="requirement"
          />
          <ListView 
            titleField="requirement"
            subtitleField="asset_name"
          />
          <TimelineView 
            dateField="due_date"
            titleField="requirement"
          />
        </div>
      </DataViewProvider>

      {/* Universal Drawer */}
      <UniversalDrawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        record={selectedRecord}
        fields={fieldConfig}
        mode={drawerMode}
        title={
          drawerMode === 'create' 
            ? 'Create Compliance Record'
            : drawerMode === 'edit'
            ? 'Edit Compliance Record'
            : 'Compliance Record Details'
        }
        onSave={handleSave}
        tabs={[
          {
            key: 'details',
            label: 'Details',
            content: null // Will be handled by UniversalDrawer
          },
          {
            key: 'history',
            label: 'History',
            content: (
              <div className="p-md">
                <p className="text-body-sm color-muted">Compliance history and audit trail will be displayed here.</p>
              </div>
            )
          }
        ]}
      />
    </div>
  );
}

'use client';

import { Shield, AlertTriangle, CheckCircle, Clock, Plus, TrendingUp } from "lucide-react";
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  DataViewProvider, 
  DataGrid, 
  KanbanBoard, 
  CalendarView, 
  ListView,
  TimelineView,
  DashboardView,
  ViewSwitcher,
  DataActions,
  UniversalDrawer
} from '@ghxstship/ui/components/DataViews';
import { Card, Badge, Button } from '@ghxstship/ui';
import CreateComplianceClient from './CreateComplianceClient';
import type { 
  DataViewConfig, 
  FieldConfig, 
