  FilterConfig,
  SortConfig 
} from '@ghxstship/ui/components/DataViews/types';
import type { AssetCompliance } from '../types';

interface ComplianceClientProps {
  orgId: string;
}

export default function ComplianceClient({ orgId }: ComplianceClientProps) {
  const t = useTranslations('assets.compliance');
  const supabase = createBrowserClient();

  const [data, setData] = useState<AssetCompliance[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedRecord, setSelectedRecord] = useState<AssetCompliance | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');
  const [createDrawerOpen, setCreateDrawerOpen] = useState(false);

  // Field configuration for compliance
  const fieldConfig: FieldConfig[] = useMemo(() => [
    {
      key: 'asset_name',
      label: 'Asset Name',
      type: 'text',
      required: true,
      sortable: true,
      searchable: true
    },
    {
      key: 'compliance_type',
      label: 'Compliance Type',
      type: 'select',
      options: [
        { value: 'safety', label: 'Safety Inspection' },
        { value: 'regulatory', label: 'Regulatory Compliance' },
        { value: 'certification', label: 'Certification' },
        { value: 'audit', label: 'Audit' },
        { value: 'insurance', label: 'Insurance' },
        { value: 'environmental', label: 'Environmental' },
        { value: 'quality', label: 'Quality Assurance' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'pending', label: 'Pending' },
        { value: 'in_progress', label: 'In Progress' },
        { value: 'compliant', label: 'Compliant' },
        { value: 'non_compliant', label: 'Non-Compliant' },
        { value: 'expired', label: 'Expired' }
      ],
      required: true,
      filterable: true,
      sortable: true,
      render: (value: string) => {
        const variants = {
          pending: 'secondary',
          in_progress: 'warning',
          compliant: 'success',
          non_compliant: 'destructive',
          expired: 'destructive'
        };
        return <Badge variant={variants[value as keyof typeof variants] as unknown}>{value.replace('_', ' ')}</Badge>;
      }
    },
    {
      key: 'requirement',
      label: 'Requirement',
      type: 'text',
      required: true,
      searchable: true
    },
    {
      key: 'due_date',
      label: 'Due Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'completed_date',
      label: 'Completed Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'assigned_to',
      label: 'Assigned To',
      type: 'text',
      searchable: true
    },
    {
      key: 'inspector',
      label: 'Inspector',
      type: 'text',
      searchable: true
    },
    {
      key: 'description',
      label: 'Description',
      type: 'textarea',
      searchable: true
    },
    {
      key: 'notes',
      label: 'Notes',
      type: 'textarea'
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'date',
      sortable: true
    }
  ], []);

  // Load compliance data
  const loadData = useCallback(async () => {
    setLoading(true);
    try {
      const { data: complianceData, error } = await supabase
        .from('asset_compliance')
