      assigned_to: 'Safety Team',
      inspector: 'John Smith, Certified Inspector',
      certificate_url: 'https://example.com/cert1.pdf',
      notes: 'All safety checks passed. Next inspection due January 2025.',
      created_at: '2023-01-15T10:00:00Z',
      updated_at: '2023-12-20T15:30:00Z'
    },
    {
      id: '2',
      organization_id: orgId,
      asset_id: '2',
      asset_name: 'Professional Sound System',
      compliance_type: 'regulatory',
      status: 'pending',
      requirement: 'FCC Equipment Authorization',
      description: 'Regulatory compliance for wireless audio equipment',
      due_date: '2024-03-01',
      assigned_to: 'Technical Team',
      inspector: 'FCC Certified Agent',
      notes: 'Documentation submitted, awaiting approval.',
      created_at: '2023-02-01T10:00:00Z',
      updated_at: '2023-02-01T10:00:00Z'
    },
    {
      id: '3',
