      key: 'serial_number',
      label: 'Serial Number',
      type: 'text',
      searchable: true
    },
    {
      key: 'purchase_date',
      label: 'Purchase Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'purchase_cost',
      label: 'Purchase Cost',
      type: 'currency',
      sortable: true
    },
    {
      key: 'current_value',
      label: 'Current Value',
      type: 'currency',
      sortable: true,
      format: formatCurrency
    },
    {
      key: 'location',
      label: 'Location',
      type: 'text',
      searchable: true
    },
    {
      key: 'description',
      label: 'Description',
      type: 'textarea',
      searchable: true
    },
    {
      key: 'notes',
      label: 'Notes',
      type: 'textarea'
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'date',
      sortable: true
    },
    {
      key: 'updated_at',
      label: 'Updated',
      type: 'date',
      sortable: true
    }
  ], []);

  const fetchAssets = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const list = await assetsService.getAssets(orgId, { ...filters, search: searchQuery });
      setRecords(list as InventoryRecord[]);
    } catch (err) {
      console.error('Failed to load assets', err);
      setError('Unable to load assets. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [orgId, filters, searchQuery]);

  useEffect(() => {
    fetchAssets();
  }, [fetchAssets]);

  useEffect(() => {
    const channel = realtimeClient
      .channel(`public:assets:${orgId}`)
      .on(
        'postgres_changes',
        { event: '*', schema: 'public', table: 'assets', filter: `organization_id=eq.${orgId}` },
        fetchAssets
      )
      .subscribe();

    return () => {
      channel.unsubscribe();
    };
  }, [realtimeClient, orgId, fetchAssets]);

  // Statistics calculation
  const stats = useMemo(() => {
    const total = records.length;
    const available = records.filter((item) => item.status === 'available').length;
    const inUse = records.filter((item) => item.status === 'in_use').length;
    const maintenance = records.filter((item) => item.status === 'under_maintenance').length;
    const totalValue = records.reduce((sum, item) => sum + (item.current_value || 0), 0);

    return { total, available, inUse, maintenance, totalValue };
  }, [records]);

  const openDrawer = useCallback((mode: 'create' | 'view' | 'edit', record?: InventoryRecord) => {
    setDrawerMode(mode);
    setSelectedRecord(record ?? null);
    setDrawerOpen(true);
  }, []);

  const closeDrawer = useCallback(() => {
    setDrawerOpen(false);
    setSelectedRecord(null);
  }, []);

  const handlePersist = useCallback(
    async (formData: AssetFormData) => {
      setBusyAction(true);
      setError(null);
      try {
        if (drawerMode === 'create') {
          await assetsService.createAsset(formData, orgId);
        } else if (drawerMode === 'edit' && selectedRecord) {
          await assetsService.updateAsset(selectedRecord.id, formData);
        }
        await fetchAssets();
        closeDrawer();
      } catch (err) {
        console.error('Failed to persist asset', err);
        setError('Unable to save asset changes.');
      } finally {
        setBusyAction(false);
      }
    },
    [drawerMode, selectedRecord, orgId, fetchAssets, closeDrawer]
  );

  const handleDelete = useCallback(
    async (ids: string[]) => {
      if (!ids.length) return;
      setBusyAction(true);
      setError(null);
      try {
        await Promise.all(ids.map((id) => assetsService.deleteAsset(id)));
        await fetchAssets();
      } catch (err) {
        console.error('Failed to delete assets', err);
        setError('Unable to delete one or more assets.');
      } finally {
        setBusyAction(false);
      }
    },
    [assetsService, fetchAssets]
  );

  const handleExport = useCallback(
    (data: DataRecord[], format: string) => {
      if (!data.length) {
        return;
      }

      const fields = fieldConfig.map((field) => field.key);
      const headers = fieldConfig.map((field) => field.label);

      if (format === 'csv') {
        const rows = [headers.join(',')];
        data.forEach((record) => {
          const row = fields.map((key) => {
            const value = (record as InventoryRecord)[key as keyof InventoryRecord];
            if (value == null) return '';
            if (Array.isArray(value)) return `"${value.join(';')}"`;
            if (typeof value === 'string') return `"${value.replace(/"/g, '""')}"`;
            return value;
          });
          rows.push(row.join(','));
        });
        downloadFile('assets-export.csv', rows.join('\n'), 'text/csv');
        return;
      }

      const payload = JSON.stringify(data, null, 2);
      downloadFile('assets-export.json', payload, 'application/json');
    },
    [fieldConfig]
  );

  const handleImport = useCallback(
    async (records: DataRecord[]) => {
      if (!records.length) return;
      setBusyAction(true);
      setError(null);
      try {
        for (const record of records) {
          const { id, organization_id, created_at, updated_at, ...payload } = record as InventoryRecord;
          await assetsService.createAsset(payload as AssetFormData, orgId);
        }
        await fetchAssets();
      } catch (err) {
        console.error('Failed to import assets', err);
        setError('Unable to import one or more assets.');
      } finally {
        setBusyAction(false);
      }
    },
    [fetchAssets, orgId]
  );

  const filteredRecords = useMemo(() => {
    if (!sorts || sorts.length === 0) return records;
    const [{ field, direction }] = sorts;
    return [...records].sort((a, b) => {
      const aValue = (a as unknown)[field];
      const bValue = (b as unknown)[field];
      if (aValue === bValue) return 0;
      if (aValue == null) return direction === 'asc' ? -1 : 1;
      if (bValue == null) return direction === 'asc' ? 1 : -1;
      if (aValue > bValue) return direction === 'asc' ? 1 : -1;
      return direction === 'asc' ? -1 : 1;
    });
  }, [records, sorts]);

  const actions: ActionConfig[] = useMemo(
    () => [
      {
        key: 'create',
        label: 'Create Asset',
        icon: <Plus className="w-4 h-4" />,
        variant: 'primary',
        onClick: () => openDrawer('create')
      },
      {
        key: 'view',
        label: 'View Asset',
        icon: <Eye className="w-4 h-4" />,
        requiresSelection: true,
        onClick: (ids) => {
          const record = records.find((item) => item.id === ids[0]);
          if (record) openDrawer('view', record);
        }
      },
      {
        key: 'edit',
        label: 'Edit Asset',
        icon: <TrendingUp className="w-4 h-4" />,
        requiresSelection: true,
        onClick: (ids) => {
          const record = records.find((item) => item.id === ids[0]);
          if (record) openDrawer('edit', record);
        }
      },
      {
        key: 'delete',
        label: 'Delete',
        icon: <Trash2 className="w-4 h-4" />,
        variant: 'destructive',
        requiresSelection: true,
        confirmMessage: 'Delete selected assets? This action cannot be undone.',
        onClick: (ids) => handleDelete(ids)
      }
    ],
    [openDrawer, handleDelete, records]
  );

  const bulkActions: ActionConfig[] = useMemo(
    () => [
      {
        key: 'export',
        label: 'Export Selected',
        icon: <Download className="w-4 h-4" />,
        requiresSelection: true,
        onClick: (ids) => {
          const selected = records.filter((record) => ids.includes(record.id));
          handleExport(selected, 'csv');
        }
      },
      {
        key: 'delete_bulk',
        label: 'Delete Selected',
        icon: <Trash2 className="w-4 h-4" />,
        variant: 'destructive',
        requiresSelection: true,
        confirmMessage: 'Delete all selected assets? This cannot be undone.',
        onClick: (ids) => handleDelete(ids)
      }
    ],
    [records, handleExport, handleDelete]
  );

  const exportConfig: ExportConfig = useMemo(
    () => ({
      formats: ['csv', 'json'],
      includeFilters: true,
      onExport: (format, data) => handleExport(data, format)
    }),
    [handleExport]
  );

  const importConfig: ImportConfig = useMemo(
    () => ({
      formats: ['csv', 'json'],
      onImport: handleImport,
      template: 'name,category,type,status,sku,barcode,manufacturer,model,serial_number,purchase_date,purchase_cost,current_value,location,description,notes'
    }),
    [handleImport]
  );

  // DataView configuration
  const dataViewConfig: DataViewConfig = useMemo(() => ({
    id: 'assets-inventory',
    name: 'Asset Inventory',
    description: 'Manage your organization\'s asset inventory',
    viewType: 'grid',
    defaultView: 'grid',
    fields: fieldConfig,
    data: filteredRecords,
    loading,
    error: error ?? undefined,
    onRefresh: fetchAssets,
    onSearch: setSearchQuery,
    onFilter: (nextFilters: FilterConfig[]) => {
      const status = nextFilters.find((f) => f.field === 'status')?.value as string | undefined;
      const category = nextFilters.find((f) => f.field === 'category')?.value as string | undefined;
      setFilters((prev) => ({
        ...prev,
        status: status || undefined,
        category: category || undefined
      }));
    },
    onSort: setSorts,
    actions,
    bulkActions,
    exportConfig,
    importConfig,
    allowExport: true,
    allowImport: true,
    onDelete: handleDelete,
    onCreate: () => openDrawer('create'),
    onEdit: (record: DataRecord) => openDrawer('edit', record as InventoryRecord),
    onDuplicate: async (record: DataRecord) => {
      const { id, organization_id, created_at, updated_at, ...payload } = record as InventoryRecord;
      await assetsService.createAsset(payload as AssetFormData, orgId);
      await fetchAssets();
    }
  }), [
    filteredRecords,
    fieldConfig,
    loading,
    error,
    fetchAssets,
    actions,
    bulkActions,
    exportConfig,
    importConfig,
    handleDelete,
    openDrawer,
    orgId,
    assetsService
  ]);

  const drawerFields: DrawerFieldConfig[] = useMemo(
    () =>
      fieldConfig.map((field) => {
        const { key, label, type, options, required } = field;
        const drawerType: DrawerFieldConfig['type'] =
          type === 'multiselect' || type === 'array' || type === 'object' ? 'text' : (type as DrawerFieldConfig['type']);

        return {
          key,
          label,
          type: drawerType,
          options,
          required,
        } as DrawerFieldConfig;
      }),
    [fieldConfig]
  );

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 font-anton uppercase">Asset Inventory</h1>
          <p className="text-body-sm color-muted">Manage your organization's physical and digital assets</p>
        </div>
        <div className="flex items-center gap-sm">
          <Button variant="secondary" className="flex items-center gap-sm" onClick={() => handleExport(records, 'csv')}>
            <Download className="w-4 h-4" />
            Export CSV
          </Button>
          <Button variant="outline" className="flex items-center gap-sm" onClick={() => handleExport(records, 'json')}>
            <Download className="w-4 h-4" />
            Export JSON
          </Button>
          <Button variant="secondary" className="flex items-center gap-sm" onClick={() => openDrawer('create')}>
            <Plus className="w-4 h-4" />
            {t('actions.add')}
          </Button>
        </div>
      </div>

      {/* Statistics Dashboard */}
      <DashboardView>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-md">
          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Total Assets</p>
                  <p className="text-heading-3">{stats.total}</p>
                </div>
                <Package className="w-8 h-8 color-accent" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Available</p>
                  <p className="text-heading-3">{stats.available}</p>
                </div>
                <CheckCircle className="w-8 h-8 color-success" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">In Use</p>
                  <p className="text-heading-3">{stats.inUse}</p>
                </div>
                <TrendingUp className="w-8 h-8 color-warning" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Maintenance</p>
                  <p className="text-heading-3">{stats.maintenance}</p>
                </div>
                <AlertTriangle className="w-8 h-8 color-destructive" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Total Value</p>
                  <p className="text-heading-3">${(stats.totalValue / 1000).toFixed(0)}K</p>
                </div>
                <TrendingUp className="w-8 h-8 color-success" />
              </div>
            </div>
          </Card>
        </div>
      </DashboardView>

      {/* Data Views */}
      <DataViewProvider config={dataViewConfig}>
        <div className="stack-md">
          <DataActions showSearch showFilters showSort showExport showImport showBulkActions showViewOptions />
          <ViewSwitcher />
          <DataGrid />
          <KanbanBoard 
            columns={[
              { id: 'available', title: 'Available' },
              { id: 'in_use', title: 'In Use' },
              { id: 'under_maintenance', title: 'Under Maintenance' },
              { id: 'damaged', title: 'Damaged' },
              { id: 'retired', title: 'Retired' }
            ]}
            statusField="status"
            titleField="name"
          />
          <CalendarView 
            startDateField="purchase_date"
            titleField="name"
          />
          <ListView 
            titleField="name"
            subtitleField="category"
          />
          <TimelineView 
            startDateField="purchase_date"
            titleField="name"
          />
        </div>
      </DataViewProvider>

      {/* Universal Drawer */}
      <UniversalDrawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        fields={drawerFields}
        mode={drawerMode}
        title={
          drawerMode === 'create' 
            ? 'Create Asset'
            : drawerMode === 'edit'
            ? 'Edit Asset'
            : 'Asset Details'
        }
        record={selectedRecord as DataRecord | null}
        onSave={async (data) => {
          await handlePersist(data as AssetFormData);
        }}
        tabs={[
          {
            key: 'details',
            label: 'Details',
            content: null // Will be handled by UniversalDrawer
          },
          {
            key: 'maintenance',
            label: 'Maintenance',
            content: (
              <div className="p-md">
                <p className="text-body-sm color-muted">Maintenance history and schedules will be displayed here.</p>
              </div>
            )
          },
          {
            key: 'assignments',
            label: 'Assignments',
            content: (
              <div className="p-md">
                <p className="text-body-sm color-muted">Asset assignment history will be displayed here.</p>
              </div>
            )
          }
        ]}
      />
    </div>
  );
}

'use client';

import { TrendingDown, RotateCcw, Archive, DollarSign, Calendar, Plus } from "lucide-react";
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  DataViewProvider, 
  DataGrid, 
  KanbanBoard, 
  CalendarView, 
  ListView,
  TimelineView,
  DashboardView,
  ViewSwitcher,
  DataActions,
  UniversalDrawer
} from '@ghxstship/ui/components/DataViews';
import { Card, Badge, Button } from '@ghxstship/ui';
import type { 
  DataViewConfig, 
  FieldConfig, 
  DataRecord,
  FilterConfig,
  SortConfig 
} from '@ghxstship/ui/components/DataViews/types';

interface LifecycleClientProps {
  orgId: string;
}

interface AssetLifecycle extends DataRecord {
  id: string;
  asset_id: string;
  asset_name: string;
  lifecycle_stage: string;
  acquisition_date: string;
  acquisition_cost: number;
  current_value: number;
  depreciation_method: string;
  depreciation_rate: number;
  useful_life_years: number;
  salvage_value: number;
  accumulated_depreciation: number;
  remaining_life_years: number;
  utilization_rate: number;
  maintenance_cost_total: number;
  roi_percentage: number;
  disposal_date?: string;
  disposal_value?: number;
  disposal_method?: string;
  replacement_recommended: boolean;
  replacement_date?: string;
  replacement_cost?: number;
  notes?: string;
  created_at: string;
  updated_at: string;
}

export default function LifecycleClient({ orgId }: LifecycleClientProps) {
  const t = useTranslations();
  const supabase = createBrowserClient();

  const [data, setData] = useState<AssetLifecycle[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedRecord, setSelectedRecord] = useState<AssetLifecycle | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');

  // Field configuration for lifecycle data
  const fieldConfig: FieldConfig[] = useMemo(() => [
    {
      key: 'asset_name',
      label: 'Asset Name',
      type: 'text',
      required: true,
      sortable: true,
      searchable: true
    },
    {
      key: 'lifecycle_stage',
      label: 'Lifecycle Stage',
      type: 'select',
      options: [
        { value: 'acquisition', label: 'Acquisition' },
        { value: 'deployment', label: 'Deployment' },
        { value: 'operation', label: 'Operation' },
        { value: 'maintenance', label: 'Maintenance' },
        { value: 'optimization', label: 'Optimization' },
        { value: 'decline', label: 'Decline' },
        { value: 'disposal', label: 'Disposal' },
        { value: 'retired', label: 'Retired' }
      ],
      required: true,
      filterable: true,
      sortable: true,
      render: (value: string) => {
        const variants = {
          acquisition: 'secondary',
          deployment: 'warning',
          operation: 'success',
          maintenance: 'warning',
          optimization: 'success',
          decline: 'destructive',
          disposal: 'outline',
          retired: 'outline'
        };
        return <Badge variant={variants[value as keyof typeof variants] as unknown}>{value}</Badge>;
      }
    },
    {
      key: 'acquisition_date',
      label: 'Acquisition Date',
      type: 'date',
      required: true,
      sortable: true
    },
    {
      key: 'acquisition_cost',
      label: 'Acquisition Cost',
      type: 'currency',
      required: true,
      sortable: true
    },
    {
      key: 'current_value',
      label: 'Current Value',
      type: 'currency',
      sortable: true
    },
    {
      key: 'depreciation_method',
      label: 'Depreciation Method',
      type: 'select',
      options: [
        { value: 'straight_line', label: 'Straight Line' },
        { value: 'declining_balance', label: 'Declining Balance' },
        { value: 'sum_of_years', label: 'Sum of Years Digits' },
        { value: 'units_of_production', label: 'Units of Production' },
        { value: 'custom', label: 'Custom' }
      ],
      filterable: true
    },
    {
      key: 'depreciation_rate',
      label: 'Depreciation Rate (%)',
      type: 'number',
      sortable: true
    },
    {
      key: 'useful_life_years',
      label: 'Useful Life (Years)',
      type: 'number',
      required: true,
      sortable: true
    },
    {
      key: 'remaining_life_years',
      label: 'Remaining Life (Years)',
      type: 'number',
      sortable: true
    },
    {
      key: 'salvage_value',
      label: 'Salvage Value',
      type: 'currency',
      sortable: true
    },
    {
      key: 'accumulated_depreciation',
      label: 'Accumulated Depreciation',
      type: 'currency',
      sortable: true
    },
    {
      key: 'utilization_rate',
      label: 'Utilization Rate (%)',
      type: 'number',
      sortable: true
    },
    {
      key: 'maintenance_cost_total',
      label: 'Total Maintenance Cost',
      type: 'currency',
      sortable: true
    },
    {
      key: 'roi_percentage',
      label: 'ROI (%)',
      type: 'number',
      sortable: true
    },
    {
      key: 'replacement_recommended',
      label: 'Replacement Recommended',
      type: 'boolean',
      filterable: true,
      render: (value: boolean) => {
        return value ? <Badge variant="warning">Yes</Badge> : <Badge variant="success">No</Badge>;
      }
    },
    {
      key: 'replacement_date',
      label: 'Recommended Replacement Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'replacement_cost',
      label: 'Estimated Replacement Cost',
      type: 'currency',
      sortable: true
    },
    {
      key: 'disposal_date',
      label: 'Disposal Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'disposal_value',
      label: 'Disposal Value',
      type: 'currency',
      sortable: true
    },
    {
      key: 'disposal_method',
      label: 'Disposal Method',
      type: 'select',
      options: [
        { value: 'sale', label: 'Sale' },
        { value: 'trade_in', label: 'Trade-in' },
        { value: 'donation', label: 'Donation' },
        { value: 'recycling', label: 'Recycling' },
        { value: 'scrap', label: 'Scrap' },
        { value: 'destruction', label: 'Destruction' }
      ],
      filterable: true
    },
    {
      key: 'notes',
      label: 'Notes',
      type: 'textarea'
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'date',
      sortable: true
    },
    {
      key: 'updated_at',
      label: 'Updated',
      type: 'date',
      sortable: true
    }
  ], []);

  // Load lifecycle data
  const loadData = useCallback(async () => {
    setLoading(true);
    try {
      const { data: lifecycleData, error } = await supabase
        .from('asset_lifecycle')
        .select(`
          *,
          assets!inner(name)
        `)
        .eq('organization_id', orgId)
        .order('acquisition_date', { ascending: false });

      if (error) {
        console.error('Error loading lifecycle data:', error);
        // Use demo data as fallback
        setData(generateDemoData());
      } else {
        const formattedData = (lifecycleData || []).map(item => ({
          ...item,
          asset_name: item.assets?.name || 'Unknown Asset'
        }));
        setData(formattedData);
      }
    } catch (error) {
      console.error('Error loading lifecycle data:', error);
      setData(generateDemoData());
    } finally {
      setLoading(false);
    }
  }, [supabase, orgId]);

  // Generate demo data
  const generateDemoData = (): AssetLifecycle[] => [
    {
      id: 'demo-1',
      asset_id: 'asset-1',
      asset_name: 'Main Stage Rigging System',
      lifecycle_stage: 'operation',
      acquisition_date: '2020-01-15',
      acquisition_cost: 45000,
      current_value: 32000,
      depreciation_method: 'straight_line',
      depreciation_rate: 10,
      useful_life_years: 10,
      remaining_life_years: 6,
      salvage_value: 5000,
      accumulated_depreciation: 13000,
      utilization_rate: 85,
      maintenance_cost_total: 3500,
      roi_percentage: 125,
      replacement_recommended: false,
      replacement_date: '2030-01-15',
      replacement_cost: 55000,
      notes: 'Excellent condition. Regular maintenance performed.',
      created_at: '2024-01-01T10:00:00Z',
