      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'scheduled', label: 'Scheduled' },
        { value: 'in_progress', label: 'In Progress' },
        { value: 'completed', label: 'Completed' },
        { value: 'cancelled', label: 'Cancelled' },
        { value: 'overdue', label: 'Overdue' }
      ],
      required: true,
      filterable: true,
      sortable: true,
      render: (value: string) => {
        const variants = {
          scheduled: 'secondary',
          in_progress: 'warning',
          completed: 'success',
          cancelled: 'outline',
          overdue: 'destructive'
        };
        return <Badge variant={variants[value as keyof typeof variants] as unknown}>{value.replace('_', ' ')}</Badge>;
      }
    },
    {
      key: 'priority',
      label: 'Priority',
      type: 'select',
      options: [
        { value: 'low', label: 'Low' },
        { value: 'medium', label: 'Medium' },
        { value: 'high', label: 'High' },
        { value: 'critical', label: 'Critical' }
      ],
      required: true,
      filterable: true,
      sortable: true,
      render: (value: string) => {
        const variants = {
          low: 'outline',
          medium: 'secondary',
          high: 'warning',
          critical: 'destructive'
        };
        return <Badge variant={variants[value as keyof typeof variants] as unknown}>{value}</Badge>;
      }
    },
    {
      key: 'title',
      label: 'Title',
      type: 'text',
      required: true,
      searchable: true
    },
    {
      key: 'scheduled_date',
      label: 'Scheduled Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'completed_date',
      label: 'Completed Date',
      type: 'date',
      sortable: true
    },
    {
      key: 'technician',
      label: 'Technician',
      type: 'text',
      searchable: true
    },
    {
      key: 'vendor',
      label: 'Vendor',
      type: 'text',
      searchable: true
    },
    {
      key: 'cost',
      label: 'Cost',
      type: 'currency',
      sortable: true
    },
    {
      key: 'downtime_hours',
      label: 'Downtime (Hours)',
      type: 'number',
      sortable: true
    },
    {
      key: 'description',
      label: 'Description',
      type: 'textarea',
      searchable: true
    },
    {
      key: 'work_performed',
      label: 'Work Performed',
      type: 'textarea',
      searchable: true
    },
    {
      key: 'notes',
      label: 'Notes',
      type: 'textarea'
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'date',
      sortable: true
    }
  ], []);

  // Load maintenance data
  const loadData = useCallback(async () => {
    setLoading(true);
    try {
      const { data: maintenanceData, error } = await supabase
        .from('asset_maintenance')
        .select(`
          *,
          assets!inner(name)
        `)
        .eq('organization_id', orgId)
        .order('scheduled_date', { ascending: false });

      if (error) {
        console.error('Error loading maintenance:', error);
        setData(generateDemoData());
      } else {
        const transformedData = maintenanceData?.map(item => ({
          ...item,
          asset_name: item.assets?.name || 'Unknown Asset'
        })) || [];
        setData(transformedData);
      }
    } catch (error) {
      console.error('Error loading maintenance:', error);
      setData(generateDemoData());
    } finally {
      setLoading(false);
    }
  }, [supabase, orgId]);

  // Generate demo data
  const generateDemoData = (): AssetMaintenance[] => [
    {
      id: '1',
      organization_id: orgId,
      asset_id: '1',
      asset_name: 'Main Stage Rigging System',
      maintenance_type: 'preventive',
      status: 'completed',
      priority: 'high',
      title: 'Quarterly Safety Inspection',
      description: 'Comprehensive inspection of rigging system components',
      scheduled_date: '2023-12-15T09:00:00Z',
      completed_date: '2023-12-15T14:30:00Z',
      technician: 'Mike Johnson',
      vendor: 'Stage Tech Pro',
      cost: 2500,
      parts_used: ['Safety cables', 'Inspection tags'],
      work_performed: 'Inspected all rigging points, replaced worn cables, updated safety documentation',
      next_maintenance_date: '2024-03-15',
      warranty_impact: false,
      downtime_hours: 5.5,
      notes: 'All systems passed inspection. Next maintenance scheduled for March 2024.',
      created_at: '2023-11-01T10:00:00Z',
      updated_at: '2023-12-15T14:30:00Z'
    },
    {
      id: '2',
      organization_id: orgId,
      asset_id: '2',
      asset_name: 'Professional Sound System',
      maintenance_type: 'corrective',
      status: 'in_progress',
      priority: 'medium',
      title: 'Audio Mixer Repair',
      description: 'Repair faulty channel on main mixing console',
      scheduled_date: '2024-01-10T10:00:00Z',
      technician: 'Sarah Wilson',
      vendor: 'Audio Pro Services',
      cost: 800,
      parts_used: ['Channel fader', 'Circuit board'],
      work_performed: 'Diagnosed faulty channel, ordered replacement parts',
      warranty_impact: true,
      downtime_hours: 2,
      notes: 'Waiting for parts delivery. Expected completion by end of week.',
      created_at: '2024-01-08T10:00:00Z',
      updated_at: '2024-01-10T12:00:00Z'
    },
    {
      id: '3',
      organization_id: orgId,
      asset_id: '3',
      asset_name: 'LED Video Wall Panels',
      maintenance_type: 'emergency',
      status: 'overdue',
      priority: 'critical',
      title: 'Panel Replacement - Display Failure',
      description: 'Multiple LED panels showing dead pixels and color distortion',
      scheduled_date: '2024-01-05T08:00:00Z',
      technician: 'Tech Team',
      vendor: 'Visual Systems Inc',
      cost: 3200,
      downtime_hours: 8,
      notes: 'URGENT: Display failure affecting upcoming event. Immediate attention required.',
      created_at: '2024-01-04T16:00:00Z',
      updated_at: '2024-01-05T08:00:00Z'
    }
  ];

  // Statistics calculation
  const stats = useMemo(() => {
    const total = data.length;
    const scheduled = data.filter(item => item.status === 'scheduled').length;
    const inProgress = data.filter(item => item.status === 'in_progress').length;
    const completed = data.filter(item => item.status === 'completed').length;
    const overdue = data.filter(item => item.status === 'overdue').length;
    const totalCost = data.reduce((sum, item) => sum + (item.cost || 0), 0);
    const avgDowntime = data.length > 0 ? data.reduce((sum, item) => sum + (item.downtime_hours || 0), 0) / data.length : 0;

    return { total, scheduled, inProgress, completed, overdue, totalCost, avgDowntime };
  }, [data]);

  // Event handlers
  const handleCreate = () => {
    setCreateDrawerOpen(true);
  };

  const handleEdit = (record: AssetMaintenance) => {
    setSelectedRecord(record);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleView = (record: AssetMaintenance) => {
    setSelectedRecord(record);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSave = async (formData: unknown) => {
    try {
      if (drawerMode === 'create') {
        const { error } = await supabase
          .from('asset_maintenance')
          .insert([{ ...formData, organization_id: orgId }]);

        if (error) throw error;
      } else if (drawerMode === 'edit' && selectedRecord) {
        const { error } = await supabase
          .from('asset_maintenance')
          .update(formData)
          .eq('id', selectedRecord.id);

        if (error) throw error;
      }

      await loadData();
      setDrawerOpen(false);
    } catch (error) {
      console.error('Error saving maintenance:', error);
    }
  };

  const handleDelete = async (record: AssetMaintenance) => {
    try {
      const { error } = await supabase
        .from('asset_maintenance')
        .delete()
        .eq('id', record.id);

      if (error) throw error;
      await loadData();
    } catch (error) {
      console.error('Error deleting maintenance:', error);
    }
  };

  // DataView configuration
  const dataViewConfig: DataViewConfig = useMemo(() => ({
    id: 'assets-maintenance',
    name: 'Asset Maintenance',
    description: 'Manage asset maintenance schedules and records',
    viewType: 'list',
    defaultView: 'list',
    fields: fieldConfig,
    data: data,
    loading: loading,
    onRefresh: loadData,
    onSearch: (query: string) => {
      console.log('Search:', query);
    },
    onFilter: (filters: FilterConfig[]) => {
      console.log('Filter:', filters);
    },
    onSort: (sorts: SortConfig[]) => {
      console.log('Sort:', sorts);
    },
    actions: [
      {
        key: 'create',
        id: 'create',
        label: 'Schedule Maintenance',
        icon: 'Plus',
        variant: 'primary' as const,
        onClick: handleCreate
      },
      {
        key: 'edit',
        id: 'edit',
        label: 'Edit',
        icon: 'Edit',
        variant: 'secondary' as const,
        onClick: handleEdit
      },
      {
        key: 'view',
        id: 'view',
        label: 'View',
        icon: 'Eye',
        variant: 'outline' as const,
        onClick: handleView
      },
      {
        key: 'delete',
        id: 'delete',
        label: 'Delete',
        icon: 'Trash',
        variant: 'destructive' as const,
        onClick: handleDelete
      }
    ]
  }), [fieldConfig, data, loading, loadData]);

  useEffect(() => {
    loadData();
  }, [loadData]);

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 font-anton uppercase">Asset Maintenance</h1>
          <p className="text-body-sm color-muted">Schedule and track asset maintenance activities</p>
        </div>
        <Button onClick={handleCreate} className="flex items-center gap-sm">
          <Plus className="w-4 h-4" />
          Schedule Maintenance
        </Button>
      </div>

      {/* Statistics Dashboard */}
      <DashboardView>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-7 gap-md">
          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Total Records</p>
                  <p className="text-heading-3">{stats.total}</p>
                </div>
                <Wrench className="w-8 h-8 color-accent" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Scheduled</p>
                  <p className="text-heading-3">{stats.scheduled}</p>
                </div>
                <Clock className="w-8 h-8 color-secondary" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">In Progress</p>
                  <p className="text-heading-3">{stats.inProgress}</p>
                </div>
                <TrendingUp className="w-8 h-8 color-warning" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Completed</p>
                  <p className="text-heading-3">{stats.completed}</p>
                </div>
                <CheckCircle className="w-8 h-8 color-success" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Overdue</p>
                  <p className="text-heading-3">{stats.overdue}</p>
                </div>
                <AlertCircle className="w-8 h-8 color-destructive" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Total Cost</p>
                  <p className="text-heading-3">${(stats.totalCost / 1000).toFixed(0)}K</p>
                </div>
                <DollarSign className="w-8 h-8 color-info" />
              </div>
            </div>
          </Card>

          <Card>
            <div className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-muted">Avg Downtime</p>
                  <p className="text-heading-3">{stats.avgDowntime.toFixed(1)}h</p>
                </div>
                <Clock className="w-8 h-8 color-muted" />
              </div>
            </div>
          </Card>
        </div>
      </DashboardView>

      {/* Data Views */}
      <DataViewProvider config={dataViewConfig}>
        <div className="stack-md">
          <DataActions />
          <ViewSwitcher />
          <DataGrid />
          <KanbanBoard 
            columns={[
              { id: 'scheduled', title: 'Scheduled' },
              { id: 'in_progress', title: 'In Progress' },
              { id: 'completed', title: 'Completed' },
              { id: 'cancelled', title: 'Cancelled' },
              { id: 'overdue', title: 'Overdue' }
            ]}
            statusField="status"
            titleField="title"
          />
          <CalendarView 
            startDateField="scheduled_date"
            endDateField="completed_date"
            titleField="title"
          />
          <ListView 
            titleField="title"
            subtitleField="maintenance_type"
          />
          <TimelineView 
            startDateField="scheduled_date"
            endDateField="completed_date"
            titleField="title"
          />
        </div>
      </DataViewProvider>

      {/* Universal Drawer */}
      <UniversalDrawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        record={selectedRecord}
        fields={fieldConfig}
        mode={drawerMode}
        title={
          drawerMode === 'create' 
            ? 'Schedule Maintenance'
            : drawerMode === 'edit'
            ? 'Edit Maintenance'
            : 'Maintenance Details'
        }
        onSave={handleSave}
        tabs={[
          {
            key: 'details',
            label: 'Details',
            content: null
          },
          {
            key: 'work',
            label: 'Work Performed',
            content: (
              <div className="p-md">
                <p className="text-body-sm color-muted">Work details and parts used will be displayed here.</p>
              </div>
            )
          },
          {
            key: 'costs',
            label: 'Costs',
            content: (
              <div className="p-md">
                <p className="text-body-sm color-muted">Cost breakdown and billing information will be displayed here.</p>
              </div>
            )
          },
          {
            key: 'history',
            label: 'History',
            content: (
              <div className="p-md">
                <p className="text-body-sm color-muted">Maintenance history will be displayed here.</p>
              </div>
            )
          }
        ]}
      />

      {/* Create Maintenance Drawer */}
      <CreateMaintenanceClient
        isOpen={createDrawerOpen}
        onClose={() => setCreateDrawerOpen(false)}
        onSuccess={() => {
          loadData();
          setCreateDrawerOpen(false);
        }}
        orgId={orgId}
      />
    </div>
  );
}

