                {stats.pendingMaintenance} asset{stats.pendingMaintenance !== 1 ? 's' : ''} require{stats.pendingMaintenance === 1 ? 's' : ''} maintenance attention
              </p>
            </div>
            <Button className="ml-auto">
              View Details
            </Button>
          </div>
        </Card>
      )}
    </div>
  );
}

'use client';

