'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import React from 'react';
import { Card, Badge } from '@ghxstship/ui';
import type { Asset } from '../types';

interface AssetGridViewProps {
  assets: Asset[];
  onEdit?: (asset: Asset) => void;
  onView?: (asset: Asset) => void;
  onDelete?: (asset: Asset) => void;
  loading?: boolean;
}

export default function AssetGridView({ 
  assets, 
  onEdit, 
  onView, 
  onDelete, 
  loading = false 
}: AssetGridViewProps) {
  const getStatusColor = (status: string) => {
    const colors = {
      available: 'success',
      in_use: 'warning',
      under_maintenance: 'secondary',
      damaged: 'destructive',
      missing: 'destructive',
      retired: 'outline'
    };
    return colors[status as keyof typeof colors] || 'secondary';
  };

  const getCategoryIcon = (category: string) => {
    // Return appropriate icon based on category
    return <Package className="w-5 h-5" />;
  };

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {Array.from({ length: 8 }).map((_, i) => (
          <Card key={i} className="animate-pulse">
            <div className="p-md">
              <div className="h-4 bg-muted rounded mb-sm"></div>
              <div className="h-3 bg-muted rounded mb-sm w-3/4"></div>
              <div className="h-3 bg-muted rounded w-1/2"></div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (assets.length === 0) {
    return (
      <div className="text-center py-xl">
        <Package className="w-12 h-12 mx-auto color-muted mb-md" />
        <h3 className="text-heading-4 mb-sm">No Assets Found</h3>
        <p className="text-body-sm color-muted">
          No assets match your current filters. Try adjusting your search criteria.
        </p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
      {assets.map((asset) => (
        <Card key={asset.id} className="group hover:shadow-md transition-shadow">
          <div className="p-md">
            {/* Header */}
            <div className="flex items-start justify-between mb-sm">
              <div className="flex items-center gap-sm">
                {getCategoryIcon(asset.category)}
                <Badge variant={getStatusColor(asset.status) as unknown} size="sm">
                  {asset.status.replace('_', ' ')}
                </Badge>
              </div>
              <div className="flex items-center gap-xs opacity-0 group-hover:opacity-100 transition-opacity">
                {onView && (
                  <button
                    onClick={() => onView(asset)}
                    className="p-xs rounded hover:bg-muted transition-colors"
                    title="View details"
                  >
                    <Eye className="w-4 h-4" />
                  </button>
                )}
                {onEdit && (
                  <button
                    onClick={() => onEdit(asset)}
                    className="p-xs rounded hover:bg-muted transition-colors"
                    title="Edit asset"
                  >
                    <Edit className="w-4 h-4" />
                  </button>
                )}
                {onDelete && (
                  <button
                    onClick={() => onDelete(asset)}
                    className="p-xs rounded hover:bg-destructive/10 color-destructive transition-colors"
                    title="Delete asset"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                )}
              </div>
            </div>

            {/* Content */}
            <div className="space-y-sm">
              <div>
                <h3 className="text-body font-medium line-clamp-2">{asset.name}</h3>
                {asset.description && (
                  <p className="text-body-sm color-muted line-clamp-2 mt-xs">
                    {asset.description}
                  </p>
                )}
              </div>

              {/* Metadata */}
              <div className="space-y-xs">
                {asset.location && (
                  <div className="flex items-center gap-xs text-body-xs color-muted">
                    <MapPin className="w-3 h-3" />
                    <span>{asset.location}</span>
                  </div>
                )}

                {asset.sku && (
                  <div className="text-body-xs color-muted">
                    SKU: {asset.sku}
                  </div>
                )}

                {asset.manufacturer && asset.model && (
                  <div className="text-body-xs color-muted">
                    {asset.manufacturer} {asset.model}
                  </div>
                )}

                {asset.purchase_date && (
                  <div className="flex items-center gap-xs text-body-xs color-muted">
                    <Calendar className="w-3 h-3" />
                    <span>{new Date(asset.purchase_date).toLocaleDateString()}</span>
                  </div>
                )}

                {asset.current_value && (
                  <div className="flex items-center gap-xs text-body-xs color-muted">
                    <DollarSign className="w-3 h-3" />
                    <span>${asset.current_value.toLocaleString()}</span>
                  </div>
                )}
              </div>

              {/* Tags */}
              {asset.tags && asset.tags.length > 0 && (
                <div className="flex flex-wrap gap-xs">
                  {asset.tags.slice(0, 3).map((tag, index) => (
                    <span
                      key={index}
                      className="px-xs py-xs text-body-xs bg-muted/50 rounded text-muted-foreground"
                    >
                      {tag}
                    </span>
                  ))}
                  {asset.tags.length > 3 && (
                    <span className="px-xs py-xs text-body-xs bg-muted/50 rounded text-muted-foreground">
                      +{asset.tags.length - 3}
                    </span>
                  )}
                </div>
              )}
            </div>
          </div>
        </Card>
      ))}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import React from 'react';
import { Card, Badge } from '@ghxstship/ui';
import type { Asset } from '../types';

interface AssetKanbanViewProps {
  assets: Asset[];
  onEdit?: (asset: Asset) => void;
  onView?: (asset: Asset) => void;
  onDelete?: (asset: Asset) => void;
  onStatusChange?: (asset: Asset, newStatus: string) => void;
  loading?: boolean;
}

const STATUS_COLUMNS = [
  { id: 'available', title: 'Available', color: 'border-success' },
  { id: 'in_use', title: 'In Use', color: 'border-warning' },
  { id: 'under_maintenance', title: 'Under Maintenance', color: 'border-secondary' },
  { id: 'damaged', title: 'Damaged', color: 'border-destructive' },
  { id: 'missing', title: 'Missing', color: 'border-destructive' },
  { id: 'retired', title: 'Retired', color: 'border-muted' }
];

export default function AssetKanbanView({ 
  assets, 
  onEdit, 
  onView, 
  onDelete, 
  onStatusChange,
  loading = false 
}: AssetKanbanViewProps) {
  const getAssetsByStatus = (status: string) => {
    return assets.filter(asset => asset.status === status);
  };

  const handleDragStart = (e: React.DragEvent, asset: Asset) => {
    e.dataTransfer.setData('application/json', JSON.stringify(asset));
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = (e: React.DragEvent, newStatus: string) => {
    e.preventDefault();
    const assetData = e.dataTransfer.getData('application/json');
    if (assetData && onStatusChange) {
      const asset = JSON.parse(assetData);
      if (asset.status !== newStatus) {
        onStatusChange(asset, newStatus);
      }
    }
  };

  const getCategoryIcon = (category: string) => {
    return <Package className="w-4 h-4" />;
  };

  if (loading) {
    return (
      <div className="flex gap-md overflow-x-auto pb-md">
        {STATUS_COLUMNS.map((column) => (
          <div key={column.id} className="flex-shrink-0 w-80">
            <div className={`border-t-4 ${column.color} bg-background rounded-lg`}>
              <div className="p-md border-b border-border">
                <h3 className="text-body font-medium">{column.title}</h3>
                <p className="text-body-sm color-muted">Loading...</p>
              </div>
              <div className="p-md space-y-sm">
                {Array.from({ length: 3 }).map((_, i) => (
                  <div key={i} className="p-md border border-border rounded-md animate-pulse">
                    <div className="h-4 bg-muted rounded mb-sm"></div>
                    <div className="h-3 bg-muted rounded w-3/4"></div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="flex gap-md overflow-x-auto pb-md">
      {STATUS_COLUMNS.map((column) => {
        const columnAssets = getAssetsByStatus(column.id);

        return (
          <div key={column.id} className="flex-shrink-0 w-80">
            <div 
              className={`border-t-4 ${column.color} bg-background rounded-lg min-h-96`}
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, column.id)}
            >
              {/* Column Header */}
              <div className="p-md border-b border-border">
                <h3 className="text-body font-medium">{column.title}</h3>
                <p className="text-body-sm color-muted">
                  {columnAssets.length} asset{columnAssets.length !== 1 ? 's' : ''}
                </p>
              </div>

              {/* Column Content */}
              <div className="p-md space-y-sm">
                {columnAssets.length === 0 ? (
                  <div className="text-center py-lg">
                    <Package className="w-8 h-8 mx-auto color-muted mb-sm" />
                    <p className="text-body-sm color-muted">No assets</p>
                  </div>
                ) : (
                  columnAssets.map((asset) => (
                    <Card
                      key={asset.id}
                      className="group cursor-move hover:shadow-md transition-shadow"
                      draggable
                      onDragStart={(e) => handleDragStart(e, asset)}
                    >
                      <div className="p-md">
                        {/* Header */}
                        <div className="flex items-start justify-between mb-sm">
                          <div className="flex items-center gap-sm">
                            {getCategoryIcon(asset.category)}
                            <span className="text-body-xs color-muted capitalize">
                              {asset.category.replace('_', ' ')}
                            </span>
                          </div>
                          <div className="flex items-center gap-xs opacity-0 group-hover:opacity-100 transition-opacity">
                            {onView && (
                              <button
                                onClick={() => onView(asset)}
                                className="p-xs rounded hover:bg-muted transition-colors"
                                title="View details"
                              >
                                <Eye className="w-3 h-3" />
                              </button>
                            )}
                            {onEdit && (
                              <button
                                onClick={() => onEdit(asset)}
                                className="p-xs rounded hover:bg-muted transition-colors"
                                title="Edit asset"
                              >
                                <Edit className="w-3 h-3" />
                              </button>
                            )}
                            {onDelete && (
                              <button
                                onClick={() => onDelete(asset)}
                                className="p-xs rounded hover:bg-destructive/10 color-destructive transition-colors"
                                title="Delete asset"
                              >
                                <Trash2 className="w-3 h-3" />
                              </button>
                            )}
                          </div>
                        </div>

                        {/* Content */}
                        <div className="space-y-sm">
                          <div>
                            <h4 className="text-body-sm font-medium line-clamp-2">
                              {asset.name}
                            </h4>
                            {asset.description && (
                              <p className="text-body-xs color-muted line-clamp-2 mt-xs">
                                {asset.description}
                              </p>
                            )}
                          </div>

                          {/* Metadata */}
                          <div className="space-y-xs">
                            {asset.sku && (
                              <div className="text-body-xs color-muted">
                                SKU: {asset.sku}
                              </div>
                            )}

                            {asset.manufacturer && asset.model && (
                              <div className="text-body-xs color-muted">
                                {asset.manufacturer} {asset.model}
                              </div>
                            )}

                            {asset.location && (
                              <div className="flex items-center gap-xs text-body-xs color-muted">
                                <MapPin className="w-3 h-3" />
                                <span className="truncate">{asset.location}</span>
                              </div>
                            )}

                            {asset.current_value && (
                              <div className="flex items-center gap-xs text-body-xs color-muted">
                                <DollarSign className="w-3 h-3" />
                                <span>${asset.current_value.toLocaleString()}</span>
                              </div>
                            )}
                          </div>

                          {/* Tags */}
                          {asset.tags && asset.tags.length > 0 && (
                            <div className="flex flex-wrap gap-xs">
                              {asset.tags.slice(0, 2).map((tag, index) => (
                                <span
                                  key={index}
                                  className="px-xs py-xs text-body-xs bg-muted/50 rounded text-muted-foreground"
                                >
                                  {tag}
                                </span>
                              ))}
                              {asset.tags.length > 2 && (
                                <span className="px-xs py-xs text-body-xs bg-muted/50 rounded text-muted-foreground">
                                  +{asset.tags.length - 2}
                                </span>
                              )}
                            </div>
                          )}
                        </div>
                      </div>
                    </Card>
                  ))
                )}
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import React from 'react';
import { Badge, Button } from '@ghxstship/ui';
import type { Asset } from '../types';

interface AssetListViewProps {
  assets: Asset[];
  onEdit?: (asset: Asset) => void;
  onView?: (asset: Asset) => void;
  onDelete?: (asset: Asset) => void;
  loading?: boolean;
}

export default function AssetListView({ 
  assets, 
  onEdit, 
  onView, 
  onDelete, 
  loading = false 
}: AssetListViewProps) {
  const getStatusColor = (status: string) => {
    const colors = {
      available: 'success',
      in_use: 'warning',
      under_maintenance: 'secondary',
      damaged: 'destructive',
      missing: 'destructive',
      retired: 'outline'
    };
    return colors[status as keyof typeof colors] || 'secondary';
  };

  const getCategoryIcon = (category: string) => {
    // Return appropriate icon based on category
    return <Package className="w-4 h-4" />;
  };

  if (loading) {
    return (
      <div className="space-y-sm">
        {Array.from({ length: 10 }).map((_, i) => (
          <div key={i} className="flex items-center gap-md p-md border border-border rounded-md animate-pulse">
            <div className="w-4 h-4 bg-muted rounded"></div>
            <div className="flex-1">
              <div className="h-4 bg-muted rounded mb-xs"></div>
              <div className="h-3 bg-muted rounded w-3/4"></div>
            </div>
            <div className="w-16 h-6 bg-muted rounded"></div>
            <div className="w-8 h-8 bg-muted rounded"></div>
          </div>
        ))}
      </div>
    );
  }

  if (assets.length === 0) {
    return (
      <div className="text-center py-xl">
        <Package className="w-12 h-12 mx-auto color-muted mb-md" />
        <h3 className="text-heading-4 mb-sm">No Assets Found</h3>
        <p className="text-body-sm color-muted">
          No assets match your current filters. Try adjusting your search criteria.
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-sm">
      {/* Header */}
      <div className="hidden md:flex items-center gap-md p-md bg-muted/20 rounded-md text-body-sm font-medium color-muted">
        <div className="w-4"></div>
        <div className="flex-1">Asset</div>
        <div className="w-24">Category</div>
        <div className="w-20">Status</div>
        <div className="w-24">Location</div>
        <div className="w-24">Value</div>
        <div className="w-16">Actions</div>
      </div>

      {/* Assets List */}
      {assets.map((asset) => (
        <div 
          key={asset.id} 
          className="flex items-center gap-md p-md border border-border rounded-md hover:bg-muted/20 transition-colors group"
        >
          {/* Icon */}
          <div className="flex-shrink-0">
            {getCategoryIcon(asset.category)}
          </div>

          {/* Asset Info */}
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-sm mb-xs">
              <h3 className="text-body font-medium truncate">{asset.name}</h3>
              {asset.sku && (
                <span className="text-body-xs color-muted bg-muted/50 px-xs py-xs rounded">
                  {asset.sku}
                </span>
              )}
            </div>
            {asset.description && (
              <p className="text-body-sm color-muted line-clamp-1">
                {asset.description}
              </p>
            )}
            <div className="flex items-center gap-md mt-xs text-body-xs color-muted">
              {asset.manufacturer && asset.model && (
                <span>{asset.manufacturer} {asset.model}</span>
              )}
              {asset.purchase_date && (
                <div className="flex items-center gap-xs">
                  <Calendar className="w-3 h-3" />
                  <span>{new Date(asset.purchase_date).toLocaleDateString()}</span>
                </div>
              )}
            </div>
          </div>

          {/* Category - Hidden on mobile */}
          <div className="hidden md:block w-24">
            <span className="text-body-sm color-muted capitalize">
              {asset.category.replace('_', ' ')}
            </span>
          </div>

          {/* Status */}
          <div className="hidden md:block w-20">
            <Badge variant={getStatusColor(asset.status) as unknown} size="sm">
              {asset.status.replace('_', ' ')}
            </Badge>
          </div>

          {/* Location - Hidden on mobile */}
          <div className="hidden md:block w-24">
            {asset.location ? (
              <div className="flex items-center gap-xs text-body-sm color-muted">
                <MapPin className="w-3 h-3" />
                <span className="truncate">{asset.location}</span>
              </div>
            ) : (
              <span className="text-body-sm color-muted">-</span>
            )}
          </div>

          {/* Value - Hidden on mobile */}
          <div className="hidden md:block w-24">
            {asset.current_value ? (
              <div className="flex items-center gap-xs text-body-sm color-muted">
                <DollarSign className="w-3 h-3" />
                <span>${asset.current_value.toLocaleString()}</span>
              </div>
            ) : (
              <span className="text-body-sm color-muted">-</span>
            )}
          </div>

          {/* Mobile Status and Value */}
          <div className="md:hidden flex flex-col items-end gap-xs">
            <Badge variant={getStatusColor(asset.status) as unknown} size="sm">
              {asset.status.replace('_', ' ')}
            </Badge>
            {asset.current_value && (
              <span className="text-body-xs color-muted">
                ${asset.current_value.toLocaleString()}
              </span>
            )}
          </div>

          {/* Actions */}
          <div className="flex-shrink-0 w-16">
            <div className="flex items-center gap-xs opacity-0 group-hover:opacity-100 transition-opacity">
              {onView && (
                <button
                  onClick={() => onView(asset)}
                  className="p-xs rounded hover:bg-muted transition-colors"
                  title="View details"
                >
                  <Eye className="w-4 h-4" />
                </button>
              )}
              {onEdit && (
                <button
                  onClick={() => onEdit(asset)}
                  className="p-xs rounded hover:bg-muted transition-colors"
                  title="Edit asset"
                >
                  <Edit className="w-4 h-4" />
                </button>
              )}
              {onDelete && (
                <button
                  onClick={() => onDelete(asset)}
                  className="p-xs rounded hover:bg-destructive/10 color-destructive transition-colors"
                  title="Delete asset"
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              )}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import type { User } from '@supabase/supabase-js';
import {
  Badge,
  Button,
  Card,
  Skeleton,
  useToastContext,
} from '@ghxstship/ui';
import {
  CalendarView,
  DataActions,
  DataGrid,
  DataViewProvider,
  GalleryView,
  KanbanBoard,
  ListView,
  StateManagerProvider,
  TimelineView,
  ViewSwitcher,
} from '@ghxstship/ui/components/DataViews';
import type {
  ActionConfig,
  DataRecord,
  DataViewConfig,
  ExportConfig,
  FilterConfig,
  ImportConfig,
  SortConfig,
} from '@ghxstship/ui/components/DataViews/types';

import { COMPANY_FIELD_CONFIGS } from './lib/field-config';
import { CompaniesService } from './lib/companies-service';
import type {
  Company,
  CompanyFilters,
  CompanyStats,
} from './types';

interface CompaniesClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

export default function CompaniesClient({
  user,
  orgId,
  translations,
}: CompaniesClientProps) {
  const { showToast } = useToastContext();
  const [companies, setCompanies] = useState<Company[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [stats, setStats] = useState<CompanyStats | null>(null);
  const [filters, setFilters] = useState<CompanyFilters>({});
  const [currentView, setCurrentView] = useState<'grid' | 'list' | 'kanban' | 'calendar' | 'timeline' | 'gallery'>('grid');

  const companiesService = useMemo(() => new CompaniesService(), []);

  // Load companies data
  const loadCompanies = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const [companiesData, statsData] = await Promise.all([
        companiesService.getCompanies(orgId, filters),
        companiesService.getCompanyStats(orgId)
      ]);

      setCompanies(companiesData.data);
      setStats(statsData);
    } catch (err) {
      console.error('Error loading companies:', err);
      setError(err instanceof Error ? err.message : 'Failed to load companies');
      showToast({
        title: 'Error',
        description: 'Failed to load companies data',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  }, [orgId, filters, companiesService, showToast]);

  useEffect(() => {
    loadCompanies();
  }, [loadCompanies]);

  // Convert companies to DataRecord format
  const dataRecords: DataRecord[] = useMemo(() => {
    return companies.map((company) => ({
      id: company.id,
      name: company.name,
      description: company.description || '',
      industry: company.industry || 'Not specified',
      status: company.status,
      size: company.size || 'Not specified',
      website: company.website || '',
      email: company.email || '',
      phone: company.phone || '',
      address: company.address || '',
      city: company.city || '',
      state: company.state || '',
      country: company.country || '',
      postal_code: company.postal_code || '',
      founded_year: company.founded_year || 0,
      logo_url: company.logo_url || '',
      notes: company.notes || '',
      created_at: company.created_at,
      updated_at: company.updated_at,
      _raw: company,
    }));
  }, [companies]);

  // Data view configuration
  const dataViewConfig: DataViewConfig = useMemo(() => ({
    fields: COMPANY_FIELD_CONFIGS,
    defaultView: 'grid',
    enabledViews: ['grid', 'list', 'kanban'],
    itemsPerPage: 50,
    enableSearch: true,
    enableFilters: true,
    enableSort: true,
    enableExport: true,
    enableImport: true,
    enableBulkActions: true,
  }), []);

  // Filter configuration
  const filterConfig: FilterConfig = useMemo(() => ({
    fields: [
      {
        key: 'status',
        label: 'Status',
        type: 'select',
        options: [
          { label: 'Active', value: 'active' },
          { label: 'Pending', value: 'pending' },
          { label: 'Inactive', value: 'inactive' },
          { label: 'Blacklisted', value: 'blacklisted' },
        ],
        multiple: true,
      },
      {
        key: 'industry',
        label: 'Industry',
        type: 'text',
      },
      {
        key: 'size',
        label: 'Company Size',
        type: 'select',
        options: [
          { label: 'Startup', value: 'startup' },
          { label: 'Small', value: 'small' },
          { label: 'Medium', value: 'medium' },
          { label: 'Large', value: 'large' },
          { label: 'Enterprise', value: 'enterprise' },
        ],
        multiple: true,
      },
      {
        key: 'country',
        label: 'Country',
        type: 'text',
      },
    ],
  }), []);

  // Sort configuration
  const sortConfig: SortConfig = useMemo(() => ({
    defaultSort: { field: 'name', direction: 'asc' },
    fields: [
      { key: 'name', label: 'Company Name' },
      { key: 'industry', label: 'Industry' },
      { key: 'status', label: 'Status' },
      { key: 'size', label: 'Size' },
      { key: 'founded_year', label: 'Founded Year' },
      { key: 'created_at', label: 'Created Date' },
      { key: 'updated_at', label: 'Updated Date' },
    ],
  }), []);

  // Action configuration
  const actionConfig: ActionConfig = useMemo(() => ({
    primary: {
      label: 'Add Company',
      icon: Plus,
      action: () => {
        // Handle add company
        showToast({
          title: 'Info',
          description: 'Add company functionality will be implemented',
        });
      },
    },
    bulk: [
      {
        label: 'Export Selected',
        icon: Download,
        action: (selectedIds: string[]) => {
          showToast({
            title: 'Info',
            description: `Exporting ${selectedIds.length} companies`,
          });
        },
      },
    ],
    row: [
      {
        label: 'View Details',
        icon: Building,
        action: (record: DataRecord) => {
          showToast({
            title: 'Info',
            description: `Viewing details for ${record.name}`,
          });
        },
      },
    ],
  }), [showToast]);

  // Export configuration
  const exportConfig: ExportConfig = useMemo(() => ({
    formats: ['csv', 'json', 'xlsx'],
    filename: 'companies',
    fields: COMPANY_FIELD_CONFIGS.map(field => ({
      key: field.key,
      label: field.label,
    })),
  }), []);

  // Import configuration
  const importConfig: ImportConfig = useMemo(() => ({
    formats: ['csv', 'json', 'xlsx'],
    fields: COMPANY_FIELD_CONFIGS.map(field => ({
      key: field.key,
      label: field.label,
      required: field.required || false,
    })),
    onImport: async (data: unknown[]) => {
      showToast({
        title: 'Info',
        description: `Importing ${data.length} companies`,
      });
    },
  }), [showToast]);

  // Handle filter changes
  const handleFiltersChange = useCallback((newFilters: Record<string, unknown>) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
  }, []);

  // Handle view change
  const handleViewChange = useCallback((view: string) => {
    setCurrentView(view as unknown);
  }, []);

  // Render statistics cards
  const renderStats = () => {
    if (!stats) return null;

    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md mb-lg">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Total Companies</p>
              <p className="text-2xl font-bold">{stats.totalCompanies}</p>
            </div>
            <Building className="h-8 w-8 text-primary" />
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Active Companies</p>
              <p className="text-2xl font-bold text-green-600">{stats.activeCompanies}</p>
            </div>
            <Badge variant="success" className="h-8 w-8 rounded-full p-0 flex items-center justify-center">
              <span className="text-xs font-bold">{Math.round((stats.activeCompanies / stats.totalCompanies) * 100)}%</span>
            </Badge>
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Active Contracts</p>
              <p className="text-2xl font-bold text-blue-600">{stats.activeContracts}</p>
            </div>
            <FileText className="h-8 w-8 text-blue-600" />
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Average Rating</p>
              <p className="text-2xl font-bold text-yellow-600">{stats.averageRating.toFixed(1)}</p>
            </div>
            <Star className="h-8 w-8 text-yellow-600" />
          </div>
        </Card>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="space-y-md">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
          {Array.from({ length: 4 }).map((_, i) => (
            <Card key={i} className="p-md">
              <Skeleton className="h-16 w-full" />
            </Card>
          ))}
        </div>
        <Card className="p-md">
          <Skeleton className="h-96 w-full" />
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <Card className="p-lg text-center">
        <p className="text-destructive mb-md">{error}</p>
        <Button onClick={loadCompanies}>Retry</Button>
      </Card>
    );
  }

  return (
    <div className="space-y-md">
      {renderStats()}

      <StateManagerProvider>
        <DataViewProvider
          data={dataRecords}
          config={dataViewConfig}
          onFiltersChange={handleFiltersChange}
          onViewChange={handleViewChange}
        >
          <Card className="p-md">
            <div className="flex items-center justify-between mb-md">
              <div>
                <h2 className="text-xl font-semibold">{translations.title}</h2>
                <p className="text-muted-foreground">{translations.subtitle}</p>
              </div>
              <div className="flex items-center gap-sm">
                <ViewSwitcher />
                <DataActions
                  actionConfig={actionConfig}
                  exportConfig={exportConfig}
                  importConfig={importConfig}
                />
              </div>
            </div>

            {currentView === 'grid' && (
              <DataGrid
                filterConfig={filterConfig}
                sortConfig={sortConfig}
              />
            )}

            {currentView === 'list' && (
              <ListView
                filterConfig={filterConfig}
                sortConfig={sortConfig}
              />
            )}

            {currentView === 'kanban' && (
              <KanbanBoard
                groupBy="status"
                filterConfig={filterConfig}
              />
            )}

            {currentView === 'calendar' && (
              <CalendarView
                dateField="created_at"
                filterConfig={filterConfig}
              />
            )}

            {currentView === 'timeline' && (
              <TimelineView
                dateField="created_at"
                filterConfig={filterConfig}
              />
            )}

            {currentView === 'gallery' && (
              <GalleryView
                imageField="logo_url"
                filterConfig={filterConfig}
              />
            )}
          </Card>
        </DataViewProvider>
      </StateManagerProvider>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import type { User } from '@supabase/supabase-js';
import {
  Badge,
  Button,
  Card,
  Skeleton,
  useToastContext,
} from '@ghxstship/ui';
import {
  DataActions,
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
} from '@ghxstship/ui/components/DataViews';
import type {
  ActionConfig,
  ExportConfig,
  FilterConfig,
  ImportConfig,
} from '@ghxstship/ui/components/DataViews/types';

import { ContractsService } from './lib/contracts-service';
import type { CompanyContract, ContractFilters, ContractStats } from './types';
import ContractsGridView from './views/ContractsGridView';
import ContractsListView from './views/ContractsListView';
import CreateContractDrawer from './drawers/CreateContractDrawer';

interface ContractsClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface CompanyContract {
  id: string;
  companyId: string;
  companyName?: string;
  organizationId: string;
  projectId?: string;
  name: string;
  description?: string;
  type: 'msa' | 'sow' | 'nda' | 'service' | 'supply' | 'other';
  status: 'draft' | 'pending' | 'active' | 'expired' | 'terminated' | 'renewed';
  value?: number;
  currency: string;
  startDate: string;
  endDate?: string;
  renewalDate?: string;
  terms?: string;
  documentUrl?: string;
  signedDate?: string;
  signedBy?: string;
  autoRenewal: boolean;
  renewalNoticeDays?: number;
  createdAt?: string;
  updatedAt?: string;
  createdBy?: string;
}

interface CompanySummary {
  id: string;
  name: string;
}

interface SupabaseContractRow {
  id: string;
  company_id: string;
  organization_id: string;
  project_id?: string | null;
  name: string;
  description?: string | null;
  type: CompanyContract['type'];
  status: CompanyContract['status'];
  value?: number | null;
  currency?: string | null;
  start_date: string;
  end_date?: string | null;
  renewal_date?: string | null;
  terms?: string | null;
  document_url?: string | null;
  signed_date?: string | null;
  signed_by?: string | null;
  auto_renewal: boolean | null;
  renewal_notice_days?: number | null;
  created_at?: string | null;
  updated_at?: string | null;
  created_by?: string | null;
  companies?: {
    name?: string | null;
  } | null;
};

export default function ContractsClient({ user, orgId, translations }: ContractsClientProps) {
  const [loading, setLoading] = useState(true);
  const [contracts, setContracts] = useState<CompanyContract[]>([]);
  const [selectedContract, setSelectedContract] = useState<CompanyContract | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');
  const [currentView, setCurrentView] = useState<'grid' | 'list'>('grid');
  const [typeFilter, setTypeFilter] = useState<string>('all');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [companies, setCompanies] = useState<CompanySummary[]>([]);

  const supabase = useMemo(() => createBrowserClient(), []);

  const loadContracts = useCallback(async () => {
    try {
      setLoading(true);
      let query = supabase
        .from('company_contracts')
        .select(
          `id, company_id, organization_id, project_id, name, description, type, status, value, currency,
           start_date, end_date, renewal_date, terms, document_url, signed_date, signed_by, auto_renewal,
           renewal_notice_days, created_at, updated_at, created_by, companies(name)`
        )
        .eq('organization_id', orgId);

      if (typeFilter !== 'all') {
        query = query.eq('type', typeFilter);
      }

      if (statusFilter !== 'all') {
        query = query.eq('status', statusFilter);
      }

      const { data, error } = await query.order('created_at', { ascending: false });

      if (error) throw error;

      const rows = (data ?? []) as SupabaseContractRow[];

      const normalizedContracts: CompanyContract[] = rows.map(contract => ({
        id: contract.id,
        companyId: contract.company_id,
        companyName: contract.companies?.name ?? undefined,
        organizationId: contract.organization_id,
        projectId: contract.project_id ?? undefined,
        name: contract.name,
        description: contract.description ?? undefined,
        type: contract.type,
        status: contract.status,
        value: contract.value ?? undefined,
        currency: contract.currency ?? 'USD',
        startDate: contract.start_date,
        endDate: contract.end_date ?? undefined,
        renewalDate: contract.renewal_date ?? undefined,
        terms: contract.terms ?? undefined,
        documentUrl: contract.document_url ?? undefined,
        signedDate: contract.signed_date ?? undefined,
        signedBy: contract.signed_by ?? undefined,
        autoRenewal: Boolean(contract.auto_renewal),
        renewalNoticeDays: contract.renewal_notice_days ?? undefined,
        createdAt: contract.created_at ?? undefined,
        updatedAt: contract.updated_at ?? undefined,
        createdBy: contract.created_by ?? undefined
      }));

      setContracts(normalizedContracts);
    } catch (error) {
      console.error('Error loading contracts:', error);
    } finally {
      setLoading(false);
    }
  }, [orgId, statusFilter, supabase, typeFilter]);

  const loadCompanies = useCallback(async () => {
    try {
      const { data, error } = await supabase
        .from('companies')
        .select('id, name')
        .eq('organization_id', orgId)
        .eq('status', 'active')
        .order('name');

      if (error) throw error;
      const rows = (data ?? []) as { id: string; name: string }[];
      const normalizedCompanies: CompanySummary[] = rows.map(company => ({
        id: company.id,
        name: company.name
      }));

      setCompanies(normalizedCompanies);
    } catch (error) {
      console.error('Error loading companies:', error);
    }
  }, [orgId, supabase]);

  useEffect(() => {
    void loadContracts();
  }, [loadContracts]);

  useEffect(() => {
    void loadCompanies();
  }, [loadCompanies]);

  const handleCreateContract = () => {
    setSelectedContract(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditContract = (contract: CompanyContract) => {
    setSelectedContract(contract);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewContract = (contract: CompanyContract) => {
    setSelectedContract(contract);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleDeleteContract = async (contractId: string) => {
    if (!confirm('Are you sure you want to delete this contract?')) return;

    try {
      const { error } = await supabase
        .from('company_contracts')
        .delete()
        .eq('id', contractId)
        .eq('organization_id', orgId);

      if (error) throw error;
      await loadContracts();
    } catch (error) {
      console.error('Error deleting contract:', error);
    }
  };

  const handleRenewContract = async (contractId: string) => {
    try {
      const contract = contracts.find(c => c.id === contractId);
      if (!contract) return;

      const newEndDate = new Date(contract.endDate || contract.startDate);
      newEndDate.setFullYear(newEndDate.getFullYear() + 1);

      const { error } = await supabase
        .from('company_contracts')
        .update({
          status: 'active',
          end_date: newEndDate.toISOString().split('T')[0],
          renewal_date: new Date().toISOString().split('T')[0]
        })
        .eq('id', contractId)
        .eq('organization_id', orgId);

      if (error) throw error;
      await loadContracts();
    } catch (error) {
      console.error('Error renewing contract:', error);
    }
  };

  const handleSaveContract = async (contractData: Partial<CompanyContract>) => {
    try {
      if (drawerMode === 'create') {
        const { error } = await supabase
          .from('company_contracts')
          .insert({
            ...contractData,
            organization_id: orgId,
            created_by: user.id,
            status: 'draft',
            currency: contractData.currency || 'USD',
            auto_renewal: contractData.autoRenewal || false
          });

        if (error) throw error;
      } else if (drawerMode === 'edit' && selectedContract) {
        const { error } = await supabase
          .from('company_contracts')
          .update(contractData)
          .eq('id', selectedContract.id)
          .eq('organization_id', orgId);

        if (error) throw error;
      }

      setDrawerOpen(false);
      await loadContracts();
    } catch (error) {
      console.error('Error saving contract:', error);
    }
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  const getStatusBadge = (status: string) => {
    const config = {
      draft: { variant: 'secondary' as const, label: 'Draft' },
      pending: { variant: 'outline' as const, label: 'Pending' },
      active: { variant: 'default' as const, label: 'Active' },
      expired: { variant: 'destructive' as const, label: 'Expired' },
      terminated: { variant: 'destructive' as const, label: 'Terminated' },
      renewed: { variant: 'default' as const, label: 'Renewed' }
    };

    const statusConfig = config[status as keyof typeof config] || config.draft;
    return <Badge variant={statusConfig.variant}>{statusConfig.label}</Badge>;
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'msa':
        return <FileText className="h-5 w-5 color-accent" />;
      case 'sow':
        return <FileText className="h-5 w-5 color-success" />;
      case 'nda':
        return <FileText className="h-5 w-5 color-secondary" />;
      case 'service':
        return <FileText className="h-5 w-5 color-warning" />;
      case 'supply':
        return <FileText className="h-5 w-5 text-info" />;
      default:
        return <FileText className="h-5 w-5 color-muted" />;
    }
  };

  const isExpiringSoon = (endDate?: string) => {
    if (!endDate) return false;
    const end = new Date(endDate);
    const thirtyDaysFromNow = new Date();
    thirtyDaysFromNow.setDate(thirtyDaysFromNow.getDate() + 30);
    return end <= thirtyDaysFromNow && end >= new Date();
  };


  const typeCounts = {
    all: contracts.length,
    msa: contracts.filter(c => c.type === 'msa').length,
    sow: contracts.filter(c => c.type === 'sow').length,
    nda: contracts.filter(c => c.type === 'nda').length,
    service: contracts.filter(c => c.type === 'service').length,
    supply: contracts.filter(c => c.type === 'supply').length,
    other: contracts.filter(c => c.type === 'other').length
  };

  const statusCounts = {
    all: contracts.length,
    draft: contracts.filter(c => c.status === 'draft').length,
    pending: contracts.filter(c => c.status === 'pending').length,
    active: contracts.filter(c => c.status === 'active').length,
    expired: contracts.filter(c => c.status === 'expired').length,
    terminated: contracts.filter(c => c.status === 'terminated').length
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="flex items-center justify-between">
          <Skeleton className="h-8 w-48" />
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
          {Array.from({ length: 6 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <Skeleton className="h-4 w-32 mb-sm" />
              <Skeleton className="h-6 w-24 mb-md" />
              <Skeleton className="h-4 w-20" />
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <StateManagerProvider>
      <div className="stack-lg">
        {/* Header */}
        <div className="flex items-center justify-between mb-lg">
          <div>
            <h2 className="text-heading-2">Company Contracts</h2>
            <p className="text-sm text-muted-foreground mt-xs">Manage contracts and agreements with companies</p>
          </div>
          <div className="flex items-center cluster-sm">
            <div className="flex border rounded-md">
              <Button
                variant={currentView === 'grid' ? 'default' : 'ghost'}
                onClick={() => setCurrentView('grid')}
              >
                Grid
              </Button>
              <Button
                variant={currentView === 'list' ? 'default' : 'ghost'}
                onClick={() => setCurrentView('list')}
              >
                List
              </Button>
            </div>
            <Button onClick={handleCreateContract}>
              <IconWithText icon={Plus} text="New Contract" size="sm" />
            </Button>
          </div>
        </div>

        {/* Filter Tabs */}
        <div className="flex flex-wrap items-center gap-md">
          {/* Type Filter */}
          <div className="flex cluster-xs bg-secondary p-xs rounded-lg">
            {Object.entries(typeCounts).map(([type, count]) => (
              <Button
                key={type}
                variant={typeFilter === type ? 'default' : 'ghost'}
                onClick={() => setTypeFilter(type)}
                className="capitalize"
              >
                {type === 'msa' ? 'MSA' : type === 'sow' ? 'SOW' : type === 'nda' ? 'NDA' : type} ({count})
              </Button>
            ))}
          </div>

          {/* Status Filter */}
          <div className="flex cluster-xs bg-secondary p-xs rounded-lg">
            {Object.entries(statusCounts).map(([status, count]) => (
              <Button
                key={status}
                variant={statusFilter === status ? 'default' : 'ghost'}
                onClick={() => setStatusFilter(status)}
                className="capitalize"
              >
                {status} ({count})
              </Button>
            ))}
          </div>
        </div>

        {/* Contract Grid/List View */}
        {currentView === 'grid' ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
            {contracts.map(contract => (
              <Card key={contract.id} className="p-lg hover:shadow-elevated transition-shadow cursor-pointer" onClick={() => handleViewContract(contract)}>
                <div className="flex items-start justify-between mb-md">
                  <div className="flex items-center cluster-sm">
                    {getTypeIcon(contract.type)}
                    <div>
                      <h3 className="text-heading-4 color-foreground">{contract.name}</h3>
                      <p className="text-body-sm color-foreground/70">{contract.companyName}</p>
                    </div>
                  </div>
                  {getStatusBadge(contract.status)}
                </div>

                {contract.description && (
                  <p className="text-body-sm color-foreground/70 mb-md line-clamp-2">
                    {contract.description}
                  </p>
                )}

                <div className="stack-sm mb-md">
                  <div className="flex justify-between text-body-sm">
                    <span className="color-foreground/70">Type</span>
                    <span className="form-label capitalize">
                      {contract.type === 'msa' ? 'MSA' : contract.type === 'sow' ? 'SOW' : contract.type === 'nda' ? 'NDA' : contract.type}
                    </span>
                  </div>

                  {contract.value && (
                    <div className="flex justify-between text-body-sm">
                      <span className="color-foreground/70">Value</span>
                      <span className="form-label color-success">{formatCurrency(contract.value, contract.currency)}</span>
                    </div>
                  )}

                  <div className="flex justify-between text-body-sm">
                    <span className="color-foreground/70">Start Date</span>
                    <span className="form-label">{formatDate(contract.startDate)}</span>
                  </div>

                  {contract.endDate && (
                    <div className="flex justify-between text-body-sm">
                      <span className="color-foreground/70">End Date</span>
                      <span className={`form-label ${isExpiringSoon(contract.endDate) ? 'color-warning' : 'color-foreground'}`}>
                        {formatDate(contract.endDate)}
                        {isExpiringSoon(contract.endDate) && (
                          <AlertTriangle className="inline h-3 w-3 ml-xs" />
                        )}
                      </span>
                    </div>
                  )}

                  {contract.autoRenewal && (
                    <div className="flex items-center gap-sm">
                      <Icon icon={FileText} size="sm" color="muted" />
                      <span className="text-sm text-muted-foreground">Total Contracts</span>
                    </div>
                  )}
                </div>

                <div className="flex items-center justify-between pt-md border-t border-border">
                  <div className="flex cluster-sm">
                    {contract.status === 'expired' && (
                      <Button
                        variant="default"
                        onClick={(e: React.MouseEvent) => {
                          e.stopPropagation();
                          handleRenewContract(contract.id);
                        }}
                      >
                        Renew
                      </Button>
                    )}
                    {contract.documentUrl && (
                      <Button
                        variant="ghost"
                        onClick={(e: React.MouseEvent) => {
                          e.stopPropagation();
                          window.open(contract.documentUrl, '_blank');
                        }}
                      >
                        <Download className="h-3 w-3" />
                      </Button>
                    )}
                  </div>

                  <div className="flex cluster-sm">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setSelectedContract(contract)}
                    >
                      View Details
                    </Button>
                    <Button
                      variant="default"
                      size="sm"
                      onClick={() => {
                        setSelectedContract(contract);
                        setDrawerMode('edit');
                        setDrawerOpen(true);
                      }}
                    >
                      Edit
                    </Button>
                  </div>
                </div>
              </Card>
            ))}
          </div>
        ) : (
          <Card className="p-lg">
            <div className="stack-md">
              {contracts.map(contract => (
                <div key={contract.id} className="border rounded-lg p-md">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-body text-heading-4">{contract.name}</h3>
                      <p className="text-body-sm color-muted">{contract.type}</p>
                    </div>
                    <div className="flex items-center gap-sm">
                      <Badge variant={contract.status === 'active' ? 'success' : 'outline'}>
                        {contract.status}
                      </Badge>
                      <Button onClick={() => handleEditContract(contract)}>
                        Edit
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </Card>
        )}

        {/* Empty State */}
        {contracts.length === 0 && (
          <Card className="p-2xl text-center">
            <FileText className="h-12 w-12 mx-auto mb-md color-foreground/30" />
            <h3 className="text-body text-heading-4 color-foreground mb-sm">No contracts found</h3>
            <p className="color-foreground/70 mb-md">Create your first company contract to get started</p>
            <Button onClick={handleCreateContract}>
              <Plus className="h-4 w-4 mr-sm" />
              New Contract
            </Button>
          </Card>
        )}

        {/* Drawer for CRUD operations */}
        <AppDrawer
          open={drawerOpen}
          onClose={() => setDrawerOpen(false)}
          record={selectedContract as unknown}
          fields={[]}
          mode={drawerMode === 'create' ? 'create' : drawerMode === 'edit' ? 'edit' : 'view'}
          title={drawerMode === 'create' ? 'New Contract' : drawerMode === 'edit' ? 'Edit Contract' : 'Contract Details'}
          tabs={[
            {
              key: 'content',
              label: 'Details',
              content: (
                <div className="p-lg">
                  <p className="color-muted">Contract details will be displayed here.</p>
                </div>
              )
            }
          ]}
        />
      </div>
    </StateManagerProvider>
  );
}

"use client";

import { useCallback, useEffect, useMemo, useState } from 'react';
import type { User } from '@supabase/supabase-js';
import {
  Badge,
  Button,
  Card,
  Skeleton,
  useToastContext,
} from '@ghxstship/ui';
import {
  CalendarView,
  DataActions,
  DataGrid,
  DataViewProvider,
  GalleryView,
  KanbanBoard,
  ListView,
  StateManagerProvider,
  TimelineView,
  ViewSwitcher,
} from '@ghxstship/ui/components/DataViews';
import type {
  ActionConfig,
  DataRecord,
  DataViewConfig,
  ExportConfig,
  FilterConfig,
  ImportConfig,
  SortConfig,
} from '@ghxstship/ui/components/DataViews/types';

import { COMPANY_FIELD_CONFIGS } from '../lib/field-config';
import { DirectoryService } from './lib/directory-service';
import type {
  Company,
  DirectoryFilters,
  DirectoryStats,
} from './types';
import CreateCompanyDrawer from './drawers/CreateCompanyDrawer';
import EditCompanyDrawer from './drawers/EditCompanyDrawer';

interface DirectoryClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

const EXPORT_FORMATS: ExportConfig['formats'] = ['csv', 'excel', 'json'];
const IMPORT_FORMATS: ImportConfig['formats'] = ['csv', 'json'];

export default function DirectoryClient({ user, orgId, translations }: DirectoryClientProps) {
  const { toast } = useToastContext();
  const directoryService = useMemo(() => new DirectoryService(), []);

  const [loading, setLoading] = useState(true);
  const [companies, setCompanies] = useState<Company[]>([]);
  const [stats, setStats] = useState<DirectoryStats | null>(null);
  const [filters, setFilters] = useState<DirectoryFilters>({});
  const [searchQuery, setSearchQuery] = useState<string>("");
  const [sorts, setSorts] = useState<SortConfig[]>([]);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(25);
  const [total, setTotal] = useState(0);
  const [selectedCompany, setSelectedCompany] = useState<Company | null>(null);
  const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
  const [editDrawerOpen, setEditDrawerOpen] = useState(false);

  const appliedFilters = useMemo(() => ({
    ...filters,
    ...(searchQuery ? { search: searchQuery } : {}),
  }), [filters, searchQuery]);

  const loadDirectory = useCallback(async () => {
    setLoading(true);
    try {
      const result = await directoryService.getCompanies(orgId, appliedFilters, page, pageSize);
      let nextData = result.data ?? [];

      if (sorts.length) {
        const { field, direction } = sorts[0];
        nextData = [...nextData].sort((a: any, b: unknown) => {
          const aValue = a?.[field];
          const bValue = b?.[field];

          if (aValue === bValue) return 0;
          if (aValue == null) return direction === "asc" ? -1 : 1;
          if (bValue == null) return direction === "asc" ? 1 : -1;

          if (typeof aValue === "number" && typeof bValue === "number") {
            return direction === "asc" ? aValue - bValue : bValue - aValue;
          }

          return direction === "asc"
            ? String(aValue).localeCompare(String(bValue))
            : String(bValue).localeCompare(String(aValue));
        });
      }

      setCompanies(nextData as Company[]);
      setTotal(result.count ?? nextData.length);
      const directoryStats = await directoryService.getDirectoryStats(orgId);
      setStats(directoryStats);
    } catch (error) {
      console.error("Error loading directory", error);
      toast.error('Failed to load directory', error instanceof Error ? error.message : 'Unknown error');
    } finally {
      setLoading(false);
    }
  }, [directoryService, orgId, appliedFilters, page, pageSize, sorts, toast]);

  useEffect(() => {
    void loadDirectory();
  }, [loadDirectory]);

  const handleSearch = useCallback((query: string) => {
    setSearchQuery(query.trim());
    setPage(1);
  }, []);

  const handleFilter = useCallback((filterConfigs: FilterConfig[]) => {
    const next: DirectoryFilters = {};

    filterConfigs.forEach((config) => {
      if (!config.field) return;
      const value = config.value;
      if (value == null || value === "" || (Array.isArray(value) && value.length === 0)) return;

      switch (config.field) {
        case "industry":
          next.industry = Array.isArray(value) ? value.filter(Boolean) : [value];
          break;
        case "status":
          next.status = Array.isArray(value) ? value.filter(Boolean) as DirectoryFilters["status"] : [value];
          break;
        case "size":
          next.size = Array.isArray(value) ? value.filter(Boolean) as DirectoryFilters["size"] : [value];
          break;
        case "country":
          next.country = Array.isArray(value) ? value.filter(Boolean) : [value];
          break;
        case "city":
          next.city = Array.isArray(value) ? value.filter(Boolean) : [value];
          break;
        case "founded_year":
          if (typeof value === "object" && value !== null) {
            const { min, max } = value as { min?: number; max?: number };
            if (min) next.founded_year_min = min;
            if (max) next.founded_year_max = max;
          }
          break;
        default:
          break;
      }
    });

    setFilters(next);
    setPage(1);
  }, []);

  const handleSort = useCallback((nextSorts: SortConfig[]) => {
    setSorts(nextSorts);
  }, []);

  const companyRecords = useMemo<DataRecord[]>(
    () =>
      companies.map((company) => ({
        id: company.id,
        name: company.name,
        industry: company.industry ?? "",
        status: company.status,
        size: company.size ?? "",
        website: company.website ?? "",
        email: company.email ?? "",
        phone: company.phone ?? "",
        city: company.city ?? "",
        country: company.country ?? "",
        founded_year: company.founded_year ?? null,
        created_at: company.created_at,
        updated_at: company.updated_at,
      })),
    [companies]
  );

  const downloadBlob = (blob: Blob, filename: string) => {
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const exportData = useCallback(
    async (format: string, records?: DataRecord[]) => {
      const dataset = records && records.length
        ? records
        : await directoryService.exportData(orgId, "companies", { format: "json", filters: appliedFilters });

      if (!dataset || dataset.length === 0) {
        toast.warning('No records to export', 'Adjust filters and try again.');
        return;
      }

      if (format === "json") {
        const blob = new Blob([JSON.stringify(dataset, null, 2)], { type: "application/json" });
        downloadBlob(blob, `companies-${new Date().toISOString()}.json`);
      } else {
        const headers = Object.keys(dataset[0]);
        const rows = [headers.join(",")];
        dataset.forEach((row: unknown) => {
          const values = headers.map((header) => {
            const value = row[header] ?? "";
            const serialized = String(value).replace(/"/g, '""');
            return serialized.includes(",") ? `"${serialized}"` : serialized;
          });
          rows.push(values.join(","));
        });
        const blob = new Blob([rows.join("\n")], { type: "text/csv" });
        downloadBlob(blob, `companies-${new Date().toISOString()}.csv`);
      }

      toast.success('Export complete', `Records exported as ${format.toUpperCase()}.`);
    },
    [directoryService, orgId, appliedFilters, toast]
  );

  const importData = useCallback(
    async (records: DataRecord[]) => {
      if (!records.length) {
        toast.warning('No records detected', 'Upload a file containing company data.');
        return;
      }

      const payload = records.map((record) => ({
        organization_id: orgId,
        name: record.name ?? record.company_name,
        description: record.description ?? null,
        industry: record.industry ?? null,
        website: record.website ?? null,
        email: record.email ?? null,
        phone: record.phone ?? null,
        address: record.address ?? null,
        city: record.city ?? null,
        state: record.state ?? null,
        country: record.country ?? null,
        postal_code: record.postal_code ?? null,
        size: record.size ?? null,
        founded_year: record.founded_year ?? null,
        status: (record.status as Company["status"]) ?? "pending",
        notes: record.notes ?? null,
        created_by: user.id,
      }));

      const result = await directoryService.importCompanies(orgId, payload);

      if (!result.success) {
        toast.error('Import failed', result.errors?.join('\n') ?? 'Unknown error');
        return;
      }

      toast.success('Import complete', `${result.inserted} records imported successfully.`);
      void loadDirectory();
    },
    [directoryService, orgId, user.id, toast, loadDirectory]
  );

  const handleBulkStatus = useCallback(
    async (ids: string[], status: Company["status"]) => {
      if (!ids.length) return;
      await directoryService.bulkUpdateStatus(orgId, ids, status);
      toast.success(`Companies marked as ${status}`, `${ids.length} record(s) updated.`);
      void loadDirectory();
    },
    [directoryService, orgId, loadDirectory, toast]
  );

  const handleBulkDelete = useCallback(
    async (ids: string[]) => {
      if (!ids.length) return;
      await directoryService.bulkDelete(orgId, ids);
      toast.success('Companies deleted', `${ids.length} record(s) removed.`);
      void loadDirectory();
    },
    [directoryService, orgId, loadDirectory, toast]
  );

  const bulkActions = useMemo<ActionConfig[]>(
    () => [
      {
        key: "activate",
        label: "Mark as Active",
        icon: <Badge variant="success">Active</Badge>,
        onClick: (ids) => handleBulkStatus(ids, "active"),
        requiresSelection: true,
      },
      {
        key: "deactivate",
        label: "Mark as Inactive",
        icon: <Badge variant="secondary">Inactive</Badge>,
        onClick: (ids) => handleBulkStatus(ids, "inactive"),
        requiresSelection: true,
      },
      {
        key: "export",
        label: "Export Selected",
        icon: <Download className="h-4 w-4" />,
        onClick: async (selectedIds) => {
          const subset = companyRecords.filter((record) => selectedIds.includes(record.id));
          await exportData('csv', subset);
        },
        requiresSelection: true,
      },
      {
        key: "delete",
        label: "Delete Selected",
        icon: <Badge variant="destructive">Delete</Badge>,
        onClick: handleBulkDelete,
        requiresSelection: true,
        confirmMessage: "Delete selected companies? This cannot be undone.",
      },
    ],
    [handleBulkStatus, exportData, handleBulkDelete]
  );

  const statsCards = stats ? (
    <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-5 gap-md">
      <Card className="p-lg">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-body-sm color-foreground/70">Total Companies</p>
            <p className="text-heading-3 color-foreground">{stats.totalCompanies}</p>
            <p className="text-body-sm color-success/80">{stats.activeCompanies} active</p>
          </div>
          <Building className="h-8 w-8 color-accent" />
        </div>
      </Card>
      <Card className="p-lg">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-body-sm color-foreground/70">Pending Review</p>
            <p className="text-heading-3 color-warning">{stats.pendingCompanies}</p>
            <p className="text-body-sm color-foreground/50">Require follow-up</p>
          </div>
          <Badge variant="warning">Pending</Badge>
        </div>
      </Card>
      <Card className="p-lg">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-body-sm color-foreground/70">Inactive Records</p>
            <p className="text-heading-3 color-muted">{stats.inactiveCompanies}</p>
            <p className="text-body-sm color-foreground/50">Keep data fresh</p>
          </div>
          <Badge variant="secondary">Inactive</Badge>
        </div>
      </Card>
      <Card className="p-lg">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-body-sm color-foreground/70">Blacklisted</p>
            <p className="text-heading-3 color-destructive">{stats.blacklistedCompanies}</p>
            <p className="text-body-sm color-foreground/50">Review compliance</p>
          </div>
          <Badge variant="destructive">Flagged</Badge>
        </div>
      </Card>
      <Card className="p-lg">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-body-sm color-foreground/70">Industries Tracked</p>
            <p className="text-heading-3 color-foreground">
              {Object.keys(stats.byIndustry).length}
            </p>
            <p className="text-body-sm color-foreground/50">Diverse portfolio</p>
          </div>
          <Badge variant="outline">Insights</Badge>
        </div>
      </Card>
    </div>
  ) : null;

  const dataViewConfig = useMemo<DataViewConfig>(
    () => ({
      id: "companies-directory",
      name: translations.title,
      viewType: "grid",
      defaultView: "grid",
      data: companyRecords,
      fields: COMPANY_FIELD_CONFIGS,
      loading,
      bulkActions,
      exportConfig: {
        formats: EXPORT_FORMATS,
        onExport: (format, records, _config) => exportData(format, records),
      },
      importConfig: {
        formats: IMPORT_FORMATS,
        onImport: importData,
      },
      onSearch: handleSearch,
      onFilter: handleFilter,
      onSort: handleSort,
      pagination: {
        page,
        pageSize,
        total,
      },
      onPaginate: (nextPage, nextPageSize) => {
        setPage(nextPage);
        setPageSize(nextPageSize);
      },
      actions: [
        {
          key: "view",
          label: "View Details",
          icon: <ArrowUpRight className="h-4 w-4" />,
          onClick: (record: DataRecord) => {
            const company = companies.find((item) => item.id === record.id);
            if (company) {
              setSelectedCompany(company);
              setEditDrawerOpen(true);
            }
          },
        },
      ],
    }),
    [
      translations.title,
      companyRecords,
      loading,
      bulkActions,
      exportData,
      importData,
      handleSearch,
      handleFilter,
      handleSort,
      page,
      pageSize,
      total,
      companies,
    ]
  );

  if (loading && !companies.length) {
    return (
      <div className="stack-lg">
        <div className="flex items-center justify-between">
          <div className="space-y-xs">
            <Skeleton className="h-8 w-56" />
            <Skeleton className="h-4 w-80" />
          </div>
          <Skeleton className="h-10 w-28" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-5 gap-md">
          {Array.from({ length: 5 }).map((_, index) => (
            <Card key={index} className="p-lg space-y-sm">
              <Skeleton className="h-4 w-32" />
              <Skeleton className="h-6 w-24" />
              <Skeleton className="h-3 w-20" />
            </Card>
          ))}
        </div>
        <Card className="p-2xl">
          <div className="stack-sm">
            <Skeleton className="h-5 w-48" />
            <Skeleton className="h-4 w-full" />
            <Skeleton className="h-4 w-full" />
            <Skeleton className="h-4 w-3/4" />
          </div>
        </Card>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-2 font-anton uppercase color-foreground">{translations.title}</h1>
          <p className="text-body-sm color-foreground/70">{translations.subtitle}</p>
        </div>
        <div className="flex items-center gap-sm">
          <Button
            variant="outline"
            onClick={() => toast.info('Import companies', 'Use the import control in the toolbar.')}
          >
            <Upload className="h-4 w-4 mr-xs" />
            Prepare Import
          </Button>
          <Button onClick={() => setCreateDrawerOpen(true)}>
            <Building className="h-4 w-4 mr-xs" />
            Add Company
          </Button>
        </div>
      </div>

      {statsCards}

      <DataViewProvider config={dataViewConfig}>
        <StateManagerProvider>
          <div className="stack-md">
            <DataActions />
            <ViewSwitcher />
            <DataGrid />
            <KanbanBoard
              statusField="status"
              titleField="name"
              columns={[
                { id: "active", title: "Active" },
                { id: "pending", title: "Pending" },
                { id: "inactive", title: "Inactive" },
                { id: "blacklisted", title: "Blacklisted" },
              ]}
            />
            <CalendarView startDateField="created_at" titleField="name" />
            <ListView titleField="name" />
            <TimelineView startDateField="created_at" titleField="name" />
            <GalleryView titleField="name" />
          </div>
        </StateManagerProvider>
      </DataViewProvider>

      <CreateCompanyDrawer
        open={createDrawerOpen}
        onClose={() => setCreateDrawerOpen(false)}
        orgId={orgId}
        onSuccess={() => {
          setCreateDrawerOpen(false);
          void loadDirectory();
        }}
      />

      <EditCompanyDrawer
        open={editDrawerOpen}
        onClose={() => {
          setEditDrawerOpen(false);
          setSelectedCompany(null);
        }}
        company={selectedCompany}
        orgId={orgId}
        onSuccess={() => {
          setEditDrawerOpen(false);
          setSelectedCompany(null);
          void loadDirectory();
        }}
      />
    </div>
  );
}

'use client';

import { Building, Globe, Mail, Phone, MapPin, Users, Edit, Eye, Trash2 } from "lucide-react";
import { Card, Button, Badge } from '@ghxstship/ui';
import type { Company } from '../types';

interface DirectoryGridViewProps {
  companies: Company[];
  onEdit: (company: Company) => void;
  onView: (company: Company) => void;
  onDelete: (company: Company) => void;
  loading?: boolean;
}

export default function DirectoryGridView({
  companies,
  onEdit,
  onView,
  onDelete,
  loading = false
}: DirectoryGridViewProps) {
  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {[...Array(8)].map((_, i) => (
          <Card key={i} className="p-6 animate-pulse">
            <div className="h-4 bg-muted rounded w-3/4 mb-2"></div>
            <div className="h-3 bg-muted rounded w-1/2 mb-4"></div>
            <div className="space-y-2">
              <div className="h-3 bg-muted rounded w-full"></div>
              <div className="h-3 bg-muted rounded w-2/3"></div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (companies.length === 0) {
    return (
      <div className="text-center py-12">
        <Building className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
        <h3 className="text-lg font-semibold mb-2">No companies found</h3>
        <p className="text-muted-foreground">
          Try adjusting your search criteria or add a new company.
        </p>
      </div>
    );
  }

  const getStatusVariant = (status: Company['status']) => {
    switch (status) {
      case 'active':
        return 'success';
      case 'pending':
        return 'warning';
      case 'inactive':
        return 'secondary';
      case 'blacklisted':
        return 'destructive';
      default:
        return 'secondary';
    }
  };

  const getSizeLabel = (size?: Company['size']) => {
    switch (size) {
      case 'startup':
        return '1-10 employees';
      case 'small':
        return '11-50 employees';
      case 'medium':
        return '51-200 employees';
      case 'large':
        return '201-1000 employees';
      case 'enterprise':
        return '1000+ employees';
      default:
        return 'Unknown size';
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {companies.map((company) => (
        <Card key={company.id} className="p-6 hover:shadow-md transition-shadow">
          <div className="flex items-start justify-between mb-4">
            <div className="flex-1">
              <h3 className="font-semibold text-lg mb-1 truncate" title={company.name}>
                {company.name}
              </h3>
              {company.industry && (
                <p className="text-sm text-muted-foreground mb-2">{company.industry}</p>
              )}
              <Badge variant={getStatusVariant(company.status)}>
                {company.status}
              </Badge>
            </div>
            {company.logo_url && (
              <img
                src={company.logo_url}
                alt={`${company.name} logo`}
                className="w-12 h-12 rounded-lg object-cover"
              />
            )}
          </div>

          {company.description && (
            <p className="text-sm text-muted-foreground mb-4 line-clamp-2">
              {company.description}
            </p>
          )}

          <div className="space-y-2 mb-4">
            {company.website && (
              <div className="flex items-center text-sm text-muted-foreground">
                <Globe className="h-4 w-4 mr-2 flex-shrink-0" />
                <a
                  href={company.website}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="hover:text-primary truncate"
                >
                  {company.website.replace(/^https?:\/\//, '')}
                </a>
              </div>
            )}

            {company.email && (
              <div className="flex items-center text-sm text-muted-foreground">
                <Mail className="h-4 w-4 mr-2 flex-shrink-0" />
                <a
                  href={`mailto:${company.email}`}
                  className="hover:text-primary truncate"
                >
                  {company.email}
                </a>
              </div>
            )}

            {company.phone && (
              <div className="flex items-center text-sm text-muted-foreground">
                <Phone className="h-4 w-4 mr-2 flex-shrink-0" />
                <a
                  href={`tel:${company.phone}`}
                  className="hover:text-primary"
                >
                  {company.phone}
                </a>
              </div>
            )}

            {(company.city || company.country) && (
              <div className="flex items-center text-sm text-muted-foreground">
                <MapPin className="h-4 w-4 mr-2 flex-shrink-0" />
                <span className="truncate">
                  {[company.city, company.country].filter(Boolean).join(', ')}
                </span>
              </div>
            )}

            {company.size && (
              <div className="flex items-center text-sm text-muted-foreground">
                <Users className="h-4 w-4 mr-2 flex-shrink-0" />
                <span>{getSizeLabel(company.size)}</span>
              </div>
            )}
          </div>

          <div className="flex items-center justify-between pt-4 border-t">
            <div className="text-xs text-muted-foreground">
              {company.founded_year && `Founded ${company.founded_year}`}
            </div>
            <div className="flex items-center space-x-1">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onView(company)}
              >
                <Eye className="h-4 w-4" />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onEdit(company)}
              >
                <Edit className="h-4 w-4" />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onDelete(company)}
              >
                <Trash2 className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </Card>
      ))}
    </div>
  );
}

'use client';

import { Building, Globe, Mail, Phone, MapPin, Users, Edit, Eye, Trash2, Calendar } from "lucide-react";
import { Card, Button, Badge } from '@ghxstship/ui';
import type { Company } from '../types';

interface DirectoryListViewProps {
  companies: Company[];
  onEdit: (company: Company) => void;
  onView: (company: Company) => void;
  onDelete: (company: Company) => void;
  loading?: boolean;
}

export default function DirectoryListView({
  companies,
  onEdit,
  onView,
  onDelete,
  loading = false
}: DirectoryListViewProps) {
  if (loading) {
    return (
      <div className="space-y-4">
        {[...Array(6)].map((_, i) => (
          <Card key={i} className="p-6 animate-pulse">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4 flex-1">
                <div className="w-12 h-12 bg-muted rounded-lg"></div>
                <div className="flex-1">
                  <div className="h-4 bg-muted rounded w-1/4 mb-2"></div>
                  <div className="h-3 bg-muted rounded w-1/6 mb-1"></div>
                  <div className="h-3 bg-muted rounded w-1/3"></div>
                </div>
              </div>
              <div className="flex space-x-2">
                <div className="w-8 h-8 bg-muted rounded"></div>
                <div className="w-8 h-8 bg-muted rounded"></div>
                <div className="w-8 h-8 bg-muted rounded"></div>
              </div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (companies.length === 0) {
    return (
      <Card className="p-12 text-center">
        <Building className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
        <h3 className="text-lg font-semibold mb-2">No companies found</h3>
        <p className="text-muted-foreground">
          Try adjusting your search criteria or add a new company.
        </p>
      </Card>
    );
  }

  const getStatusVariant = (status: Company['status']) => {
    switch (status) {
      case 'active':
        return 'success';
      case 'pending':
        return 'warning';
      case 'inactive':
        return 'secondary';
      case 'blacklisted':
        return 'destructive';
      default:
        return 'secondary';
    }
  };

  const getSizeLabel = (size?: Company['size']) => {
    switch (size) {
      case 'startup':
        return '1-10';
      case 'small':
        return '11-50';
      case 'medium':
        return '51-200';
      case 'large':
        return '201-1K';
      case 'enterprise':
        return '1K+';
      default:
        return 'Unknown';
    }
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return '';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  return (
    <div className="space-y-4">
      {companies.map((company) => (
        <Card key={company.id} className="p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4 flex-1 min-w-0">
              {/* Logo/Icon */}
              <div className="flex-shrink-0">
                {company.logo_url ? (
                  <img
                    src={company.logo_url}
                    alt={`${company.name} logo`}
                    className="w-12 h-12 rounded-lg object-cover"
                  />
                ) : (
                  <div className="w-12 h-12 rounded-lg bg-muted flex items-center justify-center">
                    <Building className="h-6 w-6 text-muted-foreground" />
                  </div>
                )}
              </div>

              {/* Company Info */}
              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-3 mb-1">
                  <h3 className="font-semibold text-lg truncate" title={company.name}>
                    {company.name}
                  </h3>
                  <Badge variant={getStatusVariant(company.status)}>
                    {company.status}
                  </Badge>
                  {company.industry && (
                    <span className="text-sm text-muted-foreground">
                      {company.industry}
                    </span>
                  )}
                </div>

                <div className="flex items-center space-x-6 text-sm text-muted-foreground">
                  {company.website && (
                    <div className="flex items-center space-x-1">
                      <Globe className="h-4 w-4" />
                      <a
                        href={company.website}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="hover:text-primary truncate max-w-32"
                      >
                        {company.website.replace(/^https?:\/\//, '')}
                      </a>
                    </div>
                  )}

                  {company.email && (
                    <div className="flex items-center space-x-1">
                      <Mail className="h-4 w-4" />
                      <a
                        href={`mailto:${company.email}`}
                        className="hover:text-primary truncate max-w-40"
                      >
                        {company.email}
                      </a>
                    </div>
                  )}

                  {company.phone && (
                    <div className="flex items-center space-x-1">
                      <Phone className="h-4 w-4" />
                      <a
                        href={`tel:${company.phone}`}
                        className="hover:text-primary"
                      >
                        {company.phone}
                      </a>
                    </div>
                  )}

                  {(company.city || company.country) && (
                    <div className="flex items-center space-x-1">
                      <MapPin className="h-4 w-4" />
                      <span className="truncate max-w-32">
                        {[company.city, company.country].filter(Boolean).join(', ')}
                      </span>
                    </div>
                  )}

                  {company.size && (
                    <div className="flex items-center space-x-1">
                      <Users className="h-4 w-4" />
                      <span>{getSizeLabel(company.size)} employees</span>
                    </div>
                  )}

                  {company.founded_year && (
                    <div className="flex items-center space-x-1">
                      <Calendar className="h-4 w-4" />
                      <span>Founded {company.founded_year}</span>
                    </div>
                  )}
                </div>

                {company.description && (
                  <p className="text-sm text-muted-foreground mt-2 line-clamp-1">
                    {company.description}
                  </p>
                )}
              </div>
            </div>

            {/* Actions */}
            <div className="flex items-center space-x-2 flex-shrink-0 ml-4">
              <div className="text-xs text-muted-foreground text-right">
                {company.created_at && (
                  <div>Added {formatDate(company.created_at)}</div>
                )}
                {company.updated_at && company.updated_at !== company.created_at && (
                  <div>Updated {formatDate(company.updated_at)}</div>
                )}
              </div>
              <div className="flex items-center space-x-1">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => onView(company)}
                  title="View company details"
                >
                  <Eye className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => onEdit(company)}
                  title="Edit company"
                >
                  <Edit className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => onDelete(company)}
                  title="Delete company"
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </Card>
      ))}
    </div>
  );
}

'use client';

import { Building, Briefcase, MessageSquare, Save, Star, X } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Button,
  Card,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
  UnifiedInput,
} from '@ghxstship/ui';
import AppDrawer, { type DrawerTab } from '@/app/_components/shared/AppDrawer';

interface CreateRatingClientProps {
  user: User;
  orgId: string;
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: (rating: CreatedRating) => void;
}

type RatingCategory =
  | 'quality'
  | 'professionalism'
  | 'communication'
  | 'timeliness'
  | 'value'
  | 'safety'
  | 'other';

type RatingScore = 1 | 2 | 3 | 4 | 5;

interface RatingFormData {
  companyId: string;
  projectId: string;
  category: RatingCategory;
  score: RatingScore;
  title: string;
  review: string;
  isPublic: boolean;
}

interface CompanyOption {
  id: string;
  name: string;
}

interface ProjectOption {
  id: string;
  name: string;
}

interface CreatedRating {
  id: string;
  company_id: string;
  project_id: string | null;
  rating: number;
  category: string;
  title: string;
  review: string;
  is_public: boolean;
  created_at: string;
}

const RATING_CATEGORIES: ReadonlyArray<{ value: RatingCategory; label: string }> = [
  { value: 'quality', label: 'Quality of Work' },
  { value: 'professionalism', label: 'Professionalism' },
  { value: 'communication', label: 'Communication' },
  { value: 'timeliness', label: 'Timeliness' },
  { value: 'value', label: 'Value for Money' },
  { value: 'safety', label: 'Safety Compliance' },
  { value: 'other', label: 'Other' },
];

const SCORE_OPTIONS: RatingScore[] = [1, 2, 3, 4, 5];

const INITIAL_FORM_DATA: RatingFormData = {
  companyId: '',
  projectId: '',
  category: 'quality',
  score: 5,
  title: '',
  review: '',
  isPublic: false,
};

export default function CreateRatingClient({ user, orgId, isOpen, onClose, onSuccess }: CreateRatingClientProps) {
  const supabase = createBrowserClient();
  const [companies, setCompanies] = useState<CompanyOption[]>([]);
  const [projects, setProjects] = useState<ProjectOption[]>([]);
  const [formData, setFormData] = useState<RatingFormData>(INITIAL_FORM_DATA);
  const [submitting, setSubmitting] = useState(false);

  const loadCompanies = useCallback(async () => {
    const { data, error } = await supabase
      .from('companies')
      .select('id, name')
      .eq('organization_id', orgId)
      .order('name');

    if (error) {
      console.error('Error loading companies', error);
      return;
    }

    setCompanies((data ?? []).map(company => ({ id: company.id, name: company.name })));
  }, [orgId, supabase]);

  const loadProjects = useCallback(async () => {
    const { data, error } = await supabase
      .from('projects')
      .select('id, name')
      .eq('organization_id', orgId)
      .order('name');

    if (error) {
      console.error('Error loading projects', error);
      return;
    }

    setProjects((data ?? []).map(project => ({ id: project.id, name: project.name })));
  }, [orgId, supabase]);

  useEffect(() => {
    if (!isOpen) {
      setFormData(INITIAL_FORM_DATA);
      return;
    }

    void Promise.all([loadCompanies(), loadProjects()]);
  }, [isOpen, loadCompanies, loadProjects]);

  const updateForm = useCallback(<Key extends keyof RatingFormData>(field: Key, value: RatingFormData[Key]) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  }, []);

  const handleTextChange = useCallback((event: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    updateForm(name as keyof RatingFormData, value as RatingFormData[keyof RatingFormData]);
  }, [updateForm]);

  const handleTextareaChange = useCallback((event: ChangeEvent<HTMLTextAreaElement>) => {
    const { name, value } = event.target;
    updateForm(name as keyof RatingFormData, value as RatingFormData[keyof RatingFormData]);
  }, [updateForm]);

  const handleCheckboxChange = useCallback((event: ChangeEvent<HTMLInputElement>) => {
    updateForm('isPublic', event.target.checked);
  }, [updateForm]);

  const handleScoreChange = useCallback((score: RatingScore) => {
    updateForm('score', score);
  }, [updateForm]);

  const handleSubmit = useCallback(async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (!formData.companyId || !formData.title.trim() || submitting) {
      return;
    }

    setSubmitting(true);

    try {
      const ratingId = crypto.randomUUID();
      const { data: insertedRating, error } = await supabase
        .from('company_ratings')
        .insert({
          id: ratingId,
          organization_id: orgId,
          company_id: formData.companyId,
          project_id: formData.projectId || null,
          rating: formData.score,
          category: formData.category,
          title: formData.title.trim(),
          review: formData.review.trim(),
          is_public: formData.isPublic,
          created_by: user.id,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        })
        .select()
        .single<CreatedRating>();

      if (error) throw error;

      onSuccess?.(insertedRating);
      setFormData(INITIAL_FORM_DATA);
      onClose();
    } catch (error) {
      console.error('Error submitting rating', error);
    } finally {
      setSubmitting(false);
    }
  }, [formData, onClose, onSuccess, orgId, submitting, supabase, user.id]);

  const companyOptions = useMemo(() => companies.map(company => (
    <SelectItem key={company.id} value={company.id}>
      {company.name}
    </SelectItem>
  )), [companies]);

  const projectOptions = useMemo(() => (
    projects.map(project => (
      <SelectItem key={project.id} value={project.id}>
        {project.name}
      </SelectItem>
    ))
  ), [projects]);

  const categoryOptions = useMemo(() => RATING_CATEGORIES.map(category => (
    <SelectItem key={category.value} value={category.value}>
      {category.label}
    </SelectItem>
  )), []);

  const scoreButtons = useMemo(() => SCORE_OPTIONS.map(score => (
    <button
      key={score}
      type="button"
      onClick={() => handleScoreChange(score)}
      className={`p-xs rounded-full transition-colors ${score <= formData.score ? 'bg-warning/90 text-white' : 'bg-muted text-muted-foreground'}`}
      aria-pressed={score === formData.score}
      aria-label={`${score} star${score === 1 ? '' : 's'}`}
    >
      <Star className="h-4 w-4" />
    </button>
  )), [formData.score, handleScoreChange]);

  const drawerTabs: DrawerTab[] = useMemo(() => ([
    {
      key: 'rating-details',
      label: 'Rating Details',
      content: (
        <form onSubmit={handleSubmit} className="stack-lg">
          <Card className="p-md">
            <div className="flex items-center gap-sm mb-md">
              <Building className="h-5 w-5 color-accent" />
              <h3 className="text-body text-heading-4">Company & Project</h3>
            </div>

            <div className="stack-md">
              <div>
                <label htmlFor="companyId" className="block text-body-sm form-label mb-xs">
                  Company *
                </label>
                <Select
                  value={formData.companyId}
                  onValueChange={value => updateForm('companyId', value)}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select a company" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="">Select a company</SelectItem>
                    {companyOptions}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label htmlFor="projectId" className="block text-body-sm form-label mb-xs">
                  Project (optional)
                </label>
                <Select
                  value={formData.projectId}
                  onValueChange={value => updateForm('projectId', value)}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="No project association" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="">No project association</SelectItem>
                    {projectOptions}
                  </SelectContent>
                </Select>
              </div>
            </div>
          </Card>

          <Card className="p-md">
            <div className="flex items-center gap-sm mb-md">
              <Briefcase className="h-5 w-5 color-accent" />
              <h3 className="text-body text-heading-4">Rating Details</h3>
            </div>

            <div className="stack-md">
              <div>
                <label htmlFor="category" className="block text-body-sm form-label mb-xs">
                  Rating Category *
                </label>
                <Select
                  value={formData.category}
                  onValueChange={value => updateForm('category', value as RatingCategory)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {categoryOptions}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="block text-body-sm form-label mb-sm">
                  Rating *
                </label>
                <div className="flex items-center gap-xs" role="radiogroup" aria-label="Rating score">
                  {scoreButtons}
                  <span className="text-body-sm color-muted ml-sm">{formData.score}/5</span>
                </div>
              </div>

              <div>
                <label htmlFor="title" className="block text-body-sm form-label mb-xs">
                  Review Title *
                </label>
                <UnifiedInput
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleTextChange}
                  placeholder="Brief title for your review"
                  required
                />
              </div>
            </div>
          </Card>

          <Card className="p-md">
            <div className="flex items-center gap-sm mb-md">
              <MessageSquare className="h-5 w-5 color-accent" />
              <h3 className="text-body text-heading-4">Review Content</h3>
            </div>

            <div className="stack-md">
              <div>
                <label htmlFor="review" className="block text-body-sm form-label mb-xs">
                  Detailed Review
                </label>
                <Textarea
                  id="review"
                  name="review"
                  value={formData.review}
                  onChange={handleTextareaChange}
                  placeholder="Share your experience working with this company..."
                  rows={5}
                />
              </div>

              <div className="flex items-center gap-sm">
                <input
                  type="checkbox"
                  id="isPublic"
                  checked={formData.isPublic}
                  onChange={handleCheckboxChange}
                  className="rounded border-border"
                />
                <label htmlFor="isPublic" className="text-body-sm form-label">
                  Make this review public (visible to other organizations)
                </label>
              </div>
            </div>
          </Card>

          <div className="flex justify-end gap-sm pt-md border-t">
            <Button type="button" variant="outline" onClick={onClose} disabled={submitting}>
              <X className="h-4 w-4 mr-sm" />
              Cancel
            </Button>
            <Button type="submit" disabled={submitting || !formData.companyId || !formData.title.trim()}>
              <Save className="h-4 w-4 mr-sm" />
              {submitting ? 'Submittingâ€¦' : 'Submit Rating'}
            </Button>
          </div>
        </form>
      ),
    },
  ]), [categoryOptions, companyOptions, formData.companyId, formData.projectId, formData.category, formData.score, formData.title, formData.review, handleCheckboxChange, handleSubmit, handleTextareaChange, handleTextChange, onClose, projectOptions, scoreButtons, submitting, updateForm]);

  return (
    <AppDrawer
      open={isOpen}
      onClose={onClose}
      title="Submit Company Rating"
      record={null}
      mode="create"
      fields={[]}
      tabs={drawerTabs}
      loading={submitting}
    />
  );
}

'use client';


import { Star, Plus, Edit, Trash2, Building, Grid, List } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Button, Badge, Skeleton } from '@ghxstship/ui';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface RatingsClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface Rating {
  id: string;
  company_name: string;
  rating: number;
  review: string;
  reviewer_name: string;
  created_at: string;
}

export default function RatingsClient({ user, orgId, translations }: RatingsClientProps) {
  const [ratings, setRatings] = useState<Rating[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState<'grid' | 'list'>('grid');
  const [isCreateDrawerOpen, setIsCreateDrawerOpen] = useState(false);
  const supabase = createBrowserClient();

  useEffect(() => {
    fetchRatings();
  }, []);

  const fetchRatings = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('ratings')
        .select('*')
        .eq('org_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setRatings(data || []);
    } catch (error) {
      console.error('Error fetching ratings:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateRating = () => {
    setIsCreateDrawerOpen(true);
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`h-4 w-4 ${
          i < rating ? 'fill-warning color-warning' : 'color-muted'
        }`}
      />
    ));
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="flex items-center justify-between">
          <div>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-64 mt-sm" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-lg">
          {Array.from({ length: 6 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <Skeleton className="h-6 w-32 mb-md" />
              <Skeleton className="h-4 w-24 mb-sm" />
              <Skeleton className="h-16 w-full mb-md" />
              <Skeleton className="h-4 w-20" />
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
          <p className="text-body-sm color-foreground/70 mt-xs">{translations.subtitle}</p>
        </div>
        <div className="flex items-center cluster-sm">
          <div className="flex items-center cluster-xs bg-secondary rounded-lg p-xs">
            <Button
              variant={currentView === 'grid' ? 'default' : 'ghost'}
              onClick={() => setCurrentView('grid')}
            >
              <Grid className="h-4 w-4" />
            </Button>
            <Button
              variant={currentView === 'list' ? 'default' : 'ghost'}
              onClick={() => setCurrentView('list')}
            >
              <List className="h-4 w-4" />
            </Button>
          </div>
          <Button onClick={handleCreateRating}>
            <Plus className="h-4 w-4 mr-sm" />
            Add Rating
          </Button>
        </div>
      </div>

      {/* Ratings Grid/List */}
      {currentView === 'grid' ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-lg">
          {ratings.map((rating: unknown) => (
            <Card key={rating.id} className="p-lg hover:shadow-floating transition-shadow">
              <div className="flex items-start justify-between mb-md">
                <div>
                  <h3 className="text-heading-4 text-body">{rating.company_name}</h3>
                  <div className="flex items-center cluster-xs mt-xs">
                    {renderStars(rating.rating)}
                    <span className="text-body-sm color-muted ml-sm">
                      {rating.rating}/5
                    </span>
                  </div>
                </div>
                <div className="flex cluster-xs">
                  <Button variant="ghost">
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button variant="ghost">
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
              <p className="text-body-sm color-muted mb-md line-clamp-3">
                {rating.review}
              </p>
              <div className="flex items-center justify-between text-body-sm color-muted">
                <span>By {rating.reviewer_name}</span>
                <span>{new Date(rating.created_at).toLocaleDateString()}</span>
              </div>
            </Card>
          ))}
        </div>
      ) : (
        <div className="stack-md">
          {ratings.map((rating: unknown) => (
            <Card key={rating.id} className="p-lg">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center cluster mb-sm">
                    <h3 className="text-heading-4 text-body">{rating.company_name}</h3>
                    <div className="flex items-center cluster-xs">
                      {renderStars(rating.rating)}
                      <span className="text-body-sm color-muted ml-sm">
                        {rating.rating}/5
                      </span>
                    </div>
                  </div>
                  <p className="text-body-sm color-muted mb-sm">
                    {rating.review}
                  </p>
                  <div className="flex items-center cluster text-body-sm color-muted">
                    <span>By {rating.reviewer_name}</span>
                    <span>{new Date(rating.created_at).toLocaleDateString()}</span>
                  </div>
                </div>
                <div className="flex cluster-xs">
                  <Button variant="ghost">
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button variant="ghost">
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </Card>
          ))}
        </div>
      )}

      {/* Empty State */}
      {ratings.length === 0 && (
        <div className="text-center py-2xl">
          <Building className="h-12 w-12 color-muted mx-auto mb-md" />
          <h3 className="text-body text-heading-4 mb-sm">No ratings yet</h3>
          <p className="color-muted mb-md">
            Start by adding your first company rating.
          </p>
          <Button onClick={handleCreateRating}>
            <Plus className="h-4 w-4 mr-sm" />
            Add Rating
          </Button>
        </div>
      )}

      {/* Create Rating Drawer */}
      <AppDrawer
        open={isCreateDrawerOpen}
        onClose={() => setIsCreateDrawerOpen(false)}
        record={null}
        fields={[]}
        mode="create"
        title="Add New Rating"
        tabs={[
          {
            key: 'content',
            label: 'Details',
            content: (
              <div className="p-lg">
                <p className="color-muted">Rating creation form will be implemented here.</p>
              </div>
            )
          }
        ]}
      />
    </div>
  );
}

