  const [selected, setSelected] = useState<DashboardListItem | null>(null);
  const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
  const [editDrawerOpen, setEditDrawerOpen] = useState(false);
  const [metrics, setMetrics] = useState<OverviewMetric[]>([]);
  const [insights, setInsights] = useState<DashboardQuickInsight[]>([]);
  const [widgets, setWidgets] = useState<DashboardWidget[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { addToast } = useToast();

  const supabase = useMemo(() => createBrowserClient(), []);
  const service = useMemo(() => new DashboardService(), []);

  // Remove the incorrect useStateManager call - we'll use StateManagerProvider instead

  const loadData = async () => {
    try {
      setError(null);
      setIsLoading(true);

      const [dashboardData, metricsData, insightsData] = await Promise.all([
        service.getDashboards(orgId),
        service.getDashboardOverviewMetrics(orgId),
        service.getQuickInsights(orgId)
      ]);

      setDashboards(dashboardData);
      setMetrics(metricsData);
      setInsights(insightsData);
      setSelected(dashboardData[0] ?? null);

      if (dashboardData[0]) {
        const { data: widgetData } = await supabase
          .from('dashboard_widgets')
          .select('*')
          .eq('dashboard_id', dashboardData[0].id)
          .eq('organization_id', orgId)
          .order('position', { ascending: true });

        setWidgets(widgetData ?? []);
      } else {
        setWidgets([]);
      }
    } catch (err) {
      console.error('Failed to load dashboard data', err);
      setError('Unable to load dashboards. Please try again later.');
    } finally {
      setIsLoading(false);
      setIsRefreshing(false);
    }
  };

  useEffect(() => {
    loadData();

    // Set up real-time subscriptions
    const channel = supabase
      .channel('dashboard-changes')
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'dashboards',
          filter: `organization_id=eq.${orgId}`
        },
        (payload) => {
          console.log('Dashboard change:', payload);
          if (payload.eventType === 'INSERT') {
            setDashboards(prev => [payload.new as DashboardListItem, ...prev]);
            addToast({
              type: 'success',
              title: 'New dashboard created',
              description: `${payload.new.name} has been added`
            });
          } else if (payload.eventType === 'UPDATE') {
            setDashboards(prev => 
              prev.map(d => d.id === payload.new.id ? payload.new as DashboardListItem : d)
            );
          } else if (payload.eventType === 'DELETE') {
            setDashboards(prev => prev.filter(d => d.id !== payload.old.id));
            addToast({
              type: 'info',
              title: 'Dashboard removed',
              description: `${payload.old.name} has been deleted`
            });
          }
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'dashboard_widgets',
          filter: `organization_id=eq.${orgId}`
        },
        (payload) => {
          console.log('Widget change:', payload);
          // Update widget counts in real-time
          loadData();
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [orgId]);

  const handleRefresh = async () => {
    setIsRefreshing(true);
    await loadData();
    addToast({
      type: 'success',
      title: 'Dashboard refreshed',
      description: 'Latest dashboard data has been loaded.'
    });
  };

  const handleCreateDashboard = async (payload: Parameters<typeof service.createDashboard>[1]) => {
    try {
      const dashboard = await service.createDashboard(orgId, payload);
      setDashboards((prev) => [dashboard, ...prev]);
      setCreateDrawerOpen(false);
      addToast({
        type: 'success',
        title: 'Dashboard created',
        description: `${dashboard.name} is now available.`
      });
    } catch (err) {
      console.error('Failed to create dashboard', err);
      addToast({
        type: 'error',
        title: 'Create failed',
        description: 'We could not create this dashboard.'
      });
    }
  };

  const handleUpdateDashboard = async (id: string, payload: Parameters<typeof service.updateDashboard>[2]) => {
    try {
      const updated = await service.updateDashboard(orgId, id, payload);
      setDashboards((prev) => prev.map((dash) => (dash.id === id ? updated : dash)));
      setSelected((prev) => (prev?.id === id ? updated : prev));
      setEditDrawerOpen(false);
      addToast({
        type: 'success',
        title: 'Dashboard updated',
        description: `${updated.name} has been updated.`
      });
    } catch (err) {
      console.error('Failed to update dashboard', err);
      addToast({
        type: 'error',
        title: 'Update failed',
        description: 'Could not update this dashboard.'
      });
    }
  };

  const handleDeleteDashboard = async (id: string) => {
    try {
      await service.deleteDashboard(orgId, id);
      setDashboards((prev) => prev.filter((dash) => dash.id !== id));
      if (selected?.id === id) {
        setSelected(null);
      }
      addToast({
        type: 'success',
        title: 'Dashboard deleted',
        description: 'The dashboard has been removed.'
      });
    } catch (err) {
      console.error('Failed to delete dashboard', err);
      addToast({
        type: 'error',
        title: 'Delete failed',
        description: 'Unable to delete this dashboard.'
      });
    }
  };

  const handleExport = async (format: 'csv' | 'json' | 'excel') => {
    try {
      const data = dashboards.map(dashboard => ({
        name: dashboard.name,
        description: dashboard.description,
        type: dashboard.type,
        layout: dashboard.layout,
        access_level: dashboard.access_level,
        is_default: dashboard.is_default,
        is_template: dashboard.is_template,
        created_at: dashboard.created_at,
        updated_at: dashboard.updated_at,
        widget_count: dashboard.widget_count,
        share_count: dashboard.share_count
      }));

      if (format === 'json') {
        const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `dashboards-${new Date().toISOString().split('T')[0]}.json`;
        a.click();
      } else if (format === 'csv') {
        const headers = Object.keys(data[0] || {}).join(',');
        const rows = data.map(row => Object.values(row).join(','));
        const csv = [headers, ...rows].join('\n');
        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `dashboards-${new Date().toISOString().split('T')[0]}.csv`;
        a.click();
      }

      addToast({
        type: 'success',
        title: 'Export successful',
        description: `Dashboards exported as ${format.toUpperCase()}`
      });
    } catch (error) {
      console.error('Export error:', error);
      addToast({
        type: 'error',
        title: 'Export failed',
        description: 'Could not export dashboards'
      });
    }
  };

  const handleBulkAction = async (action: string, selectedIds: string[]) => {
    try {
      switch (action) {
        case 'delete':
          for (const id of selectedIds) {
            await service.deleteDashboard(orgId, id);
          }
          setDashboards(prev => prev.filter(d => !selectedIds.includes(d.id)));
          addToast({
            type: 'success',
            title: 'Bulk delete successful',
            description: `${selectedIds.length} dashboards deleted`
          });
          break;
        case 'archive':
          for (const id of selectedIds) {
            await service.updateDashboard(orgId, id, { is_public: false });
          }
          addToast({
            type: 'success',
            title: 'Bulk archive successful',
            description: `${selectedIds.length} dashboards made private`
          });
          break;
        case 'share':
          // Implement bulk sharing logic
          addToast({
            type: 'info',
            title: 'Bulk share initiated',
            description: `Sharing ${selectedIds.length} dashboards`
          });
          break;
      }
    } catch (error) {
      console.error('Bulk action error:', error);
      addToast({
        type: 'error',
        title: 'Bulk action failed',
        description: 'Could not complete bulk operation'
      });
    }
  };

  const handleSelectDashboard = async (dashboard: DashboardListItem) => {
    setSelected(dashboard);
    const { data, error: widgetError } = await supabase
      .from('dashboard_widgets')
      .select('*')
      .eq('dashboard_id', dashboard.id)
      .eq('organization_id', orgId)
      .order('position', { ascending: true });

    if (widgetError) {
      console.error('Failed to load widgets', widgetError);
      addToast({
        type: 'error',
        title: 'Widget load failed',
        description: 'Widgets could not be loaded for this dashboard.'
      });
      return;
    }

    setWidgets(data ?? []);
  };

  const headerMetrics = useMemo(() => metrics.slice(0, 4), [metrics]);
  const quickInsightTiles = useMemo(() => insights.slice(0, 4), [insights]);

  const viewConfig = useMemo(() => dashboardViews({ dashboards, metrics, insights }), [dashboards, metrics, insights]);

  if (isLoading) {
    return (
      <div className="space-y-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-heading-3 font-anton uppercase">Dashboard</h1>
            <p className="text-body-sm text-muted-foreground">Loading dashboard overviewâ€¦</p>
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
          {Array.from({ length: 4 }).map((_, index) => (
            <Card key={index} className="p-lg">
              <Skeleton className="h-4 w-1/2 mb-sm" />
              <Skeleton className="h-8 w-3/4" />
            </Card>
          ))}
        </div>
        <Card className="p-xl">
          <Skeleton className="h-6 w-1/3 mb-lg" />
          <Skeleton className="h-10 w-full" />
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <div className="space-y-lg">
        <Card className="p-xl text-center">
          <h2 className="text-heading-4 mb-sm">Dashboard unavailable</h2>
          <p className="text-body-sm text-muted-foreground mb-md">{error}</p>
          <Button onClick={loadData}>
            <RefreshCw className="mr-sm h-4 w-4" />Retry
          </Button>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-xl">
      <header className="flex flex-col gap-lg lg:flex-row lg:items-start lg:justify-between">
        <div className="space-y-sm">
          <div className="flex items-center gap-sm text-muted-foreground">
            <LayoutDashboard className="h-5 w-5" />
            <span className="uppercase tracking-widest text-xs">Enterprise Command Center</span>
          </div>
          <h1 className="text-heading-2 font-anton uppercase">Dashboard Overview</h1>
          <p className="text-body-sm text-muted-foreground max-w-2xl">
            Monitor dashboard adoption, sharing coverage, and widget composition across your organization.
          </p>
        </div>
        <div className="flex flex-wrap items-center gap-sm">
          <Button variant="outline" size="sm" onClick={handleRefresh} disabled={isRefreshing}>
            <RefreshCw className={`h-4 w-4 mr-sm ${isRefreshing ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
          <Button variant="secondary" size="sm" onClick={() => setCreateDrawerOpen(true)}>
            <Plus className="h-4 w-4 mr-sm" />
            New Dashboard
          </Button>
        </div>
      </header>

      <section className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-md">
        {headerMetrics.map((metric) => (
          <Card key={metric.id} className="p-lg space-y-sm">
            <div className="flex items-center justify-between">
              <span className="text-body-xs uppercase tracking-wide text-muted-foreground">{metric.label}</span>
              <Badge variant="outline">{metric.format}</Badge>
            </div>
            <div className="text-heading-2 font-semibold">
              {metric.format === 'currency'
                ? new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(Number(metric.value) || 0)
                : metric.value}
            </div>
            {metric.change !== undefined && (
              <p className="text-body-xs text-muted-foreground">
                Change: {metric.change_type === 'increase' ? '+' : ''}
                {metric.change}%
              </p>
            )}
          </Card>
        ))}
      </section>

      {quickInsightTiles.length > 0 && (
        <section className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-md">
          {quickInsightTiles.map((insight) => (
            <Card key={insight.id} className="p-lg space-y-sm">
              <div className="flex items-center justify-between">
                <h3 className="text-body-sm font-semibold">{insight.title}</h3>
                {insight.trend && (
                  <Badge variant={insight.trend === 'up' ? 'success' : insight.trend === 'down' ? 'destructive' : 'secondary'}>
                    {insight.trend.toUpperCase()}
                  </Badge>
                )}
              </div>
              <p className="text-heading-3 font-semibold">
                {typeof insight.value === 'number' && insight.format === 'currency'
                  ? new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(insight.value)
                  : insight.value}
              </p>
              <p className="text-body-xs text-muted-foreground">{insight.description}</p>
            </Card>
          ))}
        </section>
      )}

      <section className="space-y-md">
        <div className="flex flex-col gap-md lg:flex-row lg:items-center lg:justify-between">
          <Tabs value={selected?.id ?? ''} onValueChange={(value) => {
            const dashboard = dashboards.find((d) => d.id === value);
            if (dashboard) handleSelectDashboard(dashboard);
          }} className="flex-1">
            <TabsList className="w-full overflow-x-auto">
              {dashboards.map((dashboard) => (
                <TabsTrigger key={dashboard.id} value={dashboard.id} className="text-left">
                  <div className="flex flex-col items-start">
                    <span className="font-medium">{dashboard.name}</span>
                    <span className="text-body-xs text-muted-foreground">
                      {dashboard.widget_count ?? 0} widgets Â· {dashboard.share_count ?? 0} shares
                    </span>
                  </div>
                </TabsTrigger>
              ))}
            </TabsList>
            {dashboards.map((dashboard) => (
              <TabsContent key={dashboard.id} value={dashboard.id} className="mt-md">
                <Card className="p-lg space-y-sm">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-heading-4 font-semibold">{dashboard.name}</h3>
                      <p className="text-body-sm text-muted-foreground">{dashboard.description}</p>
                    </div>
                    <div className="flex gap-sm">
                      <Button variant="ghost" size="sm" onClick={() => {
                        setSelected(dashboard);
                        setEditDrawerOpen(true);
                      }}>
                        Edit
                      </Button>
                      <Button variant="ghost" size="sm" onClick={() => handleDeleteDashboard(dashboard.id)}>
                        Delete
                      </Button>
                    </div>
                  </div>
                  <div className="flex gap-sm text-body-xs text-muted-foreground">
                    <span>Type: {dashboard.type}</span>
                    <span>Default: {dashboard.is_default ? 'Yes' : 'No'}</span>
                    <span>Visibility: {dashboard.is_public ? 'Public' : 'Internal'}</span>
                  </div>
                </Card>
              </TabsContent>
            ))}
          </Tabs>
        </div>

        {selected && (
          <Card className="p-xl space-y-lg">
            <header className="flex flex-wrap items-center gap-sm justify-between">
              <div className="flex items-center gap-sm text-muted-foreground">
                <BarChart3 className="h-4 w-4" />
                <span className="text-body-sm">{selected.name} Widgets</span>
              </div>
              <div className="flex gap-sm">
                <Button size="sm" variant="secondary" onClick={() => setEditDrawerOpen(true)}>
                  Configure Widgets
                </Button>
              </div>
            </header>

            {widgets.length === 0 ? (
              <div className="border border-dashed rounded-lg p-2xl text-center text-muted-foreground">
                No widgets available. Configure widgets to visualize dashboard data.
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-lg">
                {widgets.map((widget) => (
                  <Card key={widget.id} className="p-lg space-y-sm">
                    <div className="flex items-center justify-between">
                      <h4 className="text-body font-semibold">{widget.title}</h4>
                      <Badge variant="outline">{widget.type}</Badge>
                    </div>
                    <p className="text-body-sm text-muted-foreground">
                      {widget.description ?? 'This widget pulls live analytics from configured sources.'}
                    </p>
                  </Card>
                ))}
              </div>
            )}
          </Card>
        )}
      </section>

      <section className="space-y-lg">
        <StateManagerProvider
          initialState={{
            view: 'success',
            interaction: 'default',
            loading: false,
            empty: dashboards.length === 0,
            data: dashboards,
            selectedCount: 0,
            totalCount: dashboards.length
          }}
        >
          <div className="flex flex-col gap-md lg:flex-row lg:items-center lg:justify-between">
            <div className="flex items-center gap-sm">
              <ViewSwitcher />
              <DataActions />
            </div>
          </div>

          <div className="bg-muted/40 rounded-xl p-md space-y-md">
            <DataGrid />
            <KanbanBoard 
              columns={[
                { id: 'draft', title: 'Draft', color: '#6B7280' },
                { id: 'active', title: 'Active', color: '#10B981' },
                { id: 'archived', title: 'Archived', color: '#F59E0B' }
              ]}
              statusField="status"
              titleField="name"
            />
            <CalendarView 
              titleField="name"
              startDateField="created_at"
            />
            <ListView 
              titleField="name"
            />
          </div>
        </StateManagerProvider>
      </section>

      <CreateDashboardDrawer
        open={createDrawerOpen}
        onOpenChange={setCreateDrawerOpen}
        orgId={orgId}
        onSubmit={handleCreateDashboard}
      />

      {selected && (
        <EditDashboardDrawer
          open={editDrawerOpen}
          onOpenChange={setEditDrawerOpen}
          dashboard={selected}
          onSubmit={(payload) => handleUpdateDashboard(selected.id, payload)}
        />
      )}
    </div>
  );
}

'use client';

import { Plus, Filter, Download, Settings, RefreshCw, Grid3X3, List, BarChart3, Calendar, Eye, EyeOff, Maximize2 } from "lucide-react";
import React, { useState, useEffect } from 'react';
import { Card, Button, Badge } from '@ghxstship/ui';
import EnhancedMetricWidget from '../widgets/EnhancedMetricWidget';
import EnhancedChartWidget from '../widgets/EnhancedChartWidget';
import EnhancedActivityWidget from '../widgets/EnhancedActivityWidget';
import { DashboardService } from '../lib/dashboard-service';
import type { 
  ModuleOverviewConfig, 
  OverviewMetric, 
  ActivityItem, 
  DashboardWidget,
  DataSource
} from '../types';

interface OverviewTemplateProps {
  orgId: string;
  userId: string;
  userEmail: string;
  module: DataSource;
  config: ModuleOverviewConfig;
  customWidgets?: DashboardWidget[];
  onNavigate?: (path: string) => void;
}

type ViewMode = 'grid' | 'list' | 'compact';

export default function OverviewTemplate({
  orgId,
  userId,
  userEmail,
  module,
  config,
  customWidgets = [],
  onNavigate
}: OverviewTemplateProps) {
  const [metrics, setMetrics] = useState<OverviewMetric[]>([]);
  const [activities, setActivities] = useState<ActivityItem[]>([]);
  const [widgets, setWidgets] = useState<DashboardWidget[]>(customWidgets);
  const [isLoading, setIsLoading] = useState(true);
  const [viewMode, setViewMode] = useState<ViewMode>('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [isRefreshing, setIsRefreshing] = useState(false);

  const dashboardService = new DashboardService();

  useEffect(() => {
    loadOverviewData();
  }, [orgId, module]);

  const loadOverviewData = async () => {
    setIsLoading(true);
    try {
      const [metricsData, activitiesData] = await Promise.all([
        dashboardService.getOverviewMetrics(orgId, module),
        dashboardService.getRecentActivity(orgId, 10)
      ]);

      setMetrics(metricsData);
      setActivities(activitiesData.filter(a => a.type === module || module === 'analytics'));
    } catch (error) {
      console.error('Failed to load overview data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRefresh = async () => {
    setIsRefreshing(true);
    await loadOverviewData();
    setIsRefreshing(false);
  };

  const handleQuickAction = (action: string, href?: string) => {
    if (href && onNavigate) {
      onNavigate(href);
    }
  };

  const renderMetricsGrid = () => (
    <div className={`grid gap-md ${
      viewMode === 'grid' ? 'grid-cols-1 md:grid-cols-2 lg:grid-cols-4' :
      viewMode === 'list' ? 'grid-cols-1' :
      'grid-cols-2 md:grid-cols-4 lg:grid-cols-6'
    }`}>
      {metrics.map((metric) => (
        <EnhancedMetricWidget
          key={metric.id}
          widget={{
            id: metric.id,
            dashboard_id: '',
            type: 'metric',
            title: metric.label,
            config: {
              value: metric.value,
              format: metric.format,
              change: metric.change,
              change_type: metric.change_type,
              target: metric.target,
              status: metric.status
            },
            position: { x: 0, y: 0, w: 1, h: 1 },
            refresh_interval: '5_minutes',
            is_visible: true,
            organization_id: orgId,
            created_by: userId,
            created_at: new Date().toISOString(),
            updated_at: new Date().toISOString()
          }}
          metric={metric}
          isLoading={isLoading}
        />
      ))}
    </div>
  );

  const renderQuickActions = () => (
    <Card className="p-lg">
      <div className="space-y-md">
        <h3 className="font-semibold text-foreground flex items-center space-x-sm">
          <Plus className="w-4 h-4" />
          <span>Quick Actions</span>
        </h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-sm">
          {config.quick_actions.map((action, index) => (
            <Button
              key={index}
              variant="outline"
              size="sm"
              onClick={() => handleQuickAction(action.action, action.href)}
              className="justify-start space-x-xs"
            >
              <span className="text-lg">{action.icon}</span>
              <span className="text-xs">{action.label}</span>
            </Button>
          ))}
        </div>
      </div>
    </Card>
  );

  const renderCustomWidgets = () => {
    if (widgets.length === 0) return null;

    return (
      <div className="space-y-md">
        <h3 className="font-semibold text-foreground">Custom Widgets</h3>
        <div className={`grid gap-md ${
          viewMode === 'grid' ? 'grid-cols-1 lg:grid-cols-2' :
          'grid-cols-1'
        }`}>
          {widgets.map((widget) => {
            switch (widget.type) {
              case 'metric':
              case 'kpi_card':
                return (
                  <EnhancedMetricWidget
                    key={widget.id}
                    widget={widget}
                    isLoading={isLoading}
                  />
                );
              case 'bar_chart':
              case 'line_chart':
              case 'pie_chart':
                return (
                  <EnhancedChartWidget
                    key={widget.id}
                    widget={widget}
                    isLoading={isLoading}
                  />
                );
              case 'activity_feed':
                return (
                  <EnhancedActivityWidget
                    key={widget.id}
                    widget={widget}
                    activities={activities}
                    isLoading={isLoading}
                  />
                );
              default:
                return null;
            }
          })}
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-md">
          <div className={`p-sm rounded-lg bg-${config.color}-100`}>
            <span className="text-2xl">{config.icon}</span>
