            type: 'activity_feed',
            title: 'Recent Activity',
            config: { limit: 10 },
            position: { x: 0, y: 0, w: 1, h: 1 },
            refresh_interval: '1_minute',
            is_visible: true,
            organization_id: orgId,
            created_by: userId,
