      {/* Performance Stats */}
      <Card className="p-md">
        <div className="flex items-center justify-between text-xs text-muted-foreground">
          <span>
            Last updated: {new Date().toLocaleTimeString()}
          </span>
          <span>
            {metrics.length} metrics â€¢ {activities.length} activities â€¢ {widgets.length} widgets
          </span>
        </div>
      </Card>
    </div>
  );
}

'use client';

import { Plus, Loader2 } from "lucide-react";
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
// import { z } from 'zod';
import {
  UniversalDrawer,
  Button,
  Input,
  Textarea,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from '@ghxstship/ui';
import type { DashboardLayout, AccessLevel } from '../types';

const createDashboardSchema = z.object({
  name: z.string().min(1, 'Dashboard name is required').max(100, 'Name too long'),
  description: z.string().optional(),
  slug: z.string().optional(),
  layout: z.enum(['grid', 'masonry', 'flex', 'tabs', 'accordion', 'sidebar', 'fullscreen'] as const),
  access_level: z.enum(['private', 'team', 'organization', 'public'] as const),
  is_default: z.boolean().default(false),
  is_template: z.boolean().default(false),
  tags: z.string().optional()
});

type CreateDashboardForm = z.infer<typeof createDashboardSchema>;

interface CreateDashboardDrawerProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  orgId: string;
  onSubmit: (data: CreateDashboardForm) => Promise<void>;
}

export default function CreateDashboardDrawer({
  open,
  onOpenChange,
  orgId,
  onSubmit
}: CreateDashboardDrawerProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();

  const form = useForm<CreateDashboardForm>({
    resolver: zodResolver(createDashboardSchema),
    defaultValues: {
      name: '',
      description: '',
      slug: '',
      layout: 'grid',
      access_level: 'team',
      is_default: false,
      is_template: false,
      tags: ''
    }
  });

  const handleSubmit = async (data: CreateDashboardForm) => {
    try {
      setIsSubmitting(true);

      // Process tags
      const processedData = {
        ...data,
        tags: data.tags ? data.tags.split(',').map(tag => tag.trim()).filter(Boolean) : undefined
      };

      await onSubmit(processedData);
      form.reset();
      onOpenChange(false);

      toast({
        title: 'Dashboard created',
        description: `${data.name} has been created successfully.`
      });
    } catch (error) {
      console.error('Failed to create dashboard:', error);
      toast({
        title: 'Creation failed',
        description: 'Unable to create dashboard. Please try again.',
        variant: 'destructive'
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <AppDrawer
      open={open}
      onOpenChange={onOpenChange}
      title="Create Dashboard"
      description="Create a new dashboard to organize and visualize your data."
    >
      <Form {...form}>
        <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-lg">
          <FormField
            control={form.control}
            name="name"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Dashboard Name *</FormLabel>
                <FormControl>
                  <Input
                    placeholder="e.g., Executive Overview, Team Performance"
                    {...field}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="description"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Description</FormLabel>
                <FormControl>
                  <Textarea
                    placeholder="Describe what this dashboard will show..."
                    rows={3}
                    {...field}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
            <FormField
              control={form.control}
              name="layout"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Layout Type</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select layout" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="grid">Grid Layout</SelectItem>
                      <SelectItem value="masonry">Masonry Layout</SelectItem>
                      <SelectItem value="flex">Flexible Layout</SelectItem>
                      <SelectItem value="tabs">Tabbed Layout</SelectItem>
                      <SelectItem value="accordion">Accordion Layout</SelectItem>
                      <SelectItem value="sidebar">Sidebar Layout</SelectItem>
                      <SelectItem value="fullscreen">Fullscreen Layout</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="access_level"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Access Level</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select access level" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="private">Private (Only me)</SelectItem>
                      <SelectItem value="team">Team (My team)</SelectItem>
                      <SelectItem value="organization">Organization (Everyone)</SelectItem>
                      <SelectItem value="public">Public (External access)</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <FormField
            control={form.control}
            name="slug"
            render={({ field }) => (
              <FormItem>
                <FormLabel>URL Slug (Optional)</FormLabel>
                <FormControl>
                  <Input
                    placeholder="e.g., executive-overview"
                    {...field}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="tags"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Tags (Optional)</FormLabel>
                <FormControl>
                  <Input
                    placeholder="e.g., executive, finance, quarterly"
                    {...field}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <div className="space-y-md">
            <FormField
              control={form.control}
              name="is_default"
              render={({ field }) => (
                <FormItem className="flex flex-row items-center justify-between rounded-lg border p-md">
                  <div className="space-y-xs">
                    <FormLabel className="text-body font-medium">
                      Set as Default Dashboard
                    </FormLabel>
                    <p className="text-body-sm text-muted-foreground">
                      This dashboard will be shown by default when users visit the dashboard section.
                    </p>
                  </div>
                  <FormControl>
                    <Switch
                      checked={field.value}
                      onCheckedChange={field.onChange}
                    />
                  </FormControl>
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="is_template"
              render={({ field }) => (
                <FormItem className="flex flex-row items-center justify-between rounded-lg border p-md">
                  <div className="space-y-xs">
                    <FormLabel className="text-body font-medium">
                      Save as Template
                    </FormLabel>
                    <p className="text-body-sm text-muted-foreground">
                      Allow others to create dashboards based on this configuration.
                    </p>
                  </div>
                  <FormControl>
                    <Switch
                      checked={field.value}
                      onCheckedChange={field.onChange}
                    />
                  </FormControl>
                </FormItem>
              )}
            />
          </div>

          <div className="flex justify-end gap-sm pt-lg">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              disabled={isSubmitting}
            >
              Cancel
            </Button>
            <Button type="submit" disabled={isSubmitting}>
              {isSubmitting ? (
                <>
                  <Loader2 className="mr-sm h-4 w-4 animate-spin" />
                  Creating...
                </>
              ) : (
                <>
                  <Plus className="mr-sm h-4 w-4" />
                  Create Dashboard
                </>
              )}
            </Button>
          </div>
        </form>
      </Form>
    </AppDrawer>
  );
}

'use client';

import { useMemo } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import type { DashboardListItem } from '../types';

interface DashboardGridViewProps {
  dashboards: DashboardListItem[];
  onView?: (dashboard: DashboardListItem) => void;
  onEdit?: (dashboard: DashboardListItem) => void;
  onDelete?: (dashboard: DashboardListItem) => void;
  onShare?: (dashboard: DashboardListItem) => void;
  onDuplicate?: (dashboard: DashboardListItem) => void;
  selectedIds?: string[];
  onSelectionChange?: (ids: string[]) => void;
}

export default function DashboardGridView({
  dashboards,
  onView,
  onEdit,
  onDelete,
  onShare,
  onDuplicate,
  selectedIds = [],
  onSelectionChange
}: DashboardGridViewProps) {
  const handleCardClick = (dashboard: DashboardListItem, event: React.MouseEvent) => {
    // Prevent selection when clicking action buttons
    if ((event.target as HTMLElement).closest('button')) {
      return;
    }

    if (onView) {
      onView(dashboard);
    }
  };

  const handleSelectionToggle = (dashboardId: string) => {
    if (!onSelectionChange) return;

    const newSelection = selectedIds.includes(dashboardId)
      ? selectedIds.filter(id => id !== dashboardId)
      : [...selectedIds, dashboardId];

    onSelectionChange(newSelection);
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'system':
        return 'bg-blue-100 text-blue-800';
      case 'custom':
        return 'bg-green-100 text-green-800';
      case 'template':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-600';
    }
  };

  const getAccessLevelColor = (level: string) => {
    switch (level) {
      case 'private':
        return 'bg-red-100 text-red-800';
      case 'team':
        return 'bg-blue-100 text-blue-800';
      case 'organization':
        return 'bg-green-100 text-green-800';
      case 'public':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-600';
    }
  };

  if (dashboards.length === 0) {
    return (
      <div className="text-center py-2xl">
        <BarChart3 className="mx-auto h-12 w-12 text-muted-foreground mb-md" />
        <h3 className="text-heading-4 font-semibold mb-sm">No dashboards found</h3>
        <p className="text-body-sm text-muted-foreground">
          Create your first dashboard to start visualizing your data.
        </p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-lg">
      {dashboards.map((dashboard) => (
        <Card
          key={dashboard.id}
          className={`p-lg cursor-pointer transition-all hover:shadow-md ${
            selectedIds.includes(dashboard.id) ? 'ring-2 ring-primary' : ''
          }`}
          onClick={(e) => handleCardClick(dashboard, e)}
        >
          <div className="space-y-md">
            {/* Header */}
            <div className="flex items-start justify-between">
              <div className="flex-1 min-w-0">
                <h3 className="text-body font-semibold truncate">
                  {dashboard.name}
                </h3>
                {dashboard.description && (
                  <p className="text-body-sm text-muted-foreground mt-xs line-clamp-2">
                    {dashboard.description}
                  </p>
                )}
              </div>

              <div className="flex items-center gap-xs ml-sm">
                <input
                  type=\"checkbox\"
                  checked={selectedIds.includes(dashboard.id)}
                  onChange={() => handleSelectionToggle(dashboard.id)}
                  className=\"rounded border-gray-300\"
                  onClick={(e) => e.stopPropagation()}
                />

                <Button
                  variant=\"ghost\"
                  size=\"sm\"
                  onClick={(e) => {
                    e.stopPropagation();
                    // Show context menu or actions
                  }}
                >
                  <MoreHorizontal className=\"h-4 w-4\" />
                </Button>
              </div>
            </div>

            {/* Badges */}
            <div className=\"flex flex-wrap gap-xs\">
              <Badge className={getTypeColor(dashboard.type)}>
                {dashboard.type}
              </Badge>

              <Badge className={getAccessLevelColor(dashboard.access_level)}>
                {dashboard.access_level}
              </Badge>

              {dashboard.is_default && (
                <Badge className=\"bg-yellow-100 text-yellow-800\">
                  Default
                </Badge>
              )}

              {dashboard.is_public && (
                <Badge className=\"bg-indigo-100 text-indigo-800\">
                  Public
                </Badge>
              )}
            </div>

            {/* Stats */}
            <div className=\"grid grid-cols-2 gap-md text-center\">
              <div>
                <div className=\"text-heading-4 font-semibold\">
                  {dashboard.widget_count || 0}
                </div>
                <div className=\"text-body-xs text-muted-foreground\">
                  Widgets
                </div>
              </div>

              <div>
                <div className=\"text-heading-4 font-semibold\">
                  {dashboard.share_count || 0}
                </div>
                <div className=\"text-body-xs text-muted-foreground\">
                  Shares
                </div>
              </div>
            </div>

            {/* Tags */}
            {dashboard.tags && dashboard.tags.length > 0 && (
              <div className=\"flex flex-wrap gap-xs\">
                {dashboard.tags.slice(0, 3).map((tag, index) => (
                  <span
                    key={index}
                    className=\"inline-flex items-center px-xs py-xs rounded-full text-xs font-medium bg-gray-100 text-gray-700\"
                  >
                    {tag}
                  </span>
                ))}
                {dashboard.tags.length > 3 && (
                  <span className=\"text-body-xs text-muted-foreground\">
                    +{dashboard.tags.length - 3} more
                  </span>
                )}
              </div>
            )}

            {/* Actions */}
            <div className=\"flex items-center justify-between pt-sm border-t\">
              <div className=\"text-body-xs text-muted-foreground\">
                {dashboard.created_at && new Date(dashboard.created_at).toLocaleDateString('en-US', {
                  month: 'short',
                  day: 'numeric',
                  year: 'numeric'
                })}
              </div>

              <div className=\"flex items-center gap-xs\">
                {onView && (
                  <Button
                    variant=\"ghost\"
                    size=\"sm\"
                    onClick={(e) => {
                      e.stopPropagation();
                      onView(dashboard);
                    }}
                  >
                    <Eye className=\"h-4 w-4\" />
                  </Button>
                )}

                {onEdit && (
                  <Button
                    variant=\"ghost\"
                    size=\"sm\"
                    onClick={(e) => {
                      e.stopPropagation();
                      onEdit(dashboard);
                    }}
                  >
                    <Edit className=\"h-4 w-4\" />
                  </Button>
                )}

                {onShare && (
                  <Button
                    variant=\"ghost\"
                    size=\"sm\"
                    onClick={(e) => {
                      e.stopPropagation();
                      onShare(dashboard);
                    }}
                  >
                    <Share className=\"h-4 w-4\" />
                  </Button>
                )}

                {onDuplicate && (
                  <Button
                    variant=\"ghost\"
                    size=\"sm\"
                    onClick={(e) => {
                      e.stopPropagation();
                      onDuplicate(dashboard);
                    }}
                  >
                    <Copy className=\"h-4 w-4\" />
                  </Button>
                )}
              </div>
            </div>
          </div>
        </Card>
      ))}
    </div>
  );
}

'use client';


import { MoreHorizontal, RefreshCw } from "lucide-react";
import React, { useState, useEffect } from 'react';
import { Card } from '@ghxstship/ui';
import { createClient } from '@/lib/supabase/client';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  ChartOptions,
} from 'chart.js';
import { Line, Bar, Pie, Doughnut } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

interface ChartWidgetProps {
  id: string;
  title: string;
  config: {
    chartType: 'line' | 'bar' | 'pie' | 'doughnut';
    dataSource: string;
    xAxis: string;
    yAxis: string | string[];
    groupBy?: string;
    aggregation: 'count' | 'sum' | 'avg' | 'min' | 'max';
    colors?: string[];
    showLegend?: boolean;
    showGrid?: boolean;
    timeframe?: string;
  };
  organizationId: string;
  onEdit?: () => void;
  onDelete?: () => void;
}

export default function ChartWidget({ 
  id, 
  title, 
  config, 
  organizationId, 
  onEdit, 
  onDelete 
}: ChartWidgetProps) {
  const [chartData, setChartData] = useState<unknown>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [lastUpdated, setLastUpdated] = useState<Date>(new Date());

  const supabase = createClient();

  const defaultColors = [
    'hsl(var(--primary))', 'hsl(var(--destructive))', 'hsl(var(--success))', 'hsl(var(--warning))', 'hsl(var(--info))',
    'hsl(var(--secondary))', 'hsl(var(--accent))', 'hsl(var(--muted))', 'hsl(var(--border))', 'hsl(var(--ring))'
  ];

  useEffect(() => {
    async function fetchChartData() {
      try {
        setLoading(true);

        // Fetch data based on data source
        let query;
        switch (config.dataSource) {
          case 'projects':
            query = supabase
              .from('projects')
              .select('*')
              .eq('organization_id', organizationId);
            break;
          case 'people':
            query = supabase
              .from('people')
              .select('*')
              .eq('organization_id', organizationId);
            break;
          case 'finance':
            query = supabase
              .from('budgets')
              .select('*')
              .eq('organization_id', organizationId);
            break;
          case 'jobs':
            query = supabase
              .from('jobs')
              .select('*')
              .eq('organization_id', organizationId);
            break;
          default:
            throw new Error(`Unsupported data source: ${config.dataSource}`);
        }

        const { data: rawData, error } = await query;

        if (error) throw error;

        // Process data for chart
        const processedData = processChartData(rawData || []);
        setChartData(processedData);
        setLastUpdated(new Date());
      } catch (err) {
        console.error('Error fetching chart data:', err);
        setError(err instanceof Error ? err.message : 'Failed to load data');
      } finally {
        setLoading(false);
      }
    }

    fetchChartData();

    // Set up real-time subscription
    const subscription = supabase
      .channel(`chart-widget-${id}`)
      .on('postgres_changes', 
        { 
          event: '*', 
          schema: 'public', 
          table: config.dataSource,
          filter: `organization_id=eq.${organizationId}`
        }, 
        () => {
          fetchChartData();
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [id, config, organizationId, supabase]);

  const processChartData = (rawData: unknown[]) => {
    if (!rawData.length) return null;

    const colors = config.colors || defaultColors;

    if (config.groupBy) {
      // Group data by specified field
      const grouped = rawData.reduce((acc, item) => {
        const key = item[config.groupBy!] || 'Unknown';
        if (!acc[key]) acc[key] = [];
        acc[key].push(item);
        return acc;
      }, {});

      const labels = Object.keys(grouped);
      const values = labels.map(label => {
        const items = grouped[label];
        switch (config.aggregation) {
          case 'count':
            return items.length;
          case 'sum':
            return items.reduce((sum: number, item) => sum + (item[config.yAxis as string] || 0), 0);
          case 'avg':
            const total = items.reduce((sum: number, item) => sum + (item[config.yAxis as string] || 0), 0);
            return total / items.length;
          default:
            return items.length;
        }
      });

      return {
        labels,
        datasets: [{
          label: title,
          data: values,
          backgroundColor: colors.slice(0, labels.length),
          borderColor: colors.slice(0, labels.length),
          borderWidth: 2,
          fill: false,
        }]
      };
    } else {
      // Time series or simple data
      const labels = rawData.map(item => item[config.xAxis] || 'Unknown');
      const values = rawData.map(item => item[config.yAxis as string] || 0);

      return {
        labels,
        datasets: [{
          label: title,
          data: values,
          backgroundColor: colors[0] + '80',
          borderColor: colors[0],
          borderWidth: 2,
          fill: config.chartType === 'line' ? false : true,
        }]
      };
    }
  };

  const getChartOptions = (): ChartOptions<unknown> => {
    const baseOptions: ChartOptions<unknown> = {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        legend: {
          display: config.showLegend !== false,
          position: 'top' as const,
        },
        title: {
          display: false,
        },
      },
    };

    if (config.chartType === 'line' || config.chartType === 'bar') {
      return {
        ...baseOptions,
        scales: {
          x: {
            display: true,
            grid: {
              display: config.showGrid !== false,
            },
          },
          y: {
            display: true,
            grid: {
              display: config.showGrid !== false,
            },
          },
        },
      };
    }

    return baseOptions;
  };

  const renderChart = () => {
    if (!chartData) return null;

    const options = getChartOptions();

    switch (config.chartType) {
      case 'line':
        return <Line data={chartData} options={options} />;
      case 'bar':
        return <Bar data={chartData} options={options} />;
      case 'pie':
        return <Pie data={chartData} options={options} />;
      case 'doughnut':
        return <Doughnut data={chartData} options={options} />;
      default:
        return <Bar data={chartData} options={options} />;
    }
  };

  if (loading) {
    return (
      <Card className="p-lg h-full">
        <div className="animate-pulse">
          <div className="h-4 bg-secondary/50 rounded w-3/4 mb-md"></div>
          <div className="h-64 bg-secondary/50 rounded"></div>
        </div>
      </Card>
    );
  }

  if (error) {
    return (
      <Card className="p-lg h-full">
        <div className="text-center">
          <p className="text-body-sm color-destructive mb-sm">Error loading chart</p>
          <p className="text-body-sm color-muted">{error}</p>
        </div>
      </Card>
    );
  }

  return (
    <Card className="p-lg h-full relative group">
      {/* Widget Header */}
      <div className="flex items-center justify-between mb-md">
        <h3 className="text-body text-heading-4">{title}</h3>
        <div className="flex items-center cluster-sm">
          <button
            onClick={() => window.location.reload()}
            className="p-xs color-muted hover:color-foreground rounded opacity-0 group-hover:opacity-100 transition-opacity"
            aria-label="Refresh chart"
          >
            <RefreshCw className="h-4 w-4" />
          </button>
          {(onEdit || onDelete) && (
            <button
              onClick={onEdit}
              className="p-xs color-muted hover:color-foreground rounded opacity-0 group-hover:opacity-100 transition-opacity"
              aria-label="Edit widget"
            >
              <MoreHorizontal className="h-4 w-4" />
            </button>
          )}
        </div>
      </div>

      {/* Chart */}
      <div className="h-64 mb-md">
        {chartData ? renderChart() : (
          <div className="flex items-center justify-center h-full color-muted">
            No data available
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="flex items-center justify-between text-body-sm color-muted">
        <span>
          Data source: {config.dataSource}
        </span>
        <span>
          Updated: {lastUpdated.toLocaleTimeString()}
        </span>
      </div>
    </Card>
  );
}

'use client';

import { Activity, Clock, User, FileText, CheckCircle, AlertCircle, Plus, ArrowRight, MoreHorizontal } from "lucide-react";
import React from 'react';
import { Card, Avatar, Badge, Button } from '@ghxstship/ui';
import type { BaseWidgetProps, ActivityItem } from '../types';

interface EnhancedActivityWidgetProps extends BaseWidgetProps {
  activities?: ActivityItem[];
  onViewAll?: () => void;
}

export default function EnhancedActivityWidget({ 
  widget, 
  data, 
  isLoading,
  activities,
  onViewAll
}: EnhancedActivityWidgetProps) {
  if (isLoading) {
    return (
      <Card className="p-lg">
        <div className="space-y-md">
          <div className="flex items-center justify-between">
            <div className="h-6 bg-muted rounded w-1/3 animate-pulse"></div>
            <div className="h-8 w-16 bg-muted rounded animate-pulse"></div>
          </div>
          <div className="space-y-md">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="flex items-start space-x-sm">
                <div className="w-8 h-8 bg-muted rounded-full animate-pulse"></div>
                <div className="flex-1 space-y-xs">
                  <div className="h-4 bg-muted rounded w-3/4 animate-pulse"></div>
                  <div className="h-3 bg-muted rounded w-1/2 animate-pulse"></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </Card>
    );
  }

  const activityData = activities || data?.data || [];

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'project':
        return <FileText className="w-4 h-4 text-blue-500" />;
      case 'task':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'user':
        return <User className="w-4 h-4 text-purple-500" />;
      case 'system':
        return <AlertCircle className="w-4 h-4 text-orange-500" />;
      default:
        return <Activity className="w-4 h-4 text-gray-500" />;
    }
  };

  const getActivityColor = (type: string) => {
    switch (type) {
      case 'project':
        return 'bg-blue-50 border-blue-200';
      case 'task':
        return 'bg-green-50 border-green-200';
      case 'user':
        return 'bg-purple-50 border-purple-200';
      case 'system':
        return 'bg-orange-50 border-orange-200';
      default:
        return 'bg-gray-50 border-gray-200';
    }
  };

  const formatRelativeTime = (timestamp: string) => {
    const now = new Date();
    const time = new Date(timestamp);
    const diffInMinutes = Math.floor((now.getTime() - time.getTime()) / (1000 * 60));

    if (diffInMinutes < 1) return 'Just now';
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`;

    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `${diffInHours}h ago`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays}d ago`;

    return time.toLocaleDateString();
  };

  return (
    <Card className="p-lg">
      <div className="space-y-md">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-sm">
            <Activity className="w-5 h-5 text-blue-500" />
            <h3 className="font-semibold text-foreground">{widget.title}</h3>
          </div>
          {onViewAll && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onViewAll}
              className="text-xs"
            >
              View All
              <ArrowRight className="w-3 h-3 ml-xs" />
            </Button>
          )}
        </div>

        {/* Activity List */}
        <div className="space-y-sm">
          {activityData.length === 0 ? (
            <div className="text-center py-lg text-muted-foreground">
              <Activity className="w-8 h-8 mx-auto mb-sm opacity-50" />
              <p className="text-sm">No recent activity</p>
            </div>
          ) : (
            activityData.slice(0, widget.config.limit || 10).map((activity) => (
              <div
                key={activity.id}
                className={`p-sm rounded-lg border transition-colors hover:bg-muted/50 ${getActivityColor(activity.type)}`}
              >
                <div className="flex items-start space-x-sm">
                  {/* Activity Icon */}
                  <div className="flex-shrink-0 mt-xs">
                    {getActivityIcon(activity.type)}
                  </div>

                  {/* Content */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <p className="text-sm font-medium text-foreground truncate">
                          {activity.title}
                        </p>
                        {activity.description && (
                          <p className="text-xs text-muted-foreground mt-xs">
                            {activity.description}
                          </p>
                        )}
                      </div>
                      <div className="flex-shrink-0 ml-sm">
                        <span className="text-xs text-muted-foreground">
                          {formatRelativeTime(activity.timestamp)}
                        </span>
                      </div>
                    </div>

                    {/* User Info */}
                    {activity.user_name && (
                      <div className="flex items-center space-x-xs mt-xs">
                        <Avatar className="w-4 h-4">
                          <img 
                            src={activity.user_avatar || '/default-avatar.png'} 
                            alt={activity.user_name}
                            className="w-full h-full object-cover"
                          />
                        </Avatar>
                        <span className="text-xs text-muted-foreground">
                          {activity.user_name}
                        </span>
                      </div>
                    )}

                    {/* Metadata */}
                    {activity.metadata && Object.keys(activity.metadata).length > 0 && (
                      <div className="flex flex-wrap gap-xs mt-xs">
                        {Object.entries(activity.metadata).slice(0, 3).map(([key, value]) => (
                          <Badge key={key} variant="secondary" className="text-xs">
                            {key}: {String(value)}
                          </Badge>
                        ))}
                      </div>
                    )}

                    {/* Action Link */}
                    {activity.action_url && (
                      <div className="mt-xs">
                        <a
                          href={activity.action_url}
                          className="text-xs text-blue-600 hover:text-blue-800 font-medium"
                        >
                          View Details â†’
                        </a>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        {/* Footer */}
        {data?.metadata && (
          <div className="flex items-center justify-between text-xs text-muted-foreground pt-sm border-t">
            <span>
              {data.metadata.total_count} total activities
            </span>
            <span>
              Updated {new Date(data.metadata.last_updated).toLocaleTimeString()}
            </span>
          </div>
        )}
      </div>
    </Card>
  );
}

'use client';

import React from 'react';
import { Card, Button } from '@ghxstship/ui';
import type { BaseWidgetProps } from '../types';

interface EnhancedChartWidgetProps extends BaseWidgetProps {
  onExpand?: () => void;
  onExport?: () => void;
}

const COLORS = [
  '#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6',
  '#06B6D4', '#84CC16', '#F97316', '#EC4899', '#6B7280'
];

export default function EnhancedChartWidget({ 
  widget, 
  data, 
  isLoading,
  onExpand,
  onExport
}: EnhancedChartWidgetProps) {
  if (isLoading) {
    return (
      <Card className="p-lg">
        <div className="space-y-md">
          <div className="flex items-center justify-between">
            <div className="h-6 bg-muted rounded w-1/3 animate-pulse"></div>
            <div className="flex space-x-sm">
              <div className="h-8 w-8 bg-muted rounded animate-pulse"></div>
              <div className="h-8 w-8 bg-muted rounded animate-pulse"></div>
            </div>
          </div>
          <div className="h-64 bg-muted rounded animate-pulse"></div>
        </div>
      </Card>
    );
  }

  const chartData = data?.data || [];
  const chartType = widget.config.chartType || 'bar';

  const renderChart = () => {
    if (!chartData.length) {
      return (
        <div className="flex items-center justify-center h-64 text-muted-foreground">
          <div className="text-center space-y-sm">
            <BarChart3 className="w-12 h-12 mx-auto opacity-50" />
            <p>No data available</p>
          </div>
        </div>
      );
    }

    const maxValue = Math.max(...chartData.map((item: unknown) => item.value || 0));

    switch (chartType) {
      case 'bar':
      case 'column':
        return (
          <div className="h-64 flex items-end justify-around space-x-2 p-4">
            {chartData.slice(0, 10).map((item: any, index: number) => {
              const height = maxValue > 0 ? (item.value / maxValue) * 200 : 0;
              return (
                <div key={index} className="flex flex-col items-center space-y-2">
                  <div className="text-xs text-muted-foreground">{item.value}</div>
                  <div
                    className="w-8 rounded-t transition-all duration-300"
                    style={{
                      height: `${height}px`,
                      backgroundColor: COLORS[index % COLORS.length],
                      minHeight: '4px'
                    }}
                  />
                  <div className="text-xs text-muted-foreground max-w-12 truncate">
                    {item.name || item.label}
                  </div>
                </div>
              );
            })}
          </div>
        );

      case 'line':
        return (
          <div className="h-64 flex items-center justify-center">
            <div className="text-center space-y-sm">
              <LineChart className="w-12 h-12 mx-auto text-blue-500" />
              <p className="text-sm text-muted-foreground">Line Chart</p>
              <p className="text-xs text-muted-foreground">{chartData.length} data points</p>
            </div>
          </div>
        );

      case 'pie':
      case 'donut':
        return (
          <div className="h-64 flex items-center justify-center">
            <div className="text-center space-y-sm">
              <PieChart className="w-12 h-12 mx-auto text-purple-500" />
              <p className="text-sm text-muted-foreground">
                {chartType === 'donut' ? 'Donut' : 'Pie'} Chart
              </p>
              <div className="space-y-1">
                {chartData.slice(0, 5).map((item: any, index: number) => (
                  <div key={index} className="flex items-center justify-between text-xs">
                    <div className="flex items-center space-x-2">
                      <div
                        className="w-3 h-3 rounded"
                        style={{ backgroundColor: COLORS[index % COLORS.length] }}
                      />
                      <span>{item.name || item.label}</span>
                    </div>
                    <span className="font-medium">{item.value}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        );

      default:
        return (
          <div className="flex items-center justify-center h-64 text-muted-foreground">
            <div className="text-center space-y-sm">
              <BarChart3 className="w-12 h-12 mx-auto opacity-50" />
              <p>Chart type: {chartType}</p>
              <p className="text-xs">{chartData.length} data points</p>
            </div>
          </div>
        );
    }
  };

  const getChartIcon = () => {
    switch (chartType) {
      case 'line':
      case 'area':
        return <LineChart className="w-4 h-4" />;
      case 'pie':
      case 'donut':
        return <PieChart className="w-4 h-4" />;
      default:
        return <BarChart3 className="w-4 h-4" />;
    }
  };

  return (
    <Card className="p-lg">
      <div className="space-y-md">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-sm">
            {getChartIcon()}
            <div>
              <h3 className="font-semibold text-foreground">{widget.title}</h3>
              {widget.description && (
                <p className="text-sm text-muted-foreground">{widget.description}</p>
              )}
            </div>
          </div>
          <div className="flex items-center space-x-sm">
            {onExport && (
              <Button
                variant="ghost"
                size="sm"
                onClick={onExport}
                className="p-xs"
              >
                <Download className="w-4 h-4" />
              </Button>
            )}
            {onExpand && (
              <Button
                variant="ghost"
                size="sm"
                onClick={onExpand}
                className="p-xs"
              >
                <Maximize2 className="w-4 h-4" />
              </Button>
            )}
          </div>
        </div>

        {/* Chart */}
        <div className="w-full">
          {renderChart()}
        </div>

        {/* Footer Stats */}
        {data?.metadata && (
          <div className="flex items-center justify-between text-xs text-muted-foreground pt-sm border-t">
            <span>
              {data.metadata.total_count} data points
            </span>
            <span>
              Updated {new Date(data.metadata.last_updated).toLocaleTimeString()}
            </span>
          </div>
        )}
      </div>
    </Card>
  );
}

