    return (
      <div className="flex flex-col items-center justify-center py-12 text-center">
        <Calendar className="h-12 w-12 text-muted-foreground mb-4" />
        <h3 className="text-lg font-semibold mb-2">No call sheets found</h3>
        <p className="text-muted-foreground">Create your first call sheet to get started.</p>
      </div>
    );
  }

  // Group call sheets by date
  const groupedCallSheets = callSheets.reduce((groups, callSheet) => {
    const date = format(new Date(callSheet.call_date), 'yyyy-MM-dd');
    if (!groups[date]) {
      groups[date] = [];
    }
    groups[date].push(callSheet);
    return groups;
  }, {} as Record<string, CallSheet[]>);

  // Sort dates
  const sortedDates = Object.keys(groupedCallSheets).sort();

  return (
    <div className="space-y-8">
      {sortedDates.map((date) => {
        const callSheetsForDate = groupedCallSheets[date];
        const dateObj = parseISO(date);
        const isToday = isSameDay(dateObj, new Date());

        return (
          <div key={date} className="relative">
            {/* Date Header */}
            <div className="sticky top-0 z-10 bg-background/80 backdrop-blur-sm border-b pb-2 mb-4">
              <div className="flex items-center space-x-3">
                <div className={`w-3 h-3 rounded-full ${isToday ? 'bg-primary' : 'bg-muted-foreground'}`} />
                <h2 className={`text-lg font-semibold ${isToday ? 'text-primary' : 'text-foreground'}`}>
                  {format(dateObj, 'EEEE, MMMM dd, yyyy')}
                  {isToday && <span className="ml-2 text-sm text-primary">(Today)</span>}
                </h2>
                <Badge variant="outline" className="text-xs">
                  {callSheetsForDate.length} call sheet{callSheetsForDate.length !== 1 ? 's' : ''}
                </Badge>
              </div>
            </div>

            {/* Timeline Items */}
            <div className="space-y-4 ml-6 border-l-2 border-muted pl-6">
              {callSheetsForDate
                .sort((a, b) => {
                  // Sort by call time if available, otherwise by creation time
                  const timeA = a.call_time || '00:00';
                  const timeB = b.call_time || '00:00';
                  return timeA.localeCompare(timeB);
                })
                .map((callSheet, index) => (
                  <div key={callSheet.id} className="relative">
                    {/* Timeline Dot */}
                    <div className={`absolute -left-8 w-4 h-4 rounded-full border-2 border-background ${
                      STATUS_VARIANTS[callSheet.status] === 'success' ? 'bg-success' :
                      STATUS_VARIANTS[callSheet.status] === 'warning' ? 'bg-warning' :
                      STATUS_VARIANTS[callSheet.status] === 'destructive' ? 'bg-destructive' :
                      STATUS_VARIANTS[callSheet.status] === 'info' ? 'bg-info' : 'bg-muted-foreground'
                    }`} />

                    <Card className="p-4 hover:shadow-md transition-shadow">
                      <div className="flex items-start justify-between">
                        <div className="flex items-start space-x-3 flex-1">
                          <input
                            type="checkbox"
                            checked={selected.has(callSheet.id)}
                            onChange={() => onSelect(callSheet.id)}
                            className="mt-1"
                          />

                          <div className="flex-1 min-w-0">
                            <div className="flex items-center space-x-2 mb-2">
                              <h3 className="font-semibold text-base">{callSheet.title}</h3>
                              <Badge variant={STATUS_VARIANTS[callSheet.status]} className="text-xs">
                                {callSheet.status.replace('_', ' ')}
                              </Badge>
                            </div>

                            {callSheet.description && (
                              <p className="text-muted-foreground text-sm mb-3 line-clamp-2">
                                {callSheet.description}
                              </p>
                            )}

                            <div className="grid grid-cols-1 md:grid-cols-3 gap-3 text-sm mb-3">
                              {callSheet.call_time && (
                                <div className="flex items-center text-muted-foreground">
                                  <Clock className="h-4 w-4 mr-2" />
                                  <span className="font-medium">{callSheet.call_time}</span>
                                </div>
                              )}

                              {callSheet.location && (
                                <div className="flex items-center text-muted-foreground">
                                  <MapPin className="h-4 w-4 mr-2" />
                                  <span className="truncate">{callSheet.location}</span>
                                </div>
                              )}

                              <div className="flex items-center text-muted-foreground">
                                <Users className="h-4 w-4 mr-2" />
                                <span>
                                  {(callSheet.crew_calls?.length || 0) + (callSheet.talent_calls?.length || 0)} people
                                </span>
                              </div>
                            </div>

                            {/* Crew and Talent Details */}
                            {(callSheet.crew_calls?.length > 0 || callSheet.talent_calls?.length > 0) && (
                              <div className="space-y-2 mb-3">
                                {callSheet.crew_calls?.length > 0 && (
                                  <div className="text-xs">
                                    <span className="font-medium text-muted-foreground">Crew ({callSheet.crew_calls.length}):</span>
                                    <div className="flex flex-wrap gap-1 mt-1">
                                      {callSheet.crew_calls.slice(0, 3).map((crew, idx) => (
                                        <Badge key={idx} variant="outline" className="text-xs">
                                          {crew.role} - {crew.call_time}
                                        </Badge>
                                      ))}
                                      {callSheet.crew_calls.length > 3 && (
                                        <Badge variant="outline" className="text-xs">
                                          +{callSheet.crew_calls.length - 3} more
                                        </Badge>
                                      )}
                                    </div>
                                  </div>
                                )}

                                {callSheet.talent_calls?.length > 0 && (
                                  <div className="text-xs">
                                    <span className="font-medium text-muted-foreground">Talent ({callSheet.talent_calls.length}):</span>
                                    <div className="flex flex-wrap gap-1 mt-1">
                                      {callSheet.talent_calls.slice(0, 3).map((talent, idx) => (
                                        <Badge key={idx} variant="outline" className="text-xs">
                                          {talent.talent_name} - {talent.call_time}
                                        </Badge>
                                      ))}
                                      {callSheet.talent_calls.length > 3 && (
                                        <Badge variant="outline" className="text-xs">
                                          +{callSheet.talent_calls.length - 3} more
                                        </Badge>
                                      )}
                                    </div>
                                  </div>
                                )}
                              </div>
                            )}

                            {callSheet.special_instructions && (
                              <div className="p-2 bg-muted rounded text-xs mb-3">
                                <strong>Special Instructions:</strong> {callSheet.special_instructions}
                              </div>
                            )}

                            {callSheet.weather && (
                              <div className="text-xs text-muted-foreground">
                                <strong>Weather:</strong> {callSheet.weather}
                              </div>
                            )}
                          </div>
                        </div>

                        <div className="flex items-center space-x-1 ml-4">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => onView(callSheet)}
                            title="View call sheet"
                          >
                            <Eye className="h-4 w-4" />
                          </Button>

                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => onEdit(callSheet)}
                            title="Edit call sheet"
                          >
                            <Pencil className="h-4 w-4" />
                          </Button>

                          {callSheet.status === 'published' && onDistribute && (
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => onDistribute(callSheet)}
                              title="Distribute call sheet"
                            >
                              <Share2 className="h-4 w-4" />
                            </Button>
                          )}

                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => onDelete(callSheet)}
                            title="Delete call sheet"
                            className="text-destructive hover:text-destructive"
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    </Card>
                  </div>
                ))}
            </div>
          </div>
        );
      })}
    </div>
  );
}

'use client';

import { Upload, X } from "lucide-react";
import { useCallback, useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
// import { z } from 'zod';
import { Button, Input, Textarea, Select, Checkbox, Badge } from '@ghxstship/ui';
import { ResourcesService } from '../lib/resources-service';
import { RESOURCE_TYPE_OPTIONS, RESOURCE_STATUS_OPTIONS, RESOURCE_VISIBILITY_OPTIONS, RESOURCE_LANGUAGE_OPTIONS, RESOURCE_CATEGORY_OPTIONS } from '../lib/field-config';
import type { Resource, CreateResourceData, UpdateResourceData } from '../types';

const resourceSchema = z.object({
  title: z.string().min(1, 'Title is required'),
  description: z.string().optional(),
  content: z.string().min(1, 'Content is required'),
  type: z.enum(['policy', 'guide', 'training', 'template', 'procedure', 'featured']),
  category: z.string().min(1, 'Category is required'),
  status: z.enum(['draft', 'under_review', 'published', 'archived']).default('draft'),
  visibility: z.enum(['public', 'private', 'team', 'role_based']).default('public'),
  version: z.string().default('1.0'),
  language: z.string().default('en'),
  tags: z.string().optional(),
  is_featured: z.boolean().default(false),
  is_pinned: z.boolean().default(false)
});

type ResourceFormData = z.infer<typeof resourceSchema>;

interface CreateResourceClientProps {
  resource?: Resource | null;
  onSuccess: () => void;
  onCancel: () => void;
}

export default function CreateResourceClient({ resource, onSuccess, onCancel }: CreateResourceClientProps) {
  const [file, setFile] = useState<File | null>(null);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState<string | null>(null);

  const resourcesService = new ResourcesService();

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset,
    setValue,
    watch
  } = useForm<ResourceFormData>({
    resolver: zodResolver(resourceSchema),
    defaultValues: {
      title: '',
      description: '',
      content: '',
      type: 'policy',
      category: '',
      status: 'draft',
      visibility: 'public',
      version: '1.0',
      language: 'en',
      tags: '',
      is_featured: false,
      is_pinned: false
    }
  });

  // Populate form when editing
  useEffect(() => {
    if (resource) {
      reset({
        title: resource.title,
        description: resource.description || '',
        content: resource.content,
        type: resource.type,
        category: resource.category,
        status: resource.status,
        visibility: resource.visibility,
        version: resource.version,
        language: resource.language,
        tags: resource.tags.join(', '),
        is_featured: resource.is_featured,
        is_pinned: resource.is_pinned
      });
    }
  }, [resource, reset]);

  const handleFileChange = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = event.target.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
      setUploadError(null);
    }
  }, []);

  const onSubmit = useCallback(async (data: ResourceFormData) => {
    try {
      setUploading(true);
      setUploadError(null);

      let fileUrl: string | undefined;
      let fileSize: number | undefined;
      let fileType: string | undefined;

      // Upload file if provided
      if (file) {
        try {
          const uploadResult = await resourcesService.uploadFile(file, resource?.id);
          fileUrl = uploadResult.url;
          fileSize = uploadResult.size;
          fileType = uploadResult.type;
        } catch (err) {
          setUploadError(err instanceof Error ? err.message : 'Failed to upload file');
          return;
        }
      }

      // Prepare resource data
      const resourceData = {
        title: data.title,
        description: data.description || undefined,
        content: data.content,
        type: data.type,
        category: data.category,
        status: data.status,
        visibility: data.visibility,
        version: data.version,
        language: data.language,
        tags: data.tags ? data.tags.split(',').map(tag => tag.trim()).filter(Boolean) : [],
        is_featured: data.is_featured,
        is_pinned: data.is_pinned,
        file_url: fileUrl || resource?.file_url,
        file_size: fileSize || resource?.file_size,
        file_type: fileType || resource?.file_type
      };

      // Create or update resource
      if (resource) {
        const updateData: UpdateResourceData = {
          id: resource.id,
          ...resourceData
        };
        await resourcesService.updateResource(updateData);
      } else {
        const createData: CreateResourceData = resourceData;
        await resourcesService.createResource(createData);
      }

      onSuccess();
    } catch (err) {
      console.error('Error saving resource:', err);
      setUploadError(err instanceof Error ? err.message : 'Failed to save resource');
    } finally {
      setUploading(false);
    }
  }, [file, resource, resourcesService, onSuccess]);

  const tagsValue = watch('tags');
  const parsedTags = tagsValue ? tagsValue.split(',').map(tag => tag.trim()).filter(Boolean) : [];

  return (
    <div className="stack-lg">
      <form onSubmit={handleSubmit(onSubmit)} className="stack-lg">
        {/* Title */}
        <div>
          <label className="block text-body-sm form-label mb-sm">Title *</label>
          <Input
            {...register('title')}
            placeholder="Enter resource title"
            error={errors.title?.message}
          />
        </div>

        {/* Description */}
        <div>
          <label className="block text-body-sm form-label mb-sm">Description</label>
          <Textarea
            {...register('description')}
            placeholder="Enter resource description"
            rows={3}
          />
        </div>

        {/* Content */}
        <div>
          <label className="block text-body-sm form-label mb-sm">Content *</label>
          <Textarea
            {...register('content')}
            placeholder="Enter resource content"
            rows={6}
            error={errors.content?.message}
          />
        </div>

        {/* Type and Category */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
          <div>
            <label className="block text-body-sm form-label mb-sm">Type *</label>
            <Select {...register('type')}>
              {RESOURCE_TYPE_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </Select>
          </div>

          <div>
            <label className="block text-body-sm form-label mb-sm">Category *</label>
            <Select {...register('category')} error={errors.category?.message}>
              <option value="">Select category</option>
              {RESOURCE_CATEGORY_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </Select>
          </div>
        </div>

        {/* Status and Visibility */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
          <div>
            <label className="block text-body-sm form-label mb-sm">Status</label>
            <Select {...register('status')}>
              {RESOURCE_STATUS_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </Select>
          </div>

          <div>
            <label className="block text-body-sm form-label mb-sm">Visibility</label>
            <Select {...register('visibility')}>
              {RESOURCE_VISIBILITY_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </Select>
          </div>
        </div>

        {/* Version and Language */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
          <div>
            <label className="block text-body-sm form-label mb-sm">Version</label>
            <Input
              {...register('version')}
              placeholder="1.0"
            />
          </div>

          <div>
            <label className="block text-body-sm form-label mb-sm">Language</label>
            <Select {...register('language')}>
              {RESOURCE_LANGUAGE_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </Select>
          </div>
        </div>

        {/* Tags */}
        <div>
          <label className="block text-body-sm form-label mb-sm">Tags</label>
          <Input
            {...register('tags')}
            placeholder="Enter tags separated by commas"
          />
          {parsedTags.length > 0 && (
            <div className="flex flex-wrap gap-xs mt-sm">
              {parsedTags.map((tag, index) => (
                <Badge key={index} variant="secondary">
                  {tag}
                </Badge>
              ))}
            </div>
          )}
          <p className="text-body-sm color-muted mt-xs">Separate multiple tags with commas</p>
        </div>

        {/* File Upload */}
        <div>
          <label className="block text-body-sm form-label mb-sm">
            File {!resource && '*'}
          </label>
          <div className="border-2 border-dashed border-border rounded-md p-md">
            <input
              type="file"
              onChange={handleFileChange}
              className="w-full"
              accept=".pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx,.txt,.md"
            />
            {file && (
              <p className="text-body-sm color-muted mt-sm">
                Selected: {file.name} ({(file.size / 1024 / 1024).toFixed(2)} MB)
              </p>
            )}
            {resource?.file_url && !file && (
              <p className="text-body-sm color-muted mt-sm">
                Current file: {resource.file_url.split('/').pop()}
              </p>
            )}
          </div>
          {uploadError && <p className="color-destructive text-body-sm mt-xs">{uploadError}</p>}
        </div>

        {/* Featured and Pinned */}
        <div className="flex items-center gap-lg">
          <div className="flex items-center gap-sm">
            <Checkbox
              id="is_featured"
              {...register('is_featured')}
            />
            <label htmlFor="is_featured" className="text-body-sm form-label">
              Mark as featured resource
            </label>
          </div>

          <div className="flex items-center gap-sm">
            <Checkbox
              id="is_pinned"
              {...register('is_pinned')}
            />
            <label htmlFor="is_pinned" className="text-body-sm form-label">
              Pin to top
            </label>
          </div>
        </div>

        {/* Actions */}
        <div className="flex justify-end gap-sm pt-md border-t">
          <Button type="button" variant="outline" onClick={onCancel}>
            Cancel
          </Button>
          <Button type="submit" disabled={isSubmitting || uploading}>
            {uploading ? (
              <>
                <div className="w-4 h-4 border-2 border-background/30 border-t-background rounded-full animate-spin mr-sm"></div>
                {resource ? 'Updating...' : 'Creating...'}
              </>
            ) : (
              <>
                <Upload className="w-4 h-4 mr-sm" />
                {resource ? 'Update Resource' : 'Create Resource'}
              </>
            )}
          </Button>
        </div>
      </form>
    </div>
  );
}

"use client";

import { Eye, Edit, Download, FileText, Calendar, User, Tag, Lock, Globe, Users, HardDrive, Clock, Activity, MessageSquare, Link, Share2 } from "lucide-react";
import {
  Button,
  Badge,
  Tabs,
  TabsList,
  TabsTrigger,
  TabsContent,
} from "@ghxstship/ui";
import AppDrawer from "@/app/_components/shared/AppDrawer";
import { format, parseISO, formatDistanceToNow } from "date-fns";
import type { ProjectFile } from "./FilesClient";

interface ViewFileDrawerProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  file: ProjectFile;
  onEdit?: () => void;
  onDownload?: () => void;
  formatFileSize: (bytes: number) => string;
}

export default function ViewFileDrawer({
  open,
  onOpenChange,
  file,
  onEdit,
  onDownload,
  formatFileSize,
}: ViewFileDrawerProps) {
  const getAccessIcon = (level: string) => {
    switch (level) {
      case "public":
        return Globe;
      case "team":
        return Users;
      case "restricted":
        return Lock;
      default:
        return Lock;
    }
  };

  const getAccessBadgeVariant = (level: string) => {
    switch (level) {
      case "public":
        return "success";
      case "team":
        return "info";
      case "restricted":
        return "destructive";
      default:
        return "secondary";
    }
  };

  const getCategoryBadgeVariant = (category: string) => {
    switch (category) {
      case "document":
        return "info";
      case "image":
        return "success";
      case "video":
        return "warning";
      case "audio":
        return "secondary";
      default:
        return "default";
    }
  };

  const AccessIcon = getAccessIcon(file.access_level);

  // Custom tabs for file details
  const tabs = [
    {
      key: "overview",
      label: "Overview",
      content: (
        <div className="space-y-md">
          {/* File Preview (for images) */}
          {file.category === "image" && (
            <div className="bg-muted rounded-lg p-md">
              <img
                src={file.file_url}
                alt={file.name}
                className="max-w-full max-h-[400px] mx-auto object-contain rounded"
              />
            </div>
          )}

          {/* File Information */}
          <div className="grid grid-cols-2 gap-md">
            <div className="space-y-xs">
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <FileText className="h-4 w-4" />
                <span>File Type</span>
              </div>
              <p className="font-medium">{file.file_type}</p>
            </div>

            <div className="space-y-xs">
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <HardDrive className="h-4 w-4" />
                <span>File Size</span>
              </div>
              <p className="font-medium">{formatFileSize(file.file_size)}</p>
            </div>

            <div className="space-y-xs">
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <AccessIcon className="h-4 w-4" />
                <span>Access Level</span>
              </div>
              <Badge variant={getAccessBadgeVariant(file.access_level)}>
                {file.access_level}
              </Badge>
            </div>

            <div className="space-y-xs">
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <Tag className="h-4 w-4" />
                <span>Category</span>
              </div>
              <Badge variant={getCategoryBadgeVariant(file.category)}>
                {file.category}
              </Badge>
            </div>
          </div>

          {/* Description */}
          {file.description && (
            <div className="space-y-xs">
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <FileText className="h-4 w-4" />
                <span>Description</span>
              </div>
              <p className="text-sm">{file.description}</p>
            </div>
          )}

          {/* Tags */}
          {file.tags && file.tags.length > 0 && (
            <div className="space-y-xs">
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <Tag className="h-4 w-4" />
                <span>Tags</span>
              </div>
              <div className="flex flex-wrap gap-xs">
                {file.tags.map((tag, index) => (
                  <Badge key={index} variant="outline">
                    {tag}
                  </Badge>
                ))}
              </div>
            </div>
          )}

          {/* Project */}
          {file.project && (
            <div className="space-y-xs">
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <Link className="h-4 w-4" />
                <span>Project</span>
              </div>
              <p className="font-medium">{file.project.name}</p>
            </div>
          )}
        </div>
      ),
    },
    {
      key: "details",
      label: "Details",
      content: (
        <div className="space-y-md">
          {/* Version Information */}
          <div className="space-y-sm">
            <h4 className="font-semibold">Version Information</h4>
            <div className="grid grid-cols-2 gap-md">
              <div className="space-y-xs">
                <span className="text-sm text-muted-foreground">Version</span>
                <p className="font-medium">v{file.version}</p>
              </div>
              <div className="space-y-xs">
                <span className="text-sm text-muted-foreground">Latest</span>
                <Badge variant={file.is_latest ? "success" : "secondary"}>
                  {file.is_latest ? "Yes" : "No"}
                </Badge>
              </div>
            </div>
          </div>

          {/* Upload Information */}
          <div className="space-y-sm">
            <h4 className="font-semibold">Upload Information</h4>
            <div className="space-y-sm">
              <div className="flex items-center justify-between p-sm bg-muted rounded-lg">
                <div className="flex items-center gap-sm">
                  <User className="h-4 w-4 text-muted-foreground" />
                  <span className="text-sm">Uploaded By</span>
                </div>
                <span className="font-medium">
                  {file.uploaded_by_user?.full_name || file.uploaded_by_user?.email || "Unknown"}
                </span>
              </div>

              <div className="flex items-center justify-between p-sm bg-muted rounded-lg">
                <div className="flex items-center gap-sm">
                  <Calendar className="h-4 w-4 text-muted-foreground" />
                  <span className="text-sm">Upload Date</span>
                </div>
                <span className="font-medium">
                  {format(parseISO(file.created_at), "MMM d, yyyy 'at' h:mm a")}
                </span>
              </div>

              <div className="flex items-center justify-between p-sm bg-muted rounded-lg">
                <div className="flex items-center gap-sm">
                  <Clock className="h-4 w-4 text-muted-foreground" />
                  <span className="text-sm">Last Modified</span>
                </div>
                <span className="font-medium">
                  {formatDistanceToNow(parseISO(file.updated_at), { addSuffix: true })}
                </span>
              </div>
            </div>
          </div>

          {/* Usage Statistics */}
          <div className="space-y-sm">
            <h4 className="font-semibold">Usage Statistics</h4>
            <div className="p-sm bg-muted rounded-lg">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-sm">
                  <Download className="h-4 w-4 text-muted-foreground" />
                  <span className="text-sm">Total Downloads</span>
                </div>
                <span className="font-medium">{file.download_count}</span>
              </div>
            </div>
          </div>
        </div>
      ),
    },
    {
      key: "activity",
      label: "Activity",
      content: (
        <div className="text-center py-lg text-muted-foreground">
          <MessageSquare className="mx-auto h-12 w-12 mb-sm opacity-50" />
          <p>Activity tracking coming soon</p>
        </div>
      ),
    },
  ];

  return (
    <AppDrawer
      open={open}
      onClose={() => onOpenChange(false)}
      title={file.name}
      mode="view"
      tabs={tabs}
      actions={[
        {
          key: "download",
          label: "Download",
          icon: <Download className="h-4 w-4" />,
          onClick: onDownload,
        },
        {
          key: "share",
          label: "Share",
          icon: <Share2 className="h-4 w-4" />,
          onClick: async () => {
            await navigator.clipboard.writeText(file.file_url);
            // Toast would show "Link copied to clipboard"
          },
        },
        {
          key: "edit",
          label: "Edit",
          icon: <Edit className="h-4 w-4" />,
          onClick: onEdit,
        },
      ]}
    />
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { DataViewProvider, StateManagerProvider, UniversalDrawer, ViewSwitcher, DataActions } from '@ghxstship/ui';
import { Card, Badge, Button } from '@ghxstship/ui';
import { ResourcesService } from '../lib/resources-service';
import { getFieldConfigsForType } from '../lib/field-config';
import CreateResourceClient from '../drawers/CreateResourceClient';
import type { Resource, ResourceFilters, ResourceStats } from '../types';

export default function FeaturedClient() {
  const [resources, setResources] = useState<Resource[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedResources, setSelectedResources] = useState<string[]>([]);
  const [showCreateDrawer, setShowCreateDrawer] = useState(false);
  const [editingResource, setEditingResource] = useState<Resource | null>(null);
  const [currentView, setCurrentView] = useState<'grid' | 'kanban' | 'calendar' | 'list'>('grid');

  const resourcesService = useMemo(() => new ResourcesService(), []);

  // Fetch featured resources
  const fetchFeaturedResources = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const { resources: fetchedResources } = await resourcesService.getResources({
        filters: { is_featured: true },
        sort_by: 'view_count',
        sort_order: 'desc'
      });

      setResources(fetchedResources);
    } catch (err) {
      console.error('Error fetching featured resources:', err);
      setError(err instanceof Error ? err.message : 'Failed to load featured resources');
    } finally {
      setLoading(false);
    }
  }, [resourcesService]);

  useEffect(() => {
    fetchFeaturedResources();
  }, [fetchFeaturedResources]);

  // Handle resource creation
  const handleCreate = useCallback(() => {
    setEditingResource(null);
    setShowCreateDrawer(true);
  }, []);

  // Handle resource editing
  const handleEdit = useCallback((resource: Resource) => {
    setEditingResource(resource);
    setShowCreateDrawer(true);
  }, []);

  // Handle resource deletion
  const handleDelete = useCallback(async (resourceIds: string[]) => {
    try {
      if (resourceIds.length === 1) {
        await resourcesService.deleteResource(resourceIds[0]);
      } else {
        await resourcesService.deleteResources(resourceIds);
      }
      await fetchFeaturedResources();
      setSelectedResources([]);
    } catch (err) {
      console.error('Error deleting resources:', err);
    }
  }, [resourcesService, fetchFeaturedResources]);

  // Handle resource download
  const handleDownload = useCallback(async (resource: Resource) => {
    if (!resource.file_url) return;

    try {
      await resourcesService.incrementDownloadCount(resource.id);
      window.open(resource.file_url, '_blank');
      await fetchFeaturedResources();
    } catch (err) {
      console.error('Error downloading resource:', err);
    }
  }, [resourcesService, fetchFeaturedResources]);

  // Handle resource view
  const handleView = useCallback(async (resource: Resource) => {
    try {
      await resourcesService.incrementViewCount(resource.id);
      await fetchFeaturedResources();
    } catch (err) {
      console.error('Error updating view count:', err);
    }
  }, [resourcesService, fetchFeaturedResources]);

  // Handle toggle featured status
  const handleToggleFeatured = useCallback(async (resource: Resource) => {
    try {
      await resourcesService.updateResource({
        id: resource.id,
        is_featured: !resource.is_featured
      });
      await fetchFeaturedResources();
    } catch (err) {
      console.error('Error toggling featured status:', err);
    }
  }, [resourcesService, fetchFeaturedResources]);

  // Handle drawer close
  const handleDrawerClose = useCallback(() => {
    setShowCreateDrawer(false);
    setEditingResource(null);
  }, []);

  // Handle successful resource save
  const handleResourceSave = useCallback(async () => {
    await fetchFeaturedResources();
    handleDrawerClose();
  }, [fetchFeaturedResources, handleDrawerClose]);

  // Data view configuration
  const dataViewConfig = useMemo(() => ({
    fields: getFieldConfigsForType('featured'),
    data: resources,
    loading,
    error,
    selectedItems: selectedResources,
    onSelectionChange: setSelectedResources,
    onItemClick: handleView,
    onItemEdit: handleEdit,
    onItemDelete: (ids: string[]) => handleDelete(ids),
    currentView,
    onViewChange: setCurrentView,
    actions: [
      {
        id: 'toggle-featured',
        label: 'Remove from Featured',
        icon: Star,
        onClick: handleToggleFeatured
      },
      {
        id: 'download',
        label: 'Download',
        icon: Download,
        onClick: (resource: Resource) => handleDownload(resource),
        show: (resource: Resource) => !!resource.file_url
      },
      {
        id: 'edit',
        label: 'Edit',
        icon: Edit,
        onClick: handleEdit
      }
    ],
    bulkActions: [
      {
        id: 'remove-featured',
        label: 'Remove from Featured',
        onClick: async (resourceIds: string[]) => {
          for (const id of resourceIds) {
            const resource = resources.find(r => r.id === id);
            if (resource) {
              await resourcesService.updateResource({
                id: resource.id,
                is_featured: false
              });
            }
          }
          await fetchFeaturedResources();
          setSelectedResources([]);
        }
      },
      {
        id: 'delete',
        label: 'Delete Selected',
        onClick: handleDelete,
        variant: 'destructive' as const
      }
    ]
  }), [
    resources, loading, error, selectedResources, currentView,
    handleView, handleEdit, handleDelete, handleDownload, handleToggleFeatured, resourcesService, fetchFeaturedResources
  ]);

  // Calculate stats
  const stats = useMemo(() => {
    const totalViews = resources.reduce((sum, r) => sum + r.view_count, 0);
    const totalDownloads = resources.reduce((sum, r) => sum + r.download_count, 0);
    const avgViews = resources.length > 0 ? Math.round(totalViews / resources.length) : 0;
    const mostPopular = resources.reduce((max, r) => r.view_count > max.view_count ? r : max, resources[0]);

    return {
      totalFeatured: resources.length,
      totalViews,
      totalDownloads,
      avgViews,
      mostPopular
    };
  }, [resources]);

  return (
    <StateManagerProvider>
      <div className="stack-lg">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-heading-3">Featured Resources</h1>
            <p className="color-muted">Showcase your most important organizational resources</p>
          </div>
          <Button onClick={handleCreate}>
            <Plus className="w-4 h-4 mr-sm" />
            Add Featured Resource
          </Button>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-md">
          <Card>
            <div className="flex items-center gap-sm">
              <Star className="w-5 h-5 color-warning" />
              <div>
                <div className="text-heading-3 color-accent">{stats.totalFeatured}</div>
                <div className="text-body-sm color-muted">Featured Resources</div>
              </div>
            </div>
          </Card>
          <Card>
            <div className="flex items-center gap-sm">
              <Eye className="w-5 h-5 color-secondary" />
              <div>
                <div className="text-heading-3 color-secondary">{stats.totalViews}</div>
                <div className="text-body-sm color-muted">Total Views</div>
              </div>
            </div>
          </Card>
          <Card>
            <div className="flex items-center gap-sm">
              <Download className="w-5 h-5 color-accent" />
              <div>
                <div className="text-heading-3 color-accent">{stats.totalDownloads}</div>
                <div className="text-body-sm color-muted">Total Downloads</div>
              </div>
            </div>
          </Card>
          <Card>
            <div className="flex items-center gap-sm">
              <TrendingUp className="w-5 h-5 color-success" />
              <div>
                <div className="text-heading-3 color-success">{stats.avgViews}</div>
                <div className="text-body-sm color-muted">Avg Views</div>
              </div>
            </div>
          </Card>
        </div>

        {/* Most Popular Resource */}
        {stats.mostPopular && (
          <Card>
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-heading-4 mb-xs">Most Popular Featured Resource</h3>
                <p className="text-body color-foreground">{stats.mostPopular.title}</p>
                <div className="flex items-center gap-md mt-sm">
                  <Badge variant="outline">{stats.mostPopular.type}</Badge>
                  <span className="text-body-sm color-muted">{stats.mostPopular.view_count} views</span>
                  <span className="text-body-sm color-muted">{stats.mostPopular.download_count} downloads</span>
                </div>
              </div>
              <Button onClick={() => handleView(stats.mostPopular)}>
                View Resource
              </Button>
            </div>
          </Card>
        )}

        {/* ATLVS DataViews */}
        <DataViewProvider config={dataViewConfig}>
          <div className="flex items-center justify-between mb-md">
            <ViewSwitcher />
            <DataActions />
          </div>

          <div className="min-h-96">
            {loading && (
              <div className="text-center py-xl">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-md"></div>
                <p className="color-muted">Loading featured resources...</p>
              </div>
            )}

            {error && (
              <Card>
                <div className="text-center py-xl">
                  <Star className="w-12 h-12 color-destructive mx-auto mb-md" />
                  <h3 className="text-body form-label color-foreground mb-sm">Error loading featured resources</h3>
                  <p className="color-muted mb-md">{error}</p>
                  <Button onClick={fetchFeaturedResources}>
                    Try Again
                  </Button>
                </div>
              </Card>
            )}

            {!loading && !error && resources.length === 0 && (
              <Card>
                <div className="text-center py-xl">
                  <Star className="w-12 h-12 color-muted mx-auto mb-md" />
                  <h3 className="text-body form-label color-foreground mb-sm">No featured resources</h3>
                  <p className="color-muted mb-md">
                    Start showcasing your most important resources by marking them as featured.
                  </p>
                  <Button onClick={handleCreate}>
                    <Plus className="w-4 h-4 mr-sm" />
                    Add Featured Resource
                  </Button>
                </div>
              </Card>
            )}
          </div>
        </DataViewProvider>

        {/* Create/Edit Drawer */}
        <UniversalDrawer
          isOpen={showCreateDrawer}
          onClose={handleDrawerClose}
          title={editingResource ? 'Edit Featured Resource' : 'Create Featured Resource'}
          size="lg"
        >
          <CreateResourceClient
            resource={editingResource}
            onSuccess={handleResourceSave}
            onCancel={handleDrawerClose}
          />
        </UniversalDrawer>
      </div>
    </StateManagerProvider>
  );
}

'use client';

import { useCallback, useEffect, useMemo, useState } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import { FilesService } from '../lib/files-service';
import type { DigitalAsset, AssetFilters } from '../types';

const mediaTypeIcons = {
  image: Image,
  video: Video,
  audio: Music,
  other: FileCode,
};

export default function MediaClient() {
  const [assets, setAssets] = useState<DigitalAsset[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedAssets, setSelectedAssets] = useState<string[]>([]);
  const [showUploadDrawer, setShowUploadDrawer] = useState(false);

  const filesService = useMemo(() => new FilesService(), []);

  const fetchMediaAssets = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      // Filter for media types only
      const mediaFilters: AssetFilters = {
        category: 'image' // We'll need to expand this to support multiple media types
      };

      const result = await filesService.getAssets(undefined, {
        filters: mediaFilters,
        sortField: 'created_at',
        sortDirection: 'desc'
      });

      if (result.error) {
        setError(result.error);
      } else {
        setAssets(result.data?.data || []);
      }
    } catch (err) {
      console.error('Error fetching media assets:', err);
      setError(err instanceof Error ? err.message : 'Failed to load media assets');
    } finally {
      setLoading(false);
    }
  }, [filesService]);

  useEffect(() => {
    fetchMediaAssets();
  }, [fetchMediaAssets]);

  const handleUpload = useCallback(() => {
    setShowUploadDrawer(true);
  }, []);

  const handleView = useCallback(async (asset: DigitalAsset) => {
    try {
      await filesService.incrementViewCount(asset.id);
      await fetchMediaAssets();
    } catch (err) {
      console.error('Error updating view count:', err);
    }
  }, [filesService, fetchMediaAssets]);

  const handleDownload = useCallback(async (asset: DigitalAsset) => {
    if (!asset.file_url) return;

    try {
      await filesService.incrementDownloadCount(asset.id);
      window.open(asset.file_url, '_blank');
      await fetchMediaAssets();
    } catch (err) {
      console.error('Error downloading asset:', err);
    }
  }, [filesService, fetchMediaAssets]);

  // Calculate stats
  const stats = useMemo(() => {
    const totalSize = assets.reduce((sum, asset) => sum + (asset.file_size || 0), 0);
    const imageCount = assets.filter(asset => asset.category === 'image').length;
    const videoCount = assets.filter(asset => asset.category === 'video').length;
    const audioCount = assets.filter(asset => asset.category === 'audio').length;

    return {
      total: assets.length,
      totalSize: Math.round(totalSize / (1024 * 1024)), // Convert to MB
      imageCount,
      videoCount,
      audioCount
    };
  }, [assets]);

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3">Media Assets</h1>
          <p className="color-muted">Manage multimedia content and digital assets</p>
        </div>
        <Button onClick={handleUpload}>
          <Upload className="w-4 h-4 mr-sm" />
          Upload Media
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-md">
        <Card>
          <div className="flex items-center gap-sm">
            <FileCode className="w-5 h-5 color-accent" />
            <div>
              <div className="text-heading-3">{stats.total}</div>
              <div className="text-body-sm color-muted">Total Assets</div>
            </div>
          </div>
        </Card>
        <Card>
          <div className="flex items-center gap-sm">
            <Image className="w-5 h-5 color-success" />
            <div>
              <div className="text-heading-3 color-success">{stats.imageCount}</div>
              <div className="text-body-sm color-muted">Images</div>
            </div>
          </div>
        </Card>
        <Card>
          <div className="flex items-center gap-sm">
            <Video className="w-5 h-5 color-warning" />
            <div>
              <div className="text-heading-3 color-warning">{stats.videoCount}</div>
              <div className="text-body-sm color-muted">Videos</div>
            </div>
          </div>
        </Card>
        <Card>
          <div className="flex items-center gap-sm">
            <Music className="w-5 h-5 color-secondary" />
            <div>
              <div className="text-heading-3 color-secondary">{stats.audioCount}</div>
              <div className="text-body-sm color-muted">Audio</div>
            </div>
          </div>
        </Card>
      </div>

      {/* Media Grid */}
      {loading && (
        <div className="text-center py-xl">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-md"></div>
          <p className="color-muted">Loading media assets...</p>
        </div>
      )}

      {error && (
        <Card>
          <div className="text-center py-xl">
            <FileCode className="w-12 h-12 color-destructive mx-auto mb-md" />
            <h3 className="text-body form-label color-foreground mb-sm">Error loading media</h3>
            <p className="color-muted mb-md">{error}</p>
            <Button onClick={fetchMediaAssets}>
              Try Again
            </Button>
          </div>
        </Card>
      )}

      {!loading && !error && assets.length === 0 && (
        <Card>
          <div className="text-center py-xl">
            <Image className="w-12 h-12 color-muted mx-auto mb-md" />
            <h3 className="text-body form-label color-foreground mb-sm">No media assets</h3>
            <p className="color-muted mb-md">
              Start building your media library by uploading images, videos, and audio files.
            </p>
            <Button onClick={handleUpload}>
              <Upload className="w-4 h-4 mr-sm" />
              Upload First Asset
            </Button>
          </div>
        </Card>
      )}

      {!loading && !error && assets.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
          {assets.map(asset => {
            const IconComponent = mediaTypeIcons[asset.category as keyof typeof mediaTypeIcons] || FileCode;
            const isSelected = selectedAssets.includes(asset.id);

            return (
              <Card 
                key={asset.id}
                className={`cursor-pointer hover:shadow-elevated transition-shadow ${
                  isSelected ? 'ring-2 ring-primary' : ''
                }`}
                onClick={() => handleView(asset)}
              >
                <div className="aspect-square bg-muted/50 rounded-md mb-sm flex items-center justify-center">
                  {asset.file_url && (asset.category === 'image') ? (
                    <img 
                      src={asset.file_url} 
                      alt={asset.title}
                      className="w-full h-full object-cover rounded-md"
                    />
                  ) : (
                    <IconComponent className="w-12 h-12 color-muted" />
                  )}
                </div>

                <div className="space-y-sm">
                  <h3 className="text-body form-label line-clamp-2">{asset.title}</h3>

                  <div className="flex items-center justify-between">
                    <Badge variant="outline">{asset.category}</Badge>
                    <div className="flex items-center gap-xs text-body-sm color-muted">
                      <Eye className="w-3 h-3" />
                      <span>{asset.view_count}</span>
                    </div>
                  </div>

                  <div className="flex items-center gap-sm">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDownload(asset);
                      }}
                      className="flex-1"
                    >
                      <Download className="w-3 h-3 mr-xs" />
                      Download
                    </Button>
                  </div>
                </div>
              </Card>
            );
          })}
        </div>
      )}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { Card, Badge, Button } from '@ghxstship/ui';
import type { MediaAsset } from '../types';

const mediaTypeIcons = {
  image: Image,
  video: Video,
  audio: Music,
  other: FileCode,
};

interface MediaGridViewProps {
  assets: MediaAsset[];
  onAssetClick: (asset: MediaAsset) => void;
  onAssetEdit: (asset: MediaAsset) => void;
  onAssetDownload: (asset: MediaAsset) => void;
  selectedAssets: string[];
  onSelectionChange: (assetIds: string[]) => void;
}

export default function MediaGridView({
  assets,
  onAssetClick,
  onAssetEdit,
  onAssetDownload,
  selectedAssets,
  onSelectionChange
}: MediaGridViewProps) {
  const handleAssetSelect = (assetId: string, selected: boolean) => {
    if (selected) {
      onSelectionChange([...selectedAssets, assetId]);
    } else {
      onSelectionChange(selectedAssets.filter(id => id !== assetId));
    }
  };

  const formatFileSize = (bytes: number) => {
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    if (bytes === 0) return '0 Bytes';
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return Math.round(bytes / Math.pow(1024, i) * 100) / 100 + ' ' + sizes[i];
  };

  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
      {assets.map(asset => {
        const IconComponent = mediaTypeIcons[asset.category] || FileCode;
        const isSelected = selectedAssets.includes(asset.id);

        return (
          <Card 
            key={asset.id}
            className={`cursor-pointer hover:shadow-elevated transition-shadow ${
              isSelected ? 'ring-2 ring-primary' : ''
            }`}
            onClick={() => onAssetClick(asset)}
          >
            <div className="relative">
              <input
                type="checkbox"
                checked={isSelected}
                onChange={(e) => {
                  e.stopPropagation();
                  handleAssetSelect(asset.id, e.target.checked);
                }}
                className="absolute top-sm left-sm z-10 rounded border-border"
              />

              <div className="aspect-square bg-muted/50 rounded-md mb-sm flex items-center justify-center relative overflow-hidden">
                {asset.thumbnail_url || (asset.category === 'image' && asset.file_url) ? (
                  <img 
                    src={asset.thumbnail_url || asset.file_url} 
                    alt={asset.title}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <IconComponent className="w-12 h-12 color-muted" />
                )}

                {asset.category === 'video' && (
                  <div className="absolute inset-0 flex items-center justify-center bg-black/20">
                    <Play className="w-8 h-8 text-white" />
                  </div>
                )}

                {asset.duration && (
                  <div className="absolute bottom-xs right-xs bg-black/70 text-white text-xs px-xs py-xxs rounded">
                    {formatDuration(asset.duration)}
                  </div>
                )}
              </div>
            </div>

            <div className="space-y-sm">
              <h3 className="text-body form-label line-clamp-2">{asset.title}</h3>

              {asset.description && (
                <p className="text-body-sm color-muted line-clamp-2">{asset.description}</p>
              )}

              <div className="flex items-center justify-between">
                <Badge variant="outline">{asset.category}</Badge>
                <span className="text-xs color-muted">{formatFileSize(asset.file_size)}</span>
              </div>

              {asset.dimensions && (
                <div className="text-xs color-muted">
                  {asset.dimensions.width} Ã— {asset.dimensions.height}
                </div>
              )}

              <div className="flex items-center justify-between text-body-sm color-muted">
                <div className="flex items-center gap-xs">
                  <Eye className="w-3 h-3" />
                  <span>{asset.view_count}</span>
                </div>
                <div className="flex items-center gap-xs">
                  <Download className="w-3 h-3" />
                  <span>{asset.download_count}</span>
                </div>
              </div>

              <div className="flex items-center gap-sm pt-sm border-t">
                <Button
                  size="sm"
                  variant="outline"
                  onClick={(e) => {
                    e.stopPropagation();
                    onAssetEdit(asset);
                  }}
                  className="flex-1"
                >
                  <Edit className="w-3 h-3 mr-xs" />
                  Edit
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={(e) => {
                    e.stopPropagation();
                    onAssetDownload(asset);
                  }}
                  className="flex-1"
                >
                  <Download className="w-3 h-3 mr-xs" />
                  Download
                </Button>
              </div>
            </div>
          </Card>
        );
      })}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { DataViewProvider, StateManagerProvider, UniversalDrawer, ViewSwitcher, DataActions } from '@ghxstship/ui';
import { Card, Badge, Button } from '@ghxstship/ui';
import { ResourcesService } from '../lib/resources-service';
import { getFieldConfigsForType } from '../lib/field-config';
import CreateResourceClient from '../drawers/CreateResourceClient';
import type { Resource } from '../types';

export default function PoliciesClient() {
  const [resources, setResources] = useState<Resource[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showCreateDrawer, setShowCreateDrawer] = useState(false);
  const [editingResource, setEditingResource] = useState<Resource | null>(null);

  const resourcesService = useMemo(() => new ResourcesService(), []);

  const fetchPolicies = useCallback(async () => {
    try {
      setLoading(true);
      const { resources: fetchedResources } = await resourcesService.getResources({
        filters: { type: 'policy' },
        sort_by: 'updated_at',
        sort_order: 'desc'
      });
      setResources(fetchedResources);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load policies');
    } finally {
      setLoading(false);
    }
  }, [resourcesService]);

  useEffect(() => {
    fetchPolicies();
  }, [fetchPolicies]);

  const handleCreate = useCallback(() => {
    setEditingResource(null);
    setShowCreateDrawer(true);
  }, []);

  const handleResourceSave = useCallback(async () => {
    await fetchPolicies();
    setShowCreateDrawer(false);
    setEditingResource(null);
  }, [fetchPolicies]);

  const stats = useMemo(() => {
    const published = resources.filter(r => r.status === 'published').length;
    const draft = resources.filter(r => r.status === 'draft').length;
    const underReview = resources.filter(r => r.status === 'under_review').length;

    return { total: resources.length, published, draft, underReview };
  }, [resources]);

  return (
    <StateManagerProvider>
      <div className="stack-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-heading-3">Organizational Policies</h1>
            <p className="color-muted">Manage company policies and governance documents</p>
          </div>
          <Button onClick={handleCreate}>
            <Plus className="w-4 h-4 mr-sm" />
            Add Policy
          </Button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-md">
          <Card>
            <div className="flex items-center gap-sm">
              <FileText className="w-5 h-5 color-accent" />
              <div>
                <div className="text-heading-3">{stats.total}</div>
                <div className="text-body-sm color-muted">Total Policies</div>
              </div>
            </div>
          </Card>
          <Card>
            <div className="flex items-center gap-sm">
              <Shield className="w-5 h-5 color-success" />
              <div>
                <div className="text-heading-3 color-success">{stats.published}</div>
                <div className="text-body-sm color-muted">Published</div>
              </div>
            </div>
          </Card>
          <Card>
            <div className="flex items-center gap-sm">
              <Eye className="w-5 h-5 color-warning" />
              <div>
                <div className="text-heading-3 color-warning">{stats.underReview}</div>
                <div className="text-body-sm color-muted">Under Review</div>
              </div>
            </div>
          </Card>
          <Card>
            <div className="flex items-center gap-sm">
              <Users className="w-5 h-5 color-secondary" />
              <div>
                <div className="text-heading-3 color-secondary">{stats.draft}</div>
                <div className="text-body-sm color-muted">Draft</div>
              </div>
            </div>
          </Card>
        </div>

        {!loading && !error && resources.length === 0 && (
          <Card>
            <div className="text-center py-xl">
              <FileText className="w-12 h-12 color-muted mx-auto mb-md" />
              <h3 className="text-body form-label color-foreground mb-sm">No policies found</h3>
              <p className="color-muted mb-md">Start building your policy library.</p>
              <Button onClick={handleCreate}>
                <Plus className="w-4 h-4 mr-sm" />
                Create First Policy
              </Button>
            </div>
          </Card>
        )}

        <UniversalDrawer
          isOpen={showCreateDrawer}
          onClose={() => setShowCreateDrawer(false)}
          title={editingResource ? 'Edit Policy' : 'Create Policy'}
          size="lg"
        >
          <CreateResourceClient
            resource={editingResource}
            onSuccess={handleResourceSave}
            onCancel={() => setShowCreateDrawer(false)}
          />
        </UniversalDrawer>
      </div>
    </StateManagerProvider>
  );
}

'use client';
