'use client';

import { Plus, Search, Filter, Download, Upload, MoreHorizontal, FileText, Mic, Utensils, Settings, Calendar as CalendarIcon, Shield, Car, Hotel, Film, Users, Grid3X3, List, BarChart3, Clock } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  DataActions,
  DataGrid,
  DataViewProvider,
  KanbanBoard,
  ListView,
  StateManagerProvider,
  ViewSwitcher,
  Input,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  type DataRecord,
} from '@ghxstship/ui';
import type { DataViewConfig, FieldConfig, FilterConfig, SortConfig } from '@ghxstship/ui/src/components/DataViews/types';

import type {
  ProgrammingRider,
  RiderFilters,
  RiderSort,
  ViewType,
  RiderKind,
  RiderStatus,
  RiderPriority,
  RiderEvent,
  RiderProject,
  STATUS_BADGE,
  PRIORITY_BADGE,
  RIDER_KIND_BADGE,
} from './types';

// Import view components
import ProgrammingRidersListView from './views/ProgrammingRidersListView';
import ProgrammingRidersGridView from './views/ProgrammingRidersGridView';
import ProgrammingRidersTimelineView from './views/ProgrammingRidersTimelineView';
import ProgrammingRidersAnalyticsView from './views/ProgrammingRidersAnalyticsView';

// Import drawer components
import CreateProgrammingRiderDrawer from './drawers/CreateProgrammingRiderDrawer';
import EditProgrammingRiderDrawer from './drawers/EditProgrammingRiderDrawer';
import ViewProgrammingRiderDrawer from './drawers/ViewProgrammingRiderDrawer';

interface User {
  id: string;
  email: string;
  full_name?: string | null;
  avatar_url?: string | null;
}

interface ProgrammingRidersClientProps {
  orgId: string;
  currentUserId: string;
  initialRiders: ProgrammingRider[];
  projects: RiderProject[];
  events: RiderEvent[];
  users: User[];
}

export default function ProgrammingRidersClient({
  orgId,
  currentUserId,
  initialRiders,
  projects,
  events,
  users,
}: ProgrammingRidersClientProps) {
  const supabase = useMemo(() => createBrowserClient(), []);

  // State management
  const [riders, setRiders] = useState<ProgrammingRider[]>(initialRiders);
  const [loading, setLoading] = useState(false);
  const [selectedRiders, setSelectedRiders] = useState<string[]>([]);
  const [currentView, setCurrentView] = useState<ViewType>('list');
  const [searchQuery, setSearchQuery] = useState('');
  const [filters, setFilters] = useState<RiderFilters>({});
  const [sort, setSort] = useState<RiderSort>({ field: 'created_at', direction: 'desc' });

  // Drawer state
  const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
  const [editDrawerOpen, setEditDrawerOpen] = useState(false);
  const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
  const [selectedRider, setSelectedRider] = useState<ProgrammingRider | null>(null);

  // Real-time subscription
  useEffect(() => {
    const channel = supabase
      .channel(`programming_riders_${orgId}`)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'programming_riders',
          filter: `organization_id=eq.${orgId}`,
        },
        (payload) => {
          if (payload.eventType === 'INSERT') {
            fetchRiders();
          } else if (payload.eventType === 'UPDATE') {
            setRiders((prev) =>
              prev.map((rider) =>
                rider.id === payload.new.id ? { ...rider, ...payload.new } : rider
              )
            );
          } else if (payload.eventType === 'DELETE') {
            setRiders((prev) => prev.filter((rider) => rider.id !== payload.old.id));
          }
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [supabase, orgId]);

  // Fetch riders with filters
  const fetchRiders = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();

      if (filters.event_id) params.append('event_id', filters.event_id);
      if (filters.project_id) params.append('project_id', filters.project_id);
      if (filters.kind) params.append('kind', filters.kind);
      if (filters.status) params.append('status', filters.status);
      if (filters.priority) params.append('priority', filters.priority);
      if (searchQuery) params.append('search', searchQuery);
      if (filters.start_date) params.append('start_date', filters.start_date);
      if (filters.end_date) params.append('end_date', filters.end_date);
      if (filters.fulfilled !== undefined) params.append('fulfilled', filters.fulfilled.toString());
      if (filters.approved !== undefined) params.append('approved', filters.approved.toString());

      const response = await fetch(`/api/v1/programming/riders?${params}`);
      if (!response.ok) throw new Error('Failed to fetch riders');

      const data = await response.json();
      setRiders(data);
    } catch (error) {
      console.error('Error fetching riders:', error);
    } finally {
      setLoading(false);
    }
  }, [filters, searchQuery]);

  // Trigger fetch when filters change
  useEffect(() => {
    fetchRiders();
  }, [fetchRiders]);

  // Filter and sort riders
  const filteredAndSortedRiders = useMemo(() => {
    let filtered = [...riders];

    // Apply local search if no server search
    if (searchQuery && !filters.search) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (rider) =>
          rider.title?.toLowerCase().includes(query) ||
          rider.description?.toLowerCase().includes(query) ||
          rider.requirements?.toLowerCase().includes(query) ||
          rider.event?.title?.toLowerCase().includes(query)
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      const aValue = a[sort.field];
      const bValue = b[sort.field];

      if (aValue === null || aValue === undefined) return 1;
      if (bValue === null || bValue === undefined) return -1;

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sort.direction === 'asc'
          ? aValue.localeCompare(bValue)
          : bValue.localeCompare(aValue);
      }

      if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
      return 0;
    });

    return filtered;
  }, [riders, searchQuery, filters.search, sort]);

  // Handlers
  const handleCreateRider = () => {
    setCreateDrawerOpen(true);
  };

  const handleEditRider = (rider: ProgrammingRider) => {
    setSelectedRider(rider);
    setEditDrawerOpen(true);
  };

  const handleViewRider = (rider: ProgrammingRider) => {
    setSelectedRider(rider);
    setViewDrawerOpen(true);
  };

  const handleDeleteRider = async (riderId: string) => {
    try {
      const response = await fetch(`/api/v1/programming/riders/${riderId}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Failed to delete rider');

      setRiders((prev) => prev.filter((rider) => rider.id !== riderId));
    } catch (error) {
      console.error('Error deleting rider:', error);
    }
  };

  const handleBulkAction = async (action: string, riderIds: string[]) => {
    try {
      setLoading(true);

      switch (action) {
        case 'delete':
          await Promise.all(
            riderIds.map((id) =>
              fetch(`/api/v1/programming/riders/${id}`, { method: 'DELETE' })
            )
          );
          setRiders((prev) => prev.filter((rider) => !riderIds.includes(rider.id)));
          break;
        case 'approve':
          await Promise.all(
            riderIds.map((id) =>
              fetch(`/api/v1/programming/riders/${id}`, {
                method: 'PATCH',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                  status: 'approved',
                  approved_at: new Date().toISOString(),
                  approved_by: currentUserId,
                }),
              })
            )
          );
          fetchRiders();
          break;
        case 'fulfill':
          await Promise.all(
            riderIds.map((id) =>
              fetch(`/api/v1/programming/riders/${id}`, {
                method: 'PATCH',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                  status: 'fulfilled',
                  fulfilled_at: new Date().toISOString(),
                  fulfilled_by: currentUserId,
                }),
              })
            )
          );
          fetchRiders();
          break;
      }

      setSelectedRiders([]);
    } catch (error) {
      console.error('Error performing bulk action:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSuccess = () => {
    fetchRiders();
    setCreateDrawerOpen(false);
    setEditDrawerOpen(false);
    setSelectedRider(null);
  };

  // Export functionality
  const handleExport = () => {
    const csvContent = [
      ['Title', 'Kind', 'Status', 'Priority', 'Event', 'Created At'].join(','),
      ...filteredAndSortedRiders.map((rider) =>
        [
          rider.title,
          rider.kind,
          rider.status,
          rider.priority,
          rider.event?.title || '',
          new Date(rider.created_at).toLocaleDateString(),
        ].join(',')
      ),
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `programming-riders-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  // View icons
  const viewIcons = {
    list: List,
    grid: Grid3X3,
    timeline: Clock,
    analytics: BarChart3,
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Programming Riders</h1>
          <p className="text-muted-foreground">
            Manage technical, hospitality, and production requirements
          </p>
        </div>
        <Button onClick={handleCreateRider}>
          <Plus className="mr-2 h-4 w-4" />
          Create Rider
        </Button>
      </div>

      {/* Filters and Search */}
      <Card className="p-4">
        <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
          <div className="flex flex-1 items-center gap-4">
            <div className="relative flex-1 max-w-sm">
              <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
              <Input
                placeholder="Search riders..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9"
              />
            </div>

            <Select
              value={filters.kind || ''}
              onValueChange={(value) =>
                setFilters((prev) => ({ ...prev, kind: value as RiderKind || undefined }))
              }
            >
              <SelectTrigger className="w-40">
                <SelectValue placeholder="All Types" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Types</SelectItem>
                <SelectItem value="technical">Technical</SelectItem>
                <SelectItem value="hospitality">Hospitality</SelectItem>
                <SelectItem value="stage_plot">Stage Plot</SelectItem>
                <SelectItem value="security">Security</SelectItem>
                <SelectItem value="catering">Catering</SelectItem>
                <SelectItem value="transportation">Transportation</SelectItem>
                <SelectItem value="accommodation">Accommodation</SelectItem>
                <SelectItem value="production">Production</SelectItem>
                <SelectItem value="artist">Artist</SelectItem>
                <SelectItem value="crew">Crew</SelectItem>
              </SelectContent>
            </Select>

            <Select
              value={filters.status || ''}
              onValueChange={(value) =>
                setFilters((prev) => ({ ...prev, status: value as RiderStatus || undefined }))
              }
            >
              <SelectTrigger className="w-40">
                <SelectValue placeholder="All Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Status</SelectItem>
                <SelectItem value="draft">Draft</SelectItem>
                <SelectItem value="pending_review">Pending Review</SelectItem>
                <SelectItem value="under_review">Under Review</SelectItem>
                <SelectItem value="approved">Approved</SelectItem>
                <SelectItem value="rejected">Rejected</SelectItem>
                <SelectItem value="fulfilled">Fulfilled</SelectItem>
                <SelectItem value="cancelled">Cancelled</SelectItem>
              </SelectContent>
            </Select>

            <Select
              value={filters.event_id || ''}
              onValueChange={(value) =>
                setFilters((prev) => ({ ...prev, event_id: value || undefined }))
              }
            >
              <SelectTrigger className="w-48">
                <SelectValue placeholder="All Events" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Events</SelectItem>
                {events.map((event) => (
                  <SelectItem key={event.id} value={event.id}>
                    {event.title}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="flex items-center gap-2">
            <Button variant="outline" size="sm" onClick={handleExport}>
              <Download className="mr-2 h-4 w-4" />
              Export
            </Button>

            {/* View Switcher */}
            <div className="flex items-center rounded-lg border p-1">
              {(['list', 'grid', 'timeline', 'analytics'] as ViewType[]).map((view) => {
                const Icon = viewIcons[view];
                return (
                  <Button
                    key={view}
                    variant={currentView === view ? 'default' : 'ghost'}
                    size="sm"
                    onClick={() => setCurrentView(view)}
                    className="h-8 w-8 p-0"
                  >
                    <Icon className="h-4 w-4" />
                  </Button>
                );
              })}
            </div>
          </div>
        </div>

        {/* Bulk Actions */}
        {selectedRiders.length > 0 && (
          <div className="mt-4 flex items-center gap-2 rounded-lg border bg-muted/50 p-3">
            <span className="text-sm text-muted-foreground">
              {selectedRiders.length} rider(s) selected
            </span>
            <div className="flex gap-2">
              <Button
                size="sm"
                variant="outline"
                onClick={() => handleBulkAction('approve', selectedRiders)}
              >
                Approve
              </Button>
              <Button
                size="sm"
                variant="outline"
                onClick={() => handleBulkAction('fulfill', selectedRiders)}
              >
                Fulfill
              </Button>
              <Button
                size="sm"
                variant="destructive"
                onClick={() => handleBulkAction('delete', selectedRiders)}
              >
                Delete
              </Button>
            </div>
          </div>
        )}
      </Card>

      {/* Content */}
      <div className="min-h-[400px]">
        {currentView === 'list' && (
          <ProgrammingRidersListView
            riders={filteredAndSortedRiders}
            loading={loading}
            selectedRiders={selectedRiders}
            onSelectionChange={setSelectedRiders}
            onEdit={handleEditRider}
            onView={handleViewRider}
            onDelete={handleDeleteRider}
            sort={sort}
            onSortChange={setSort}
          />
        )}

        {currentView === 'grid' && (
          <ProgrammingRidersGridView
            riders={filteredAndSortedRiders}
            loading={loading}
            onEdit={handleEditRider}
            onView={handleViewRider}
            onDelete={handleDeleteRider}
          />
        )}

        {currentView === 'timeline' && (
          <ProgrammingRidersTimelineView
            riders={filteredAndSortedRiders}
            loading={loading}
            onEdit={handleEditRider}
            onView={handleViewRider}
          />
        )}

        {currentView === 'analytics' && (
          <ProgrammingRidersAnalyticsView
            riders={filteredAndSortedRiders}
            loading={loading}
          />
        )}
      </div>

      {/* Drawers */}
      <CreateProgrammingRiderDrawer
        open={createDrawerOpen}
        onOpenChange={setCreateDrawerOpen}
        orgId={orgId}
        currentUserId={currentUserId}
        projects={projects}
        events={events}
        onSuccess={handleSuccess}
      />

      {selectedRider && (
        <>
          <EditProgrammingRiderDrawer
            open={editDrawerOpen}
            onOpenChange={setEditDrawerOpen}
            rider={selectedRider}
            orgId={orgId}
            currentUserId={currentUserId}
            projects={projects}
            events={events}
            onSuccess={handleSuccess}
          />

          <ViewProgrammingRiderDrawer
            open={viewDrawerOpen}
            onOpenChange={setViewDrawerOpen}
            rider={selectedRider}
            users={users}
            onEdit={() => {
              setViewDrawerOpen(false);
              setEditDrawerOpen(true);
            }}
            onDelete={() => {
              handleDeleteRider(selectedRider.id);
              setViewDrawerOpen(false);
              setSelectedRider(null);
            }}
          />
        </>
      )}
    </div>
  );
}

'use client';

import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  DataActions,
  DataGrid,
  DataViewProvider,
  KanbanBoard,
  ListView,
  StateManagerProvider,
  ViewSwitcher,
  type DataRecord,
} from '@ghxstship/ui';
import type { DataViewConfig, FieldConfig, FilterConfig, SortConfig } from '@ghxstship/ui/src/components/DataViews/types';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface RiderRecord extends DataRecord {
  id: string;
  event_id: string;
  event_name: string;
  kind: 'technical' | 'hospitality' | 'stage_plot';
  status: 'draft' | 'pending_review' | 'approved' | 'fulfilled';
  priority: 'low' | 'medium' | 'high' | 'critical';
  created_at: string;
  details?: string | Record<string, unknown> | null;
}

export default function RidersClient({ orgId }: { orgId: string }) {
  const supabase = useMemo(() => createBrowserClient(), []);

  const [data, setData] = useState<RiderRecord[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedRecord, setSelectedRecord] = useState<RiderRecord | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');

  // Define field configuration for riders
  const fields: FieldConfig[] = [
    {
      key: 'event_name',
      label: 'Event',
      type: 'text',
      sortable: true,
      filterable: true,
    },
    {
      key: 'kind',
      label: 'Rider Type',
      type: 'select',
      options: [
        { value: 'technical', label: 'Technical' },
        { value: 'hospitality', label: 'Hospitality' },
        { value: 'stage_plot', label: 'Stage Plot' }
      ],
      required: true,
      sortable: true,
      filterable: true,
      groupable: true,
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'pending_review', label: 'Pending Review' },
        { value: 'approved', label: 'Approved' },
        { value: 'fulfilled', label: 'Fulfilled' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
    },
    {
      key: 'priority',
      label: 'Priority',
      type: 'select',
      options: [
        { value: 'low', label: 'Low' },
        { value: 'medium', label: 'Medium' },
        { value: 'high', label: 'High' },
        { value: 'critical', label: 'Critical' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'date',
      sortable: true,
      filterable: true,
    }
  ];

  const loadRiders = useCallback(async () => {
    if (!orgId) {
      return;
    }

    try {
      setLoading(true);

      const { data: ridersData, error } = await supabase
        .from('riders')
        .select(`
          *,
          events!inner(
            name,
            projects!inner(
              organization_id
            )
          )
        `)
        .eq('events.projects.organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Transform data to include event name and computed fields
      const transformedData: RiderRecord[] = (ridersData || []).map((rider: unknown) => ({
        id: rider.id,
        event_id: rider.event_id,
        event_name: rider.events?.name || 'Unknown Event',
        kind: rider.kind ?? 'technical',
        status: rider.status ?? 'draft',
        priority: rider.priority ?? 'medium',
        created_at: rider.created_at,
        details: rider.details,
        ...rider,
      }));

      setData(transformedData);
    } catch (error) {
      console.error('Error loading riders:', error);
    } finally {
      setLoading(false);
    }
  }, [orgId, supabase]);

  useEffect(() => {
    void loadRiders();
  }, [loadRiders]);

  const handleCreateRider = () => {
    setSelectedRecord(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditRider = (rider: RiderRecord) => {
    setSelectedRecord(rider);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewRider = (rider: RiderRecord) => {
    setSelectedRecord(rider);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSaveRider = async (riderData: Partial<RiderRecord>) => {
    try {
      if (drawerMode === 'create') {
        const { error } = await supabase.from('riders').insert(riderData);
        if (error) throw error;
      } else if (drawerMode === 'edit') {
        if (!selectedRecord) {
          throw new Error('No rider selected for update');
        }
        const { error } = await supabase
          .from('riders')
          .update(riderData)
          .eq('id', selectedRecord.id);
        if (error) throw error;
      }

      await loadRiders();
      setDrawerOpen(false);
      setSelectedRecord(null);
    } catch (error) {
      console.error('Error saving rider:', error);
    }
  };

  const getRiderIcon = (kind: string) => {
    switch (kind) {
      case 'technical':
        return Settings;
      case 'hospitality':
        return Utensils;
      case 'stage_plot':
        return Mic;
      default:
        return FileText;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'draft':
        return 'color-muted bg-secondary/50';
      case 'pending_review':
        return 'color-warning-foreground bg-warning/10';
      case 'approved':
        return 'text-info-foreground bg-info/10';
      case 'fulfilled':
        return 'color-success-foreground bg-success/10';
      default:
        return 'color-muted bg-secondary/50';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'low':
        return 'color-muted bg-secondary/50';
      case 'medium':
        return 'text-info-foreground bg-info/10';
      case 'high':
        return 'color-warning-foreground bg-warning/10';
      case 'critical':
        return 'color-destructive-foreground bg-destructive/10';
      default:
        return 'color-muted bg-secondary/50';
    }
  };

  // Configure DataView
  const config: DataViewConfig = {
    id: 'riders-management',
    name: 'Riders Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data,

    onSearch: (query: string) => {
      console.log('Search riders:', query);
    },
    onFilter: (filters: FilterConfig[]) => {
      console.log('Filter riders:', filters);
    },
    onSort: (sorts: SortConfig[]) => {
      console.log('Sort riders:', sorts);
    },
    onRefresh: loadRiders,
    onExport: (records: DataRecord[], format: string) => {
      console.log('Export riders:', format, records);
    },
    onImport: (records: DataRecord[]) => {
      console.log('Import riders:', records);
    }
  };

  const ridersByType = useMemo(() => {
    return data.reduce<Record<string, RiderRecord[]>>((acc, rider) => {
      const type = rider.kind || 'technical';
      if (!acc[type]) {
        acc[type] = [];
      }
      acc[type].push(rider);
      return acc;
    }, {});
  }, [data]);

  const riderTypeLabels: { [key: string]: string } = {
    technical: 'Technical Riders',
    hospitality: 'Hospitality Riders',
    stage_plot: 'Stage Plots'
  };

  const handleCloseDrawer = useCallback(() => {
    if (loading) {
      return;
    }
    setDrawerOpen(false);
    setSelectedRecord(null);
  }, [loading]);

  return (
    <div className="stack-md">
      <DataViewProvider config={config}>
        <StateManagerProvider>
          <div className="stack-md">
            {/* Header Actions */}
            <div className="flex items-center justify-between mb-md">
              <div className="flex items-center gap-md">
                <h2 className="text-body text-heading-4">Riders Management</h2>
                <Button onClick={handleCreateRider}>
                  <Plus className="h-4 w-4 mr-sm" />
                  Create Rider
                </Button>
              </div>
              <div className="flex items-center gap-sm">
                <ViewSwitcher />
                <DataActions />
              </div>
            </div>

            {/* Rider Type Overview Cards */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-md mb-lg">
              {Object.entries(ridersByType).map(([type, typeRiders]) => {
                const IconComponent = getRiderIcon(type);
                const pendingCount = typeRiders.filter(r => r.status === 'pending_review').length;
                const approvedCount = typeRiders.filter(r => r.status === 'approved').length;

                return (
                  <Card key={type} className="p-md">
                    <div className="flex items-center justify-between mb-sm">
                      <h3 className="text-heading-4 flex items-center gap-sm">
                        <IconComponent className="h-4 w-4" />
                        {riderTypeLabels[type] || type}
                      </h3>
                      <Badge variant="secondary">
                        {typeRiders.length} riders
                      </Badge>
                    </div>

                    <div className="stack-sm">
                      <div className="flex justify-between text-body-sm">
                        <span className="color-muted">Pending Review:</span>
                        <span className="form-label color-warning">{pendingCount}</span>
                      </div>
                      <div className="flex justify-between text-body-sm">
                        <span className="color-muted">Approved:</span>
                        <span className="form-label color-success">{approvedCount}</span>
                      </div>
                    </div>

                    <div className="mt-sm stack-xs">
                      {typeRiders.slice(0, 2).map(rider => (
                        <div
                          key={rider.id}
                          className="flex items-center justify-between text-body-sm p-sm rounded border cursor-pointer hover:bg-secondary/50"
                          onClick={() => handleViewRider(rider)}
                        >
                          <div>
                            <div className="form-label">{rider.event_name}</div>
                            <div className="color-muted flex items-center gap-xs">
                              <CalendarIcon className="h-3 w-3" />
                              {rider.created_at ? new Date(rider.created_at).toLocaleDateString() : 'Unknown date'}
                            </div>
                          </div>
                          <div className="flex flex-col gap-xs">
                            <Badge variant="outline">
                              {rider.status.replace('_', ' ')}
                            </Badge>
                            <Badge variant="outline">
                              {rider.priority}
                            </Badge>
                          </div>
                        </div>
                      ))}

                      {typeRiders.length > 2 && (
                        <div className="text-body-sm color-muted text-center pt-xs">
                          +{typeRiders.length - 2} more riders
                        </div>
                      )}
                    </div>
                  </Card>
                );
              })}
            </div>

            {/* Data Views */}
            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'draft', title: 'Draft' },
                { id: 'pending_review', title: 'Pending Review' },
                { id: 'approved', title: 'Approved' },
                { id: 'fulfilled', title: 'Fulfilled' }
              ]}
              statusField="status"
              titleField="event_name"
              onCardClick={handleViewRider}
            />

            <ListView 
              titleField="event_name"
              subtitleField="kind"
              onItemClick={handleViewRider}
            />

            {/* Rider Details Drawer */}
            <AppDrawer
              open={drawerOpen}
              onClose={handleCloseDrawer}
              record={selectedRecord}
              fields={[]}
              mode={drawerMode}
              title={
                drawerMode === 'create' 
                  ? 'Create New Rider' 
                  : `${selectedRecord?.kind?.replace('_', ' ')} Rider` || 'Rider Details'
              }
              tabs={[{
                key: 'content',
                label: 'Details',
                content: (
                  <div>
                    {selectedRecord && (
                      <div className="stack-md mt-lg">
                        <div className="grid grid-cols-2 gap-md">
                          <div className="flex items-center gap-sm text-body-sm color-muted">
                            <FileText className="h-4 w-4" />
                            <span>
                              {selectedRecord.kind?.replace('_', ' ') || 'Technical'} Rider
                            </span>
                          </div>
                          <div className="flex items-center gap-sm text-body-sm color-muted">
                            <CalendarIcon className="h-4 w-4" />
                            <span>
                              Created {new Date(selectedRecord.created_at).toLocaleDateString()}
                            </span>
                          </div>
                        </div>

                        <div className="flex items-center gap-md">
                          <Badge className={getStatusColor(selectedRecord.status)}>
                            {selectedRecord.status?.replace('_', ' ') || 'Draft'}
                          </Badge>
                          <Badge className={getPriorityColor(selectedRecord.priority)}>
                            {selectedRecord.priority || 'Medium'} Priority
                          </Badge>
                        </div>

                        {selectedRecord.details && (
                          <div className="pt-md border-t">
                            <h4 className="form-label mb-sm">Rider Details</h4>
                            <div className="text-body-sm color-muted bg-secondary/50 p-sm rounded">
                              {typeof selectedRecord.details === 'object' 
                                ? JSON.stringify(selectedRecord.details, null, 2)
                                : selectedRecord.details
                              }
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                )
              }]}
            />

            {/* Empty State */}
            {!loading && data.length === 0 && (
              <Card className="p-xl text-center">
                <FileText className="h-12 w-12 mx-auto mb-md color-muted" />
                <h3 className="text-body text-heading-4 mb-sm">No Riders Yet</h3>
                <p className="color-muted mb-md">
                  Start creating technical and hospitality riders for your events to ensure all requirements are met.
                </p>
                <Button onClick={handleCreateRider}>
                  <Plus className="h-4 w-4 mr-sm" />
                  Create First Rider
                </Button>
              </Card>
            )}
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import { ResourcesService } from '../lib/resources-service';
import type { Resource } from '../types';

export default function TemplatesClient() {
  const [resources, setResources] = useState<Resource[]>([]);
  const [loading, setLoading] = useState(true);

  const resourcesService = useMemo(() => new ResourcesService(), []);

  const fetchTemplates = useCallback(async () => {
    try {
      setLoading(true);
      const { resources: fetchedResources } = await resourcesService.getResources({
        filters: { type: 'template' },
        sort_by: 'download_count',
        sort_order: 'desc'
      });
      setResources(fetchedResources);
    } catch (err) {
      console.error('Error fetching templates:', err);
    } finally {
      setLoading(false);
    }
  }, [resourcesService]);

  useEffect(() => {
    fetchTemplates();
  }, [fetchTemplates]);

  const stats = useMemo(() => {
    const totalDownloads = resources.reduce((sum, r) => sum + r.download_count, 0);
    const mostUsed = resources.reduce((max, r) => r.download_count > max.download_count ? r : max, resources[0]);

    return { 
      total: resources.length, 
      totalDownloads,
      mostUsed: mostUsed?.title || 'None'
    };
  }, [resources]);

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3">Templates & Forms</h1>
          <p className="color-muted">Reusable templates and document forms</p>
        </div>
        <Button>
          <Plus className="w-4 h-4 mr-sm" />
          Add Template
        </Button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
        <Card>
          <div className="flex items-center gap-sm">
            <File className="w-5 h-5 color-accent" />
            <div>
              <div className="text-heading-3">{stats.total}</div>
              <div className="text-body-sm color-muted">Total Templates</div>
            </div>
          </div>
        </Card>
        <Card>
          <div className="flex items-center gap-sm">
            <Download className="w-5 h-5 color-success" />
            <div>
              <div className="text-heading-3 color-success">{stats.totalDownloads}</div>
              <div className="text-body-sm color-muted">Total Downloads</div>
            </div>
          </div>
        </Card>
        <Card>
          <div className="flex items-center gap-sm">
            <Users className="w-5 h-5 color-secondary" />
            <div>
              <div className="text-heading-4 color-secondary truncate">{stats.mostUsed}</div>
              <div className="text-body-sm color-muted">Most Popular</div>
            </div>
          </div>
        </Card>
      </div>

      {!loading && resources.length === 0 && (
        <Card>
          <div className="text-center py-xl">
            <File className="w-12 h-12 color-muted mx-auto mb-md" />
            <h3 className="text-body form-label color-foreground mb-sm">No templates found</h3>
            <p className="color-muted mb-md">Create reusable templates and forms.</p>
            <Button>
              <Plus className="w-4 h-4 mr-sm" />
              Create First Template
            </Button>
          </div>
        </Card>
      )}
    </div>
  );
}

"use client";

import { useState } from "react";
import { Card, Badge, Button, Checkbox } from "@ghxstship/ui";
import type { ProjectFile } from "../FilesClient";
import type { LucideIcon } from "lucide-react";

interface FileFolderViewProps {
  groupedFiles: Record<string, ProjectFile[]>;
  selectedFiles: Set<string>;
  onSelectFile: (id: string) => void;
  onView: (file: ProjectFile) => void;
  onDownload: (file: ProjectFile) => void;
  onDelete: (file: ProjectFile) => void;
  formatFileSize: (bytes: number) => string;
  getCategoryIcon: (category: string) => LucideIcon;
}

export default function FileFolderView({
  groupedFiles,
  selectedFiles,
  onSelectFile,
  onView,
  onDownload,
  onDelete,
  formatFileSize,
  getCategoryIcon,
}: FileFolderViewProps) {
  const [expandedFolders, setExpandedFolders] = useState<Set<string>>(
    new Set(Object.keys(groupedFiles))
  );

  const toggleFolder = (category: string) => {
    setExpandedFolders((prev) => {
      const next = new Set(prev);
      if (next.has(category)) {
        next.delete(category);
      } else {
        next.add(category);
      }
      return next;
    });
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case "document":
        return "text-blue-600";
      case "image":
        return "text-green-600";
      case "video":
        return "text-purple-600";
      case "audio":
        return "text-yellow-600";
      case "drawing":
        return "text-indigo-600";
      case "specification":
        return "text-pink-600";
      case "report":
        return "text-orange-600";
      default:
        return "text-gray-600";
    }
  };

  const getTotalSize = (files: ProjectFile[]) => {
    const total = files.reduce((sum, file) => sum + file.file_size, 0);
    return formatFileSize(total);
  };

  return (
    <div className="space-y-md">
      {Object.entries(groupedFiles).map(([category, files]) => {
        const CategoryIcon = getCategoryIcon(category);
        const isExpanded = expandedFolders.has(category);

        return (
          <div key={category} className="border rounded-lg">
            {/* Folder Header */}
            <div
              className="flex items-center justify-between p-md bg-muted/50 cursor-pointer hover:bg-muted/70 transition-colors"
              onClick={() => toggleFolder(category)}
            >
              <div className="flex items-center gap-sm">
                <Button variant="ghost" size="sm" className="p-0">
                  {isExpanded ? (
                    <ChevronDown className="h-4 w-4" />
                  ) : (
                    <ChevronRight className="h-4 w-4" />
                  )}
                </Button>
                <Folder className={`h-5 w-5 ${getCategoryColor(category)}`} />
                <h3 className="font-semibold capitalize">{category}</h3>
                <Badge variant="secondary">{files.length} files</Badge>
                <span className="text-sm text-muted-foreground">
                  {getTotalSize(files)}
                </span>
              </div>
            </div>

            {/* Folder Contents */}
            {isExpanded && (
              <div className="p-md">
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-sm">
                  {files.map((file) => (
                    <div
                      key={file.id}
                      className={`flex items-center gap-sm p-sm border rounded-lg hover:bg-muted/50 cursor-pointer transition-colors ${
                        selectedFiles.has(file.id) ? "bg-primary/5 border-primary" : ""
                      }`}
                      onClick={() => onView(file)}
                    >
                      <Checkbox
                        checked={selectedFiles.has(file.id)}
                        onChange={() => onSelectFile(file.id)}
                        onClick={(e: React.MouseEvent) => e.stopPropagation()}
                      />

                      <CategoryIcon className="h-4 w-4 text-muted-foreground flex-shrink-0" />

                      <div className="flex-1 min-w-0">
                        <div className="font-medium truncate" title={file.name}>
                          {file.name}
                        </div>
                        <div className="flex items-center gap-sm text-xs text-muted-foreground">
                          <span>{formatFileSize(file.file_size)}</span>
                          {file.version !== "1.0" && (
                            <Badge variant="outline" className="scale-75">
                              v{file.version}
                            </Badge>
                          )}
                          {file.is_latest && (
                            <Badge variant="success" className="scale-75">
                              Latest
                            </Badge>
                          )}
                        </div>
                      </div>

                      <div className="flex items-center gap-xs" onClick={(e) => e.stopPropagation()}>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onDownload(file)}
                        >
                          <Download className="h-3 w-3" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onDelete(file)}
                          className="text-destructive"
                        >
                          <Trash2 className="h-3 w-3" />
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        );
      })}

      {Object.keys(groupedFiles).length === 0 && (
        <div className="text-center py-xl text-muted-foreground">
          No files to display
        </div>
      )}
    </div>
  );
}

"use client";

import { useState } from "react";
import { Card, Button } from "@ghxstship/ui";
import type { ProjectFile } from "../FilesClient";

interface FileGalleryViewProps {
  files: ProjectFile[];
  onView: (file: ProjectFile) => void;
  onDownload: (file: ProjectFile) => void;
  onDelete: (file: ProjectFile) => void;
}

export default function FileGalleryView({
  files,
  onView,
  onDownload,
  onDelete,
}: FileGalleryViewProps) {
  const [selectedImage, setSelectedImage] = useState<ProjectFile | null>(null);
  const [hoveredId, setHoveredId] = useState<string | null>(null);

  return (
    <>
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-md">
        {files.map((file) => (
          <div
            key={file.id}
            className="relative group cursor-pointer"
            onMouseEnter={() => setHoveredId(file.id)}
            onMouseLeave={() => setHoveredId(null)}
            onClick={() => setSelectedImage(file)}
          >
            {/* Image Container */}
            <div className="aspect-square bg-muted rounded-lg overflow-hidden">
              <img
                src={file.file_url}
                alt={file.name}
                className="w-full h-full object-cover transition-transform group-hover:scale-105"
                loading="lazy"
              />
            </div>

            {/* Overlay on Hover */}
            {hoveredId === file.id && (
              <div className="absolute inset-0 bg-black/60 rounded-lg flex items-center justify-center gap-sm animate-in fade-in-0 duration-200">
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onView(file);
                  }}
                >
                  <Eye className="h-4 w-4" />
                </Button>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onDownload(file);
                  }}
                >
                  <Download className="h-4 w-4" />
                </Button>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onDelete(file);
                  }}
                  className="text-destructive"
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            )}

            {/* File Name */}
            <div className="mt-sm">
              <p className="text-sm font-medium truncate" title={file.name}>
                {file.name}
              </p>
              {file.project && (
                <p className="text-xs text-muted-foreground truncate">
                  {file.project.name}
                </p>
              )}
            </div>
          </div>
        ))}
      </div>

      {files.length === 0 && (
        <div className="text-center py-xl text-muted-foreground">
          No images to display
        </div>
      )}

      {/* Lightbox Modal */}
      {selectedImage && (
        <div
          className="fixed inset-0 z-50 bg-black/90 flex items-center justify-center p-md animate-in fade-in-0 duration-200"
          onClick={() => setSelectedImage(null)}
        >
          <div className="relative max-w-[90vw] max-h-[90vh]">
            <img
              src={selectedImage.file_url}
              alt={selectedImage.name}
              className="max-w-full max-h-full object-contain"
              onClick={(e) => e.stopPropagation()}
            />

            {/* Close Button */}
            <Button
              variant="secondary"
              size="sm"
              className="absolute top-4 right-4"
              onClick={() => setSelectedImage(null)}
            >
              <X className="h-4 w-4" />
            </Button>

            {/* Image Info */}
            <div className="absolute bottom-4 left-4 right-4 bg-black/70 text-white p-md rounded-lg">
              <h3 className="font-semibold mb-xs">{selectedImage.name}</h3>
              {selectedImage.description && (
                <p className="text-sm mb-sm">{selectedImage.description}</p>
              )}
              <div className="flex items-center gap-md">
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onView(selectedImage);
                    setSelectedImage(null);
                  }}
                >
                  <Eye className="mr-2 h-4 w-4" />
                  View Details
                </Button>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onDownload(selectedImage);
                  }}
                >
                  <Download className="mr-2 h-4 w-4" />
                  Download
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

"use client";

import { Card, Badge, Button, Checkbox } from "@ghxstship/ui";
import { formatDistanceToNow } from "date-fns";
import type { ProjectFile } from "../FilesClient";
import type { LucideIcon } from "lucide-react";

interface FileGridViewProps {
  files: ProjectFile[];
  selectedFiles: Set<string>;
  onSelectFile: (id: string) => void;
  onView: (file: ProjectFile) => void;
  onEdit: (file: ProjectFile) => void;
  onDownload: (file: ProjectFile) => void;
  onDelete: (file: ProjectFile) => void;
  onShare: (file: ProjectFile) => void;
  formatFileSize: (bytes: number) => string;
  getCategoryIcon: (category: string) => LucideIcon;
  getAccessIcon: (level: string) => LucideIcon;
}

export default function FileGridView({
  files,
  selectedFiles,
  onSelectFile,
  onView,
  onEdit,
  onDownload,
  onDelete,
  onShare,
  formatFileSize,
  getCategoryIcon,
  getAccessIcon,
}: FileGridViewProps) {
  const getCategoryColor = (category: string) => {
    switch (category) {
      case "document":
        return "bg-blue-100 text-blue-800";
      case "image":
        return "bg-green-100 text-green-800";
      case "video":
        return "bg-purple-100 text-purple-800";
      case "audio":
        return "bg-yellow-100 text-yellow-800";
      case "drawing":
        return "bg-indigo-100 text-indigo-800";
      case "specification":
        return "bg-pink-100 text-pink-800";
      case "report":
        return "bg-orange-100 text-orange-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
      {files.map((file) => {
        const CategoryIcon = getCategoryIcon(file.category);
        const AccessIcon = getAccessIcon(file.access_level);

        return (
          <Card
            key={file.id}
            className={`p-md cursor-pointer hover:shadow-lg transition-shadow ${
              selectedFiles.has(file.id) ? "ring-2 ring-primary" : ""
            }`}
            onClick={() => onView(file)}
          >
            <div className="flex items-start justify-between mb-sm">
              <Checkbox
                checked={selectedFiles.has(file.id)}
                onChange={() => onSelectFile(file.id)}
                onClick={(e: React.MouseEvent) => e.stopPropagation()}
              />
              <div className="flex items-center gap-xs">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onDownload(file);
                  }}
                >
                  <Download className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onShare(file);
                  }}
                >
                  <Share2 className="h-4 w-4" />
                </Button>
              </div>
            </div>

            <div className="space-y-sm">
              {/* File Icon and Category */}
              <div className="flex items-center justify-center h-24 bg-muted rounded-lg">
                <CategoryIcon className="h-12 w-12 text-muted-foreground" />
              </div>

              {/* File Name */}
              <h3 className="font-semibold truncate" title={file.name}>
                {file.name}
              </h3>

              {/* Badges */}
              <div className="flex items-center gap-xs flex-wrap">
                <Badge className={getCategoryColor(file.category)}>
                  {file.category}
                </Badge>
                {file.version !== "1.0" && (
                  <Badge variant="secondary">v{file.version}</Badge>
                )}
                {file.is_latest && (
                  <Badge variant="success">Latest</Badge>
                )}
              </div>

              {/* File Info */}
              <div className="space-y-xs text-sm text-muted-foreground">
                <div className="flex items-center justify-between">
                  <span>Size:</span>
                  <span>{formatFileSize(file.file_size)}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span>Access:</span>
                  <div className="flex items-center gap-xs">
                    <AccessIcon className="h-3 w-3" />
                    <span className="capitalize">{file.access_level}</span>
                  </div>
                </div>
                {file.project && (
                  <div className="truncate" title={file.project.name}>
                    Project: {file.project.name}
                  </div>
                )}
              </div>

              {/* Footer */}
              <div className="pt-sm border-t text-xs text-muted-foreground">
                <div className="flex items-center justify-between">
                  <span>
                    {formatDistanceToNow(new Date(file.created_at), { addSuffix: true })}
                  </span>
                  {file.download_count > 0 && (
                    <span>{file.download_count} downloads</span>
                  )}
                </div>
                {file.uploaded_by_user && (
                  <div className="mt-xs truncate">
                    by {file.uploaded_by_user.full_name || file.uploaded_by_user.email}
                  </div>
                )}
              </div>

              {/* Tags */}
              {file.tags && file.tags.length > 0 && (
                <div className="flex flex-wrap gap-xs">
                  {file.tags.slice(0, 3).map((tag, index) => (
                    <Badge key={index} variant="outline" className="text-xs">
                      {tag}
                    </Badge>
                  ))}
                  {file.tags.length > 3 && (
                    <Badge variant="outline" className="text-xs">
                      +{file.tags.length - 3}
                    </Badge>
                  )}
                </div>
              )}
            </div>
          </Card>
        );
      })}
    </div>
  );
}

"use client";

import { Badge, Button, Checkbox } from "@ghxstship/ui";
import { format, parseISO } from "date-fns";
import type { ProjectFile } from "../FilesClient";

interface FileListViewProps {
  files: ProjectFile[];
  selectedFiles: Set<string>;
  fieldVisibility: Array<{ id: string; label: string; visible: boolean; sortable: boolean }>;
  sortField: string;
  sortDirection: "asc" | "desc";
  onSelectAll: () => void;
  onSelectFile: (id: string) => void;
  onSort: (field: string) => void;
  onView: (file: ProjectFile) => void;
  onEdit: (file: ProjectFile) => void;
  onDownload: (file: ProjectFile) => void;
  onDelete: (file: ProjectFile) => void;
  onShare: (file: ProjectFile) => void;
  formatFileSize: (bytes: number) => string;
}

export default function FileListView({
  files,
  selectedFiles,
  fieldVisibility,
  sortField,
  sortDirection,
  onSelectAll,
  onSelectFile,
  onSort,
  onView,
  onEdit,
  onDownload,
  onDelete,
  onShare,
  formatFileSize,
}: FileListViewProps) {
  const visibleFields = fieldVisibility.filter((f) => f.visible);

  const getCategoryBadgeVariant = (category: string) => {
    switch (category) {
      case "document":
        return "info";
      case "image":
        return "success";
      case "video":
        return "warning";
      case "audio":
        return "secondary";
      default:
        return "default";
    }
  };

  const getAccessBadgeVariant = (level: string) => {
    switch (level) {
      case "public":
        return "success";
      case "team":
        return "info";
      case "restricted":
        return "destructive";
      default:
        return "secondary";
    }
  };

  const renderFieldValue = (file: ProjectFile, fieldId: string) => {
    switch (fieldId) {
      case "name":
        return (
          <div className="font-medium">
            {file.name}
            {file.is_latest && (
              <Badge variant="success" className="ml-sm scale-90">
                Latest
              </Badge>
            )}
          </div>
        );
      case "category":
        return (
          <Badge variant={getCategoryBadgeVariant(file.category)}>
            {file.category}
          </Badge>
        );
      case "file_type":
        return <span className="text-sm">{file.file_type}</span>;
      case "file_size":
        return <span className="text-sm">{formatFileSize(file.file_size)}</span>;
      case "version":
        return <Badge variant="secondary">v{file.version}</Badge>;
      case "uploaded_by":
        return (
          <span className="text-sm">
            {file.uploaded_by_user?.full_name || file.uploaded_by_user?.email || "Unknown"}
          </span>
        );
      case "access_level":
        return (
          <Badge variant={getAccessBadgeVariant(file.access_level)}>
            {file.access_level}
          </Badge>
        );
      case "tags":
        return (
          <div className="flex flex-wrap gap-xs">
            {file.tags?.slice(0, 2).map((tag, index) => (
              <Badge key={index} variant="outline" className="text-xs">
                {tag}
              </Badge>
            ))}
            {file.tags?.length > 2 && (
              <Badge variant="outline" className="text-xs">
                +{file.tags.length - 2}
              </Badge>
            )}
          </div>
        );
      case "download_count":
        return <span className="text-sm">{file.download_count}</span>;
      case "created_at":
        return <span className="text-sm">{format(parseISO(file.created_at), "MMM d, yyyy")}</span>;
      case "updated_at":
        return <span className="text-sm">{format(parseISO(file.updated_at), "MMM d, yyyy")}</span>;
      default:
        return "-";
    }
  };

  return (
    <div className="overflow-x-auto">
      <table className="w-full">
        <thead>
          <tr className="border-b">
            <th className="text-left p-sm">
              <Checkbox
                checked={selectedFiles.size === files.length && files.length > 0}
                onChange={onSelectAll}
              />
            </th>
            {visibleFields.map((field) => (
              <th
                key={field.id}
                className={`text-left p-sm ${
                  field.sortable ? "cursor-pointer hover:bg-muted/50" : ""
                }`}
                onClick={() => field.sortable && onSort(field.id)}
              >
                <div className="flex items-center gap-xs">
                  {field.label}
                  {field.sortable && (
                    <>
                      {sortField === field.id ? (
                        sortDirection === "asc" ? (
                          <ArrowUp className="h-3 w-3" />
                        ) : (
                          <ArrowDown className="h-3 w-3" />
                        )
                      ) : (
                        <ArrowUpDown className="h-3 w-3 opacity-30" />
                      )}
                    </>
                  )}
                </div>
              </th>
            ))}
            <th className="text-left p-sm">Actions</th>
          </tr>
        </thead>
        <tbody>
          {files.map((file) => (
            <tr
              key={file.id}
              className={`border-b hover:bg-muted/50 cursor-pointer ${
                selectedFiles.has(file.id) ? "bg-primary/5" : ""
              }`}
              onClick={() => onView(file)}
            >
              <td className="p-sm" onClick={(e) => e.stopPropagation()}>
                <Checkbox
                  checked={selectedFiles.has(file.id)}
                  onChange={() => onSelectFile(file.id)}
                />
              </td>
              {visibleFields.map((field) => (
                <td key={field.id} className="p-sm">
                  {renderFieldValue(file, field.id)}
                </td>
              ))}
              <td className="p-sm" onClick={(e) => e.stopPropagation()}>
                <div className="flex items-center gap-xs">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDownload(file)}
                  >
                    <Download className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onShare(file)}
                  >
                    <Share2 className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(file)}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(file)}
                    className="text-destructive"
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {files.length === 0 && (
        <div className="text-center py-xl text-muted-foreground">
          No files to display
        </div>
      )}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { Card, Badge, Button } from '@ghxstship/ui';
import type { Resource } from '../types';

const resourceTypeIcons = {
  policy: FileText,
  guide: BookOpen,
  training: GraduationCap,
  template: File,
  procedure: Clipboard,
  featured: Star,
};

const statusColors = {
  draft: 'bg-secondary/50 color-muted',
  under_review: 'bg-accent/10 color-accent',
  published: 'bg-success/10 color-success',
  archived: 'bg-warning/10 color-warning',
};

interface ResourceGridViewProps {
  resources: Resource[];
  onResourceClick: (resource: Resource) => void;
  onResourceEdit: (resource: Resource) => void;
  onResourceDownload: (resource: Resource) => void;
  selectedResources: string[];
  onSelectionChange: (resourceIds: string[]) => void;
}

export default function ResourceGridView({
  resources,
  onResourceClick,
  onResourceEdit,
  onResourceDownload,
  selectedResources,
  onSelectionChange
}: ResourceGridViewProps) {
  const handleResourceSelect = (resourceId: string, selected: boolean) => {
    if (selected) {
      onSelectionChange([...selectedResources, resourceId]);
    } else {
      onSelectionChange(selectedResources.filter(id => id !== resourceId));
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
      {resources.map(resource => {
        const IconComponent = resourceTypeIcons[resource.type];
        const isSelected = selectedResources.includes(resource.id);

        return (
          <Card 
            key={resource.id}
            className={`cursor-pointer hover:shadow-elevated transition-shadow ${
              isSelected ? 'ring-2 ring-primary' : ''
            }`}
            onClick={() => onResourceClick(resource)}
          >
            <div className="flex items-start justify-between mb-sm">
              <div className="flex items-center gap-sm">
                <input
                  type="checkbox"
                  checked={isSelected}
