import { Calculator, Plus, Edit, Trash2, RefreshCw, AlertTriangle, CheckCircle, Clock, DollarSign, TrendingUp, TrendingDown, Building, FileText, Hash, Search, Filter } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Card, 
  Button, 
  Badge, 
  Skeleton, 
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView,
  DataActions,
  UniversalDrawer,
  type DataRecord,
  type FieldConfig,
  type DataViewConfig
} from '@ghxstship/ui';

interface AccountsClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface GLAccount {
  id: string;
  account_number: string;
  name: string;
  description?: string;
  type: 'asset' | 'liability' | 'equity' | 'revenue' | 'expense';
  subtype?: string;
  parent_account_id?: string;
  is_active: boolean;
  currency: string;
  balance: number;
  normal_balance: 'debit' | 'credit';
  notes?: string;
  created_by?: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
}

export default function AccountsClient({ user, orgId, translations }: AccountsClientProps) {
  const [loading, setLoading] = useState(true);
  const [accounts, setAccounts] = useState<GLAccount[]>([]);
  const [selectedAccount, setSelectedAccount] = useState<GLAccount | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');
  const [filterType, setFilterType] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  const supabase = createBrowserClient();

  useEffect(() => {
    loadAccounts();
  }, [orgId]);

  const loadAccounts = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('accounts')
        .select('*')
        .eq('organization_id', orgId)
        .order('account_number', { ascending: true });

      if (error) throw error;
      setAccounts(data || []);
    } catch (error) {
      console.error('Error loading accounts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateAccount = () => {
    setSelectedAccount(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditAccount = (account: GLAccount) => {
    setSelectedAccount(account);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewAccount = (account: GLAccount) => {
    setSelectedAccount(account);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleDeleteAccount = async (accountId: string) => {
    if (!confirm('Are you sure you want to delete this account? This action cannot be undone.')) return;

    try {
      const response = await fetch(`/api/v1/finance/accounts`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: accountId })
      });

      if (!response.ok) throw new Error('Failed to delete account');
      await loadAccounts();
    } catch (error) {
      console.error('Error deleting account:', error);
    }
  };

  const handleSaveAccount = async (accountData: Partial<GLAccount>) => {
    try {
      if (drawerMode === 'create') {
        const response = await fetch('/api/v1/finance/accounts', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            accountNumber: accountData.account_number,
            name: accountData.name,
            description: accountData.description,
            type: accountData.type,
            subtype: accountData.subtype,
            parentAccountId: accountData.parent_account_id,
            isActive: accountData.is_active,
            currency: accountData.currency,
            balance: accountData.balance,
            notes: accountData.notes
          })
        });

        if (!response.ok) throw new Error('Failed to create account');
      } else if (drawerMode === 'edit' && selectedAccount) {
        const response = await fetch('/api/v1/finance/accounts', {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: selectedAccount.id,
            accountNumber: accountData.account_number,
            name: accountData.name,
            description: accountData.description,
            type: accountData.type,
            subtype: accountData.subtype,
            parentAccountId: accountData.parent_account_id,
            isActive: accountData.is_active,
            currency: accountData.currency,
            balance: accountData.balance,
            notes: accountData.notes
          })
        });

        if (!response.ok) throw new Error('Failed to update account');
      }

      setDrawerOpen(false);
      await loadAccounts();
    } catch (error) {
      console.error('Error saving account:', error);
    }
  };

  const filteredAccounts = accounts.filter(account => {
    const matchesSearch = searchTerm === '' || 
      account.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      account.account_number.includes(searchTerm);
    const matchesType = filterType === 'all' || account.type === filterType;
    return matchesSearch && matchesType;
  });

  const getAccountIcon = (account: GLAccount) => {
    switch (account.type) {
      case 'asset':
        return <Building className="h-5 w-5" />;
      case 'liability':
        return <AlertTriangle className="h-5 w-5" />;
      case 'equity':
        return <CheckCircle className="h-5 w-5" />;
      case 'revenue':
        return <TrendingUp className="h-5 w-5" />;
      case 'expense':
        return <TrendingDown className="h-5 w-5" />;
      default:
        return <Calculator className="h-5 w-5" />;
    }
  };

  const getAccountTypeColor = (account: GLAccount) => {
    switch (account.type) {
      case 'asset':
        return 'bg-blue-100 text-blue-800';
      case 'liability':
        return 'bg-red-100 text-red-800';
      case 'equity':
        return 'bg-purple-100 text-purple-800';
      case 'revenue':
        return 'bg-green-100 text-green-800';
      case 'expense':
        return 'bg-orange-100 text-orange-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  // ATLVS DataViews field configuration
  const fields: FieldConfig[] = [
    {
      key: 'account_number',
      label: 'Account Number',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'name',
      label: 'Account Name',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'type',
      label: 'Type',
      type: 'select',
      options: [
        { value: 'asset', label: 'Asset' },
        { value: 'liability', label: 'Liability' },
        { value: 'equity', label: 'Equity' },
        { value: 'revenue', label: 'Revenue' },
        { value: 'expense', label: 'Expense' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'subtype',
      label: 'Subtype',
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'balance',
      label: 'Balance',
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'normal_balance',
      label: 'Normal Balance',
      type: 'select',
      options: [
        { value: 'debit', label: 'Debit' },
        { value: 'credit', label: 'Credit' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'is_active',
      label: 'Active',
      type: 'boolean',
      filterable: true,
      sortable: true
    },
    {
      key: 'description',
      label: 'Description',
      type: 'textarea',
      rows: 2,
      filterable: true
    }
  ];

  // ATLVS DataViews configuration
  const accountsConfig: DataViewConfig = {
    id: 'gl-accounts-dataviews',
    name: 'Chart of Accounts',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data: filteredAccounts.map(account => ({
      ...account,
      record_type: 'gl_account'
    })),
    pagination: {
      page: 1,
      pageSize: 25,
      total: filteredAccounts.length
    },
    onSearch: (query: string) => {
      setSearchTerm(query);
    },
    onFilter: (filters: unknown) => {
      if (filters.type) setFilterType(filters.type);
    },
    onSort: (sorts: unknown) => {
      console.log('Sort:', sorts);
    },
    onRefresh: () => {
      loadAccounts();
    },
    onExport: (data, format) => {
      console.log('Export:', format, data);
    },
    onImport: (data: unknown) => {
      console.log('Import:', data);
    },
    onRowAction: (action: string, record: DataRecord) => {
      const account = record as GLAccount;
      switch (action) {
        case 'view':
          handleViewAccount(account);
          break;
        case 'edit':
          handleEditAccount(account);
          break;
        case 'delete':
          handleDeleteAccount(account.id);
          break;
      }
    }
  };

  const configWithData = {
    ...accountsConfig,
    data: filteredAccounts.map(account => ({
      ...account,
      record_type: 'gl_account'
    }))
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <div>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-96 mt-2" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <Skeleton key={i} className="h-32" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Chart of Accounts</h1>
          <p className="text-gray-600">Manage your general ledger accounts</p>
        </div>
        <Button onClick={handleCreateAccount} className="flex items-center gap-2">
          <Plus className="h-4 w-4" />
          New Account
        </Button>
      </div>

      {/* Filters */}
      <div className="flex gap-4 mb-6">
        <div className="flex-1">
          <UnifiedInput
            placeholder="Search accounts..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            leftIcon={<Search className="h-4 w-4" />}
          />
        </div>
        <Select value={filterType} onValueChange={setFilterType}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by type" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="asset">Assets</SelectItem>
            <SelectItem value="liability">Liabilities</SelectItem>
            <SelectItem value="equity">Equity</SelectItem>
            <SelectItem value="revenue">Revenue</SelectItem>
            <SelectItem value="expense">Expenses</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* ATLVS DataViews */}
      <div className="flex-1">
        <DataViewProvider config={configWithData}>
          <StateManagerProvider>
            <div className="flex-1 space-y-4">
              {/* View Switcher and Actions */}
              <div className="flex justify-between items-center">
                <ViewSwitcher />
                <DataActions />
              </div>

              {/* Data Views */}
              <div className="flex-1">
                <DataGrid />
                <KanbanBoard 
                  columns={[
                    { id: 'asset', title: 'Assets' },
                    { id: 'liability', title: 'Liabilities' },
                    { id: 'equity', title: 'Equity' },
                    { id: 'revenue', title: 'Revenue' },
                    { id: 'expense', title: 'Expenses' }
                  ]}
                  statusField="type"
                  titleField="name"
                />
                <CalendarView 
                  startDateField="created_at"
                  titleField="name"
                />
                <ListView titleField="name" />
              </div>
            </div>
          </StateManagerProvider>
        </DataViewProvider>
      </div>

      {/* Universal Drawer for CRUD operations */}
      <UniversalDrawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={
          drawerMode === 'create' ? 'Create Account' :
          drawerMode === 'edit' ? 'Edit Account' : 'Account Details'
        }
        mode={drawerMode}
        data={selectedAccount}
        fields={fields}
        onSave={handleSaveAccount}
        tabs={[
          {
            key: 'details',
            label: 'Account Details',
            content: (
              <div className="space-y-4">
                {selectedAccount && (
                  <>
                    <div className="flex items-center gap-3">
                      {getAccountIcon(selectedAccount)}
                      <div>
                        <h3 className="font-semibold">{selectedAccount.name}</h3>
                        <p className="text-sm text-gray-600">#{selectedAccount.account_number}</p>
                      </div>
                      <Badge className={getAccountTypeColor(selectedAccount)}>
                        {selectedAccount.type}
                      </Badge>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium text-gray-700">Balance</label>
                        <p className="text-lg font-semibold">{formatCurrency(selectedAccount.balance)}</p>
