                        <p className="capitalize">{selectedAccount.normal_balance}</p>
                      </div>
                    </div>
                    {selectedAccount.description && (
                      <div>
                        <label className="text-sm font-medium text-gray-700">Description</label>
                        <p className="text-sm text-gray-600">{selectedAccount.description}</p>
                      </div>
                    )}
                  </>
                )}
              </div>
            )
          }
        ]}
      />
    </div>
  );
}

'use client';

import { Plus, Calculator, Building, AlertTriangle, CheckCircle, TrendingUp, TrendingDown } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Button,
  Card,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
  UnifiedInput,
  Checkbox,
} from '@ghxstship/ui';
import AppDrawer, { type DrawerTab } from '@/app/_components/shared/AppDrawer';

interface CreateAccountClientProps {
  user: User;
  orgId: string;
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: (account: CreatedAccount) => void;
}

type AccountType = 'asset' | 'liability' | 'equity' | 'revenue' | 'expense';
type AccountCurrency = 'USD' | 'EUR' | 'GBP' | 'CAD' | 'AUD';

interface AccountFormData {
  accountNumber: string;
  name: string;
  description: string;
  type: AccountType;
  subtype: string;
  parentAccountId: string;
  isActive: boolean;
  currency: AccountCurrency;
  balance: number;
  notes: string;
}

interface CreatedAccount {
  id: string;
  organization_id: string;
  created_at: string;
}

const ACCOUNT_TYPES: ReadonlyArray<{ value: AccountType; label: string; icon: React.ReactNode }> = [
  { value: 'asset', label: 'Asset', icon: <Building className="h-4 w-4" /> },
  { value: 'liability', label: 'Liability', icon: <AlertTriangle className="h-4 w-4" /> },
  { value: 'equity', label: 'Equity', icon: <CheckCircle className="h-4 w-4" /> },
  { value: 'revenue', label: 'Revenue', icon: <TrendingUp className="h-4 w-4" /> },
  { value: 'expense', label: 'Expense', icon: <TrendingDown className="h-4 w-4" /> },
];

const ASSET_SUBTYPES = [
  'current', 'fixed', 'intangible', 'contra', 'investment'
];

const LIABILITY_SUBTYPES = [
  'current', 'long-term', 'contingent'
];

const EQUITY_SUBTYPES = [
  'capital', 'retained', 'distribution', 'other'
];

const REVENUE_SUBTYPES = [
  'operating', 'non-operating', 'other'
];

const EXPENSE_SUBTYPES = [
  'cogs', 'operating', 'non-operating', 'other'
];

const CURRENCIES: ReadonlyArray<{ value: AccountCurrency; label: string }> = [
  { value: 'USD', label: 'US Dollar (USD)' },
  { value: 'EUR', label: 'Euro (EUR)' },
  { value: 'GBP', label: 'British Pound (GBP)' },
  { value: 'CAD', label: 'Canadian Dollar (CAD)' },
  { value: 'AUD', label: 'Australian Dollar (AUD)' },
];

const initialFormData: AccountFormData = {
  accountNumber: '',
  name: '',
  description: '',
  type: 'asset',
  subtype: '',
  parentAccountId: '',
  isActive: true,
  currency: 'USD',
  balance: 0,
  notes: '',
};

export default function CreateAccountClient({
  user,
  orgId,
  isOpen,
  onClose,
  onSuccess,
}: CreateAccountClientProps) {
  const [formData, setFormData] = useState<AccountFormData>(initialFormData);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState<Partial<Record<keyof AccountFormData, string>>>({});
  const [parentAccounts, setParentAccounts] = useState<Array<{ id: string; name: string; account_number: string }>>([]);

  const supabase = createBrowserClient();

  useEffect(() => {
    if (isOpen) {
      loadParentAccounts();
    }
  }, [isOpen, formData.type]);

  const loadParentAccounts = async () => {
    try {
      const { data, error } = await supabase
        .from('accounts')
        .select('id, name, account_number')
        .eq('organization_id', orgId)
        .eq('type', formData.type)
        .eq('is_active', true)
        .is('parent_account_id', null)
        .order('account_number');

      if (error) throw error;
      setParentAccounts(data || []);
    } catch (error) {
      console.error('Error loading parent accounts:', error);
    }
  };

  const getSubtypeOptions = useMemo(() => {
    switch (formData.type) {
      case 'asset':
        return ASSET_SUBTYPES;
      case 'liability':
        return LIABILITY_SUBTYPES;
      case 'equity':
        return EQUITY_SUBTYPES;
      case 'revenue':
        return REVENUE_SUBTYPES;
      case 'expense':
        return EXPENSE_SUBTYPES;
      default:
        return [];
    }
  }, [formData.type]);

  const handleInputChange = useCallback((field: keyof AccountFormData, value: unknown) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
  }, [errors]);

  const validateForm = useCallback((): boolean => {
    const newErrors: Partial<Record<keyof AccountFormData, string>> = {};

    if (!formData.accountNumber.trim()) {
      newErrors.accountNumber = 'Account number is required';
    }

    if (!formData.name.trim()) {
      newErrors.name = 'Account name is required';
    }

    if (!formData.type) {
      newErrors.type = 'Account type is required';
    }

    if (formData.balance < 0) {
      newErrors.balance = 'Balance cannot be negative';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [formData]);

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    try {
      const response = await fetch('/api/v1/finance/accounts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          accountNumber: formData.accountNumber,
          name: formData.name,
          description: formData.description || undefined,
          type: formData.type,
          subtype: formData.subtype || undefined,
          parentAccountId: formData.parentAccountId || undefined,
          isActive: formData.isActive,
          currency: formData.currency,
          balance: formData.balance,
          notes: formData.notes || undefined,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to create account');
      }

      const { account } = await response.json();

      setFormData(initialFormData);
      setErrors({});
      onSuccess?.(account);
      onClose();
    } catch (error) {
      console.error('Error creating account:', error);
      setErrors({ name: error instanceof Error ? error.message : 'Failed to create account' });
    } finally {
      setLoading(false);
    }
  };

  const handleClose = useCallback(() => {
    setFormData(initialFormData);
    setErrors({});
    onClose();
  }, [onClose]);

  const tabs: DrawerTab[] = [
    {
      key: 'basic',
      label: 'Basic Information',
      content: (
        <div className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Account Number *
              </label>
              <UnifiedInput
                value={formData.accountNumber}
                onChange={(e) => handleInputChange('accountNumber', e.target.value)}
                placeholder="e.g., 1000, 2000, 4000"
                error={errors.accountNumber}
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Account Type *
              </label>
              <Select
                value={formData.type}
                onValueChange={(value: AccountType) => {
                  handleInputChange('type', value);
                  handleInputChange('subtype', '');
                  handleInputChange('parentAccountId', '');
                }}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {ACCOUNT_TYPES.map((type) => (
                    <SelectItem key={type.value} value={type.value}>
                      <div className="flex items-center gap-2">
                        {type.icon}
                        {type.label}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.type && <p className="text-sm text-red-600 mt-1">{errors.type}</p>}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Account Name *
            </label>
            <UnifiedInput
              value={formData.name}
              onChange={(e) => handleInputChange('name', e.target.value)}
              placeholder="Enter account name"
              error={errors.name}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Description
            </label>
            <Textarea
              value={formData.description}
              onChange={(e) => handleInputChange('description', e.target.value)}
              placeholder="Optional description of the account"
              rows={3}
            />
          </div>
        </div>
      ),
    },
    {
      key: 'details',
      label: 'Account Details',
      content: (
        <div className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Subtype
              </label>
              <Select
                value={formData.subtype}
                onValueChange={(value) => handleInputChange('subtype', value)}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select subtype" />
                </SelectTrigger>
                <SelectContent>
                  {getSubtypeOptions.map((subtype) => (
                    <SelectItem key={subtype} value={subtype}>
                      {subtype.charAt(0).toUpperCase() + subtype.slice(1)}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Parent Account
              </label>
              <Select
                value={formData.parentAccountId}
                onValueChange={(value) => handleInputChange('parentAccountId', value)}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select parent account" />
                </SelectTrigger>
                <SelectContent>
                  {parentAccounts.map((account) => (
                    <SelectItem key={account.id} value={account.id}>
                      {account.account_number} - {account.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Currency
              </label>
              <Select
                value={formData.currency}
                onValueChange={(value: AccountCurrency) => handleInputChange('currency', value)}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {CURRENCIES.map((currency) => (
                    <SelectItem key={currency.value} value={currency.value}>
                      {currency.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Opening Balance
              </label>
              <UnifiedInput
                type="number"
                value={formData.balance.toString()}
                onChange={(e) => handleInputChange('balance', parseFloat(e.target.value) || 0)}
                placeholder="0.00"
                step="0.01"
                error={errors.balance}
              />
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <Checkbox
              id="isActive"
              checked={formData.isActive}
              onCheckedChange={(checked) => handleInputChange('isActive', checked)}
            />
            <label htmlFor="isActive" className="text-sm font-medium text-gray-700">
              Account is active
            </label>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Notes
            </label>
            <Textarea
              value={formData.notes}
              onChange={(e) => handleInputChange('notes', e.target.value)}
              placeholder="Additional notes or comments"
              rows={3}
            />
          </div>
        </div>
      ),
    },
  ];

  return (
    <AppDrawer
      title="Create New Account"
      open={isOpen}
      onClose={handleClose}
      tabs={tabs}
      actions={
        <div className="flex gap-2">
          <Button variant="outline" onClick={handleClose} disabled={loading}>
            Cancel
          </Button>
          <Button onClick={handleSubmit} disabled={loading}>
            {loading ? 'Creating...' : 'Create Account'}
          </Button>
        </div>
      }
    />
  );
}

import { Building, CreditCard, TrendingUp, DollarSign, Minus, Save, X } from "lucide-react";
/**
 * Create Account Drawer Component
 * Specialized drawer for creating/editing GL Accounts
 */

'use client';

import React, { useState, useEffect } from 'react';
import { 
  Drawer,
  DrawerContent,
  DrawerHeader,
  DrawerTitle,
  DrawerDescription,
  DrawerFooter,
  Button,
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
  Switch,
  Badge,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger
} from '@ghxstship/ui';
import type { GLAccount, AccountType, AccountFormData } from '../types';
import { accountsService } from '../lib/accountsService';

interface CreateAccountDrawerProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
  editAccount?: GLAccount | null;
  orgId: string;
}

export default function CreateAccountDrawer({
  isOpen,
  onClose,
  onSuccess,
  editAccount,
  orgId
}: CreateAccountDrawerProps) {
  const [loading, setLoading] = useState(false);
  const [parentAccounts, setParentAccounts] = useState<GLAccount[]>([]);
  const [formData, setFormData] = useState<AccountFormData>({
    account_number: '',
    name: '',
    description: '',
    type: 'asset',
    subtype: '',
    parent_account_id: '',
    is_active: true,
    currency: 'USD',
    opening_balance: 0,
    notes: ''
  });

  const [errors, setErrors] = useState<Record<string, string>>({});

  useEffect(() => {
    if (editAccount) {
      setFormData({
        account_number: editAccount.account_number,
        name: editAccount.name,
        description: editAccount.description || '',
        type: editAccount.type,
        subtype: editAccount.subtype || '',
        parent_account_id: editAccount.parent_account_id || '',
        is_active: editAccount.is_active,
        currency: editAccount.currency,
        opening_balance: editAccount.balance,
        notes: ''
      });
    } else {
      // Reset form for new account
      setFormData({
        account_number: '',
        name: '',
        description: '',
        type: 'asset',
        subtype: '',
        parent_account_id: '',
        is_active: true,
        currency: 'USD',
        opening_balance: 0,
        notes: ''
      });
    }
    setErrors({});
  }, [editAccount, isOpen]);

  useEffect(() => {
    if (formData.type && isOpen) {
      loadParentAccounts();
    }
  }, [formData.type, isOpen]);

  const loadParentAccounts = async () => {
    try {
      const accounts = await accountsService.getParentAccounts(orgId, formData.type);
      setParentAccounts(accounts);
    } catch (error) {
      console.error('Error loading parent accounts:', error);
    }
  };

  const getAccountTypeIcon = (type: AccountType) => {
    const icons = {
      asset: <Building className="h-4 w-4" />,
      liability: <CreditCard className="h-4 w-4" />,
      equity: <TrendingUp className="h-4 w-4" />,
      revenue: <DollarSign className="h-4 w-4" />,
      expense: <Minus className="h-4 w-4" />
    };
    return icons[type];
  };

  const getSubtypeOptions = (type: AccountType) => {
    const subtypes = {
      asset: [
        { value: 'current_assets', label: 'Current Assets' },
        { value: 'fixed_assets', label: 'Fixed Assets' },
        { value: 'intangible_assets', label: 'Intangible Assets' },
        { value: 'investments', label: 'Investments' },
        { value: 'other_assets', label: 'Other Assets' }
      ],
      liability: [
        { value: 'current_liabilities', label: 'Current Liabilities' },
        { value: 'long_term_liabilities', label: 'Long-term Liabilities' },
        { value: 'other_liabilities', label: 'Other Liabilities' }
      ],
      equity: [
        { value: 'owners_equity', label: 'Owner\'s Equity' },
        { value: 'retained_earnings', label: 'Retained Earnings' },
        { value: 'capital_stock', label: 'Capital Stock' },
        { value: 'other_equity', label: 'Other Equity' }
      ],
      revenue: [
        { value: 'operating_revenue', label: 'Operating Revenue' },
        { value: 'non_operating_revenue', label: 'Non-operating Revenue' },
        { value: 'other_income', label: 'Other Income' }
      ],
      expense: [
        { value: 'operating_expenses', label: 'Operating Expenses' },
        { value: 'cost_of_goods_sold', label: 'Cost of Goods Sold' },
        { value: 'administrative_expenses', label: 'Administrative Expenses' },
        { value: 'other_expenses', label: 'Other Expenses' }
      ]
    };
    return subtypes[type] || [];
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.account_number.trim()) {
      newErrors.account_number = 'Account number is required';
    }

    if (!formData.name.trim()) {
      newErrors.name = 'Account name is required';
    }

    if (!formData.type) {
      newErrors.type = 'Account type is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    if (!validateForm()) return;

    setLoading(true);
    try {
      const accountData = {
        accountNumber: formData.account_number,
        name: formData.name,
        description: formData.description,
        type: formData.type,
        subtype: formData.subtype,
        parentAccountId: formData.parent_account_id || undefined,
        isActive: formData.is_active,
        currency: formData.currency,
        openingBalance: formData.opening_balance || 0
      };

      if (editAccount) {
        await accountsService.updateAccount({
          id: editAccount.id,
          ...accountData
        });
      } else {
        await accountsService.createAccount(accountData);
      }

      onSuccess();
      onClose();
    } catch (error) {
      console.error('Error saving account:', error);
      setErrors({ submit: 'Failed to save account. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field: keyof AccountFormData, value: unknown) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const normalBalance = formData.type === 'asset' || formData.type === 'expense' ? 'debit' : 'credit';

  return (
    <Drawer open={isOpen} onOpenChange={onClose}>
      <DrawerContent className="max-w-2xl mx-auto">
        <DrawerHeader>
          <DrawerTitle className="flex items-center gap-2">
            {getAccountTypeIcon(formData.type)}
            {editAccount ? 'Edit Account' : 'Create New Account'}
          </DrawerTitle>
          <DrawerDescription>
            {editAccount 
              ? 'Update the account information below.'
              : 'Create a new GL account for your chart of accounts.'
            }
          </DrawerDescription>
        </DrawerHeader>

        <div className="px-6 py-4">
          <Tabs defaultValue="basic" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="basic">Basic Information</TabsTrigger>
              <TabsTrigger value="details">Account Details</TabsTrigger>
            </TabsList>

            <TabsContent value="basic" className="space-y-4 mt-6">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium text-gray-700 mb-2 block">
                    Account Number *
                  </label>
                  <UnifiedInput
                    value={formData.account_number}
                    onChange={(e) => handleInputChange('account_number', e.target.value)}
                    placeholder="e.g., 1000"
                    error={errors.account_number}
                  />
                </div>

                <div>
                  <label className="text-sm font-medium text-gray-700 mb-2 block">
                    Account Type *
                  </label>
                  <Select 
                    value={formData.type} 
                    onValueChange={(value) => handleInputChange('type', value as AccountType)}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="asset">
                        <div className="flex items-center gap-2">
                          <Building className="h-4 w-4" />
                          Asset
                        </div>
                      </SelectItem>
                      <SelectItem value="liability">
                        <div className="flex items-center gap-2">
                          <CreditCard className="h-4 w-4" />
                          Liability
                        </div>
                      </SelectItem>
                      <SelectItem value="equity">
                        <div className="flex items-center gap-2">
                          <TrendingUp className="h-4 w-4" />
                          Equity
                        </div>
                      </SelectItem>
                      <SelectItem value="revenue">
                        <div className="flex items-center gap-2">
                          <DollarSign className="h-4 w-4" />
                          Revenue
                        </div>
                      </SelectItem>
                      <SelectItem value="expense">
                        <div className="flex items-center gap-2">
                          <Minus className="h-4 w-4" />
                          Expense
                        </div>
                      </SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div>
                <label className="text-sm font-medium text-gray-700 mb-2 block">
                  Account Name *
                </label>
                <UnifiedInput
                  value={formData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  placeholder="e.g., Cash and Cash Equivalents"
                  error={errors.name}
                />
              </div>

              <div>
                <label className="text-sm font-medium text-gray-700 mb-2 block">
                  Description
                </label>
                <Textarea
                  value={formData.description}
                  onChange={(e) => handleInputChange('description', e.target.value)}
                  placeholder="Optional description of the account..."
                  rows={3}
                />
              </div>

              <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div>
                  <div className="font-medium text-gray-900">Normal Balance</div>
                  <div className="text-sm text-gray-600">
                    This account type has a normal {normalBalance} balance
                  </div>
                </div>
                <Badge className={
                  normalBalance === 'debit' 
                    ? 'bg-blue-100 text-blue-800' 
                    : 'bg-green-100 text-green-800'
                }>
                  {normalBalance}
                </Badge>
              </div>
            </TabsContent>

            <TabsContent value="details" className="space-y-4 mt-6">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium text-gray-700 mb-2 block">
                    Subtype
                  </label>
                  <Select 
                    value={formData.subtype} 
                    onValueChange={(value) => handleInputChange('subtype', value)}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select subtype..." />
                    </SelectTrigger>
                    <SelectContent>
                      {getSubtypeOptions(formData.type).map((option) => (
                        <SelectItem key={option.value} value={option.value}>
                          {option.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium text-gray-700 mb-2 block">
                    Parent Account
                  </label>
                  <Select 
                    value={formData.parent_account_id} 
                    onValueChange={(value) => handleInputChange('parent_account_id', value)}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select parent account..." />
                    </SelectTrigger>
                    <SelectContent>
                      {parentAccounts.map((account) => (
                        <SelectItem key={account.id} value={account.id}>
                          {account.account_number} - {account.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium text-gray-700 mb-2 block">
                    Currency
                  </label>
                  <Select 
                    value={formData.currency} 
                    onValueChange={(value) => handleInputChange('currency', value)}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="USD">USD - US Dollar</SelectItem>
                      <SelectItem value="EUR">EUR - Euro</SelectItem>
                      <SelectItem value="GBP">GBP - British Pound</SelectItem>
                      <SelectItem value="CAD">CAD - Canadian Dollar</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium text-gray-700 mb-2 block">
                    Opening Balance
                  </label>
                  <UnifiedInput
                    type="number"
                    step="0.01"
                    value={formData.opening_balance?.toString() || '0'}
                    onChange={(e) => handleInputChange('opening_balance', parseFloat(e.target.value) || 0)}
                    placeholder="0.00"
                  />
                </div>
              </div>

              <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div>
                  <div className="font-medium text-gray-900">Account Status</div>
                  <div className="text-sm text-gray-600">
                    {formData.is_active ? 'Account is active and available for transactions' : 'Account is inactive'}
                  </div>
                </div>
                <Switch
                  checked={formData.is_active}
                  onCheckedChange={(checked) => handleInputChange('is_active', checked)}
                />
              </div>

              <div>
                <label className="text-sm font-medium text-gray-700 mb-2 block">
                  Notes
                </label>
                <Textarea
                  value={formData.notes}
                  onChange={(e) => handleInputChange('notes', e.target.value)}
                  placeholder="Additional notes about this account..."
                  rows={3}
                />
              </div>
            </TabsContent>
          </Tabs>

          {errors.submit && (
            <div className="mt-4 p-3 bg-red-50 border border-red-200 rounded-md">
              <p className="text-sm text-red-600">{errors.submit}</p>
            </div>
          )}
        </div>

        <DrawerFooter>
          <div className="flex gap-2">
            <Button
              onClick={handleSubmit}
              disabled={loading}
              className="flex items-center gap-2"
            >
              <Save className="h-4 w-4" />
              {loading ? 'Saving...' : editAccount ? 'Update Account' : 'Create Account'}
            </Button>
            <Button
              variant="outline"
              onClick={onClose}
              disabled={loading}
              className="flex items-center gap-2"
            >
              <X className="h-4 w-4" />
              Cancel
            </Button>
          </div>
        </DrawerFooter>
      </DrawerContent>
    </Drawer>
  );
}

'use client';

import { DollarSign, Plus, Edit, Trash2, RefreshCw, AlertTriangle, CheckCircle, Clock, TrendingUp, TrendingDown, Search, Filter, Calendar, Target } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Card, 
  Button, 
  Badge, 
  Skeleton, 
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView,
  DataActions,
  UniversalDrawer,
  type DataRecord,
  type FieldConfig,
  type DataViewConfig
} from '@ghxstship/ui';

interface BudgetsClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface Budget {
  id: string;
  name: string;
  description?: string;
  category: string;
  amount: number;
  spent: number;
  currency: string;
  status: 'draft' | 'active' | 'completed' | 'cancelled';
  period_start: string;
  period_end: string;
  project_id?: string;
  approved_by?: string;
  approved_at?: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
  created_by: string;
  // Computed fields
  remaining?: number;
  utilization?: number;
}

export default function BudgetsClient({ user, orgId, translations }: BudgetsClientProps) {
  const [loading, setLoading] = useState(true);
  const [budgets, setBudgets] = useState<Budget[]>([]);
  const [selectedBudget, setSelectedBudget] = useState<Budget | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [filterCategory, setFilterCategory] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  const supabase = createBrowserClient();

  useEffect(() => {
    loadBudgets();
  }, [orgId]);

  const loadBudgets = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('budgets')
        .select('*')
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Add computed fields
      const enrichedBudgets = (data || []).map(budget => ({
        ...budget,
        remaining: budget.amount - (budget.spent || 0),
        utilization: budget.amount > 0 ? ((budget.spent || 0) / budget.amount) * 100 : 0
      }));

      setBudgets(enrichedBudgets);
    } catch (error) {
      console.error('Error loading budgets:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateBudget = () => {
    setSelectedBudget(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditBudget = (budget: Budget) => {
    setSelectedBudget(budget);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewBudget = (budget: Budget) => {
    setSelectedBudget(budget);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleDeleteBudget = async (budgetId: string) => {
    if (!confirm('Are you sure you want to delete this budget? This action cannot be undone.')) return;

    try {
      const response = await fetch('/api/v1/finance/budgets', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: budgetId })
      });

      if (!response.ok) throw new Error('Failed to delete budget');
      await loadBudgets();
    } catch (error) {
      console.error('Error deleting budget:', error);
    }
  };

  const handleSaveBudget = async (budgetData: Partial<Budget>) => {
    try {
      if (drawerMode === 'create') {
        const response = await fetch('/api/v1/finance/budgets', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: budgetData.name,
            description: budgetData.description,
            amount: budgetData.amount,
            currency: budgetData.currency,
            category: budgetData.category,
            startDate: budgetData.period_start,
            endDate: budgetData.period_end,
            projectId: budgetData.project_id,
            status: budgetData.status
          })
        });

        if (!response.ok) throw new Error('Failed to create budget');
      } else if (drawerMode === 'edit' && selectedBudget) {
        const response = await fetch('/api/v1/finance/budgets', {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: selectedBudget.id,
            name: budgetData.name,
            description: budgetData.description,
            amount: budgetData.amount,
            currency: budgetData.currency,
            category: budgetData.category,
            startDate: budgetData.period_start,
            endDate: budgetData.period_end,
            projectId: budgetData.project_id,
            status: budgetData.status
          })
        });

        if (!response.ok) throw new Error('Failed to update budget');
      }

      setDrawerOpen(false);
      await loadBudgets();
    } catch (error) {
      console.error('Error saving budget:', error);
    }
  };

  const filteredBudgets = budgets.filter(budget => {
    const matchesSearch = searchTerm === '' || 
      budget.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      budget.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = filterStatus === 'all' || budget.status === filterStatus;
    const matchesCategory = filterCategory === 'all' || budget.category === filterCategory;
    return matchesSearch && matchesStatus && matchesCategory;
  });

  const getBudgetStatusIcon = (budget: Budget) => {
    switch (budget.status) {
      case 'active':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'draft':
        return <Clock className="h-5 w-5 text-yellow-500" />;
      case 'completed':
        return <Target className="h-5 w-5 text-blue-500" />;
      case 'cancelled':
        return <AlertTriangle className="h-5 w-5 text-red-500" />;
      default:
        return <Clock className="h-5 w-5 text-gray-500" />;
    }
  };

  const getBudgetStatusColor = (budget: Budget) => {
    switch (budget.status) {
      case 'active':
        return 'bg-green-100 text-green-800';
      case 'draft':
        return 'bg-yellow-100 text-yellow-800';
      case 'completed':
        return 'bg-blue-100 text-blue-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getUtilizationColor = (utilization: number) => {
    if (utilization >= 100) return 'text-red-600';
    if (utilization >= 80) return 'text-yellow-600';
    return 'text-green-600';
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  // ATLVS DataViews field configuration
  const fields: FieldConfig[] = [
    {
      key: 'name',
      label: 'Budget Name',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'description',
      label: 'Description',
      type: 'textarea',
      rows: 3,
      sortable: true,
      filterable: true
    },
    {
      key: 'category',
      label: 'Category',
      type: 'select',
      options: [
        { value: 'equipment', label: 'Equipment' },
        { value: 'construction', label: 'Construction' },
        { value: 'catering', label: 'Catering' },
        { value: 'travel', label: 'Travel' },
        { value: 'marketing', label: 'Marketing' },
        { value: 'operations', label: 'Operations' },
        { value: 'other', label: 'Other' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'amount',
      label: 'Budget Amount',
      type: 'currency',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'spent',
      label: 'Amount Spent',
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'remaining',
      label: 'Remaining',
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'utilization',
      label: 'Utilization %',
      type: 'number',
      sortable: true,
      filterable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'active', label: 'Active' },
        { value: 'completed', label: 'Completed' },
        { value: 'cancelled', label: 'Cancelled' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'period_start',
      label: 'Start Date',
      type: 'date',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'period_end',
      label: 'End Date',
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'currency',
      label: 'Currency',
      type: 'select',
      options: [
        { value: 'USD', label: 'USD' },
        { value: 'EUR', label: 'EUR' },
        { value: 'GBP', label: 'GBP' }
      ],
      defaultValue: 'USD',
      filterable: true
    }
  ];

  // ATLVS DataViews configuration
  const budgetsConfig: DataViewConfig = {
    id: 'budgets-dataviews',
    name: 'Budget Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data: filteredBudgets.map(budget => ({
      ...budget,
      record_type: 'budget'
    })),
    pagination: {
      page: 1,
      pageSize: 25,
      total: filteredBudgets.length
    },
    onSearch: (query: string) => {
      setSearchTerm(query);
    },
    onFilter: (filters: unknown) => {
      if (filters.status) setFilterStatus(filters.status);
      if (filters.category) setFilterCategory(filters.category);
    },
    onSort: (sorts: unknown) => {
      console.log('Sort:', sorts);
    },
    onRefresh: () => {
      loadBudgets();
    },
    onExport: (data, format) => {
      console.log('Export:', format, data);
    },
    onImport: (data: unknown) => {
      console.log('Import:', data);
    },
    onRowAction: (action: string, record: DataRecord) => {
      const budget = record as Budget;
      switch (action) {
        case 'view':
          handleViewBudget(budget);
          break;
        case 'edit':
          handleEditBudget(budget);
          break;
        case 'delete':
          handleDeleteBudget(budget.id);
          break;
      }
    }
  };

  const configWithData = {
    ...budgetsConfig,
    data: filteredBudgets.map(budget => ({
      ...budget,
      record_type: 'budget'
    }))
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <div>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-96 mt-2" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <Skeleton key={i} className="h-32" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Budget Management</h1>
          <p className="text-gray-600">Track and manage your project budgets</p>
        </div>
        <Button onClick={handleCreateBudget} className="flex items-center gap-2">
          <Plus className="h-4 w-4" />
          New Budget
        </Button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Budgets</p>
              <p className="text-2xl font-bold text-gray-900">{budgets.length}</p>
            </div>
            <Target className="h-8 w-8 text-blue-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
