'use client';

import { Plus, DollarSign, Calendar, Target } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Button,
  Card,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
  UnifiedInput,
} from '@ghxstship/ui';
import AppDrawer, { type DrawerTab } from '@/app/_components/shared/AppDrawer';

interface CreateBudgetClientProps {
  user: User;
  orgId: string;
  projectId?: string;
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: (budget: CreatedBudget) => void;
}

type BudgetCurrency = 'USD' | 'EUR' | 'GBP' | 'CAD' | 'AUD';

type BudgetCategory =
  | 'general'
  | 'marketing'
  | 'operations'
  | 'personnel'
  | 'equipment'
  | 'travel'
  | 'professional_services'
  | 'other';

interface BudgetFormData {
  name: string;
  description: string;
  amount: string;
  currency: BudgetCurrency;
  category: BudgetCategory;
  projectId: string;
  startDate: string;
  endDate: string;
}

interface CreatedBudget {
  id: string;
  organization_id: string;
  created_at: string;
}

const SUPPORTED_CURRENCIES: ReadonlyArray<{ value: BudgetCurrency; label: string }> = [
  { value: 'USD', label: 'USD - US Dollar' },
  { value: 'EUR', label: 'EUR - Euro' },
  { value: 'GBP', label: 'GBP - British Pound' },
  { value: 'CAD', label: 'CAD - Canadian Dollar' },
  { value: 'AUD', label: 'AUD - Australian Dollar' },
];

const BUDGET_CATEGORIES: ReadonlyArray<{ value: BudgetCategory; label: string }> = [
  { value: 'general', label: 'General' },
  { value: 'marketing', label: 'Marketing' },
  { value: 'operations', label: 'Operations' },
  { value: 'personnel', label: 'Personnel' },
  { value: 'equipment', label: 'Equipment' },
  { value: 'travel', label: 'Travel' },
  { value: 'professional_services', label: 'Professional Services' },
  { value: 'other', label: 'Other' },
];

const createInitialFormData = (projectId?: string): BudgetFormData => ({
  name: '',
  description: '',
  amount: '',
  currency: 'USD',
  category: 'general',
  projectId: projectId ?? '',
  startDate: new Date().toISOString().split('T')[0],
  endDate: '',
});

export default function CreateBudgetClient({ user, orgId, projectId, isOpen, onClose, onSuccess }: CreateBudgetClientProps) {
  const supabase = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState<BudgetFormData>(() => createInitialFormData(projectId));

  useEffect(() => {
    if (!isOpen) {
      setFormData(createInitialFormData(projectId));
    }
  }, [isOpen, projectId]);

  const updateForm = useCallback(<Key extends keyof BudgetFormData>(field: Key, value: BudgetFormData[Key]) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  }, []);

  const handleTextChange = useCallback((event: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    updateForm(name as keyof BudgetFormData, value as BudgetFormData[keyof BudgetFormData]);
  }, [updateForm]);

  const handleTextareaChange = useCallback((event: ChangeEvent<HTMLTextAreaElement>) => {
    const { name, value } = event.target;
    updateForm(name as keyof BudgetFormData, value as BudgetFormData[keyof BudgetFormData]);
  }, [updateForm]);

  const handleSubmit = useCallback(async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const trimmedName = formData.name.trim();
    const parsedAmount = parseFloat(formData.amount.trim());

    if (!trimmedName || Number.isNaN(parsedAmount) || parsedAmount <= 0 || loading) {
      return;
    }

    setLoading(true);

    try {
      const budgetId = crypto.randomUUID();

      const { data: budget, error } = await supabase
        .from('budgets')
        .insert({
          id: budgetId,
          organization_id: orgId,
          project_id: formData.projectId.trim() || null,
          name: trimmedName,
          description: formData.description.trim() || null,
          amount: parsedAmount,
          currency: formData.currency,
          category: formData.category,
          spent: 0,
          status: 'active',
          start_date: formData.startDate || null,
          end_date: formData.endDate || null,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        })
        .select()
        .single<CreatedBudget>();

      if (error) throw error;

      await supabase
        .from('activity_logs')
        .insert({
          id: crypto.randomUUID(),
          organization_id: orgId,
          user_id: user.id,
          action: 'budget.created',
          entity_type: 'budget',
          entity_id: budgetId,
          metadata: {
            budgetName: formData.name,
            amount: parsedAmount,
          },
          occurred_at: new Date().toISOString(),
        });

      setFormData(createInitialFormData(projectId));
      onSuccess?.(budget);
      onClose();
    } catch (error) {
      console.error('Error creating budget:', error);
    } finally {
      setLoading(false);
    }
  }, [formData, loading, onClose, onSuccess, orgId, projectId, supabase, user.id]);

  const currencyOptions = useMemo(() => SUPPORTED_CURRENCIES.map(currency => (
    <SelectItem key={currency.value} value={currency.value}>
      {currency.label}
    </SelectItem>
  )), []);

  const categoryOptions = useMemo(() => BUDGET_CATEGORIES.map(category => (
    <SelectItem key={category.value} value={category.value}>
      {category.label}
    </SelectItem>
  )), []);

  const parsedAmount = parseFloat(formData.amount.trim());

  const formattedAmount = !Number.isNaN(parsedAmount) && parsedAmount > 0
    ? new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: formData.currency,
      }).format(parsedAmount)
    : '';

  const drawerTabs: DrawerTab[] = useMemo(() => ([
    {
      key: 'details',
      label: 'Details',
      content: (
        <form onSubmit={handleSubmit} className="stack-lg">
          <Card className="p-md bg-accent/5 border-primary/20">
            <div className="flex items-center cluster-sm">
              <Target className="h-8 w-8 color-accent" />
              <div>
                <h3 className="text-heading-4 color-accent">Budget Planning</h3>
                <p className="text-body-sm color-accent/80">
                  Set financial targets and track spending against your budget
                </p>
              </div>
            </div>
          </Card>

          <div className="stack-md">
            <div>
              <label htmlFor="name" className="block text-body-sm form-label color-foreground mb-sm">
                Budget Name *
              </label>
              <UnifiedInput
                id="name"
                name="name"
                value={formData.name}
                onChange={handleTextChange}
                placeholder="e.g., Q1 Marketing Budget, Project Alpha Budget"
                required
              />
            </div>

            <div>
              <label htmlFor="description" className="block text-body-sm form-label color-foreground mb-sm">
                Description
              </label>
              <Textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleTextareaChange}
                placeholder="Describe the purpose and scope of this budget..."
                rows={3}
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label htmlFor="amount" className="block text-body-sm form-label color-foreground mb-sm">
                  Budget Amount *
                </label>
                <div className="relative">
                  <DollarSign className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="amount"
                    name="amount"
                    type="number"
                    value={formData.amount}
                    onChange={handleTextChange}
                    placeholder="0.00"
                    className="pl-2xl"
                    min="0"
                    step="0.01"
                    required
                  />
                </div>
                {formattedAmount && (
                  <p className="text-body-sm color-foreground/60 mt-xs">
                    {formattedAmount}
                  </p>
                )}
              </div>

              <div>
                <label htmlFor="currency" className="block text-body-sm form-label color-foreground mb-sm">
                  Currency
                </label>
                <Select
                  value={formData.currency}
                  onValueChange={value => updateForm('currency', value as BudgetCurrency)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {currencyOptions}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <label htmlFor="category" className="block text-body-sm form-label color-foreground mb-sm">
                Category
              </label>
              <Select
                value={formData.category}
                onValueChange={value => updateForm('category', value as BudgetCategory)}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {categoryOptions}
                </SelectContent>
              </Select>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label htmlFor="startDate" className="block text-body-sm form-label color-foreground mb-sm">
                  Start Date
                </label>
                <div className="relative">
                  <Calendar className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="startDate"
                    name="startDate"
                    type="date"
                    value={formData.startDate}
                    onChange={handleTextChange}
                    className="pl-2xl"
                  />
                </div>
              </div>

              <div>
                <label htmlFor="endDate" className="block text-body-sm form-label color-foreground mb-sm">
                  End Date
                </label>
                <div className="relative">
                  <Calendar className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="endDate"
                    name="endDate"
                    type="date"
                    value={formData.endDate}
                    onChange={handleTextChange}
                    className="pl-2xl"
                    min={formData.startDate}
                  />
                </div>
              </div>
            </div>
          </div>

          <div className="flex justify-end cluster-sm pt-lg border-t border-border">
            <Button type="button" variant="ghost" onClick={onClose} disabled={loading}>
              Cancel
            </Button>
            <Button type="submit" disabled={loading || !formData.name.trim() || Number.isNaN(parsedAmount) || parsedAmount <= 0} className="min-w-[120px]">
              {loading ? (
                <div className="flex items-center cluster-sm">
                  <div className="w-4 h-4 border-2 border-background/30 border-t-background rounded-full animate-spin" />
                  <span>Creating...</span>
                </div>
              ) : (
                <div className="flex items-center cluster-sm">
                  <Plus className="h-4 w-4" />
                  <span>Create Budget</span>
                </div>
              )}
            </Button>
          </div>
        </form>
      ),
    },
  ]), [categoryOptions, currencyOptions, formData, handleSubmit, handleTextareaChange, handleTextChange, loading, onClose, parsedAmount, updateForm]);

  return (
    <AppDrawer
      open={isOpen}
      onClose={onClose}
      title="Create Budget"
      record={null}
      mode="create"
      fields={[]}
      tabs={drawerTabs}
      loading={loading}
    />
  );
}

