                name="description"
                value={formData.description}
                onChange={handleTextChange}
                placeholder="e.g., Client dinner, Office supplies, Travel expenses"
                required
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label htmlFor="amount" className="block text-body-sm form-label color-foreground mb-sm">
                  Amount *
                </label>
                <div className="relative">
                  <DollarSign className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="amount"
                    name="amount"
                    type="number"
                    value={formData.amount}
                    onChange={handleTextChange}
                    placeholder="0.00"
                    className="pl-2xl"
                    min="0"
                    step="0.01"
                    required
                  />
                </div>
                {formattedAmount && (
                  <p className="text-body-sm color-foreground/60 mt-xs">
                    {formattedAmount}
                  </p>
                )}
              </div>

              <div>
                <label htmlFor="currency" className="block text-body-sm form-label color-foreground mb-sm">
                  Currency
                </label>
                <Select
                  value={formData.currency}
                  onValueChange={value => updateForm('currency', value as ExpenseCurrency)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {currencyOptions}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label htmlFor="category" className="block text-body-sm form-label color-foreground mb-sm">
                  Category
                </label>
                <Select
                  value={formData.category}
                  onValueChange={value => updateForm('category', value as ExpenseCategory)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {categoryOptions}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label htmlFor="expenseDate" className="block text-body-sm form-label color-foreground mb-sm">
                  Expense Date *
                </label>
                <div className="relative">
                  <Calendar className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="expenseDate"
                    name="expenseDate"
                    type="date"
                    value={formData.expenseDate}
                    onChange={handleTextChange}
                    className="pl-2xl"
                    max={new Date().toISOString().split('T')[0]}
                    required
                  />
                </div>
              </div>
            </div>

            <div>
              <label htmlFor="receiptUrl" className="block text-body-sm form-label color-foreground mb-sm">
                Receipt URL
              </label>
              <div className="relative">
                <Upload className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                <UnifiedInput
                  id="receiptUrl"
                  name="receiptUrl"
                  type="url"
                  value={formData.receiptUrl}
                  onChange={handleTextChange}
                  placeholder="https://example.com/receipt.pdf"
                  className="pl-2xl"
                />
              </div>
              <p className="text-body-sm color-foreground/60 mt-xs">
                Upload receipt to cloud storage and paste the URL here
              </p>
            </div>
          </div>

          {formattedAmount && (
            <Card className="p-md bg-secondary border-border">
              <h4 className="form-label color-foreground mb-sm">Expense Summary</h4>
              <div className="stack-xs text-body-sm">
                <div className="flex justify-between">
                  <span className="color-foreground/70">Amount:</span>
                  <span className="form-label">{formattedAmount}</span>
                </div>
                <div className="flex justify-between">
                  <span className="color-foreground/70">Category:</span>
                  <span className="form-label capitalize">{formData.category.replace('_', ' ')}</span>
                </div>
                <div className="flex justify-between">
                  <span className="color-foreground/70">Status:</span>
                  <span className="form-label color-warning">Draft</span>
                </div>
              </div>
            </Card>
          )}

          <div className="flex justify-end cluster-sm pt-lg border-t border-border">
            <Button type="button" variant="ghost" onClick={onClose} disabled={loading}>
              Cancel
            </Button>
            <Button type="submit" disabled={loading || !formData.description.trim() || Number.isNaN(parsedAmount) || parsedAmount <= 0} className="min-w-[120px]">
              {loading ? (
                <div className="flex items-center cluster-sm">
                  <div className="w-4 h-4 border-2 border-background/30 border-t-background rounded-full animate-spin" />
                  <span>Creating...</span>
                </div>
              ) : (
                <div className="flex items-center cluster-sm">
                  <Plus className="h-4 w-4" />
                  <span>Create Expense</span>
                </div>
              )}
            </Button>
          </div>
        </form>
      ),
    },
  ]), [categoryOptions, currencyOptions, formData, formattedAmount, handleSubmit, handleTextareaChange, handleTextChange, loading, onClose, parsedAmount, updateForm]);

  return (
    <AppDrawer
      open={isOpen}
      onClose={onClose}
      title="Create Expense"
      record={null}
      mode="create"
      fields={[]}
      tabs={drawerTabs}
      loading={loading}
    />
  );
}

'use client';

import { Receipt, Plus, Edit, Trash2, Check, X, Clock, DollarSign, Search, Filter, RefreshCw } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { 
  Card, 
  Button, 
  Badge, 
  Skeleton, 
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from '@ghxstship/ui';
import { ExpensesService } from './lib/expenses-service';
import CreateExpenseDrawer from './drawers/CreateExpenseDrawer';
import ExpenseGridView from './views/ExpenseGridView';
import ExpenseListView from './views/ExpenseListView';
import type { 
  Expense, 
  ExpensesClientProps, 
  ExpenseFilters, 
  ExpenseStatistics 
} from './types';

export default function ExpensesClient({ user, orgId, translations }: ExpensesClientProps) {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [loading, setLoading] = useState(true);
  const [statistics, setStatistics] = useState<ExpenseStatistics | null>(null);
  const [creating, setCreating] = useState(false);
  const [editing, setEditing] = useState<Expense | null>(null);
  const [viewing, setViewing] = useState<Expense | null>(null);
  const [selectedIds, setSelectedIds] = useState<string[]>([]);
  const [filters, setFilters] = useState<ExpenseFilters>({
    status: [],
    category: [],
    search: ''
  });
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [refreshing, setRefreshing] = useState(false);

  const expensesService = new ExpensesService();

  useEffect(() => {
    loadExpenses();
    loadStatistics();
  }, [orgId, filters]);

  const loadExpenses = async () => {
    try {
      setLoading(true);
      const data = await expensesService.getExpenses(orgId, filters);
      setExpenses(data);
    } catch (error) {
      console.error('Error loading expenses:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadStatistics = async () => {
    try {
      const stats = await expensesService.getExpenseStatistics(orgId);
      setStatistics(stats);
    } catch (error) {
      console.error('Error loading expense statistics:', error);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await Promise.all([loadExpenses(), loadStatistics()]);
    setRefreshing(false);
  };

  const handleCreateExpense = () => {
    setCreating(true);
  };

  const handleEditExpense = (expense: Expense) => {
    setEditing(expense);
  };

  const handleViewExpense = (expense: Expense) => {
    setViewing(expense);
  };

  const handleDeleteExpense = async (expense: Expense) => {
    if (confirm('Are you sure you want to delete this expense?')) {
      try {
        await expensesService.deleteExpense(orgId, expense.id);
        await loadExpenses();
      } catch (error) {
        console.error('Error deleting expense:', error);
      }
    }
  };

  const handleExpenseSaved = (expense: Expense) => {
    setCreating(false);
    setEditing(null);
    loadExpenses();
  };

  const handleCancel = () => {
    setCreating(false);
    setEditing(null);
    setViewing(null);
  };

  const handleExport = async (format: 'csv' | 'json') => {
    try {
      const data = await expensesService.exportExpenses(orgId, format, filters);
      const blob = new Blob([data], { 
        type: format === 'csv' ? 'text/csv' : 'application/json' 
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `expenses.${format}`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error exporting expenses:', error);
    }
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
          {Array.from({ length: 4 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <Skeleton className="h-4 w-20 mb-sm" />
              <Skeleton className="h-8 w-32 mb-xs" />
              <Skeleton className="h-3 w-16" />
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-2 color-foreground">Expenses</h1>
          <p className="text-body-sm color-foreground/70 mt-xs">
            Manage and track business expenses
          </p>
        </div>
        <div className="cluster-sm">
          <Button 
            variant="outline" 
            onClick={handleRefresh}
            disabled={refreshing}
          >
            <RefreshCw className={`h-4 w-4 mr-sm ${refreshing ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
          <Button onClick={handleCreateExpense}>
            <Plus className="h-4 w-4 mr-sm" />
            Add Expense
          </Button>
        </div>
      </div>

      {/* Statistics Cards */}
      {statistics && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
          <Card className="p-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm color-muted">Total Expenses</p>
                <p className="text-heading-3 color-foreground">
                  {expensesService.formatCurrency(statistics.totalExpenses)}
                </p>
              </div>
              <DollarSign className="h-8 w-8 color-primary" />
            </div>
          </Card>

          <Card className="p-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm color-muted">Pending Approval</p>
                <p className="text-heading-3 color-warning">{statistics.pendingApproval}</p>
              </div>
              <Clock className="h-8 w-8 color-warning" />
            </div>
          </Card>

          <Card className="p-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm color-muted">Approved Amount</p>
                <p className="text-heading-3 color-success">
                  {expensesService.formatCurrency(statistics.approvedAmount)}
                </p>
              </div>
              <Check className="h-8 w-8 color-success" />
            </div>
          </Card>

          <Card className="p-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-body-sm color-muted">Rejected</p>
                <p className="text-heading-3 color-destructive">{statistics.rejectedCount}</p>
              </div>
              <X className="h-8 w-8 color-destructive" />
            </div>
          </Card>
        </div>
      )}

      {/* Filters and Controls */}
      <Card className="p-lg">
        <div className="flex items-center justify-between mb-md">
          <div className="flex items-center gap-md">
            <UnifiedInput
              placeholder="Search expenses..."
              value={filters.search || ''}
              onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
              className="w-64"
            />

            <Select
              value={filters.status?.[0] || ''}
              onValueChange={(value) => setFilters(prev => ({ 
                ...prev, 
                status: value ? [value] : [] 
              }))}
            >
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Status</SelectItem>
                <SelectItem value="draft">Draft</SelectItem>
                <SelectItem value="submitted">Submitted</SelectItem>
                <SelectItem value="approved">Approved</SelectItem>
                <SelectItem value="rejected">Rejected</SelectItem>
                <SelectItem value="paid">Paid</SelectItem>
              </SelectContent>
            </Select>

            <Select
              value={filters.category?.[0] || ''}
              onValueChange={(value) => setFilters(prev => ({ 
                ...prev, 
                category: value ? [value] : [] 
              }))}
            >
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Category" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Categories</SelectItem>
                <SelectItem value="Travel">Travel</SelectItem>
                <SelectItem value="Meals & Entertainment">Meals & Entertainment</SelectItem>
                <SelectItem value="Office Supplies">Office Supplies</SelectItem>
                <SelectItem value="Software & Subscriptions">Software & Subscriptions</SelectItem>
                <SelectItem value="Marketing">Marketing</SelectItem>
                <SelectItem value="Other">Other</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="flex items-center gap-sm">
            <Button
              variant="outline"
              onClick={() => handleExport('csv')}
              size="sm"
            >
              Export CSV
            </Button>
            <Button
              variant="outline"
              onClick={() => handleExport('json')}
              size="sm"
            >
              Export JSON
            </Button>
            <div className="flex items-center gap-xs">
              <Button
                variant={viewMode === 'grid' ? 'default' : 'ghost'}
                size="sm"
                onClick={() => setViewMode('grid')}
              >
                Grid
              </Button>
              <Button
                variant={viewMode === 'list' ? 'default' : 'ghost'}
                size="sm"
                onClick={() => setViewMode('list')}
              >
                List
              </Button>
            </div>
          </div>
        </div>

        {/* Expenses Display */}
        {viewMode === 'grid' ? (
          <ExpenseGridView
            expenses={expenses}
            onEdit={handleEditExpense}
            onView={handleViewExpense}
            onDelete={handleDeleteExpense}
            onRefresh={loadExpenses}
            loading={loading}
            user={user}
            orgId={orgId}
          />
        ) : (
          <ExpenseListView
            expenses={expenses}
            onEdit={handleEditExpense}
            onView={handleViewExpense}
            onDelete={handleDeleteExpense}
            onRefresh={loadExpenses}
            onSelectionChange={setSelectedIds}
            selectedIds={selectedIds}
            loading={loading}
            user={user}
            orgId={orgId}
          />
        )}
      </Card>

      {/* Drawers */}
      {creating && (
        <CreateExpenseDrawer
          mode="create"
          onSave={handleExpenseSaved}
          onCancel={handleCancel}
          user={user}
          orgId={orgId}
        />
      )}

      {editing && (
        <CreateExpenseDrawer
          expense={editing}
          mode="edit"
          onSave={handleExpenseSaved}
          onCancel={handleCancel}
          user={user}
          orgId={orgId}
        />
      )}

      {viewing && (
        <CreateExpenseDrawer
          expense={viewing}
          mode="view"
          onCancel={handleCancel}
          user={user}
          orgId={orgId}
        />
      )}
    </div>
  );
}

'use client';

import { Receipt, Plus, Edit, Trash2, Check, X, Clock, AlertTriangle, FileText, DollarSign, Calendar, User as UserIcon, Search, Filter, CreditCard, Download, Upload, RefreshCw } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { 
  Card, 
  Button, 
  Badge, 
  Skeleton, 
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from '@ghxstship/ui';
import { ExpensesService } from './lib/expenses-service';
import CreateExpenseDrawer from './drawers/CreateExpenseDrawer';
import ExpenseGridView from './views/ExpenseGridView';
import ExpenseListView from './views/ExpenseListView';
import type { 
  Expense, 
  ExpensesClientProps, 
  ExpenseFilters, 
  ExpenseStatistics 
} from './types';
// Field configurations for ATLVS
const EXPENSE_FIELD_CONFIGS: FieldConfig[] = [
