    type: 'select',
    sortable: true,
    filterable: true,
    required: true
  },
  {
    key: 'status',
    label: 'Status',
    type: 'badge',
    sortable: true,
    filterable: true
  },
  {
    key: 'vendor',
    label: 'Vendor',
    type: 'text',
    sortable: true,
    filterable: true
  },
  {
    key: 'submitted_by',
    label: 'Submitted By',
    type: 'text',
    sortable: true,
    filterable: true
  },
  {
    key: 'created_at',
    label: 'Created',
    type: 'date',
    sortable: true,
    filterable: true
  }
];

export default function ExpensesClient({ user, orgId, translations }: ExpensesClientProps) {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [loading, setLoading] = useState(true);
  const [statistics, setStatistics] = useState<ExpenseStatistics | null>(null);
  const [creating, setCreating] = useState(false);
  const [editing, setEditing] = useState<Expense | null>(null);
  const [viewing, setViewing] = useState<Expense | null>(null);
  const [selectedIds, setSelectedIds] = useState<string[]>([]);
  const [filters, setFilters] = useState<ExpenseFilters>({
    status: [],
    category: [],
    search: ''
  });
  const [viewMode, setViewMode] = useState<'grid' | 'list' | 'kanban' | 'calendar'>('grid');
  const [refreshing, setRefreshing] = useState(false);

  const expensesService = new ExpensesService();

  useEffect(() => {
    loadExpenses();
    loadStatistics();
  }, [orgId, filters]);

  const loadExpenses = async () => {
    try {
      setLoading(true);
      const data = await expensesService.getExpenses(orgId, filters);
      setExpenses(data);
    } catch (error) {
      console.error('Error loading expenses:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadStatistics = async () => {
    try {
      const stats = await expensesService.getExpenseStatistics(orgId);
      setStatistics(stats);
    } catch (error) {
      console.error('Error loading expense statistics:', error);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await Promise.all([loadExpenses(), loadStatistics()]);
    setRefreshing(false);
  };

  const handleCreateExpense = () => {
    setSelectedExpense(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditExpense = (expense: Expense) => {
    setSelectedExpense(expense);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewExpense = (expense: Expense) => {
    setSelectedExpense(expense);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleDeleteExpense = async (expenseId: string) => {
    if (!confirm('Are you sure you want to delete this expense? This action cannot be undone.')) return;

    try {
      const response = await fetch('/api/v1/finance/expenses', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: expenseId })
      });

      if (!response.ok) throw new Error('Failed to delete expense');
      await loadExpenses();
    } catch (error) {
      console.error('Error deleting expense:', error);
    }
  };

  const handleSaveExpense = async (expenseData: Partial<Expense>) => {
    try {
      if (drawerMode === 'create') {
        const response = await fetch('/api/v1/finance/expenses', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            description: expenseData.description,
            amount: expenseData.amount,
            currency: expenseData.currency,
            category: expenseData.category,
            budgetId: expenseData.budget_id,
            projectId: expenseData.project_id,
            expenseDate: expenseData.expense_date,
            receiptUrl: expenseData.receipt_url,
            status: expenseData.status
          })
        });

        if (!response.ok) throw new Error('Failed to create expense');
      } else if (drawerMode === 'edit' && selectedExpense) {
        const response = await fetch('/api/v1/finance/expenses', {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: selectedExpense.id,
            description: expenseData.description,
            amount: expenseData.amount,
            currency: expenseData.currency,
            category: expenseData.category,
            budgetId: expenseData.budget_id,
            projectId: expenseData.project_id,
            expenseDate: expenseData.expense_date,
            receiptUrl: expenseData.receipt_url,
            status: expenseData.status
          })
        });

        if (!response.ok) throw new Error('Failed to update expense');
      }

      setDrawerOpen(false);
      await loadExpenses();
    } catch (error) {
      console.error('Error saving expense:', error);
    }
  };

  const handleApproveExpense = async (expenseId: string) => {
    try {
      const response = await fetch('/api/v1/finance/expenses', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: expenseId,
          status: 'approved',
          approvedBy: user.id,
          approvedAt: new Date().toISOString()
        })
      });

      if (!response.ok) throw new Error('Failed to approve expense');
      await loadExpenses();
    } catch (error) {
      console.error('Error approving expense:', error);
    }
  };

  const handleRejectExpense = async (expenseId: string, reason: string) => {
    try {
      const response = await fetch('/api/v1/finance/expenses', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: expenseId,
          status: 'rejected',
          rejectedReason: reason
        })
      });

      if (!response.ok) throw new Error('Failed to reject expense');
      await loadExpenses();
    } catch (error) {
      console.error('Error rejecting expense:', error);
    }
  };

  const filteredExpenses = expenses.filter(expense => {
    const matchesSearch = searchTerm === '' || 
      expense.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
      expense.category.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = filterStatus === 'all' || expense.status === filterStatus;
    const matchesCategory = filterCategory === 'all' || expense.category === filterCategory;
    return matchesSearch && matchesStatus && matchesCategory;
  });

  const getExpenseStatusIcon = (expense: Expense) => {
    switch (expense.status) {
      case 'approved':
        return <Check className="h-5 w-5 text-green-500" />;
      case 'rejected':
        return <X className="h-5 w-5 text-red-500" />;
      case 'submitted':
        return <Clock className="h-5 w-5 text-yellow-500" />;
      case 'paid':
        return <CreditCard className="h-5 w-5 text-blue-500" />;
      case 'draft':
        return <FileText className="h-5 w-5 text-gray-500" />;
      default:
        return <Clock className="h-5 w-5 text-gray-500" />;
    }
  };

  const getExpenseStatusColor = (expense: Expense) => {
    switch (expense.status) {
      case 'approved':
        return 'bg-green-100 text-green-800';
      case 'rejected':
        return 'bg-red-100 text-red-800';
      case 'submitted':
        return 'bg-yellow-100 text-yellow-800';
      case 'paid':
        return 'bg-blue-100 text-blue-800';
      case 'draft':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  // ATLVS DataViews field configuration
  const fields: FieldConfig[] = [
    {
      key: 'description',
      label: 'Description',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'amount',
      label: 'Amount',
      type: 'currency',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'currency',
      label: 'Currency',
      type: 'select',
      options: [
        { value: 'USD', label: 'USD' },
        { value: 'EUR', label: 'EUR' },
        { value: 'GBP', label: 'GBP' }
      ],
      defaultValue: 'USD',
      filterable: true
    },
    {
      key: 'category',
      label: 'Category',
      type: 'select',
      options: [
        { value: 'equipment', label: 'Equipment' },
        { value: 'construction', label: 'Construction' },
        { value: 'catering', label: 'Catering' },
        { value: 'travel', label: 'Travel' },
        { value: 'marketing', label: 'Marketing' },
        { value: 'operations', label: 'Operations' },
        { value: 'office', label: 'Office Supplies' },
        { value: 'professional', label: 'Professional Services' },
        { value: 'other', label: 'Other' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'submitted', label: 'Submitted' },
        { value: 'approved', label: 'Approved' },
        { value: 'rejected', label: 'Rejected' },
        { value: 'paid', label: 'Paid' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'expense_date',
      label: 'Expense Date',
      type: 'date',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'receipt_url',
      label: 'Receipt URL',
      type: 'url',
      sortable: false,
      filterable: false
    },
    {
      key: 'budget_id',
      label: 'Budget',
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'project_id',
      label: 'Project',
      type: 'text',
      sortable: true,
      filterable: true
    }
  ];

  // ATLVS DataViews configuration
  const expensesConfig: DataViewConfig = {
    id: 'expenses-dataviews',
    name: 'Expense Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data: filteredExpenses.map(expense => ({
      ...expense,
      record_type: 'expense'
    })),
    pagination: {
      page: 1,
      pageSize: 25,
      total: filteredExpenses.length
    },
    onSearch: (query: string) => {
      setSearchTerm(query);
    },
    onFilter: (filters: unknown) => {
      if (filters.status) setFilterStatus(filters.status);
      if (filters.category) setFilterCategory(filters.category);
    },
    onSort: (sorts: unknown) => {
      console.log('Sort:', sorts);
    },
    onRefresh: () => {
      loadExpenses();
    },
    onExport: (data, format) => {
      console.log('Export:', format, data);
    },
    onImport: (data: unknown) => {
      console.log('Import:', data);
    },
    onRowAction: (action: string, record: DataRecord) => {
      const expense = record as Expense;
      switch (action) {
        case 'view':
          handleViewExpense(expense);
          break;
        case 'edit':
          handleEditExpense(expense);
          break;
        case 'delete':
          handleDeleteExpense(expense.id);
          break;
        case 'approve':
          handleApproveExpense(expense.id);
          break;
        case 'reject':
          const reason = prompt('Please provide a reason for rejection:');
          if (reason) handleRejectExpense(expense.id, reason);
          break;
      }
    }
  };

  const configWithData = {
    ...expensesConfig,
    data: filteredExpenses.map(expense => ({
      ...expense,
      record_type: 'expense'
    }))
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <div>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-96 mt-2" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <Skeleton key={i} className="h-32" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Expense Management</h1>
          <p className="text-gray-600">Track and manage your business expenses</p>
        </div>
        <Button onClick={handleCreateExpense} className="flex items-center gap-2">
          <Plus className="h-4 w-4" />
          New Expense
        </Button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Expenses</p>
              <p className="text-2xl font-bold text-gray-900">{expenses.length}</p>
            </div>
            <Receipt className="h-8 w-8 text-blue-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Amount</p>
              <p className="text-2xl font-bold text-gray-900">
                {formatCurrency(expenses.reduce((sum, e) => sum + e.amount, 0))}
              </p>
            </div>
            <DollarSign className="h-8 w-8 text-green-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Pending Approval</p>
              <p className="text-2xl font-bold text-gray-900">
                {expenses.filter(e => e.status === 'submitted').length}
              </p>
            </div>
            <Clock className="h-8 w-8 text-yellow-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Approved</p>
              <p className="text-2xl font-bold text-gray-900">
                {expenses.filter(e => e.status === 'approved').length}
              </p>
            </div>
            <Check className="h-8 w-8 text-green-500" />
          </div>
        </Card>
      </div>

      {/* Filters */}
      <div className="flex gap-4 mb-6">
        <div className="flex-1">
          <UnifiedInput
            placeholder="Search expenses..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            leftIcon={<Search className="h-4 w-4" />}
          />
        </div>
        <Select value={filterStatus} onValueChange={setFilterStatus}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Statuses</SelectItem>
            <SelectItem value="draft">Draft</SelectItem>
            <SelectItem value="submitted">Submitted</SelectItem>
            <SelectItem value="approved">Approved</SelectItem>
            <SelectItem value="rejected">Rejected</SelectItem>
            <SelectItem value="paid">Paid</SelectItem>
          </SelectContent>
        </Select>
        <Select value={filterCategory} onValueChange={setFilterCategory}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by category" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Categories</SelectItem>
            <SelectItem value="equipment">Equipment</SelectItem>
            <SelectItem value="construction">Construction</SelectItem>
            <SelectItem value="catering">Catering</SelectItem>
            <SelectItem value="travel">Travel</SelectItem>
            <SelectItem value="marketing">Marketing</SelectItem>
            <SelectItem value="operations">Operations</SelectItem>
            <SelectItem value="office">Office Supplies</SelectItem>
            <SelectItem value="professional">Professional Services</SelectItem>
            <SelectItem value="other">Other</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* ATLVS DataViews */}
      <div className="flex-1">
        <DataViewProvider config={configWithData}>
          <StateManagerProvider>
            <div className="flex-1 space-y-4">
              {/* View Switcher and Actions */}
              <div className="flex justify-between items-center">
                <ViewSwitcher />
                <DataActions />
              </div>

              {/* Data Views */}
              <div className="flex-1">
                <DataGrid />
                <KanbanBoard 
                  columns={[
                    { id: 'draft', title: 'Draft' },
                    { id: 'submitted', title: 'Submitted' },
                    { id: 'approved', title: 'Approved' },
                    { id: 'rejected', title: 'Rejected' },
                    { id: 'paid', title: 'Paid' }
                  ]}
                  statusField="status"
                  titleField="description"
                />
                <CalendarView 
                  startDateField="expense_date"
                  titleField="description"
                />
                <ListView titleField="description" />
              </div>
            </div>
          </StateManagerProvider>
        </DataViewProvider>
      </div>

      {/* Universal Drawer for CRUD operations */}
      <UniversalDrawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={
          drawerMode === 'create' ? 'Create Expense' :
          drawerMode === 'edit' ? 'Edit Expense' : 'Expense Details'
        }
        mode={drawerMode}
        data={selectedExpense}
        fields={fields}
        onSave={handleSaveExpense}
        tabs={[
          {
            key: 'details',
            label: 'Expense Details',
            content: (
              <div className="space-y-4">
                {selectedExpense && (
                  <>
                    <div className="flex items-center gap-3">
                      {getExpenseStatusIcon(selectedExpense)}
                      <div>
                        <h3 className="font-semibold">{selectedExpense.description}</h3>
                        <p className="text-sm text-gray-600">{selectedExpense.category}</p>
                      </div>
                      <Badge className={getExpenseStatusColor(selectedExpense)}>
                        {selectedExpense.status}
                      </Badge>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium text-gray-700">Amount</label>
                        <p className="text-lg font-semibold">{formatCurrency(selectedExpense.amount)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Expense Date</label>
                        <p className="text-sm">{formatDate(selectedExpense.expense_date)}</p>
                      </div>
                    </div>
                    {selectedExpense.receipt_url && (
                      <div>
                        <label className="text-sm font-medium text-gray-700">Receipt</label>
                        <a 
                          href={selectedExpense.receipt_url} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:text-blue-800 text-sm"
                        >
                          View Receipt
                        </a>
                      </div>
                    )}
                    {selectedExpense.rejected_reason && (
                      <div>
                        <label className="text-sm font-medium text-gray-700">Rejection Reason</label>
                        <p className="text-sm text-red-600">{selectedExpense.rejected_reason}</p>
                      </div>
                    )}
                    {selectedExpense.status === 'submitted' && (
                      <div className="flex gap-2">
                        <Button 
                          onClick={() => handleApproveExpense(selectedExpense.id)}
                          className="flex items-center gap-2"
                          size="sm"
                        >
                          <Check className="h-4 w-4" />
                          Approve
                        </Button>
                        <Button 
                          onClick={() => {
                            const reason = prompt('Please provide a reason for rejection:');
                            if (reason) handleRejectExpense(selectedExpense.id, reason);
                          }}
                          variant="outline"
                          className="flex items-center gap-2"
                          size="sm"
                        >
                          <X className="h-4 w-4" />
                          Reject
                        </Button>
                      </div>
                    )}
                  </>
                )}
              </div>
            )
          }
        ]}
      />
    </div>
  );
}

'use client';

import { TrendingUp, Plus, Edit, Trash2, BarChart3, Target, Calendar, DollarSign, AlertTriangle, CheckCircle, Clock, Activity, PieChart, Search, Filter, TrendingDown } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Card, 
  Button, 
  Badge, 
  Skeleton, 
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView,
  DataActions,
  UniversalDrawer,
  type DataRecord,
  type FieldConfig,
  type DataViewConfig
} from '@ghxstship/ui';

interface ForecastsClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface Forecast {
  id: string;
  name: string;
  description?: string;
  type: 'revenue' | 'expense' | 'budget' | 'cash_flow';
  period: 'monthly' | 'quarterly' | 'annually';
  start_date: string;
  end_date: string;
  projected_amount: number;
  actual_amount?: number;
  currency: string;
  status: 'draft' | 'active' | 'completed' | 'archived';
  assumptions?: string;
  confidence_level: 'low' | 'medium' | 'high';
  variance?: number;
  accuracy?: number;
  project_id?: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
  created_by: string;
}

export default function ForecastsClient({ user, orgId, translations }: ForecastsClientProps) {
  const [loading, setLoading] = useState(true);
  const [forecasts, setForecasts] = useState<Forecast[]>([]);
  const [selectedForecast, setSelectedForecast] = useState<Forecast | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');
  const [filterType, setFilterType] = useState<string>('all');
  const [filterPeriod, setFilterPeriod] = useState<string>('all');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  const supabase = createBrowserClient();

  useEffect(() => {
    loadForecasts();
  }, [orgId]);

  const loadForecasts = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('forecasts')
        .select('*')
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Add computed fields
      const enrichedForecasts = (data || []).map(forecast => ({
        ...forecast,
        variance: forecast.actual_amount && forecast.projected_amount 
          ? forecast.actual_amount - forecast.projected_amount 
          : 0,
        accuracy: forecast.actual_amount && forecast.projected_amount && forecast.projected_amount > 0
          ? Math.abs(1 - (Math.abs(forecast.actual_amount - forecast.projected_amount) / forecast.projected_amount)) * 100
          : 0
      }));

      setForecasts(enrichedForecasts);
    } catch (error) {
      console.error('Error loading forecasts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateForecast = () => {
    setSelectedForecast(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditForecast = (forecast: Forecast) => {
    setSelectedForecast(forecast);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewForecast = (forecast: Forecast) => {
    setSelectedForecast(forecast);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleDeleteForecast = async (forecastId: string) => {
    if (!confirm('Are you sure you want to delete this forecast? This action cannot be undone.')) return;

    try {
      const response = await fetch('/api/v1/finance/forecasts', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: forecastId })
      });

      if (!response.ok) throw new Error('Failed to delete forecast');
      await loadForecasts();
    } catch (error) {
      console.error('Error deleting forecast:', error);
    }
  };

  const handleSaveForecast = async (forecastData: Partial<Forecast>) => {
    try {
      if (drawerMode === 'create') {
        const response = await fetch('/api/v1/finance/forecasts', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: forecastData.name,
            description: forecastData.description,
            type: forecastData.type,
            period: forecastData.period,
            startDate: forecastData.start_date,
            endDate: forecastData.end_date,
            baseAmount: forecastData.projected_amount,
            confidence: forecastData.confidence_level,
            assumptions: forecastData.assumptions,
            projectId: forecastData.project_id
          })
        });

        if (!response.ok) throw new Error('Failed to create forecast');
      } else if (drawerMode === 'edit' && selectedForecast) {
        const response = await fetch('/api/v1/finance/forecasts', {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: selectedForecast.id,
            name: forecastData.name,
            description: forecastData.description,
            type: forecastData.type,
            period: forecastData.period,
            startDate: forecastData.start_date,
            endDate: forecastData.end_date,
            baseAmount: forecastData.projected_amount,
            confidence: forecastData.confidence_level,
            assumptions: forecastData.assumptions,
            projectId: forecastData.project_id
          })
        });

        if (!response.ok) throw new Error('Failed to update forecast');
      }

      setDrawerOpen(false);
      await loadForecasts();
    } catch (error) {
      console.error('Error saving forecast:', error);
    }
  };

  const filteredForecasts = forecasts.filter(forecast => {
    const matchesSearch = searchTerm === '' || 
      forecast.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      forecast.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = filterType === 'all' || forecast.type === filterType;
    const matchesPeriod = filterPeriod === 'all' || forecast.period === filterPeriod;
    const matchesStatus = filterStatus === 'all' || forecast.status === filterStatus;
    return matchesSearch && matchesType && matchesPeriod && matchesStatus;
  });

  const getForecastTypeIcon = (forecast: Forecast) => {
    switch (forecast.type) {
      case 'revenue':
        return <TrendingUp className="h-5 w-5 text-green-500" />;
      case 'expense':
        return <TrendingDown className="h-5 w-5 text-red-500" />;
      case 'budget':
        return <Target className="h-5 w-5 text-blue-500" />;
      case 'cash_flow':
        return <Activity className="h-5 w-5 text-purple-500" />;
      default:
        return <BarChart3 className="h-5 w-5 text-gray-500" />;
    }
  };

  const getForecastTypeColor = (forecast: Forecast) => {
    switch (forecast.type) {
      case 'revenue':
        return 'bg-green-100 text-green-800';
      case 'expense':
        return 'bg-red-100 text-red-800';
      case 'budget':
        return 'bg-blue-100 text-blue-800';
      case 'cash_flow':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getConfidenceColor = (level: string) => {
    switch (level) {
      case 'high':
        return 'bg-green-100 text-green-800';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800';
      case 'low':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getAccuracyColor = (accuracy: number) => {
    if (accuracy >= 90) return 'text-green-600';
    if (accuracy >= 70) return 'text-yellow-600';
    return 'text-red-600';
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  // ATLVS DataViews field configuration
  const fields: FieldConfig[] = [
    {
      key: 'name',
      label: 'Forecast Name',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'description',
      label: 'Description',
      type: 'textarea',
      rows: 3,
      sortable: true,
      filterable: true
    },
    {
      key: 'type',
      label: 'Type',
      type: 'select',
      options: [
        { value: 'revenue', label: 'Revenue' },
        { value: 'expense', label: 'Expense' },
        { value: 'budget', label: 'Budget' },
        { value: 'cash_flow', label: 'Cash Flow' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'period',
      label: 'Period',
      type: 'select',
      options: [
        { value: 'monthly', label: 'Monthly' },
        { value: 'quarterly', label: 'Quarterly' },
        { value: 'annually', label: 'Annually' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'projected_amount',
      label: 'Projected Amount',
      type: 'currency',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'actual_amount',
      label: 'Actual Amount',
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'variance',
      label: 'Variance',
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'accuracy',
      label: 'Accuracy %',
      type: 'number',
      sortable: true,
      filterable: true
    },
    {
      key: 'confidence_level',
      label: 'Confidence Level',
      type: 'select',
      options: [
        { value: 'low', label: 'Low' },
        { value: 'medium', label: 'Medium' },
        { value: 'high', label: 'High' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'active', label: 'Active' },
        { value: 'completed', label: 'Completed' },
        { value: 'archived', label: 'Archived' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'start_date',
      label: 'Start Date',
      type: 'date',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'end_date',
      label: 'End Date',
      type: 'date',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'assumptions',
      label: 'Assumptions',
      type: 'textarea',
      rows: 4,
      sortable: false,
      filterable: true
    }
  ];

  // ATLVS DataViews configuration
  const forecastsConfig: DataViewConfig = {
    id: 'forecasts-dataviews',
    name: 'Financial Forecasts',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data: filteredForecasts.map(forecast => ({
      ...forecast,
      record_type: 'forecast'
    })),
    pagination: {
      page: 1,
      pageSize: 25,
      total: filteredForecasts.length
    },
    onSearch: (query: string) => {
      setSearchTerm(query);
    },
    onFilter: (filters: unknown) => {
      if (filters.type) setFilterType(filters.type);
      if (filters.period) setFilterPeriod(filters.period);
      if (filters.status) setFilterStatus(filters.status);
    },
    onSort: (sorts: unknown) => {
      console.log('Sort:', sorts);
    },
    onRefresh: () => {
      loadForecasts();
    },
    onExport: (data, format) => {
      console.log('Export:', format, data);
    },
    onImport: (data: unknown) => {
      console.log('Import:', data);
    },
    onRowAction: (action: string, record: DataRecord) => {
      const forecast = record as Forecast;
      switch (action) {
        case 'view':
          handleViewForecast(forecast);
          break;
        case 'edit':
          handleEditForecast(forecast);
          break;
        case 'delete':
          handleDeleteForecast(forecast.id);
          break;
      }
    }
  };

  const configWithData = {
    ...forecastsConfig,
    data: filteredForecasts.map(forecast => ({
      ...forecast,
      record_type: 'forecast'
    }))
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <div>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-96 mt-2" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <Skeleton key={i} className="h-32" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Financial Forecasts</h1>
          <p className="text-gray-600">Plan and track your financial projections</p>
        </div>
        <Button onClick={handleCreateForecast} className="flex items-center gap-2">
          <Plus className="h-4 w-4" />
          New Forecast
        </Button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Forecasts</p>
              <p className="text-2xl font-bold text-gray-900">{forecasts.length}</p>
            </div>
            <BarChart3 className="h-8 w-8 text-blue-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Projected</p>
              <p className="text-2xl font-bold text-gray-900">
                {formatCurrency(forecasts.reduce((sum, f) => sum + f.projected_amount, 0))}
              </p>
            </div>
            <Target className="h-8 w-8 text-green-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Active Forecasts</p>
              <p className="text-2xl font-bold text-gray-900">
                {forecasts.filter(f => f.status === 'active').length}
              </p>
            </div>
            <Activity className="h-8 w-8 text-purple-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Avg Accuracy</p>
              <p className="text-2xl font-bold text-gray-900">
                {forecasts.length > 0 
                  ? Math.round(forecasts.reduce((sum, f) => sum + (f.accuracy || 0), 0) / forecasts.length)
                  : 0}%
              </p>
            </div>
            <CheckCircle className="h-8 w-8 text-yellow-500" />
          </div>
        </Card>
      </div>

      {/* Filters */}
      <div className="flex gap-4 mb-6">
        <div className="flex-1">
          <UnifiedInput
            placeholder="Search forecasts..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            leftIcon={<Search className="h-4 w-4" />}
          />
        </div>
        <Select value={filterType} onValueChange={setFilterType}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by type" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="revenue">Revenue</SelectItem>
            <SelectItem value="expense">Expense</SelectItem>
            <SelectItem value="budget">Budget</SelectItem>
            <SelectItem value="cash_flow">Cash Flow</SelectItem>
          </SelectContent>
        </Select>
        <Select value={filterPeriod} onValueChange={setFilterPeriod}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by period" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Periods</SelectItem>
            <SelectItem value="monthly">Monthly</SelectItem>
            <SelectItem value="quarterly">Quarterly</SelectItem>
            <SelectItem value="annually">Annually</SelectItem>
          </SelectContent>
        </Select>
        <Select value={filterStatus} onValueChange={setFilterStatus}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Statuses</SelectItem>
            <SelectItem value="draft">Draft</SelectItem>
            <SelectItem value="active">Active</SelectItem>
            <SelectItem value="completed">Completed</SelectItem>
            <SelectItem value="archived">Archived</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* ATLVS DataViews */}
      <div className="flex-1">
        <DataViewProvider config={configWithData}>
          <StateManagerProvider>
            <div className="flex-1 space-y-4">
              {/* View Switcher and Actions */}
              <div className="flex justify-between items-center">
                <ViewSwitcher />
                <DataActions />
              </div>

              {/* Data Views */}
              <div className="flex-1">
                <DataGrid />
                <KanbanBoard 
                  columns={[
                    { id: 'draft', title: 'Draft' },
                    { id: 'active', title: 'Active' },
                    { id: 'completed', title: 'Completed' },
                    { id: 'archived', title: 'Archived' }
                  ]}
                  statusField="status"
                  titleField="name"
                />
                <CalendarView 
                  startDateField="start_date"
                  endDateField="end_date"
                  titleField="name"
                />
                <ListView titleField="name" />
              </div>
            </div>
          </StateManagerProvider>
        </DataViewProvider>
      </div>

      {/* Universal Drawer for CRUD operations */}
      <UniversalDrawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={
          drawerMode === 'create' ? 'Create Forecast' :
          drawerMode === 'edit' ? 'Edit Forecast' : 'Forecast Details'
        }
        mode={drawerMode}
        data={selectedForecast}
        fields={fields}
        onSave={handleSaveForecast}
        tabs={[
          {
            key: 'details',
            label: 'Forecast Details',
            content: (
              <div className="space-y-4">
                {selectedForecast && (
                  <>
                    <div className="flex items-center gap-3">
                      {getForecastTypeIcon(selectedForecast)}
                      <div>
                        <h3 className="font-semibold">{selectedForecast.name}</h3>
                        <p className="text-sm text-gray-600">{selectedForecast.period} {selectedForecast.type}</p>
                      </div>
                      <Badge className={getForecastTypeColor(selectedForecast)}>
                        {selectedForecast.type}
                      </Badge>
                      <Badge className={getConfidenceColor(selectedForecast.confidence_level)}>
                        {selectedForecast.confidence_level} confidence
                      </Badge>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium text-gray-700">Projected Amount</label>
                        <p className="text-lg font-semibold">{formatCurrency(selectedForecast.projected_amount)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Actual Amount</label>
                        <p className="text-lg font-semibold">
                          {selectedForecast.actual_amount ? formatCurrency(selectedForecast.actual_amount) : 'N/A'}
                        </p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Variance</label>
                        <p className={`text-lg font-semibold ${(selectedForecast.variance || 0) >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                          {selectedForecast.variance ? formatCurrency(selectedForecast.variance) : 'N/A'}
                        </p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Accuracy</label>
                        <p className={`text-lg font-semibold ${getAccuracyColor(selectedForecast.accuracy || 0)}`}>
                          {selectedForecast.accuracy ? Math.round(selectedForecast.accuracy) : 0}%
                        </p>
                      </div>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium text-gray-700">Start Date</label>
                        <p className="text-sm">{formatDate(selectedForecast.start_date)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">End Date</label>
                        <p className="text-sm">{formatDate(selectedForecast.end_date)}</p>
                      </div>
                    </div>
                    {selectedForecast.description && (
                      <div>
                        <label className="text-sm font-medium text-gray-700">Description</label>
                        <p className="text-sm text-gray-600">{selectedForecast.description}</p>
                      </div>
                    )}
                    {selectedForecast.assumptions && (
                      <div>
                        <label className="text-sm font-medium text-gray-700">Assumptions</label>
                        <p className="text-sm text-gray-600">{selectedForecast.assumptions}</p>
                      </div>
                    )}
                  </>
                )}
              </div>
            )
          }
        ]}
      />
    </div>
  );
}

'use client';

import { FileText, Plus, Edit, Trash2, Eye, Send, Download, Search, AlertTriangle, CheckCircle, XCircle, Clock, DollarSign, Calendar, User as UserIcon, Mail } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Card, 
  Button, 
  Badge, 
  Skeleton, 
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView,
  DataActions,
  UniversalDrawer,
  type DataRecord,
  type FieldConfig,
  type DataViewConfig
} from '@ghxstship/ui';

interface InvoicesClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface Invoice {
  id: string;
  invoice_number: string;
  description?: string;
  client_company_id?: string;
  project_id?: string;
  purchase_order_id?: string;
  status: 'draft' | 'sent' | 'paid' | 'overdue' | 'cancelled';
  amount_due: number;
  total_amount?: number;
  tax_amount?: number;
  discount_amount?: number;
  currency: string;
  issued_date?: string;
  due_at?: string;
  paid_date?: string;
  payment_terms?: string;
  line_items?: unknown[];
  notes?: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
  // Computed fields
  days_overdue?: number;
  client_name?: string;
  project_name?: string;
}

export default function InvoicesClient({ user, orgId, translations }: InvoicesClientProps) {
  const [loading, setLoading] = useState(true);
  const [invoices, setInvoices] = useState<Invoice[]>([]);
  const [selectedInvoice, setSelectedInvoice] = useState<Invoice | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  const supabase = createBrowserClient();

  useEffect(() => {
    loadInvoices();
  }, [orgId]);

  const loadInvoices = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('invoices')
        .select(`
          *,
          companies:client_company_id(name),
          projects:project_id(name)
        `)
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Add computed fields
      const enrichedInvoices = (data || []).map(invoice => {
        const today = new Date();
        const dueDate = invoice.due_at ? new Date(invoice.due_at) : null;
        const daysOverdue = dueDate && today > dueDate && invoice.status !== 'paid' 
          ? Math.floor((today.getTime() - dueDate.getTime()) / (1000 * 60 * 60 * 24))
          : 0;

        return {
          ...invoice,
          days_overdue: daysOverdue,
          client_name: invoice.companies?.name || 'Unknown Client',
          project_name: invoice.projects?.name || null,
          total_amount: invoice.total_amount || invoice.amount_due
        };
      });

      setInvoices(enrichedInvoices);
    } catch (error) {
      console.error('Error loading invoices:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateInvoice = () => {
    setSelectedInvoice(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditInvoice = (invoice: Invoice) => {
    setSelectedInvoice(invoice);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewInvoice = (invoice: Invoice) => {
    setSelectedInvoice(invoice);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleDeleteInvoice = async (invoiceId: string) => {
    if (!confirm('Are you sure you want to delete this invoice? This action cannot be undone.')) return;

    try {
      const response = await fetch('/api/v1/finance/invoices', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: invoiceId })
      });

      if (!response.ok) throw new Error('Failed to delete invoice');
      await loadInvoices();
    } catch (error) {
      console.error('Error deleting invoice:', error);
    }
  };

  const handleSaveInvoice = async (invoiceData: Partial<Invoice>) => {
    try {
      if (drawerMode === 'create') {
        const response = await fetch('/api/v1/finance/invoices', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            invoiceNumber: invoiceData.invoice_number,
            clientId: invoiceData.client_company_id,
            projectId: invoiceData.project_id,
            issueDate: invoiceData.issued_date,
            dueDate: invoiceData.due_at,
            status: invoiceData.status,
            currency: invoiceData.currency,
            subtotal: invoiceData.amount_due,
            taxAmount: invoiceData.tax_amount,
            discount: invoiceData.discount_amount,
            total: invoiceData.total_amount,
            paymentTerms: invoiceData.payment_terms,
            notes: invoiceData.notes,
            lineItems: invoiceData.line_items
          })
        });

        if (!response.ok) throw new Error('Failed to create invoice');
      } else if (drawerMode === 'edit' && selectedInvoice) {
        const response = await fetch('/api/v1/finance/invoices', {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: selectedInvoice.id,
            invoiceNumber: invoiceData.invoice_number,
            clientId: invoiceData.client_company_id,
            projectId: invoiceData.project_id,
            issueDate: invoiceData.issued_date,
            dueDate: invoiceData.due_at,
            status: invoiceData.status,
            currency: invoiceData.currency,
            subtotal: invoiceData.amount_due,
            taxAmount: invoiceData.tax_amount,
            discount: invoiceData.discount_amount,
            total: invoiceData.total_amount,
            paymentTerms: invoiceData.payment_terms,
            notes: invoiceData.notes,
            lineItems: invoiceData.line_items
          })
        });

        if (!response.ok) throw new Error('Failed to update invoice');
      }

      setDrawerOpen(false);
      await loadInvoices();
    } catch (error) {
      console.error('Error saving invoice:', error);
    }
  };

  const handleSendInvoice = async (invoiceId: string) => {
    try {
      const response = await fetch('/api/v1/finance/invoices', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: invoiceId,
          status: 'sent'
        })
      });

      if (!response.ok) throw new Error('Failed to send invoice');
      await loadInvoices();
    } catch (error) {
      console.error('Error sending invoice:', error);
    }
  };

  const handleMarkPaid = async (invoiceId: string) => {
    try {
      const response = await fetch('/api/v1/finance/invoices', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: invoiceId,
          status: 'paid',
          paidDate: new Date().toISOString()
        })
      });

      if (!response.ok) throw new Error('Failed to mark invoice as paid');
      await loadInvoices();
    } catch (error) {
      console.error('Error marking invoice as paid:', error);
    }
  };

  const filteredInvoices = invoices.filter(invoice => {
    const matchesSearch = searchTerm === '' || 
      invoice.invoice_number.toLowerCase().includes(searchTerm.toLowerCase()) ||
      invoice.client_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      invoice.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = filterStatus === 'all' || invoice.status === filterStatus;
    return matchesSearch && matchesStatus;
  });

  const getInvoiceStatusIcon = (invoice: Invoice) => {
    switch (invoice.status) {
      case 'paid':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'overdue':
        return <AlertTriangle className="h-5 w-5 text-red-500" />;
      case 'sent':
        return <Mail className="h-5 w-5 text-blue-500" />;
      case 'cancelled':
        return <XCircle className="h-5 w-5 text-gray-500" />;
      case 'draft':
        return <FileText className="h-5 w-5 text-yellow-500" />;
      default:
        return <Clock className="h-5 w-5 text-gray-500" />;
    }
  };

  const getInvoiceStatusColor = (invoice: Invoice) => {
    switch (invoice.status) {
      case 'paid':
        return 'bg-green-100 text-green-800';
      case 'overdue':
        return 'bg-red-100 text-red-800';
      case 'sent':
        return 'bg-blue-100 text-blue-800';
      case 'cancelled':
        return 'bg-gray-100 text-gray-800';
      case 'draft':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString();
  };

  // ATLVS DataViews field configuration
  const fields: FieldConfig[] = [
    {
      key: 'invoice_number',
      label: 'Invoice Number',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'description',
      label: 'Description',
      type: 'textarea',
      rows: 3,
      sortable: true,
      filterable: true
    },
    {
      key: 'client_company_id',
      label: 'Client',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'project_id',
      label: 'Project',
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'amount_due',
      label: 'Amount Due',
      type: 'currency',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'total_amount',
      label: 'Total Amount',
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'tax_amount',
      label: 'Tax Amount',
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'discount_amount',
      label: 'Discount Amount',
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'currency',
      label: 'Currency',
      type: 'select',
      options: [
        { value: 'USD', label: 'USD' },
        { value: 'EUR', label: 'EUR' },
        { value: 'GBP', label: 'GBP' }
      ],
      defaultValue: 'USD',
      filterable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'sent', label: 'Sent' },
        { value: 'paid', label: 'Paid' },
        { value: 'overdue', label: 'Overdue' },
        { value: 'cancelled', label: 'Cancelled' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'issued_date',
      label: 'Issue Date',
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'due_at',
      label: 'Due Date',
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'paid_date',
      label: 'Paid Date',
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'payment_terms',
      label: 'Payment Terms',
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'notes',
      label: 'Notes',
      type: 'textarea',
      rows: 3,
      sortable: false,
      filterable: true
    }
  ];

  // ATLVS DataViews configuration
  const invoicesConfig: DataViewConfig = {
    id: 'invoices-dataviews',
    name: 'Invoice Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data: filteredInvoices.map(invoice => ({
      ...invoice,
      record_type: 'invoice'
    })),
    pagination: {
      page: 1,
      pageSize: 25,
      total: filteredInvoices.length
    },
    onSearch: (query: string) => {
      setSearchTerm(query);
    },
    onFilter: (filters: unknown) => {
      if (filters.status) setFilterStatus(filters.status);
    },
    onSort: (sorts: unknown) => {
      console.log('Sort:', sorts);
    },
    onRefresh: () => {
      loadInvoices();
    },
    onExport: (data, format) => {
      console.log('Export:', format, data);
    },
    onImport: (data: unknown) => {
      console.log('Import:', data);
    },
    onRowAction: (action: string, record: DataRecord) => {
      const invoice = record as Invoice;
      switch (action) {
        case 'view':
          handleViewInvoice(invoice);
          break;
        case 'edit':
          handleEditInvoice(invoice);
          break;
        case 'delete':
          handleDeleteInvoice(invoice.id);
          break;
        case 'send':
          handleSendInvoice(invoice.id);
          break;
        case 'mark_paid':
          handleMarkPaid(invoice.id);
          break;
      }
    }
  };

  const configWithData = {
    ...invoicesConfig,
    data: filteredInvoices.map(invoice => ({
      ...invoice,
      record_type: 'invoice'
    }))
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <div>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-96 mt-2" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
