'use client';


import { DollarSign, TrendingUp, TrendingDown, Banknote, CreditCard, BarChart3, AlertTriangle, CheckCircle, Clock, Calendar, ArrowUp, ArrowDown, ArrowUpRight, ArrowDownRight, PieChart, Calculator, Target } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Button, Badge, Skeleton } from '@ghxstship/ui';
// Using Lucide React icons as alternative to Heroicons

interface FinanceOverviewClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface FinancialSummary {
  totalRevenue: number;
  totalExpenses: number;
  netIncome: number;
  totalBudget: number;
  budgetUtilization: number;
  pendingInvoices: number;
  overdueInvoices: number;
  cashFlow: number;
  accountsBalance: number;
  currency: string;
}

interface RecentTransaction {
  id: string;
  description: string;
  amount: number;
  kind: 'revenue' | 'expense';
  occurredAt: string;
  accountName: string;
}

interface BudgetAlert {
  id: string;
  budgetName: string;
  spent: number;
  amount: number;
  utilization: number;
  status: 'warning' | 'critical';
}

export default function FinanceOverviewClient({ user, orgId, translations }: FinanceOverviewClientProps) {
  const [loading, setLoading] = useState(true);
  const [summary, setSummary] = useState<FinancialSummary | null>(null);
  const [recentTransactions, setRecentTransactions] = useState<RecentTransaction[]>([]);
  const [budgetAlerts, setBudgetAlerts] = useState<BudgetAlert[]>([]);

  const supabase = createBrowserClient();

  useEffect(() => {
    loadFinanceOverview();
  }, [orgId]);

  const loadFinanceOverview = async () => {
    try {
      setLoading(true);

      // Load financial summary data
      const [
        revenueData,
        expensesData,
        budgetsData,
        invoicesData,
        accountsData,
        transactionsData
      ] = await Promise.all([
        supabase.from('revenue').select('amount, currency').eq('organization_id', orgId).eq('status', 'received'),
        supabase.from('expenses').select('amount, currency').eq('organization_id', orgId).eq('status', 'approved'),
        supabase.from('budgets').select('id, amount, spent, currency, name').eq('organization_id', orgId),
        supabase.from('invoices').select('amount_due, status').eq('organization_id', orgId),
        supabase.from('finance_accounts').select('balance, currency').eq('organization_id', orgId),
        supabase.from('finance_transactions').select('*, finance_accounts(name)').eq('organization_id', orgId).order('occurred_at', { ascending: false }).limit(10)
      ]);

      // Calculate summary metrics
      const totalRevenue = (revenueData.data || []).reduce((sum, item) => sum + Number(item.amount), 0);
      const totalExpenses = (expensesData.data || []).reduce((sum, item) => sum + Number(item.amount), 0);
      const totalBudget = (budgetsData.data || []).reduce((sum, item) => sum + Number(item.amount), 0);
      const budgetSpent = (budgetsData.data || []).reduce((sum, item) => sum + Number(item.spent), 0);
      const accountsBalance = (accountsData.data || []).reduce((sum, item) => sum + Number(item.balance), 0);

      const pendingInvoices = (invoicesData.data || []).filter(inv => inv.status === 'sent').length;
      const overdueInvoices = (invoicesData.data || []).filter(inv => inv.status === 'overdue').length;

      const financialSummary: FinancialSummary = {
        totalRevenue,
        totalExpenses,
        netIncome: totalRevenue - totalExpenses,
        totalBudget,
        budgetUtilization: totalBudget > 0 ? (budgetSpent / totalBudget) * 100 : 0,
        pendingInvoices,
        overdueInvoices,
        cashFlow: totalRevenue - totalExpenses,
        accountsBalance,
        currency: 'USD'
      };

      setSummary(financialSummary);

      // Process recent transactions
      const transactions = (transactionsData.data || []).map(tx => ({
        id: tx.id,
        description: tx.description,
        amount: tx.amount,
        kind: tx.kind,
        occurredAt: tx.occurred_at,
        accountName: tx.finance_accounts?.name || 'Unknown Account'
      }));
      setRecentTransactions(transactions);

      // Generate budget alerts
      const alerts = (budgetsData.data || [])
        .map((budget, index) => {
          const utilization = budget.amount > 0 ? (budget.spent / budget.amount) * 100 : 0;
          return {
            id: budget.id || `budget-${index}`,
            budgetName: budget.name,
            spent: budget.spent,
            amount: budget.amount,
            utilization,
            status: utilization >= 90 ? 'critical' as const : utilization >= 75 ? 'warning' as const : null
          };
        })
        .filter(alert => alert.status !== null) as BudgetAlert[];
      setBudgetAlerts(alerts);

    } catch (error) {
      console.error('Error loading finance overview:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
          {Array.from({ length: 8 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <Skeleton className="h-4 w-20 mb-sm" />
              <Skeleton className="h-8 w-32 mb-xs" />
              <Skeleton className="h-3 w-16" />
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">Finance Overview</h1>
          <p className="text-body-sm color-foreground/70 mt-xs">{translations.subtitle}</p>
        </div>
        <Button onClick={loadFinanceOverview}>
          <BarChart3 className="h-4 w-4 mr-sm" />
          Refresh Data
        </Button>
      </div>

      {/* Financial Metrics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
        {/* Total Revenue */}
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm color-muted">Total Revenue</p>
              <p className="text-heading-3 text-heading-3 color-success">{formatCurrency(summary?.totalRevenue || 0, summary?.currency)}</p>
              <p className="text-body-sm color-success flex items-center mt-xs">
                <TrendingUp className="h-3 w-3 mr-xs" />
                +12.5% from last month
              </p>
            </div>
            <Banknote className="h-8 w-8 color-success" />
          </div>
        </Card>

        {/* Total Expenses */}
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm color-muted">Total Expenses</p>
              <p className="text-heading-3 text-heading-3 color-destructive">{formatCurrency(summary?.totalExpenses || 0, summary?.currency)}</p>
              <p className="text-body-sm color-destructive flex items-center mt-xs">
                <TrendingUp className="h-3 w-3 mr-xs" />
                +8.2% from last month
              </p>
            </div>
            <CreditCard className="h-8 w-8 color-destructive" />
          </div>
        </Card>

        {/* Net Income */}
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Net Income</p>
              <p className={`text-heading-3 text-heading-3 ${(summary?.netIncome || 0) >= 0 ? 'color-success' : 'color-destructive'}`}>
                {formatCurrency(summary?.netIncome || 0, summary?.currency)}
              </p>
              <p className="text-body-sm color-muted flex items-center mt-xs">
                {(summary?.netIncome || 0) >= 0 ? (
                  <ArrowUp className="h-3 w-3 mr-xs color-success" />
                ) : (
                  <ArrowDown className="h-3 w-3 mr-xs color-destructive" />
                )}
                Revenue - Expenses
              </p>
            </div>
            <DollarSign className="h-8 w-8 color-accent" />
          </div>
        </Card>

        {/* Budget Utilization */}
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm color-muted">Budget Utilization</p>
              <p className="text-heading-3 text-heading-3 color-foreground">
                {(summary?.budgetUtilization || 0).toFixed(1)}%
              </p>
              <p className="text-body-sm color-foreground/60 mt-xs">
                of {formatCurrency(summary?.totalBudget || 0, summary?.currency)}
              </p>
            </div>
            <PieChart className="h-8 w-8 color-secondary" />
          </div>
        </Card>

        {/* Accounts Balance */}
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Accounts Balance</p>
              <p className="text-heading-3 text-heading-3 color-foreground">
                {formatCurrency(summary?.accountsBalance || 0, summary?.currency)}
              </p>
              <p className="text-body-sm color-foreground/60 mt-xs">Across all accounts</p>
            </div>
            <Banknote className="h-8 w-8 color-success" />
          </div>
        </Card>

        {/* Pending Invoices */}
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Pending Invoices</p>
              <p className="text-heading-3 text-heading-3 color-accent">{summary?.pendingInvoices || 0}</p>
              <p className="text-body-sm color-muted mt-xs">Awaiting payment</p>
            </div>
            <Clock className="h-5 w-5 color-warning" />
          </div>
        </Card>

        {/* Overdue Invoices */}
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Overdue Invoices</p>
              <p className="text-heading-3 text-heading-3 color-destructive">{summary?.overdueInvoices || 0}</p>
              <p className="text-body-sm color-muted mt-xs">Require attention</p>
            </div>
            <AlertTriangle className="h-5 w-5 color-destructive" />
          </div>
        </Card>

        {/* Cash Flow */}
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Cash Flow</p>
              <p className={`text-heading-3 text-heading-3 ${(summary?.cashFlow || 0) >= 0 ? 'color-success' : 'color-destructive'}`}>
                {formatCurrency(summary?.cashFlow || 0, summary?.currency)}
              </p>
              <p className="text-body-sm color-muted">+12% from last month</p>
            </div>
            <TrendingUp className="h-8 w-8 color-accent" />
          </div>
        </Card>
      </div>

      {/* Budget Alerts */}
      {budgetAlerts.length > 0 && (
        <Card className="p-lg">
          <div className="flex items-center justify-between mb-md">
            <h3 className="text-body text-heading-4 color-foreground">Budget Alerts</h3>
            <Badge variant="secondary">{budgetAlerts.length} alerts</Badge>
          </div>
          <div className="stack-sm">
            {budgetAlerts.map((alert: unknown) => (
              <div key={alert.id} className="flex items-center justify-between p-sm bg-warning/10 border border-warning/20 rounded-lg">
                <div className="flex items-center cluster-sm">
                  <AlertTriangle className={`h-5 w-5 ${alert.status === 'critical' ? 'color-destructive' : 'color-warning'}`} />
                  <div>
                    <h4 className="form-label color-warning">Budget Alert</h4>
                    <p className="text-body-sm color-foreground/70">
                      {formatCurrency(alert.spent)} of {formatCurrency(alert.amount)} spent ({alert.utilization.toFixed(1)}%)
                    </p>
                  </div>
                </div>
                <Badge variant={alert.status === 'critical' ? 'destructive' : 'secondary'}>
                  {alert.status === 'critical' ? 'Over Budget' : 'Near Limit'}
                </Badge>
              </div>
            ))}
          </div>
        </Card>
      )}

      {/* Recent Transactions */}
      <Card className="p-lg">
        <div className="flex items-center justify-between mb-md">
          <h3 className="text-body text-heading-4 color-foreground">Recent Transactions</h3>
          <Button>View All</Button>
        </div>
        <div className="stack-sm">
          {recentTransactions.length > 0 ? (
            recentTransactions.map((transaction: unknown) => (
              <div key={transaction.id} className="flex items-center justify-between p-sm border border-border rounded-lg">
                <div className="flex items-center cluster-sm">
                  {transaction.kind === 'revenue' ? (
                    <ArrowUpRight className="h-4 w-4 color-success" />
                  ) : (
                    <ArrowDownRight className="h-4 w-4 color-destructive" />
                  )}
                  <div>
                    <p className="form-label color-foreground">{transaction.description}</p>
                    <span className="text-body-sm color-warning/70">{transaction.accountName} â€¢ {formatDate(transaction.occurredAt)}</span>
                  </div>
                </div>
                <span className={`text-body-sm form-label ${
                  transaction.kind === 'revenue' ? 'color-success' : 'color-destructive'
                }`}>{transaction.kind === 'revenue' ? '+' : '-'}{formatCurrency(Math.abs(transaction.amount))}</span>
              </div>
            ))
          ) : (
            <div className="bg-warning/10 border border-warning/20 rounded-lg p-md">
              <Calendar className="h-12 w-12 mx-auto mb-md color-muted/50" />
              <p>No recent transactions found</p>
            </div>
          )}
        </div>
      </Card>
    </div>
  );
}

'use client';

import { Plus, TrendingUp, Calendar, DollarSign, Building } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Button,
  Card,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  UnifiedInput,
} from '@ghxstship/ui';
import AppDrawer, { type DrawerTab } from '@/app/_components/shared/AppDrawer';

interface CreateRevenueClientProps {
  user: User;
  orgId: string;
  projectId?: string;
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: (revenue: CreatedRevenue) => void;
}

type RevenueCurrency = 'USD' | 'EUR' | 'GBP' | 'CAD' | 'AUD';

type RevenueSource =
  | 'services'
  | 'products'
  | 'consulting'
  | 'licensing'
  | 'subscriptions'
  | 'events'
  | 'grants'
  | 'partnerships'
  | 'other';

interface RevenueFormData {
  description: string;
  amount: string;
  currency: RevenueCurrency;
  source: RevenueSource;
  projectId: string;
  recognitionDate: string;
  invoiceId: string;
}

interface CreatedRevenue {
  id: string;
  organization_id: string;
  created_at: string;
}

const SUPPORTED_CURRENCIES: ReadonlyArray<{ value: RevenueCurrency; label: string }> = [
  { value: 'USD', label: 'USD - US Dollar' },
  { value: 'EUR', label: 'EUR - Euro' },
  { value: 'GBP', label: 'GBP - British Pound' },
  { value: 'CAD', label: 'CAD - Canadian Dollar' },
  { value: 'AUD', label: 'AUD - Australian Dollar' },
];

const REVENUE_SOURCES: ReadonlyArray<{ value: RevenueSource; label: string }> = [
  { value: 'services', label: 'Professional Services' },
  { value: 'products', label: 'Product Sales' },
  { value: 'consulting', label: 'Consulting' },
  { value: 'licensing', label: 'Licensing' },
  { value: 'subscriptions', label: 'Subscriptions' },
  { value: 'events', label: 'Events & Performances' },
  { value: 'grants', label: 'Grants & Funding' },
  { value: 'partnerships', label: 'Partnerships' },
  { value: 'other', label: 'Other' },
];

const createInitialFormData = (projectId?: string): RevenueFormData => ({
  description: '',
  amount: '',
  currency: 'USD',
  source: 'services',
  projectId: projectId ?? '',
  recognitionDate: new Date().toISOString().split('T')[0],
  invoiceId: '',
});

export default function CreateRevenueClient({ user, orgId, projectId, isOpen, onClose, onSuccess }: CreateRevenueClientProps) {
  const supabase = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState<RevenueFormData>(() => createInitialFormData(projectId));

  useEffect(() => {
    if (!isOpen) {
      setFormData(createInitialFormData(projectId));
    }
  }, [isOpen, projectId]);

  const updateForm = useCallback(<Key extends keyof RevenueFormData>(field: Key, value: RevenueFormData[Key]) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  }, []);

  const handleTextChange = useCallback((event: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    updateForm(name as keyof RevenueFormData, value as RevenueFormData[keyof RevenueFormData]);
  }, [updateForm]);

  const handleSubmit = useCallback(async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    const trimmedDescription = formData.description.trim();
    const parsedAmount = parseFloat(formData.amount.trim());

    if (!trimmedDescription || Number.isNaN(parsedAmount) || parsedAmount <= 0 || loading) {
      return;
    }

    setLoading(true);

    try {
      const revenueId = crypto.randomUUID();

      const { data: revenue, error } = await supabase
        .from('revenue')
        .insert({
          id: revenueId,
          organization_id: orgId,
          project_id: formData.projectId.trim() || null,
          invoice_id: formData.invoiceId.trim() || null,
          description: trimmedDescription,
          amount: parsedAmount,
          currency: formData.currency,
          source: formData.source,
          status: 'projected',
          recognition_date: formData.recognitionDate,
          created_by: user.id,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        })
        .select()
        .single<CreatedRevenue>();

      if (error) throw error;

      await supabase
        .from('activity_logs')
        .insert({
          id: crypto.randomUUID(),
          organization_id: orgId,
          user_id: user.id,
          action: 'revenue.created',
          entity_type: 'revenue',
          entity_id: revenueId,
          metadata: {
            description: trimmedDescription,
            amount: parsedAmount,
          },
          occurred_at: new Date().toISOString(),
        });

      setFormData(createInitialFormData(projectId));
      onSuccess?.(revenue);
      onClose();
    } catch (error) {
      console.error('Error creating revenue:', error);
    } finally {
      setLoading(false);
    }
  }, [formData, loading, onClose, onSuccess, orgId, projectId, supabase, user.id]);

  const currencyOptions = useMemo(() => SUPPORTED_CURRENCIES.map(currency => (
    <SelectItem key={currency.value} value={currency.value}>
      {currency.label}
    </SelectItem>
  )), []);

  const sourceOptions = useMemo(() => REVENUE_SOURCES.map(source => (
    <SelectItem key={source.value} value={source.value}>
      {source.label}
    </SelectItem>
  )), []);

  const parsedAmount = parseFloat(formData.amount.trim());
  const formattedAmount = !Number.isNaN(parsedAmount) && parsedAmount > 0
    ? new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: formData.currency,
      }).format(parsedAmount)
    : '';

  const drawerTabs: DrawerTab[] = useMemo(() => ([
    {
      key: 'details',
      label: 'Details',
      content: (
        <form onSubmit={handleSubmit} className="stack-lg">
          <Card className="p-md bg-success/10 border-success/20">
            <div className="flex items-center cluster-sm">
              <TrendingUp className="h-8 w-8 color-success" />
              <div>
                <h3 className="text-heading-4 color-success-foreground">Revenue Tracking</h3>
                <p className="text-body-sm color-success/80">
                  Record and track revenue streams and income recognition
                </p>
              </div>
            </div>
          </Card>

          <div className="stack-md">
            <div>
              <label htmlFor="description" className="block text-body-sm form-label color-foreground mb-sm">
                Revenue Description *
              </label>
              <UnifiedInput
                id="description"
                name="description"
                value={formData.description}
                onChange={handleTextChange}
                placeholder="e.g., Project Alpha Payment, Consulting Services, Product Sales"
                required
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label htmlFor="amount" className="block text-body-sm form-label color-foreground mb-sm">
                  Amount *
                </label>
                <div className="relative">
                  <DollarSign className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="amount"
                    name="amount"
                    type="number"
                    value={formData.amount}
                    onChange={handleTextChange}
                    placeholder="0.00"
                    className="pl-2xl"
                    min="0"
                    step="0.01"
                    required
                  />
                </div>
                {formattedAmount && (
                  <p className="text-body-sm color-foreground/60 mt-xs">
                    {formattedAmount}
                  </p>
                )}
              </div>

              <div>
                <label htmlFor="currency" className="block text-body-sm form-label color-foreground mb-sm">
                  Currency
                </label>
                <Select
                  value={formData.currency}
                  onValueChange={value => updateForm('currency', value as RevenueCurrency)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {currencyOptions}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label htmlFor="source" className="block text-body-sm form-label color-foreground mb-sm">
                  Revenue Source
                </label>
                <div className="relative">
                  <Building className="pointer-events-none absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <Select
                    value={formData.source}
                    onValueChange={value => updateForm('source', value as RevenueSource)}
                  >
                    <SelectTrigger className="pl-10">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {sourceOptions}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div>
                <label htmlFor="recognitionDate" className="block text-body-sm form-label color-foreground mb-sm">
                  Recognition Date *
                </label>
                <div className="relative">
                  <Calendar className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="recognitionDate"
                    name="recognitionDate"
                    type="date"
                    value={formData.recognitionDate}
                    onChange={handleTextChange}
                    className="pl-2xl"
                    required
                  />
                </div>
              </div>
            </div>

            <div>
              <label htmlFor="invoiceId" className="block text-body-sm form-label color-foreground mb-sm">
                Related Invoice ID
              </label>
              <UnifiedInput
                id="invoiceId"
                name="invoiceId"
                value={formData.invoiceId}
                onChange={handleTextChange}
                placeholder="Optional: Link to existing invoice"
              />
              <p className="text-body-sm color-foreground/60 mt-xs">
                Connect this revenue to an existing invoice for tracking
              </p>
            </div>
          </div>

          {formattedAmount && (
            <Card className="p-md bg-secondary border-border">
              <h4 className="form-label color-foreground mb-sm">Revenue Summary</h4>
              <div className="stack-xs text-body-sm">
                <div className="flex justify-between">
                  <span className="color-foreground/70">Amount:</span>
                  <span className="form-label color-success">{formattedAmount}</span>
                </div>
                <div className="flex justify-between">
                  <span className="color-foreground/70">Source:</span>
                  <span className="form-label capitalize">{formData.source.replace('_', ' ')}</span>
                </div>
                <div className="flex justify-between">
                  <span className="color-foreground/70">Status:</span>
                  <span className="form-label color-accent">Projected</span>
                </div>
                <div className="flex justify-between">
                  <span className="color-foreground/70">Recognition Date:</span>
                  <span className="form-label">{new Date(formData.recognitionDate).toLocaleDateString()}</span>
                </div>
              </div>
            </Card>
          )}

          <div className="flex justify-end cluster-sm pt-lg border-t border-border">
            <Button type="button" variant="ghost" onClick={onClose} disabled={loading}>
              Cancel
            </Button>
            <Button type="submit" disabled={loading || !formData.description.trim() || Number.isNaN(parsedAmount) || parsedAmount <= 0} className="min-w-[120px]">
              {loading ? (
                <div className="flex items-center cluster-sm">
                  <div className="w-4 h-4 border-2 border-background/30 border-t-background rounded-full animate-spin" />
                  <span>Adding...</span>
                </div>
              ) : (
                <div className="flex items-center cluster-sm">
                  <Plus className="h-4 w-4" />
                  <span>Add Revenue</span>
                </div>
              )}
            </Button>
          </div>
        </form>
      ),
    },
  ]), [currencyOptions, formData, formattedAmount, handleSubmit, loading, onClose, parsedAmount, sourceOptions, updateForm]);

  return (
    <AppDrawer
      open={isOpen}
      onClose={onClose}
      title="Add Revenue"
      record={null}
      mode="create"
      fields={[]}
      tabs={drawerTabs}
      loading={loading}
    />
  );
}

'use client';

import { TrendingUp, Plus, Edit, Trash2, DollarSign, Calendar, Target, CheckCircle, Clock, AlertTriangle, BarChart3, Search, Filter, User as UserIcon } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Card, 
  Button, 
  Badge, 
  Skeleton, 
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView,
  DataActions,
  UniversalDrawer,
  type DataRecord,
  type FieldConfig,
  type DataViewConfig
} from '@ghxstship/ui';

interface RevenueClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface Revenue {
  id: string;
  description: string;
  amount: number;
  currency: string;
  source: string;
  status: 'projected' | 'invoiced' | 'received' | 'cancelled';
  recognition_date?: string;
  received_date?: string;
  project_id?: string;
  invoice_id?: string;
  created_by?: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
  // Computed fields
  project_name?: string;
  days_to_recognition?: number;
}

export default function RevenueClient({ user, orgId, translations }: RevenueClientProps) {
  const [loading, setLoading] = useState(true);
  const [revenues, setRevenues] = useState<Revenue[]>([]);
  const [selectedRevenue, setSelectedRevenue] = useState<Revenue | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [filterSource, setFilterSource] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  const supabase = createBrowserClient();

  useEffect(() => {
    loadRevenues();
  }, [orgId]);

  const loadRevenues = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('revenue')
        .select(`
          *,
          projects:project_id(name),
          invoices:invoice_id(invoice_number)
        `)
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Add computed fields
      const enrichedRevenues = (data || []).map(revenue => {
        const today = new Date();
        const recognitionDate = revenue.recognition_date ? new Date(revenue.recognition_date) : null;
        const daysToRecognition = recognitionDate 
          ? Math.ceil((recognitionDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24))
          : 0;

        return {
          ...revenue,
          project_name: revenue.projects?.name || null,
          days_to_recognition: daysToRecognition
        };
      });

      setRevenues(enrichedRevenues);
    } catch (error) {
      console.error('Error loading revenues:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateRevenue = () => {
    setSelectedRevenue(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditRevenue = (revenue: Revenue) => {
    setSelectedRevenue(revenue);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewRevenue = (revenue: Revenue) => {
    setSelectedRevenue(revenue);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleDeleteRevenue = async (revenueId: string) => {
    if (!confirm('Are you sure you want to delete this revenue record? This action cannot be undone.')) return;

    try {
      const response = await fetch('/api/v1/finance/revenue', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: revenueId })
      });

      if (!response.ok) throw new Error('Failed to delete revenue');
      await loadRevenues();
    } catch (error) {
      console.error('Error deleting revenue:', error);
    }
  };

  const handleSaveRevenue = async (revenueData: Partial<Revenue>) => {
    try {
      if (drawerMode === 'create') {
        const response = await fetch('/api/v1/finance/revenue', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            description: revenueData.description,
            amount: revenueData.amount,
            currency: revenueData.currency,
            source: revenueData.source,
            status: revenueData.status,
            recognitionDate: revenueData.recognition_date,
            receivedDate: revenueData.received_date,
            projectId: revenueData.project_id,
            invoiceId: revenueData.invoice_id
          })
        });

        if (!response.ok) throw new Error('Failed to create revenue');
      } else if (drawerMode === 'edit' && selectedRevenue) {
        const response = await fetch('/api/v1/finance/revenue', {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: selectedRevenue.id,
            description: revenueData.description,
            amount: revenueData.amount,
            currency: revenueData.currency,
            source: revenueData.source,
            status: revenueData.status,
            recognitionDate: revenueData.recognition_date,
            receivedDate: revenueData.received_date,
            projectId: revenueData.project_id,
            invoiceId: revenueData.invoice_id
          })
        });

        if (!response.ok) throw new Error('Failed to update revenue');
      }

      setDrawerOpen(false);
      await loadRevenues();
    } catch (error) {
      console.error('Error saving revenue:', error);
    }
  };

  const handleRecognizeRevenue = async (revenueId: string) => {
    try {
      const response = await fetch('/api/v1/finance/revenue', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: revenueId,
          status: 'received',
          receivedDate: new Date().toISOString()
        })
      });

      if (!response.ok) throw new Error('Failed to recognize revenue');
      await loadRevenues();
    } catch (error) {
      console.error('Error recognizing revenue:', error);
    }
  };

  const filteredRevenues = revenues.filter(revenue => {
    const matchesSearch = searchTerm === '' || 
      revenue.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
      revenue.source.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = filterStatus === 'all' || revenue.status === filterStatus;
    const matchesSource = filterSource === 'all' || revenue.source === filterSource;
    return matchesSearch && matchesStatus && matchesSource;
  });

  const getRevenueStatusIcon = (revenue: Revenue) => {
    switch (revenue.status) {
      case 'received':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'invoiced':
        return <BarChart3 className="h-5 w-5 text-blue-500" />;
      case 'projected':
        return <Target className="h-5 w-5 text-yellow-500" />;
      case 'cancelled':
        return <AlertTriangle className="h-5 w-5 text-red-500" />;
      default:
        return <Clock className="h-5 w-5 text-gray-500" />;
    }
  };

  const getRevenueStatusColor = (revenue: Revenue) => {
    switch (revenue.status) {
      case 'received':
        return 'bg-green-100 text-green-800';
      case 'invoiced':
        return 'bg-blue-100 text-blue-800';
      case 'projected':
        return 'bg-yellow-100 text-yellow-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString();
  };

  // ATLVS DataViews field configuration
  const fields: FieldConfig[] = [
    {
      key: 'description',
      label: 'Description',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'amount',
      label: 'Amount',
      type: 'currency',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'currency',
      label: 'Currency',
      type: 'select',
      options: [
        { value: 'USD', label: 'USD' },
        { value: 'EUR', label: 'EUR' },
        { value: 'GBP', label: 'GBP' }
      ],
      defaultValue: 'USD',
      filterable: true
    },
    {
      key: 'source',
      label: 'Source',
      type: 'select',
      options: [
        { value: 'services', label: 'Services' },
        { value: 'products', label: 'Products' },
        { value: 'subscriptions', label: 'Subscriptions' },
        { value: 'licensing', label: 'Licensing' },
        { value: 'consulting', label: 'Consulting' },
        { value: 'other', label: 'Other' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'projected', label: 'Projected' },
        { value: 'invoiced', label: 'Invoiced' },
        { value: 'received', label: 'Received' },
        { value: 'cancelled', label: 'Cancelled' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'recognition_date',
      label: 'Recognition Date',
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'received_date',
      label: 'Received Date',
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'project_id',
      label: 'Project',
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'invoice_id',
      label: 'Invoice',
      type: 'text',
      sortable: true,
      filterable: true
    }
  ];

  // ATLVS DataViews configuration
  const revenuesConfig: DataViewConfig = {
    id: 'revenues-dataviews',
    name: 'Revenue Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data: filteredRevenues.map(revenue => ({
      ...revenue,
      record_type: 'revenue'
    })),
    pagination: {
      page: 1,
      pageSize: 25,
      total: filteredRevenues.length
    },
    onSearch: (query: string) => {
      setSearchTerm(query);
    },
    onFilter: (filters: unknown) => {
      if (filters.status) setFilterStatus(filters.status);
      if (filters.source) setFilterSource(filters.source);
    },
    onSort: (sorts: unknown) => {
      console.log('Sort:', sorts);
    },
    onRefresh: () => {
      loadRevenues();
    },
    onExport: (data, format) => {
      console.log('Export:', format, data);
    },
    onImport: (data: unknown) => {
      console.log('Import:', data);
    },
    onRowAction: (action: string, record: DataRecord) => {
      const revenue = record as Revenue;
      switch (action) {
        case 'view':
          handleViewRevenue(revenue);
          break;
        case 'edit':
          handleEditRevenue(revenue);
          break;
        case 'delete':
          handleDeleteRevenue(revenue.id);
          break;
        case 'recognize':
          handleRecognizeRevenue(revenue.id);
          break;
      }
    }
  };

  const configWithData = {
    ...revenuesConfig,
    data: filteredRevenues.map(revenue => ({
      ...revenue,
      record_type: 'revenue'
    }))
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <div>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-96 mt-2" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <Skeleton key={i} className="h-32" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Revenue Management</h1>
          <p className="text-gray-600">Track and manage your revenue streams</p>
        </div>
        <Button onClick={handleCreateRevenue} className="flex items-center gap-2">
          <Plus className="h-4 w-4" />
          New Revenue
        </Button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Revenue</p>
              <p className="text-2xl font-bold text-gray-900">{revenues.length}</p>
            </div>
            <TrendingUp className="h-8 w-8 text-blue-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Amount</p>
              <p className="text-2xl font-bold text-gray-900">
                {formatCurrency(revenues.reduce((sum, r) => sum + r.amount, 0))}
              </p>
            </div>
            <DollarSign className="h-8 w-8 text-green-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Received</p>
              <p className="text-2xl font-bold text-gray-900">
                {revenues.filter(r => r.status === 'received').length}
              </p>
            </div>
            <CheckCircle className="h-8 w-8 text-green-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Projected</p>
              <p className="text-2xl font-bold text-gray-900">
                {revenues.filter(r => r.status === 'projected').length}
              </p>
            </div>
            <Target className="h-8 w-8 text-yellow-500" />
          </div>
        </Card>
      </div>

      {/* Filters */}
      <div className="flex gap-4 mb-6">
        <div className="flex-1">
          <UnifiedInput
            placeholder="Search revenues..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            leftIcon={<Search className="h-4 w-4" />}
          />
        </div>
        <Select value={filterStatus} onValueChange={setFilterStatus}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Statuses</SelectItem>
            <SelectItem value="projected">Projected</SelectItem>
            <SelectItem value="invoiced">Invoiced</SelectItem>
            <SelectItem value="received">Received</SelectItem>
            <SelectItem value="cancelled">Cancelled</SelectItem>
          </SelectContent>
        </Select>
        <Select value={filterSource} onValueChange={setFilterSource}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by source" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Sources</SelectItem>
            <SelectItem value="services">Services</SelectItem>
            <SelectItem value="products">Products</SelectItem>
            <SelectItem value="subscriptions">Subscriptions</SelectItem>
            <SelectItem value="licensing">Licensing</SelectItem>
            <SelectItem value="consulting">Consulting</SelectItem>
            <SelectItem value="other">Other</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* ATLVS DataViews */}
      <div className="flex-1">
        <DataViewProvider config={configWithData}>
          <StateManagerProvider>
            <div className="flex-1 space-y-4">
              {/* View Switcher and Actions */}
              <div className="flex justify-between items-center">
                <ViewSwitcher />
                <DataActions />
              </div>

              {/* Data Views */}
              <div className="flex-1">
                <DataGrid />
                <KanbanBoard 
                  columns={[
                    { id: 'projected', title: 'Projected' },
                    { id: 'invoiced', title: 'Invoiced' },
                    { id: 'received', title: 'Received' },
                    { id: 'cancelled', title: 'Cancelled' }
                  ]}
                  statusField="status"
                  titleField="description"
                />
                <CalendarView 
                  startDateField="recognition_date"
                  titleField="description"
                />
                <ListView titleField="description" />
              </div>
            </div>
          </StateManagerProvider>
        </DataViewProvider>
      </div>

      {/* Universal Drawer for CRUD operations */}
      <UniversalDrawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={
          drawerMode === 'create' ? 'Create Revenue' :
          drawerMode === 'edit' ? 'Edit Revenue' : 'Revenue Details'
        }
        mode={drawerMode}
        data={selectedRevenue}
        fields={fields}
        onSave={handleSaveRevenue}
        tabs={[
          {
            key: 'details',
            label: 'Revenue Details',
            content: (
              <div className="space-y-4">
                {selectedRevenue && (
                  <>
                    <div className="flex items-center gap-3">
                      {getRevenueStatusIcon(selectedRevenue)}
                      <div>
                        <h3 className="font-semibold">{selectedRevenue.description}</h3>
                        <p className="text-sm text-gray-600">{selectedRevenue.source}</p>
                      </div>
                      <Badge className={getRevenueStatusColor(selectedRevenue)}>
                        {selectedRevenue.status}
                      </Badge>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium text-gray-700">Amount</label>
                        <p className="text-lg font-semibold">{formatCurrency(selectedRevenue.amount)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Source</label>
                        <p className="text-sm">{selectedRevenue.source}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Recognition Date</label>
                        <p className="text-sm">{formatDate(selectedRevenue.recognition_date)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Received Date</label>
                        <p className="text-sm">{formatDate(selectedRevenue.received_date)}</p>
                      </div>
                    </div>
                    {selectedRevenue.project_name && (
                      <div>
                        <label className="text-sm font-medium text-gray-700">Project</label>
                        <p className="text-sm text-gray-600">{selectedRevenue.project_name}</p>
                      </div>
                    )}
                    {selectedRevenue.status === 'invoiced' && (
                      <div className="flex gap-2">
                        <Button 
                          onClick={() => handleRecognizeRevenue(selectedRevenue.id)}
                          className="flex items-center gap-2"
                          size="sm"
                        >
                          <CheckCircle className="h-4 w-4" />
                          Mark as Received
                        </Button>
                      </div>
                    )}
                  </>
                )}
              </div>
            )
          }
        ]}
      />
    </div>
  );
}

'use client';

import { Plus, ArrowUpDown, Calendar, DollarSign, Building } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Button,
  Card,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  UnifiedInput,
} from '@ghxstship/ui';
import AppDrawer, { type DrawerTab } from '@/app/_components/shared/AppDrawer';

interface CreateTransactionClientProps {
  user: User;
  orgId: string;
  accountId?: string;
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: (transaction: CreatedTransaction) => void;
}

type TransactionKind = 'revenue' | 'expense';

type TransactionCurrency = 'USD' | 'EUR' | 'GBP' | 'CAD' | 'AUD';

interface TransactionFormData {
  description: string;
  amount: string;
  currency: TransactionCurrency;
  kind: TransactionKind;
  accountId: string;
  projectId: string;
  invoiceId: string;
  referenceNumber: string;
  occurredAt: string;
}

interface FinanceAccount {
  id: string;
  name: string;
  kind: string;
  currency: string;
}

interface CreatedTransaction {
  id: string;
  organization_id: string;
  created_at: string;
}

const SUPPORTED_CURRENCIES: ReadonlyArray<{ value: TransactionCurrency; label: string }> = [
  { value: 'USD', label: 'USD - US Dollar' },
  { value: 'EUR', label: 'EUR - Euro' },
  { value: 'GBP', label: 'GBP - British Pound' },
  { value: 'CAD', label: 'CAD - Canadian Dollar' },
  { value: 'AUD', label: 'AUD - Australian Dollar' },
];

const TRANSACTION_KINDS: ReadonlyArray<{ value: TransactionKind; label: string }> = [
  { value: 'revenue', label: 'Revenue (Money In)' },
  { value: 'expense', label: 'Expense (Money Out)' },
];

const createInitialFormData = (accountId?: string): TransactionFormData => ({
  description: '',
  amount: '',
  currency: 'USD',
  kind: 'expense',
  accountId: accountId ?? '',
  projectId: '',
  invoiceId: '',
  referenceNumber: '',
  occurredAt: new Date().toISOString().slice(0, 16),
});

export default function CreateTransactionClient({ user, orgId, accountId, isOpen, onClose, onSuccess }: CreateTransactionClientProps) {
  const supabase = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [accounts, setAccounts] = useState<FinanceAccount[]>([]);
  const [formData, setFormData] = useState<TransactionFormData>(() => createInitialFormData(accountId));

  const loadAccounts = useCallback(async () => {
    try {
      const { data, error } = await supabase
        .from('finance_accounts')
        .select('id, name, kind, currency')
        .eq('organization_id', orgId)
        .eq('status', 'active')
        .order('name');

      if (error) throw error;

      setAccounts(data ?? []);
    } catch (error) {
      console.error('Error loading accounts:', error);
    }
  }, [orgId, supabase]);

  useEffect(() => {
    if (isOpen) {
      loadAccounts();
    }
  }, [isOpen, loadAccounts]);

  useEffect(() => {
    if (!isOpen) {
      setFormData(createInitialFormData(accountId));
    }
  }, [accountId, isOpen]);

  const updateForm = useCallback(<Key extends keyof TransactionFormData>(field: Key, value: TransactionFormData[Key]) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  }, []);

  const handleTextChange = useCallback((event: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    updateForm(name as keyof TransactionFormData, value as TransactionFormData[keyof TransactionFormData]);
  }, [updateForm]);

  const handleSubmit = useCallback(async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    const trimmedDescription = formData.description.trim();
    const parsedAmount = parseFloat(formData.amount.trim());

    if (!trimmedDescription || Number.isNaN(parsedAmount) || parsedAmount === 0 || !formData.accountId || loading) {
      return;
    }

    setLoading(true);

    try {
      const transactionId = crypto.randomUUID();
      const signedAmount = formData.kind === 'expense' ? -Math.abs(parsedAmount) : Math.abs(parsedAmount);

      const { data: transaction, error } = await supabase
        .from('finance_transactions')
        .insert({
          id: transactionId,
          organization_id: orgId,
          account_id: formData.accountId,
          project_id: formData.projectId.trim() || null,
          invoice_id: formData.invoiceId.trim() || null,
          description: trimmedDescription,
          amount: signedAmount,
          currency: formData.currency,
          kind: formData.kind,
          reference_number: formData.referenceNumber.trim() || null,
          status: 'completed',
          occurred_at: formData.occurredAt,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        })
        .select()
        .single<CreatedTransaction>();

      if (error) throw error;

      await supabase.rpc('update_account_balance', {
        account_id: formData.accountId,
        amount_change: signedAmount,
      });

      await supabase
        .from('activity_logs')
        .insert({
          id: crypto.randomUUID(),
          organization_id: orgId,
          user_id: user.id,
          action: 'transaction.created',
          entity_type: 'transaction',
          entity_id: transactionId,
          metadata: {
            description: trimmedDescription,
            amount: parsedAmount,
            kind: formData.kind,
          },
          occurred_at: new Date().toISOString(),
        });

      setFormData(createInitialFormData(accountId));
      onSuccess?.(transaction);
      onClose();
    } catch (error) {
      console.error('Error creating transaction:', error);
    } finally {
      setLoading(false);
    }
  }, [accountId, formData, loading, onClose, onSuccess, orgId, supabase, user.id]);

  const currencyOptions = useMemo(() => SUPPORTED_CURRENCIES.map(currency => (
    <SelectItem key={currency.value} value={currency.value}>
      {currency.label}
    </SelectItem>
  )), []);

  const kindOptions = useMemo(() => TRANSACTION_KINDS.map(kind => (
    <SelectItem key={kind.value} value={kind.value}>
      {kind.label}
    </SelectItem>
  )), []);

  const accountOptions = useMemo(() => accounts.map(account => (
    <SelectItem key={account.id} value={account.id}>
      {account.name} ({account.kind}) Â· {account.currency}
    </SelectItem>
  )), [accounts]);

  const parsedAmount = parseFloat(formData.amount.trim());
  const formattedAmount = !Number.isNaN(parsedAmount) && parsedAmount !== 0
    ? new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: formData.currency,
      }).format(Math.abs(parsedAmount))
    : '';

  const selectedAccount = useMemo(() => accounts.find(account => account.id === formData.accountId), [accounts, formData.accountId]);

  const drawerTabs: DrawerTab[] = useMemo(() => ([
    {
      key: 'details',
      label: 'Details',
      content: (
        <form onSubmit={handleSubmit} className="stack-lg">
          <Card className="p-md bg-accent/10 border-primary/20">
            <div className="flex items-center cluster-sm">
              <ArrowUpDown className="h-8 w-8 color-accent" />
              <div>
                <h3 className="text-heading-4 color-accent-foreground">Transaction Entry</h3>
                <p className="text-body-sm color-accent/80">
                  Record financial transactions and update account balances
                </p>
              </div>
            </div>
          </Card>

          <div className="stack-md">
            <div>
              <label htmlFor="description" className="block text-body-sm form-label color-foreground mb-sm">
                Transaction Description *
              </label>
              <UnifiedInput
                id="description"
                name="description"
                value={formData.description}
                onChange={handleTextChange}
                placeholder="e.g., Payment received from Client ABC, Office rent payment"
                required
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
              <div>
                <label htmlFor="kind" className="block text-body-sm form-label color-foreground mb-sm">
                  Type *
                </label>
                <Select
                  value={formData.kind}
                  onValueChange={value => updateForm('kind', value as TransactionKind)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {kindOptions}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label htmlFor="amount" className="block text-body-sm form-label color-foreground mb-sm">
                  Amount *
                </label>
                <div className="relative">
                  <DollarSign className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="amount"
                    name="amount"
                    type="number"
                    value={formData.amount}
                    onChange={handleTextChange}
                    placeholder="0.00"
                    className="pl-2xl"
                    min="0"
                    step="0.01"
                    required
                  />
                </div>
              </div>

              <div>
                <label htmlFor="currency" className="block text-body-sm form-label color-foreground mb-sm">
                  Currency
                </label>
                <Select
                  value={formData.currency}
                  onValueChange={value => updateForm('currency', value as TransactionCurrency)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {currencyOptions}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <label htmlFor="accountId" className="block text-body-sm form-label color-foreground mb-sm">
                Account *
              </label>
              <div className="relative">
                <Building className="pointer-events-none absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                <Select
                  value={formData.accountId}
                  onValueChange={value => updateForm('accountId', value)}
                >
                  <SelectTrigger className="pl-10">
                    <SelectValue placeholder="Select an account" />
                  </SelectTrigger>
                  <SelectContent>
                    {accountOptions}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label htmlFor="referenceNumber" className="block text-body-sm form-label color-foreground mb-sm">
                  Reference Number
                </label>
                <UnifiedInput
                  id="referenceNumber"
                  name="referenceNumber"
                  value={formData.referenceNumber}
                  onChange={handleTextChange}
                  placeholder="e.g., Check #1234, Wire #ABC123"
                />
              </div>

              <div>
                <label htmlFor="occurredAt" className="block text-body-sm form-label color-foreground mb-sm">
                  Transaction Date & Time *
                </label>
                <div className="relative">
                  <Calendar className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 color-foreground/50" />
                  <UnifiedInput
                    id="occurredAt"
                    name="occurredAt"
                    type="datetime-local"
                    value={formData.occurredAt}
                    onChange={handleTextChange}
                    className="pl-2xl"
                    max={new Date().toISOString().slice(0, 16)}
                    required
                  />
                </div>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label htmlFor="projectId" className="block text-body-sm form-label color-foreground mb-sm">
                  Project ID
                </label>
                <UnifiedInput
                  id="projectId"
                  name="projectId"
                  value={formData.projectId}
                  onChange={handleTextChange}
                  placeholder="Optional: Link to project"
                />
              </div>

              <div>
                <label htmlFor="invoiceId" className="block text-body-sm form-label color-foreground mb-sm">
                  Invoice ID
                </label>
                <UnifiedInput
                  id="invoiceId"
                  name="invoiceId"
                  value={formData.invoiceId}
                  onChange={handleTextChange}
                  placeholder="Optional: Link to invoice"
                />
              </div>
            </div>
          </div>

          {formattedAmount && formData.accountId && selectedAccount && (
            <Card className="p-md bg-secondary border-border">
              <h4 className="form-label color-foreground mb-sm">Transaction Summary</h4>
              <div className="stack-xs text-body-sm">
                <div className="flex justify-between">
                  <span className="color-foreground/70">Type:</span>
                  <span className={`form-label capitalize ${formData.kind === 'revenue' ? 'color-success' : 'color-destructive'}`}>
                    {formData.kind} ({formData.kind === 'revenue' ? 'Money In' : 'Money Out'})
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="color-foreground/70">Amount:</span>
                  <span className={`form-label ${formData.kind === 'revenue' ? 'color-success' : 'color-destructive'}`}>
                    {formData.kind === 'revenue' ? '+' : '-'}{formattedAmount}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="color-foreground/70">Account:</span>
                  <span className="form-label">
                    {selectedAccount.name}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="color-foreground/70">Date:</span>
                  <span className="form-label">{new Date(formData.occurredAt).toLocaleString()}</span>
                </div>
              </div>
            </Card>
          )}

          <div className="flex justify-end cluster-sm pt-lg border-t border-border">
            <Button type="button" variant="ghost" onClick={onClose} disabled={loading}>
              Cancel
            </Button>
            <Button type="submit" disabled={loading || !formData.description.trim() || Number.isNaN(parsedAmount) || parsedAmount === 0 || !formData.accountId} className="min-w-[120px]">
              {loading ? (
                <div className="flex items-center cluster-sm">
                  <div className="w-4 h-4 border-2 border-background/30 border-t-background rounded-full animate-spin" />
                  <span>Adding...</span>
                </div>
              ) : (
                <div className="flex items-center cluster-sm">
                  <Plus className="h-4 w-4" />
                  <span>Add Transaction</span>
                </div>
              )}
            </Button>
          </div>
        </form>
      ),
    },
  ]), [accountOptions, formData, formattedAmount, handleSubmit, kindOptions, loading, onClose, parsedAmount, selectedAccount, updateForm]);

  return (
    <AppDrawer
      open={isOpen}
      onClose={onClose}
      title="Add Transaction"
      record={null}
      mode="create"
      fields={[]}
      tabs={drawerTabs}
      loading={loading}
    />
  );
}

'use client';

import { ArrowUpDown, Plus, Edit, Trash2, ArrowUp, ArrowDown, DollarSign, Calendar, Filter, Search, FileText, CheckCircle, Clock, AlertTriangle, CreditCard, Building } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Card, 
  Button, 
  Badge, 
  Skeleton, 
  UnifiedInput,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView,
  DataActions,
  UniversalDrawer,
  type DataRecord,
  type FieldConfig,
  type DataViewConfig
} from '@ghxstship/ui';

interface TransactionsClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface Transaction {
  id: string;
  description: string;
  amount: number;
  currency: string;
  kind: 'debit' | 'credit';
  status: 'pending' | 'completed' | 'failed' | 'cancelled';
  category: string;
  account_id: string;
  reference_type?: 'invoice' | 'expense' | 'budget' | 'revenue';
  reference_id?: string;
  reference_number?: string;
  occurred_at: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
  // Computed fields
  account_name?: string;
  reference_description?: string;
}

export default function TransactionsClient({ user, orgId, translations }: TransactionsClientProps) {
  const [loading, setLoading] = useState(true);
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [selectedTransaction, setSelectedTransaction] = useState<Transaction | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');
  const [filterKind, setFilterKind] = useState<string>('all');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [filterCategory, setFilterCategory] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  const supabase = createBrowserClient();

  useEffect(() => {
    loadTransactions();
  }, [orgId]);

  const loadTransactions = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('finance_transactions')
        .select(`
          *,
          accounts:account_id(name, account_number)
        `)
        .eq('organization_id', orgId)
        .order('occurred_at', { ascending: false });

      if (error) throw error;

      // Add computed fields
      const enrichedTransactions = (data || []).map(transaction => ({
        ...transaction,
        account_name: transaction.accounts?.name || 'Unknown Account'
      }));

      setTransactions(enrichedTransactions);
    } catch (error) {
      console.error('Error loading transactions:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateTransaction = () => {
    setSelectedTransaction(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditTransaction = (transaction: Transaction) => {
    setSelectedTransaction(transaction);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewTransaction = (transaction: Transaction) => {
    setSelectedTransaction(transaction);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleDeleteTransaction = async (transactionId: string) => {
    if (!confirm('Are you sure you want to delete this transaction? This action cannot be undone.')) return;

    try {
      const response = await fetch('/api/v1/finance/transactions', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: transactionId })
      });

      if (!response.ok) throw new Error('Failed to delete transaction');
      await loadTransactions();
    } catch (error) {
      console.error('Error deleting transaction:', error);
    }
  };

  const handleSaveTransaction = async (transactionData: Partial<Transaction>) => {
    try {
      if (drawerMode === 'create') {
        const response = await fetch('/api/v1/finance/transactions', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            description: transactionData.description,
            amount: transactionData.amount,
            currency: transactionData.currency,
            kind: transactionData.kind,
            status: transactionData.status,
            category: transactionData.category,
            accountId: transactionData.account_id,
            referenceType: transactionData.reference_type,
            referenceId: transactionData.reference_id,
            referenceNumber: transactionData.reference_number,
            occurredAt: transactionData.occurred_at
          })
        });

        if (!response.ok) throw new Error('Failed to create transaction');
      } else if (drawerMode === 'edit' && selectedTransaction) {
        const response = await fetch('/api/v1/finance/transactions', {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: selectedTransaction.id,
            description: transactionData.description,
            amount: transactionData.amount,
            currency: transactionData.currency,
            kind: transactionData.kind,
            status: transactionData.status,
            category: transactionData.category,
            accountId: transactionData.account_id,
            referenceType: transactionData.reference_type,
            referenceId: transactionData.reference_id,
            referenceNumber: transactionData.reference_number,
            occurredAt: transactionData.occurred_at
          })
        });

        if (!response.ok) throw new Error('Failed to update transaction');
      }

      setDrawerOpen(false);
      await loadTransactions();
    } catch (error) {
      console.error('Error saving transaction:', error);
    }
  };

  const handleCompleteTransaction = async (transactionId: string) => {
    try {
      const response = await fetch('/api/v1/finance/transactions', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: transactionId,
          status: 'completed'
        })
      });

      if (!response.ok) throw new Error('Failed to complete transaction');
      await loadTransactions();
    } catch (error) {
      console.error('Error completing transaction:', error);
    }
  };

  const filteredTransactions = transactions.filter(transaction => {
    const matchesSearch = searchTerm === '' || 
      transaction.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
      transaction.reference_number?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesKind = filterKind === 'all' || transaction.kind === filterKind;
    const matchesStatus = filterStatus === 'all' || transaction.status === filterStatus;
    const matchesCategory = filterCategory === 'all' || transaction.category === filterCategory;
    return matchesSearch && matchesKind && matchesStatus && matchesCategory;
  });

  const getTransactionKindIcon = (transaction: Transaction) => {
    return transaction.kind === 'debit' 
      ? <ArrowDown className="h-5 w-5 text-red-500" />
      : <ArrowUp className="h-5 w-5 text-green-500" />;
  };

  const getTransactionStatusIcon = (transaction: Transaction) => {
    switch (transaction.status) {
      case 'completed':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'pending':
        return <Clock className="h-5 w-5 text-yellow-500" />;
      case 'failed':
        return <AlertTriangle className="h-5 w-5 text-red-500" />;
      case 'cancelled':
        return <AlertTriangle className="h-5 w-5 text-gray-500" />;
      default:
        return <Clock className="h-5 w-5 text-gray-500" />;
    }
  };

  const getTransactionStatusColor = (transaction: Transaction) => {
    switch (transaction.status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      case 'cancelled':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getTransactionKindColor = (transaction: Transaction) => {
    return transaction.kind === 'debit'
      ? 'bg-red-100 text-red-800'
      : 'bg-green-100 text-green-800';
  };

  const formatCurrency = (amount: number, currency = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  const formatDateTime = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  // ATLVS DataViews field configuration
  const fields: FieldConfig[] = [
    {
      key: 'description',
      label: 'Description',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'amount',
      label: 'Amount',
      type: 'currency',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'currency',
      label: 'Currency',
      type: 'select',
      options: [
        { value: 'USD', label: 'USD' },
        { value: 'EUR', label: 'EUR' },
        { value: 'GBP', label: 'GBP' }
      ],
      defaultValue: 'USD',
      filterable: true
    },
    {
      key: 'kind',
      label: 'Type',
      type: 'select',
      options: [
        { value: 'debit', label: 'Debit' },
        { value: 'credit', label: 'Credit' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'pending', label: 'Pending' },
        { value: 'completed', label: 'Completed' },
        { value: 'failed', label: 'Failed' },
        { value: 'cancelled', label: 'Cancelled' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'category',
      label: 'Category',
      type: 'select',
      options: [
        { value: 'revenue', label: 'Revenue' },
        { value: 'expense', label: 'Expense' },
        { value: 'transfer', label: 'Transfer' },
        { value: 'adjustment', label: 'Adjustment' },
        { value: 'fee', label: 'Fee' },
        { value: 'other', label: 'Other' }
      ],
      required: true,
      filterable: true,
      sortable: true
    },
    {
      key: 'account_id',
      label: 'Account',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'reference_type',
      label: 'Reference Type',
      type: 'select',
      options: [
        { value: 'invoice', label: 'Invoice' },
        { value: 'expense', label: 'Expense' },
        { value: 'budget', label: 'Budget' },
        { value: 'revenue', label: 'Revenue' }
      ],
      sortable: true,
      filterable: true
    },
    {
      key: 'reference_number',
      label: 'Reference Number',
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'occurred_at',
      label: 'Transaction Date',
      type: 'datetime-local',
      required: true,
      sortable: true,
      filterable: true
    }
  ];

  // ATLVS DataViews configuration
  const transactionsConfig: DataViewConfig = {
    id: 'transactions-dataviews',
    name: 'Transaction Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data: filteredTransactions.map(transaction => ({
      ...transaction,
      record_type: 'transaction'
    })),
    pagination: {
      page: 1,
      pageSize: 25,
      total: filteredTransactions.length
    },
    onSearch: (query: string) => {
      setSearchTerm(query);
    },
    onFilter: (filters: unknown) => {
      if (filters.kind) setFilterKind(filters.kind);
      if (filters.status) setFilterStatus(filters.status);
      if (filters.category) setFilterCategory(filters.category);
    },
    onSort: (sorts: unknown) => {
      console.log('Sort:', sorts);
    },
    onRefresh: () => {
      loadTransactions();
    },
    onExport: (data, format) => {
      console.log('Export:', format, data);
    },
    onImport: (data: unknown) => {
      console.log('Import:', data);
    },
    onRowAction: (action: string, record: DataRecord) => {
      const transaction = record as Transaction;
      switch (action) {
        case 'view':
          handleViewTransaction(transaction);
          break;
        case 'edit':
          handleEditTransaction(transaction);
          break;
        case 'delete':
          handleDeleteTransaction(transaction.id);
          break;
        case 'complete':
          handleCompleteTransaction(transaction.id);
          break;
      }
    }
  };

  const configWithData = {
    ...transactionsConfig,
    data: filteredTransactions.map(transaction => ({
      ...transaction,
      record_type: 'transaction'
    }))
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <div>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-96 mt-2" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <Skeleton key={i} className="h-32" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Transaction Management</h1>
          <p className="text-gray-600">Track and manage your financial transactions</p>
        </div>
        <Button onClick={handleCreateTransaction} className="flex items-center gap-2">
          <Plus className="h-4 w-4" />
          New Transaction
        </Button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Transactions</p>
              <p className="text-2xl font-bold text-gray-900">{transactions.length}</p>
            </div>
            <ArrowUpDown className="h-8 w-8 text-blue-500" />
          </div>
        </Card>
        <Card className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Debits</p>
              <p className="text-2xl font-bold text-gray-900">
