          <SelectContent>
            <SelectItem value="all">All Categories</SelectItem>
            <SelectItem value="revenue">Revenue</SelectItem>
            <SelectItem value="expense">Expense</SelectItem>
            <SelectItem value="transfer">Transfer</SelectItem>
            <SelectItem value="adjustment">Adjustment</SelectItem>
            <SelectItem value="fee">Fee</SelectItem>
            <SelectItem value="other">Other</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* ATLVS DataViews */}
      <div className="flex-1">
        <DataViewProvider config={configWithData}>
          <StateManagerProvider>
            <div className="flex-1 space-y-4">
              {/* View Switcher and Actions */}
              <div className="flex justify-between items-center">
                <ViewSwitcher />
                <DataActions />
              </div>

              {/* Data Views */}
              <div className="flex-1">
                <DataGrid />
                <KanbanBoard 
                  columns={[
                    { id: 'pending', title: 'Pending' },
                    { id: 'completed', title: 'Completed' },
                    { id: 'failed', title: 'Failed' },
                    { id: 'cancelled', title: 'Cancelled' }
                  ]}
                  statusField="status"
                  titleField="description"
                />
                <CalendarView 
                  startDateField="occurred_at"
                  titleField="description"
                />
                <ListView titleField="description" />
              </div>
            </div>
          </StateManagerProvider>
        </DataViewProvider>
      </div>

      {/* Universal Drawer for CRUD operations */}
      <UniversalDrawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={
          drawerMode === 'create' ? 'Create Transaction' :
          drawerMode === 'edit' ? 'Edit Transaction' : 'Transaction Details'
        }
        mode={drawerMode}
        data={selectedTransaction}
        fields={fields}
        onSave={handleSaveTransaction}
        tabs={[
          {
            key: 'details',
            label: 'Transaction Details',
            content: (
              <div className="space-y-4">
                {selectedTransaction && (
                  <>
                    <div className="flex items-center gap-3">
                      {getTransactionKindIcon(selectedTransaction)}
                      {getTransactionStatusIcon(selectedTransaction)}
                      <div>
                        <h3 className="font-semibold">{selectedTransaction.description}</h3>
                        <p className="text-sm text-gray-600">{selectedTransaction.category}</p>
                      </div>
                      <Badge className={getTransactionKindColor(selectedTransaction)}>
                        {selectedTransaction.kind}
                      </Badge>
                      <Badge className={getTransactionStatusColor(selectedTransaction)}>
                        {selectedTransaction.status}
                      </Badge>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium text-gray-700">Amount</label>
                        <p className="text-lg font-semibold">{formatCurrency(selectedTransaction.amount)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Account</label>
                        <p className="text-sm">{selectedTransaction.account_name}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Transaction Date</label>
                        <p className="text-sm">{formatDateTime(selectedTransaction.occurred_at)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-700">Reference</label>
                        <p className="text-sm">
                          {selectedTransaction.reference_number || selectedTransaction.reference_type || 'N/A'}
                        </p>
                      </div>
                    </div>
                    {selectedTransaction.status === 'pending' && (
                      <div className="flex gap-2">
                        <Button 
                          onClick={() => handleCompleteTransaction(selectedTransaction.id)}
                          className="flex items-center gap-2"
                          size="sm"
                        >
                          <CheckCircle className="h-4 w-4" />
                          Mark as Completed
                        </Button>
                      </div>
                    )}
                  </>
                )}
              </div>
            )
          }
        ]}
      />
    </div>
  );
}

