      label: 'Notes',
      type: 'textarea' as const,
      required: false,
      sortable: false,
      filterable: true
    },
    {
      key: 'job_id',
      label: 'Job ID',
      type: 'text' as const,
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'assignee_user_id',
      label: 'Assignee ID',
      type: 'text' as const,
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'job_status',
      label: 'Job Status',
      type: 'text' as const,
      required: false,
      sortable: true,
      filterable: true
    }
  ];

  // Enhanced Supabase data loading function with enriched data
  const loadAssignmentsData = useCallback(async (activeFilters?: Record<string, string>): Promise<DataRecord[]> => {
    try {
      const params = new URLSearchParams();
      if (activeFilters?.job_id) params.append('job_id', activeFilters.job_id);
      if (activeFilters?.assignee_user_id) params.append('assignee_user_id', activeFilters.assignee_user_id);
      if (activeFilters?.search) params.append('search', activeFilters.search);
      if (activeFilters?.job_status) params.append('job_status', activeFilters.job_status);

      const responseResult = await tryCatch(async () =>
        fetch(`/api/v1/jobs/assignments?${params.toString()}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' }
        })
      );

      if (!responseResult.success) {
        reportError(responseResult.error);
        throw new Error(responseResult.error.message);
      }

      const response = responseResult.data;
      if (!response.ok) {
        throw new Error(`Failed to fetch assignments: ${response.statusText}`);
      }

      const data: AssignmentsResponse = await response.json();

      // Enrich assignments data with job and user information
      const enrichedAssignments = (data.assignments ?? []).map((assignment: unknown) => ({
        ...assignment,
        job_title: assignment.jobs?.title || 'Untitled Job',
        job_status: assignment.jobs?.status || 'unknown',
        assignee_name: assignment.users?.email?.split('@')[0] || 'Unknown User',
        assignee_email: assignment.users?.email || '',
        assignee_avatar: assignment.users?.avatar_url || null
      }));

      return enrichedAssignments;
    } catch (error) {
      console.error('Error loading assignments:', error);
      return [];
    }
  }, []);

  // Statistics calculation
  const assignmentStats = useMemo(() => {
    const total = assignments.length;
    const byJobStatus = assignments.reduce((acc: Record<string, number>, assignment: unknown) => {
      const status = assignment.job_status || 'unknown';
      acc[status] = (acc[status] || 0) + 1;
      return acc;
    }, {});

    const recentAssignments = assignments.filter((assignment: unknown) => {
      const assignedDate = new Date(assignment.assigned_at);
      const weekAgo = new Date();
      weekAgo.setDate(weekAgo.getDate() - 7);
      return assignedDate >= weekAgo;
    }).length;

    return {
      total,
      byJobStatus,
      recentAssignments,
      activeJobs: byJobStatus.in_progress || 0,
      completedJobs: byJobStatus.done || 0,
      pendingJobs: byJobStatus.open || 0
    };
  }, [assignments]);

  // Load data on mount and when filters change
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const data = await loadAssignmentsData(filters);
      setAssignments(data);
      setLoading(false);
    };
    void fetchData();
  }, [filters, loadAssignmentsData]);

  // Drawer management
  const closeDrawer = useCallback(() => {
    setSelectedAssignment(null);
    setCreateDrawerOpen(false);
    setEditDrawerOpen(false);
  }, []);

  const openViewDrawer = useCallback((record: DataRecord) => {
    setSelectedAssignment(record);
    setCreateDrawerOpen(false);
    setEditDrawerOpen(false);
  }, []);

  const openCreateDrawer = useCallback(() => {
    setSelectedAssignment(null);
    setCreateDrawerOpen(true);
    setEditDrawerOpen(false);
  }, []);

  const openEditDrawer = useCallback((record: DataRecord) => {
    setSelectedAssignment(record);
    setCreateDrawerOpen(false);
    setEditDrawerOpen(true);
  }, []);

  // Enhanced search and filtering
  const handleSearch = useCallback((query: string) => {
    setFilters(prev => ({ ...prev, search: query }));
  }, []);

  const handleFilter = useCallback((newFilters: FilterConfig[]) => {
    const normalizedFilters = newFilters.reduce<Record<string, string>>((acc, filter) => {
      if (filter.value) {
        acc[filter.field] = String(filter.value);
      }
      return acc;
    }, {});
    setFilters(prev => ({ ...prev, ...normalizedFilters }));
  }, []);

  const handleSort = useCallback((sorts: SortConfig[]) => {
    // Implement sorting logic here
    console.debug('Sort assignments:', sorts);
    // In a real implementation, you would update the API call to include sort parameters
  }, []);

  const handleRefresh = useCallback(async () => {
    setLoading(true);
    const data = await loadAssignmentsData(filters);
    setAssignments(data);
    setLoading(false);
  }, [filters, loadAssignmentsData]);

  // Enhanced export functionality
  const handleExport = useCallback(async (data: DataRecord[], format: string) => {
    try {
      const exportData = data.map(assignment => ({
        'Assignment ID': assignment.id,
        'Job Title': assignment.job_title,
        'Job Status': assignment.job_status,
        'Assignee Name': assignment.assignee_name,
        'Assignee Email': assignment.assignee_email,
        'Assigned Date': assignment.assigned_at,
        'Notes': assignment.note || ''
      }));

      if (format === 'csv') {
        const csv = [
          Object.keys(exportData[0] || {}).join(','),
          ...exportData.map(row => Object.values(row).map(val => `"${val}"`).join(','))
        ].join('\n');

        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `assignments-${new Date().toISOString().split('T')[0]}.csv`;
        a.click();
        URL.revokeObjectURL(url);
      } else if (format === 'json') {
        const json = JSON.stringify(exportData, null, 2);
        const blob = new Blob([json], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `assignments-${new Date().toISOString().split('T')[0]}.json`;
        a.click();
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Export error:', error);
    }
  }, []);

  // Import functionality
  const handleImport = useCallback((data: DataRecord[]) => {
    console.debug('Import assignments:', data.length);
    // In a real implementation, you would validate and process the imported data
    // then make API calls to create new assignments
  }, []);

  // Enhanced delete functionality with API integration
  const handleDelete = useCallback(async (ids: string[]) => {
    try {
      setLoading(true);

      for (const id of ids) {
        const responseResult = await tryCatch(async () =>
          fetch(`/api/v1/jobs/assignments`, {
            method: 'DELETE',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ id })
          })
        );

        if (!responseResult.success) {
          reportError(responseResult.error);
          throw new Error(responseResult.error.message);
        }

        const response = responseResult.data;
        if (!response.ok) {
          throw new Error(`Failed to delete assignment ${id}: ${response.statusText}`);
        }
      }

      // Refresh data after deletion
      await handleRefresh();
    } catch (error) {
      console.error('Delete error:', error);
    } finally {
      setLoading(false);
    }
  }, [handleRefresh]);

  // Create assignment handler
  const handleCreate = useCallback(async (formData: Record<string, unknown>) => {
    try {
      setLoading(true);

      const responseResult = await tryCatch(async () =>
        fetch('/api/v1/jobs/assignments', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            job_id: formData.job_id,
            assignee_user_id: formData.assignee_user_id,
            note: formData.note || ''
          })
        })
      );

      if (!responseResult.success) {
        reportError(responseResult.error);
        throw new Error(responseResult.error.message);
      }

      const response = responseResult.data;
      if (!response.ok) {
        throw new Error(`Failed to create assignment: ${response.statusText}`);
      }

      closeDrawer();
      await handleRefresh();
    } catch (error) {
      console.error('Create error:', error);
    } finally {
      setLoading(false);
    }
  }, [closeDrawer, handleRefresh]);

  // Update assignment handler
  const handleUpdate = useCallback(async (formData: Record<string, unknown>) => {
    if (!selectedAssignment) return;

    try {
      setLoading(true);

      const responseResult = await tryCatch(async () =>
        fetch('/api/v1/jobs/assignments', {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: selectedAssignment.id,
            job_id: formData.job_id,
            assignee_user_id: formData.assignee_user_id,
            note: formData.note || ''
          })
        })
      );

      if (!responseResult.success) {
        reportError(responseResult.error);
        throw new Error(responseResult.error.message);
      }

      const response = responseResult.data;
      if (!response.ok) {
        throw new Error(`Failed to update assignment: ${response.statusText}`);
      }

      closeDrawer();
      await handleRefresh();
    } catch (error) {
      console.error('Update error:', error);
    } finally {
      setLoading(false);
    }
  }, [selectedAssignment, closeDrawer, handleRefresh]);

  const dataViewConfig = useMemo<DataViewConfig>(() => ({
    id: 'assignments-view',
    name: 'Job Assignments Management',
    viewType: viewMode,
    defaultView: 'grid',
    fields: fieldConfig,
    data: assignments,
    loading,
    onSearch: handleSearch,
    onFilter: handleFilter,
    onSort: handleSort,
    onRefresh: handleRefresh,
    onExport: handleExport,
    onImport: handleImport,
    onEdit: openEditDrawer,
    onDelete: handleDelete,
    onCreate: openCreateDrawer,
    onView: openViewDrawer
  }), [assignments, fieldConfig, handleDelete, handleExport, handleFilter, handleImport, handleRefresh, handleSearch, handleSort, loading, openCreateDrawer, openEditDrawer, openViewDrawer, viewMode]);

  return (
    <div className="h-full w-full">
      <StateManagerProvider>
        <DataViewProvider config={dataViewConfig}>
          <div className="flex flex-col h-full stack-md">
            {/* Header with Stats Dashboard */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-md mb-md">
              <Card className="p-md">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-body-sm color-muted">Total Assignments</p>
                    <p className="text-heading-2 font-semibold color-foreground">{assignmentStats.total}</p>
                  </div>
                  <Users className="h-8 w-8 color-accent" />
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-body-sm color-muted">Active Jobs</p>
                    <p className="text-heading-2 font-semibold color-success">{assignmentStats.activeJobs}</p>
                  </div>
                  <CheckCircle className="h-8 w-8 color-success" />
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-body-sm color-muted">Pending Jobs</p>
                    <p className="text-heading-2 font-semibold color-warning">{assignmentStats.pendingJobs}</p>
                  </div>
                  <Clock className="h-8 w-8 color-warning" />
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-body-sm color-muted">Recent (7 days)</p>
                    <p className="text-heading-2 font-semibold color-accent">{assignmentStats.recentAssignments}</p>
                  </div>
                  <TrendingUp className="h-8 w-8 color-accent" />
                </div>
              </Card>
            </div>

            {/* View Switcher and Actions */}
            <div className="flex items-center justify-between">
              <ViewSwitcher />
              <div className="flex items-center gap-sm">
                <DataActions />
                <Button onClick={openCreateDrawer} className="flex items-center gap-xs">
                  <Plus className="h-4 w-4" />
                  New Assignment
                </Button>
              </div>
            </div>

            {/* Data Views */}
            <div className="flex-1 min-h-0">
              {viewMode === 'grid' && <DataGrid />}
              {viewMode === 'kanban' && (
                <KanbanBoard
                  columns={[
                    { id: 'open', title: 'Open Jobs' },
                    { id: 'in_progress', title: 'In Progress' },
                    { id: 'done', title: 'Completed' },
                    { id: 'cancelled', title: 'Cancelled' }
                  ]}
                  statusField="job_status"
                  titleField="job_title"
                />
              )}
              {viewMode === 'calendar' && (
                <CalendarView
                  startDateField="assigned_at"
                  titleField="job_title"
                />
              )}
              {viewMode === 'list' && (
                <ListView
                  titleField="job_title"
                  subtitleField="assignee_name"
                />
              )}
              {viewMode === 'timeline' && (
                <TimelineView
                  startDateField="assigned_at"
                  titleField="job_title"
                />
              )}
              {viewMode === 'dashboard' && <DashboardView />}
            </div>

            {/* Create Assignment Drawer */}
            {createDrawerOpen && (
              <AppDrawer
                open={createDrawerOpen}
                onClose={closeDrawer}
                title="Create New Assignment"
                mode="create"
              >
                <div className="stack-md">
                  <div className="grid grid-cols-1 gap-md">
                    <div>
                      <label className="block text-body-sm font-medium color-foreground mb-xs">
                        Job ID
                      </label>
                      <input
                        type="text"
                        name="job_id"
                        className="w-full px-sm py-xs border border-border rounded-md"
                        placeholder="Enter job ID"
                      />
                    </div>
                    <div>
                      <label className="block text-body-sm font-medium color-foreground mb-xs">
                        Assignee User ID
                      </label>
                      <input
                        type="text"
                        name="assignee_user_id"
                        className="w-full px-sm py-xs border border-border rounded-md"
                        placeholder="Enter user ID"
                      />
                    </div>
                    <div>
                      <label className="block text-body-sm font-medium color-foreground mb-xs">
                        Notes
                      </label>
                      <textarea
                        name="note"
                        rows={3}
                        className="w-full px-sm py-xs border border-border rounded-md"
                        placeholder="Add any notes about this assignment"
                      />
                    </div>
                  </div>
                  <div className="flex justify-end gap-sm pt-md">
                    <Button variant="outline" onClick={closeDrawer}>
                      Cancel
                    </Button>
                    <Button onClick={() => {
                      const form = document.querySelector('form') as HTMLFormElement;
                      if (form) {
                        const formData = new FormData(form);
                        handleCreate(Object.fromEntries(formData));
                      }
                    }}>
                      Create Assignment
                    </Button>
                  </div>
                </div>
              </AppDrawer>
            )}

            {/* Edit Assignment Drawer */}
            {editDrawerOpen && selectedAssignment && (
              <AppDrawer
                open={editDrawerOpen}
                onClose={closeDrawer}
                title={`Edit Assignment ${selectedAssignment.id}`}
                record={selectedAssignment}
                mode="edit"
              >
                <div className="stack-md">
                  <div className="grid grid-cols-1 gap-md">
                    <div>
                      <label className="block text-body-sm font-medium color-foreground mb-xs">
                        Job ID
                      </label>
                      <input
                        type="text"
                        name="job_id"
                        defaultValue={selectedAssignment.job_id}
                        className="w-full px-sm py-xs border border-border rounded-md"
                      />
                    </div>
                    <div>
                      <label className="block text-body-sm font-medium color-foreground mb-xs">
                        Assignee User ID
                      </label>
                      <input
                        type="text"
                        name="assignee_user_id"
                        defaultValue={selectedAssignment.assignee_user_id}
                        className="w-full px-sm py-xs border border-border rounded-md"
                      />
                    </div>
                    <div>
                      <label className="block text-body-sm font-medium color-foreground mb-xs">
                        Notes
                      </label>
                      <textarea
                        name="note"
                        rows={3}
                        defaultValue={selectedAssignment.note || ''}
                        className="w-full px-sm py-xs border border-border rounded-md"
                      />
                    </div>
                  </div>
                  <div className="flex justify-end gap-sm pt-md">
                    <Button variant="outline" onClick={closeDrawer}>
                      Cancel
                    </Button>
                    <Button onClick={() => {
                      const form = document.querySelector('form') as HTMLFormElement;
                      if (form) {
                        const formData = new FormData(form);
                        handleUpdate(Object.fromEntries(formData));
                      }
                    }}>
                      Update Assignment
                    </Button>
                  </div>
                </div>
              </AppDrawer>
            )}

            {/* View Assignment Drawer */}
            {selectedAssignment && !createDrawerOpen && !editDrawerOpen && (
              <AppDrawer
                open={!!selectedAssignment}
                onClose={closeDrawer}
                title={`Assignment Details`}
                record={selectedAssignment}
                mode="view"
              >
                <div className="stack-md">
                  <div className="grid grid-cols-1 gap-md">
                    {fieldConfig.map(field => (
                      <div key={field.key} className="stack-2xs">
                        <p className="text-body-xs font-medium color-muted">{field.label}</p>
                        <p className="text-body-sm color-foreground">
                          {selectedAssignment[field.key] 
                            ? (field.type === 'date' 
                                ? new Date(selectedAssignment[field.key]).toLocaleString()
                                : String(selectedAssignment[field.key])
                              )
                            : 'â€”'
                          }
                        </p>
                      </div>
                    ))}
                  </div>
                  <div className="flex justify-end gap-sm pt-md">
                    <Button variant="outline" onClick={() => openEditDrawer(selectedAssignment)}>
                      Edit Assignment
                    </Button>
                    <Button variant="outline" onClick={closeDrawer}>
                      Close
                    </Button>
                  </div>
                </div>
              </AppDrawer>
            )}
          </div>
        </DataViewProvider>
      </StateManagerProvider>
    </div>
  );
}

import { type DataRecord } from '@ghxstship/ui';

// Core assignment types
export type AssignmentStatus = 'pending' | 'assigned' | 'in_progress' | 'completed' | 'cancelled';
export type AssignmentPriority = 'low' | 'medium' | 'high' | 'critical';
export type AssignmentAssigneeType = 'internal' | 'contractor' | 'vendor';

// Main assignment interface
export interface JobAssignment extends DataRecord {
  id: string;
  job_id: string;
  assignee_user_id: string;
  note?: string;
  assigned_at: string;
  // Enhanced fields from joins
  job_title?: string;
  job_status?: string;
  job_due_at?: string;
  assignee_name?: string;
  assignee_email?: string;
  assignee_avatar?: string;
  project_title?: string;
  organization_name?: string;
}

// API response types
export interface AssignmentsResponse {
  assignments: JobAssignment[];
  total?: number;
  page?: number;
  limit?: number;
}

// Form data types
export interface CreateAssignmentData {
  job_id: string;
  assignee_user_id: string;
  note?: string;
  priority?: AssignmentPriority;
  due_date?: string;
}

export interface UpdateAssignmentData extends Partial<CreateAssignmentData> {
  id: string;
  status?: AssignmentStatus;
}

// Filter and search types
export interface AssignmentFilters {
  status?: AssignmentStatus;
  assignee_user_id?: string;
  job_id?: string;
  priority?: AssignmentPriority;
  search?: string;
  date_from?: string;
  date_to?: string;
}

// Statistics types
export interface AssignmentStats {
  total: number;
  byStatus: Record<AssignmentStatus, number>;
  byPriority: Record<AssignmentPriority, number>;
  recentAssignments: number;
  completionRate: number;
  averageCompletionTime: number;
  overdue: number;
}

// View configuration types
export interface AssignmentViewConfig {
  showCompletedTasks: boolean;
  groupByAssignee: boolean;
  showJobDetails: boolean;
  defaultSort: 'assigned_at' | 'due_date' | 'priority' | 'status';
  defaultView: 'grid' | 'kanban' | 'calendar' | 'list' | 'timeline' | 'dashboard';
}

// Drawer types
export interface AssignmentDrawerProps {
  assignment?: JobAssignment;
  mode: 'create' | 'edit' | 'view';
  onSave?: (data: CreateAssignmentData | UpdateAssignmentData) => Promise<void>;
  onClose: () => void;
  open: boolean;
}

// Service types
export interface AssignmentService {
  getAssignments: (filters?: AssignmentFilters) => Promise<AssignmentsResponse>;
  getAssignment: (id: string) => Promise<JobAssignment>;
  createAssignment: (data: CreateAssignmentData) => Promise<JobAssignment>;
  updateAssignment: (data: UpdateAssignmentData) => Promise<JobAssignment>;
  deleteAssignment: (id: string) => Promise<void>;
  getAssignmentStats: () => Promise<AssignmentStats>;
}

