      key: 'company_id',
      label: 'Company ID',
      type: 'text' as const,
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'project_title',
      label: 'Project',
      type: 'text' as const,
      required: false,
      sortable: true,
      filterable: true
    }
  ];

  // Enhanced Supabase data loading function with enriched data
  const loadBidsData = useCallback(async (activeFilters?: Record<string, string>): Promise<DataRecord[]> => {
    try {
      const params = new URLSearchParams();
      if (activeFilters?.opportunity_id) params.append('opportunity_id', activeFilters.opportunity_id);
      if (activeFilters?.company_id) params.append('company_id', activeFilters.company_id);
      if (activeFilters?.status) params.append('status', activeFilters.status);
      if (activeFilters?.search) params.append('search', activeFilters.search);

      const responseResult = await tryCatch(async () =>
        fetch(`/api/v1/jobs/bids?${params.toString()}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' }
        })
      );

      if (!responseResult.success) {
        reportError(responseResult.error);
        throw new Error(responseResult.error.message);
      }

      const response = responseResult.data;
      if (!response.ok) {
        throw new Error(`Failed to fetch bids: ${response.statusText}`);
      }

      const data: BidsResponse = await response.json();

      // Enrich bids data with opportunity and company information
      const enrichedBids = (data.bids ?? []).map((bid: unknown) => ({
        ...bid,
        opportunity_title: bid.opportunities?.title || 'Unknown Opportunity',
        company_name: bid.companies?.name || 'Unknown Company',
        project_title: bid.opportunities?.projects?.title || 'No Project'
      }));

      return enrichedBids;
    } catch (error) {
      console.error('Error loading bids:', error);
      return [];
    }
  }, []);

  // Statistics calculation
  const bidStats = useMemo(() => {
    const total = bids.length;
    const byStatus = bids.reduce((acc: Record<string, number>, bid: unknown) => {
      const status = bid.status || 'unknown';
      acc[status] = (acc[status] || 0) + 1;
      return acc;
    }, {});

    const totalValue = bids.reduce((sum, bid: unknown) => sum + (bid.amount || 0), 0);
    const avgBidValue = total > 0 ? totalValue / total : 0;

    const recentBids = bids.filter((bid: unknown) => {
      const submittedDate = new Date(bid.submitted_at);
      const weekAgo = new Date();
      weekAgo.setDate(weekAgo.getDate() - 7);
      return submittedDate >= weekAgo;
    }).length;

    return {
      total,
      byStatus,
      totalValue,
      avgBidValue,
      recentBids,
      acceptedBids: byStatus.accepted || 0,
      pendingBids: byStatus.under_review || 0,
      rejectedBids: byStatus.rejected || 0
    };
  }, [bids]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');
  const [showCreateDialog, setShowCreateDialog] = useState(false);

  useEffect(() => {
    loadBids();
  }, [orgId]);

  const loadBids = async () => {
    try {
      setLoading(true);
      const responseResult = await tryCatch(async () => fetch('/api/v1/jobs/bids', {
        headers: {
          'x-org-id': orgId,
          'Content-Type': 'application/json',
        },
      }));
if (!responseResult.success) {
  reportError(responseResult.error);
  throw new Error(responseResult.error.message);
}
const response = responseResult.data;

      if (!response.ok) {
        throw new Error('Failed to load bids');
      }

      const data = await response.json();
      setBids(data.items || []);
    } catch (error) {
      console.error('Error loading bids:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredBids = bids.filter((bid: unknown) => {
    const matchesSearch = bid.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (bid.description?.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = statusFilter === 'all' || bid.status === statusFilter;
    const matchesType = typeFilter === 'all' || bid.type === typeFilter;

    return matchesSearch && matchesStatus && matchesType;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'draft':
        return 'bg-secondary color-muted';
      case 'submitted':
        return 'bg-accent/10 color-accent';
      case 'under_review':
        return 'bg-warning/10 color-warning';
      case 'accepted':
        return 'bg-success/10 color-success';
      case 'rejected':
        return 'bg-destructive/10 color-destructive';
      case 'withdrawn':
        return 'bg-secondary/10 color-secondary-foreground';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'fixed_price':
        return 'bg-accent/10 color-accent';
      case 'hourly':
        return 'bg-success/10 color-success';
      case 'milestone_based':
        return 'bg-secondary/10 color-secondary-foreground';
      case 'retainer':
        return 'bg-accent/10 color-accent-foreground';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const createBid = async (data: Partial<Bid>) => {
    try {
      const responseResult = await tryCatch(async () =>
        fetch('/api/v1/jobs/bids', {
          method: 'POST',
          headers: {
            'x-org-id': orgId,
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(data),
        })
      );
      if (!responseResult.success) {
        reportError(responseResult.error);
        throw new Error(responseResult.error.message);
      }
      const response = responseResult.data;

      if (!response.ok) {
        throw new Error('Failed to create bid');
      }

      await loadBids();
      setShowCreateDialog(false);
    } catch (error) {
      console.error('Error creating bid:', error);
    }
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
            <p className="color-muted">{translations.subtitle}</p>
          </div>
          <Button onClick={() => setShowCreateDialog(true)}>
            + Create Bid
          </Button>
        </div>

        <div className="grid gap-md">
          {[...Array(6)].map((_, i) => (
            <Card key={i} className="p-lg">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-sm mb-sm">
                    <div className="h-5 w-5 bg-secondary rounded" />
                    <Skeleton className="h-5 w-48" />
                  </div>
                  <Skeleton className="h-4 w-full mb-sm" />
                  <Skeleton className="h-4 w-3/4" />
                </div>
                <div className="flex gap-sm">
                  <Skeleton className="h-6 w-16" />
                  <Skeleton className="h-6 w-20" />
                </div>
              </div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
          <p className="color-muted">{translations.subtitle}</p>
        </div>
        <Button onClick={() => setShowCreateDialog(true)}>
          + Create Bid
        </Button>
      </div>

      <div className="flex gap-md mb-lg">
        <div className="flex-1">
          <UnifiedInput             placeholder="Search bids..."
            value={searchTerm}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
            className="w-full"
          />
        </div>
        <select
          value={statusFilter}
          onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setStatusFilter(e.target.value)}
          className=" px-md py-sm border border-border rounded-md bg-background"
        >
          {STATUS_OPTIONS.map((option: unknown) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
        <select
          value={typeFilter}
          onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setTypeFilter(e.target.value)}
          className=" px-md py-sm border border-border rounded-md bg-background"
        >
          {TYPE_OPTIONS.map((option: unknown) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      </div>

      {filteredBids.length === 0 ? (
        <Card className="p-2xl text-center">
          <div className="h-12 w-12 bg-secondary rounded mx-auto mb-md" />
          <h3 className="text-body form-label color-foreground mb-sm">No bids found</h3>
          <p className="color-muted mb-md">
            {searchTerm || statusFilter !== 'all' || typeFilter !== 'all'
              ? 'Try adjusting your search or filters to find bids.'
              : 'Get started by creating your first bid.'}
          </p>
          <Button onClick={() => setShowCreateDialog(true)}>
            + Create Bid
          </Button>
        </Card>
      ) : (
        <div className="grid gap-md">
          {filteredBids.map((bid: unknown) => (
            <Card key={bid.id} className="p-lg hover:shadow-elevated transition-shadow">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-sm mb-sm">
                    <div className="h-5 w-5 bg-secondary rounded" />
                    <h3 className="text-body text-heading-4 color-foreground">{bid.title}</h3>
                    <Badge className={getStatusColor(bid.status)}>
                      {bid.status}
                    </Badge>
                    <Badge className={getTypeColor(bid.type)}>
                      {bid.type.replace('_', ' ')}
                    </Badge>
                  </div>

                  {bid.description && (
                    <p className="color-muted mb-md line-clamp-2">{bid.description}</p>
                  )}

                  <div className="grid grid-cols-2 md:grid-cols-3 gap-md text-body-sm mb-md">
                    <div className="flex items-center gap-sm">
                      <div className="h-4 w-4 bg-secondary rounded" />
                      <span className="form-label">${bid.amount.toLocaleString()} {bid.currency}</span>
                    </div>
                    {bid.estimatedDuration && (
                      <div className="flex items-center gap-sm">
                        <div className="h-4 w-4 bg-secondary rounded" />
                        <span>{bid.estimatedDuration}</span>
                      </div>
                    )}
                    {bid.responseDeadline && (
                      <div className="flex items-center gap-sm">
                        <div className="h-4 w-4 bg-secondary rounded" />
                        <span>{new Date(bid.responseDeadline).toLocaleDateString()}</span>
                      </div>
                    )}
                  </div>

                  <div className="flex items-center justify-between pt-md border-t">
                    <div className="flex items-center gap-md text-body-sm color-muted">
                      {bid.createdAt && (
                        <span>Created {new Date(bid.createdAt).toLocaleDateString()}</span>
                      )}
                      {bid.submittedAt && (
                        <span>Submitted {new Date(bid.submittedAt).toLocaleDateString()}</span>
                      )}
                    </div>
                    <div className="flex gap-sm">
                      <Button>
                        View
                      </Button>
                      <Button>
                        Edit
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            </Card>
          ))}
        </div>
      )}

      {/* Create Dialog */}
      {showCreateDialog && (
        <div className="fixed inset-0 bg-foreground/30 backdrop-blur-sm flex items-center justify-center p-md z-50">
          <div className="bg-background rounded-lg p-lg w-full max-w-md">
            <h3 className="text-body text-heading-4 mb-md">Create New Bid</h3>
            <form onSubmit={(e: unknown) => {
              e.preventDefault();
              const formData = new FormData(e.currentTarget);
              createBid({
                title: formData.get('title') as string,
                description: formData.get('description') as string,
                type: formData.get('type') as unknown,
                status: 'draft',
                amount: Number(formData.get('amount')),
                currency: 'USD',
                opportunityId: formData.get('opportunityId') as string,
              });
            }}>
              <div className="stack-md">
                <div>
                  <label className="block text-body-sm form-label mb-xs">Title</label>
                  <UnifiedInput name="title" required />
                </div>
                <div>
                  <label className="block text-body-sm form-label mb-xs">Description</label>
                  <textarea name="description" className="w-full p-sm border border-border rounded bg-background" rows={3} />
                </div>
                <div>
                  <label className="block text-body-sm form-label mb-xs">Type</label>
                  <select name="type" className="w-full p-sm border border-border rounded bg-background" required>
                    {TYPE_OPTIONS.slice(1).map((option: unknown) => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-body-sm form-label mb-xs">Amount</label>
                  <UnifiedInput name="amount" type="number" required />
                </div>
                <div>
                  <label className="block text-body-sm form-label mb-xs">Opportunity ID</label>
                  <UnifiedInput name="opportunityId" required />
                </div>
              </div>
              <div className="flex gap-sm mt-lg">
                <Button type="submit">Create</Button>
                <Button type="button" variant="outline" onClick={() => setShowCreateDialog(false)}>
                  Cancel
                </Button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

import { type DataRecord } from '@ghxstship/ui';

// Core bid types
export type BidStatus = 'submitted' | 'under_review' | 'accepted' | 'rejected' | 'withdrawn';
export type BidType = 'fixed_price' | 'hourly' | 'milestone_based' | 'retainer';
export type BidCurrency = 'USD' | 'EUR' | 'GBP' | 'CAD' | 'AUD';

// Main bid interface
export interface JobBid extends DataRecord {
  id: string;
  opportunity_id: string;
  company_id: string;
  amount: number;
  status: BidStatus;
  submitted_at: string;
  // Enhanced fields from joins
  opportunity_title?: string;
  company_name?: string;
  project_title?: string;
  project_id?: string;
  organization_name?: string;
  client_contact?: string;
  response_deadline?: string;
  estimated_duration?: string;
  proposal_document_url?: string;
  notes?: string;
}

// API response types
export interface BidsResponse {
  bids: JobBid[];
  total?: number;
  page?: number;
  limit?: number;
}

// Form data types
export interface CreateBidData {
  opportunity_id: string;
  company_id: string;
  amount: number;
  currency?: BidCurrency;
  type?: BidType;
  estimated_duration?: string;
  proposal_document_url?: string;
  notes?: string;
  response_deadline?: string;
}

export interface UpdateBidData extends Partial<CreateBidData> {
  id: string;
  status?: BidStatus;
}

// Filter and search types
export interface BidFilters {
  status?: BidStatus;
  opportunity_id?: string;
  company_id?: string;
  type?: BidType;
  currency?: BidCurrency;
  amount_min?: number;
  amount_max?: number;
  search?: string;
  date_from?: string;
  date_to?: string;
}

// Statistics types
export interface BidStats {
  total: number;
  byStatus: Record<BidStatus, number>;
  byType: Record<BidType, number>;
  totalValue: number;
  averageBidValue: number;
  winRate: number;
  recentBids: number;
  activeBids: number;
  pendingBids: number;
  acceptedBids: number;
  rejectedBids: number;
}

// View configuration types
export interface BidViewConfig {
  showAmounts: boolean;
  groupByStatus: boolean;
  showOpportunityDetails: boolean;
  defaultSort: 'submitted_at' | 'amount' | 'status' | 'opportunity_title';
  defaultView: 'grid' | 'kanban' | 'calendar' | 'list' | 'timeline' | 'dashboard';
  currencyDisplay: BidCurrency;
}

// Drawer types
export interface BidDrawerProps {
  bid?: JobBid;
  mode: 'create' | 'edit' | 'view';
  onSave?: (data: CreateBidData | UpdateBidData) => Promise<void>;
  onClose: () => void;
  open: boolean;
}

// Service types
export interface BidService {
  getBids: (filters?: BidFilters) => Promise<BidsResponse>;
  getBid: (id: string) => Promise<JobBid>;
  createBid: (data: CreateBidData) => Promise<JobBid>;
  updateBid: (data: UpdateBidData) => Promise<JobBid>;
  deleteBid: (id: string) => Promise<void>;
  getBidStats: () => Promise<BidStats>;
  withdrawBid: (id: string) => Promise<JobBid>;
  submitBid: (id: string) => Promise<JobBid>;
}

// Opportunity integration types
export interface OpportunityInfo {
  id: string;
  title: string;
  description?: string;
  budget_min?: number;
  budget_max?: number;
  currency?: BidCurrency;
  deadline?: string;
  client_name?: string;
  project_title?: string;
  requirements?: string[];
}

// Company integration types
export interface CompanyInfo {
  id: string;
  name: string;
  type?: string;
  contact_email?: string;
  contact_phone?: string;
  website?: string;
  capabilities?: string[];
}

// Bid evaluation types
export interface BidEvaluation {
  bid_id: string;
  score?: number;
  criteria_scores?: Record<string, number>;
  evaluator_notes?: string;
  recommendation?: 'accept' | 'reject' | 'negotiate';
  evaluated_at?: string;
  evaluated_by?: string;
}

// Export all types
export type {
  DataRecord
} from '@ghxstship/ui';

'use client';

import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Button, Badge, UnifiedInput, Skeleton } from '@ghxstship/ui';
import {
  MagnifyingGlassIcon,
  PlusIcon,
  EyeIcon,
  PencilIcon,
  DocumentTextIcon,
  ClockIcon,
  CheckCircleIcon,
  XCircleIcon,
  ExclamationTriangleIcon,
  CalendarIcon,
  BuildingOfficeIcon,
  ShieldCheckIcon,
  DocumentCheckIcon,
  ArrowDownTrayIcon,
} from '@heroicons/react/24/outline';

interface ComplianceClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface JobCompliance {
  id: string;
  job_id: string;
  kind: 'regulatory' | 'safety' | 'quality' | 'security' | 'environmental' | 'legal' | 'financial';
  title: string;
  description: string;
  status: 'pending' | 'in_progress' | 'compliant' | 'non_compliant' | 'expired' | 'waived';
  priority: 'low' | 'medium' | 'high' | 'critical';
  due_at?: string;
  completed_at?: string;
  evidence_url?: string;
  notes?: string;
  requirements?: string[];
  responsible_party?: string;
  reviewer?: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
  // Related data
  job_title?: string;
}

const STATUS_OPTIONS = [
  { value: 'all', label: 'All Statuses' },
  { value: 'pending', label: 'Pending' },
  { value: 'in_progress', label: 'In Progress' },
  { value: 'compliant', label: 'Compliant' },
  { value: 'non_compliant', label: 'Non-Compliant' },
  { value: 'expired', label: 'Expired' },
  { value: 'waived', label: 'Waived' },
];

const KIND_OPTIONS = [
  { value: 'all', label: 'All Types' },
  { value: 'regulatory', label: 'Regulatory' },
  { value: 'safety', label: 'Safety' },
  { value: 'quality', label: 'Quality' },
  { value: 'security', label: 'Security' },
  { value: 'environmental', label: 'Environmental' },
  { value: 'legal', label: 'Legal' },
  { value: 'financial', label: 'Financial' },
];

const PRIORITY_OPTIONS = [
  { value: 'all', label: 'All Priorities' },
  { value: 'low', label: 'Low' },
  { value: 'medium', label: 'Medium' },
  { value: 'high', label: 'High' },
  { value: 'critical', label: 'Critical' },
];

export function ComplianceClient({ user, orgId, translations }: ComplianceClientProps) {
  const [compliance, setCompliance] = useState<JobCompliance[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [kindFilter, setKindFilter] = useState('all');
  const [priorityFilter, setPriorityFilter] = useState('all');
  const [showCreateDialog, setShowCreateDialog] = useState(false);

  const supabase = createBrowserClient();

  useEffect(() => {
    loadCompliance();
  }, [orgId]);

  const loadCompliance = async () => {
    try {
      setLoading(true);

      // Load compliance items with related job data
      const { data, error } = await supabase
        .from('job_compliance')
        .select(`
          *,
          jobs (
            title
          )
        `)
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Transform the data to flatten relationships
      const transformedCompliance = (data || []).map((item: unknown) => ({
        ...item,
        job_title: item.jobs?.title || 'Unknown Job',
      }));

      setCompliance(transformedCompliance);
    } catch (error) {
      console.error('Error loading compliance:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredCompliance = compliance.filter((item: unknown) => {
    const matchesSearch = item.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         item.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (item.job_title && item.job_title.toLowerCase().includes(searchTerm.toLowerCase())) ||
                         (item.notes && item.notes.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = statusFilter === 'all' || item.status === statusFilter;
    const matchesKind = kindFilter === 'all' || item.kind === kindFilter;
    const matchesPriority = priorityFilter === 'all' || item.priority === priorityFilter;

    return matchesSearch && matchesStatus && matchesKind && matchesPriority;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-warning/10 color-warning';
      case 'in_progress':
        return 'bg-accent/10 color-accent';
      case 'compliant':
        return 'bg-success/10 color-success';
      case 'non_compliant':
        return 'bg-destructive/10 color-destructive';
      case 'expired':
        return 'bg-warning/10 color-warning';
      case 'waived':
        return 'bg-secondary color-muted';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending':
        return ClockIcon;
      case 'in_progress':
        return DocumentTextIcon;
      case 'compliant':
        return CheckCircleIcon;
      case 'non_compliant':
        return XCircleIcon;
      case 'expired':
        return ExclamationTriangleIcon;
      case 'waived':
        return DocumentCheckIcon;
      default:
        return ClockIcon;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'low':
        return 'bg-secondary color-muted';
      case 'medium':
        return 'bg-accent/10 color-accent';
      case 'high':
        return 'bg-warning/10 color-warning';
      case 'critical':
        return 'bg-destructive/10 color-destructive';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const getKindColor = (kind: string) => {
    switch (kind) {
      case 'regulatory':
        return 'bg-secondary/10 color-secondary';
      case 'safety':
        return 'bg-destructive/10 color-destructive';
      case 'quality':
        return 'bg-accent/10 color-accent';
      case 'security':
        return 'bg-accent/10 color-accent';
      case 'environmental':
        return 'bg-success/10 color-success';
      case 'legal':
        return 'bg-warning/10 color-warning';
      case 'financial':
        return 'bg-warning/10 color-warning';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const isOverdue = (dueDate?: string) => {
    if (!dueDate) return false;
    return new Date(dueDate) < new Date();
  };

  const isDueSoon = (dueDate?: string) => {
    if (!dueDate) return false;
    const due = new Date(dueDate);
    const now = new Date();
    const diffDays = Math.ceil((due.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
    return diffDays <= 7 && diffDays >= 0;
  };

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
          <p className="text-body-sm color-foreground/70 mt-xs">{translations.subtitle}</p>
        </div>
        <Button onClick={() => setShowCreateDialog(true)}>
          <PlusIcon className="h-4 w-4 mr-sm" />
          Add Compliance Item
        </Button>
      </div>

      {/* Filters */}
      <Card className="p-md">
        <div className="flex flex-col sm:flex-row gap-md">
          <div className="flex-1">
            <UnifiedInput               placeholder="Search compliance items..."
              value={searchTerm}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
              className="w-full"
            />
          </div>
          <div className="flex gap-sm">
            <select
              value={statusFilter}
              onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setStatusFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {STATUS_OPTIONS.map((option: unknown) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <select
              value={kindFilter}
              onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setKindFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {KIND_OPTIONS.map((option: unknown) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <select
              value={priorityFilter}
              onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setPriorityFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {PRIORITY_OPTIONS.map((option: unknown) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>
        </div>
      </Card>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-md">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Total Items</p>
              <p className="text-heading-3 text-heading-3 color-foreground">{compliance.length}</p>
            </div>
            <ShieldCheckIcon className="h-8 w-8 color-accent" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Compliant</p>
              <p className="text-heading-3 text-heading-3 color-success">
                {compliance.filter(c => c.status === 'compliant').length}
              </p>
            </div>
            <CheckCircleIcon className="h-8 w-8 color-success" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Non-Compliant</p>
              <p className="text-heading-3 text-heading-3 color-destructive">
                {compliance.filter(c => c.status === 'non_compliant').length}
              </p>
            </div>
            <XCircleIcon className="h-8 w-8 color-destructive" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Overdue</p>
              <p className="text-heading-3 text-heading-3 color-warning">
                {compliance.filter(c => isOverdue(c.due_at)).length}
              </p>
            </div>
            <ExclamationTriangleIcon className="h-8 w-8 color-warning" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Critical</p>
              <p className="text-heading-3 text-heading-3 color-secondary">
                {compliance.filter(c => c.priority === 'critical').length}
              </p>
            </div>
            <ExclamationTriangleIcon className="h-8 w-8 color-secondary" />
          </div>
        </Card>
      </div>

      {/* Compliance List */}
      <div className="stack-md">
        {loading ? (
          Array.from({ length: 5 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <Skeleton className="h-5 w-64 mb-sm" />
                  <Skeleton className="h-4 w-32 mb-sm" />
                  <Skeleton className="h-4 w-full mb-sm" />
