interface OpportunitiesClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface Opportunity {
  id: string;
  organizationId: string;
  title: string;
  description?: string;
  type: 'construction' | 'technical' | 'creative' | 'logistics' | 'consulting' | 'other';
  status: 'lead' | 'qualified' | 'proposal' | 'negotiation' | 'won' | 'lost';
  estimatedValue?: number;
  currency?: string;
  probability?: number;
  expectedCloseDate?: string;
  clientName?: string;
  clientContact?: string;
  source?: string;
  notes?: string;
  createdAt?: string;
  updatedAt?: string;
}

const STATUS_OPTIONS = [
  { value: 'all', label: 'All Statuses' },
  { value: 'lead', label: 'Lead' },
  { value: 'qualified', label: 'Qualified' },
  { value: 'proposal', label: 'Proposal' },
  { value: 'negotiation', label: 'Negotiation' },
  { value: 'won', label: 'Won' },
  { value: 'lost', label: 'Lost' },
];

const TYPE_OPTIONS = [
  { value: 'all', label: 'All Types' },
  { value: 'construction', label: 'Construction' },
  { value: 'technical', label: 'Technical' },
  { value: 'creative', label: 'Creative' },
  { value: 'logistics', label: 'Logistics' },
  { value: 'consulting', label: 'Consulting' },
  { value: 'other', label: 'Other' },
];

export function OpportunitiesClient({ user, orgId, translations }: OpportunitiesClientProps) {
  const [opportunities, setOpportunities] = useState<Opportunity[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [selectedOpportunity, setSelectedOpportunity] = useState<Opportunity | null>(null);

  useEffect(() => {
    loadOpportunities();
  }, [orgId]);

  const loadOpportunities = async () => {
    try {
      setLoading(true);
      const responseResult = await tryCatch(async () => fetch('/api/v1/jobs/opportunities', {
        headers: {
          'x-org-id': orgId,
          'Content-Type': 'application/json',
        },
      }));
if (!responseResult.success) {
  reportError(responseResult.error);
  throw new Error(responseResult.error.message);
}
const response = responseResult.data;

      if (!response.ok) {
        throw new Error('Failed to load opportunities');
      }

      const data = await response.json();
      setOpportunities(data.items || []);
    } catch (error) {
      console.error('Error loading opportunities:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredOpportunities = opportunities.filter((opportunity: unknown) => {
    const matchesSearch = opportunity.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (opportunity.clientName?.toLowerCase().includes(searchTerm.toLowerCase())) ||
                         (opportunity.description?.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = statusFilter === 'all' || opportunity.status === statusFilter;
    const matchesType = typeFilter === 'all' || opportunity.type === typeFilter;

    return matchesSearch && matchesStatus && matchesType;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'lead':
        return 'bg-warning/10 color-warning';
      case 'qualified':
        return 'bg-accent/10 color-accent';
      case 'proposal':
        return 'bg-secondary/10 color-secondary';
      case 'negotiation':
        return 'bg-warning/10 color-warning';
      case 'won':
        return 'bg-success/10 color-success';
      case 'lost':
        return 'bg-destructive/10 color-destructive';
      default:
        return 'bg-secondary/50 color-muted';
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'construction':
        return 'bg-warning/10 color-warning';
      case 'technical':
        return 'bg-accent/10 color-accent';
      case 'creative':
        return 'bg-secondary/10 color-secondary';
      case 'logistics':
        return 'bg-success/10 color-success';
      case 'consulting':
        return 'bg-accent/10 color-accent';
      case 'other':
        return 'bg-secondary/50 color-muted';
      default:
        return 'bg-secondary/50 color-muted';
    }
  };

  const createOpportunity = async (data: Partial<Opportunity>) => {
    try {
      const responseResult = await tryCatch(async () =>
        fetch('/api/v1/jobs/opportunities', {
          method: 'POST',
          headers: {
            'x-org-id': orgId,
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(data),
        })
      );
      if (!responseResult.success) {
        reportError(responseResult.error);
        throw new Error(responseResult.error.message);
      }
      const response = responseResult.data;

      if (!response.ok) {
        throw new Error('Failed to create opportunity');
      }

      await loadOpportunities();
      setShowCreateDialog(false);
    } catch (error) {
      console.error('Error creating opportunity:', error);
    }
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
            <p className="color-muted">{translations.subtitle}</p>
          </div>
          <Button onClick={() => setShowCreateDialog(true)}>
            + Create Opportunity
          </Button>
        </div>

        <div className="flex gap-md mb-lg">
          <div className="flex-1">
            <UnifiedInput               placeholder="Search opportunities..."
              value={searchTerm}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
              className="w-full"
            />
          </div>
          <Select value={statusFilter} onValueChange={setStatusFilter}>
            {STATUS_OPTIONS.map((option: unknown) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </Select>
          <Select value={typeFilter} onValueChange={setTypeFilter}>
            {TYPE_OPTIONS.map((option: unknown) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </Select>
        </div>

        <div className="grid gap-md">
          {[...Array(6)].map((_, i) => (
            <Card key={i} className="p-lg">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-sm mb-sm">
                    <div className="h-5 w-5 bg-secondary rounded" />
                    <Skeleton className="h-5 w-48" />
                  </div>
                  <Skeleton className="h-4 w-full mb-sm" />
                  <Skeleton className="h-4 w-3/4" />
                </div>
                <div className="flex gap-sm">
                  <Skeleton className="h-6 w-16" />
                  <Skeleton className="h-6 w-20" />
                </div>
              </div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
          <p className="color-muted">{translations.subtitle}</p>
        </div>
        <Button onClick={() => setShowCreateDialog(true)}>
          + Create Opportunity
        </Button>
      </div>

      <div className="flex gap-md mb-lg">
        <div className="flex-1">
          <UnifiedInput             placeholder="Search opportunities..."
            value={searchTerm}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
            className="w-full"
          />
        </div>
        <Select value={statusFilter} onValueChange={setStatusFilter}>
          {STATUS_OPTIONS.map((option: unknown) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </Select>
        <Select value={typeFilter} onValueChange={setTypeFilter}>
          {TYPE_OPTIONS.map((option: unknown) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </Select>
      </div>

      {filteredOpportunities.length === 0 ? (
        <Card className="p-2xl text-center">
          <div className="h-12 w-12 bg-secondary rounded mx-auto mb-md" />
          <h3 className="text-body form-label color-foreground mb-sm">No opportunities found</h3>
          <p className="color-muted mb-md">
            {searchTerm || statusFilter !== 'all' || typeFilter !== 'all'
              ? 'Try adjusting your search or filters to find opportunities.'
              : 'Get started by creating your first opportunity.'}
          </p>
          <Button onClick={() => setShowCreateDialog(true)}>
            + Create Opportunity
          </Button>
        </Card>
      ) : (
        <div className="grid gap-md">
          {filteredOpportunities.map((opportunity: unknown) => (
            <Card key={opportunity.id} className="p-lg hover:shadow-elevated transition-shadow">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-sm mb-sm">
                    <div className="h-5 w-5 bg-secondary rounded" />
                    <h3 className="text-body text-heading-4 color-foreground">{opportunity.title}</h3>
                    <Badge className={getStatusColor(opportunity.status)}>
                      {opportunity.status}
                    </Badge>
                    <Badge className={getTypeColor(opportunity.type)}>
                      {opportunity.type}
                    </Badge>
                  </div>

                  {opportunity.description && (
                    <p className="color-muted mb-md line-clamp-2">{opportunity.description}</p>
                  )}

                  <div className="grid grid-cols-2 md:grid-cols-3 gap-md text-body-sm">
                    {opportunity.clientName && (
                      <div className="flex items-center gap-sm">
                        <div className="h-4 w-4 bg-secondary rounded" />
                        <span className="color-muted">{opportunity.clientName}</span>
                      </div>
                    )}
                    {opportunity.estimatedValue && (
                      <div className="flex items-center gap-sm">
                        <div className="h-4 w-4 bg-secondary rounded" />
                        <span className="color-muted">
                          ${opportunity.estimatedValue.toLocaleString()} {opportunity.currency || 'USD'}
                        </span>
                      </div>
                    )}
                    {opportunity.expectedCloseDate && (
                      <div className="flex items-center gap-sm">
                        <div className="h-4 w-4 bg-secondary rounded" />
                        <span className="color-muted">
                          {new Date(opportunity.expectedCloseDate).toLocaleDateString()}
                        </span>
                      </div>
                    )}
                  </div>

                  <div className="flex items-center justify-between mt-md pt-md border-t border-border">
                    <div className="flex items-center gap-md text-body-sm color-muted">
                      {opportunity.createdAt && (
                        <span>Created {new Date(opportunity.createdAt).toLocaleDateString()}</span>
                      )}
                      {opportunity.updatedAt && opportunity.updatedAt !== opportunity.createdAt && (
                        <span>Updated {new Date(opportunity.updatedAt).toLocaleDateString()}</span>
                      )}
                    </div>
                    <div className="flex gap-sm">
                      <Button
                        variant="outline"

                        onClick={() => setSelectedOpportunity(opportunity)}
                      >
                        View
                      </Button>
                      <Button>
                        Edit
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            </Card>
          ))}
        </div>
      )}

      {/* Create Dialog */}
      {showCreateDialog && (
        <div className="fixed inset-0 bg-foreground/30 backdrop-blur-sm flex items-center justify-center p-md z-50">
          <div className="bg-background rounded-lg p-lg w-full max-w-md">
            <h3 className="text-body text-heading-4 mb-md">Create New Opportunity</h3>
            <form onSubmit={(e: unknown) => {
              e.preventDefault();
              const formData = new FormData(e.currentTarget);
              createOpportunity({
                title: formData.get('title') as string,
                description: formData.get('description') as string,
                type: formData.get('type') as unknown,
                status: 'lead',
                clientName: formData.get('clientName') as string,
                estimatedValue: formData.get('estimatedValue') ? Number(formData.get('estimatedValue')) : undefined,
                currency: 'USD',
              });
            }}>
              <div className="stack-md">
                <div>
                  <label className="block text-body-sm form-label mb-xs">Title</label>
                  <UnifiedInput />
                </div>
                <div>
                  <label className="block text-body-sm form-label mb-xs">Description</label>
                  <textarea className="w-full p-sm border border-border rounded bg-background" rows={3} />
                </div>
                <div>
                  <label className="block text-body-sm form-label mb-xs">Type</label>
                  <Select>
                    {TYPE_OPTIONS.slice(1).map((option: unknown) => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </Select>
                </div>
                <div>
                  <label className="block text-body-sm form-label mb-xs">Client Name</label>
                  <UnifiedInput />
                </div>
                <div>
                  <label className="block text-body-sm form-label mb-xs">Estimated Value</label>
                  <UnifiedInput type="number" />
                </div>
              </div>
              <div className="flex gap-sm mt-lg">
                <Button type="submit">Create</Button>
                <Button type="button" variant="outline" onClick={() => setShowCreateDialog(false)}>
                  Cancel
                </Button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

import { type DataRecord } from '@ghxstship/ui';

// Core opportunity types
export type OpportunityStatus = 'open' | 'closed' | 'awarded' | 'cancelled';
export type OpportunityType = 'construction' | 'technical' | 'creative' | 'logistics' | 'consulting' | 'other';
export type OpportunityPriority = 'low' | 'medium' | 'high' | 'critical';
export type OpportunityStage = 'lead' | 'qualified' | 'proposal' | 'negotiation' | 'won' | 'lost';

// Main opportunity interface
export interface JobOpportunity extends DataRecord {
  id: string;
  organization_id: string;
  project_id?: string;
  title: string;
  status: OpportunityStatus;
  opens_at?: string;
  closes_at?: string;
  created_at: string;
  // Enhanced fields from joins
  project_title?: string;
  organization_name?: string;
  description?: string;
  requirements?: string[];
  budget_min?: number;
  budget_max?: number;
  currency?: string;
  client_name?: string;
  client_contact?: string;
  location?: string;
  duration?: string;
  skills_required?: string[];
  experience_level?: string;
  stage?: OpportunityStage;
  probability?: number;
  estimated_value?: number;
  notes?: string;
}

// API response types
export interface OpportunitiesResponse {
  opportunities: JobOpportunity[];
  total?: number;
  page?: number;
  limit?: number;
}

// Form data types
export interface CreateOpportunityData {
  organization_id: string;
  project_id?: string;
  title: string;
  description?: string;
  type?: OpportunityType;
  requirements?: string[];
  budget_min?: number;
  budget_max?: number;
  currency?: string;
  opens_at?: string;
  closes_at?: string;
  client_name?: string;
  client_contact?: string;
  location?: string;
  duration?: string;
  skills_required?: string[];
  experience_level?: string;
  priority?: OpportunityPriority;
  notes?: string;
}

export interface UpdateOpportunityData extends Partial<CreateOpportunityData> {
  id: string;
  status?: OpportunityStatus;
  stage?: OpportunityStage;
  probability?: number;
  estimated_value?: number;
}

// Filter and search types
export interface OpportunityFilters {
  status?: OpportunityStatus;
  type?: OpportunityType;
  stage?: OpportunityStage;
  organization_id?: string;
  project_id?: string;
  priority?: OpportunityPriority;
  budget_min?: number;
  budget_max?: number;
  value_min?: number;
  value_max?: number;
  search?: string;
  location?: string;
  skills?: string[];
  date_from?: string;
  date_to?: string;
  closing_soon?: boolean;
}

// Statistics types
export interface OpportunityStats {
  total: number;
  byStatus: Record<OpportunityStatus, number>;
  byStage: Record<OpportunityStage, number>;
  byType: Record<OpportunityType, number>;
  totalValue: number;
  averageValue: number;
  winRate: number;
  pipelineValue: number;
  activeOpportunities: number;
  closingOpportunities: number;
  recentOpportunities: number;
  conversionRate: number;
}

// View configuration types
export interface OpportunityViewConfig {
  showBudgets: boolean;
  groupByStage: boolean;
  showProjectDetails: boolean;
  showClosingAlerts: boolean;
  defaultSort: 'created_at' | 'closes_at' | 'estimated_value' | 'probability' | 'status';
  defaultView: 'grid' | 'kanban' | 'calendar' | 'list' | 'timeline' | 'dashboard';
  currencyDisplay: string;
  probabilityDisplay: boolean;
}

// Drawer types
export interface OpportunityDrawerProps {
  opportunity?: JobOpportunity;
  mode: 'create' | 'edit' | 'view';
  onSave?: (data: CreateOpportunityData | UpdateOpportunityData) => Promise<void>;
  onClose: () => void;
  open: boolean;
}

// Service types
export interface OpportunityService {
  getOpportunities: (filters?: OpportunityFilters) => Promise<OpportunitiesResponse>;
  getOpportunity: (id: string) => Promise<JobOpportunity>;
  createOpportunity: (data: CreateOpportunityData) => Promise<JobOpportunity>;
  updateOpportunity: (data: UpdateOpportunityData) => Promise<JobOpportunity>;
  deleteOpportunity: (id: string) => Promise<void>;
  getOpportunityStats: () => Promise<OpportunityStats>;
  closeOpportunity: (id: string) => Promise<JobOpportunity>;
  awardOpportunity: (id: string, winnerId: string) => Promise<JobOpportunity>;
  cancelOpportunity: (id: string, reason?: string) => Promise<JobOpportunity>;
}

// Project integration types
export interface ProjectInfo {
  id: string;
  name: string;
  description?: string;
  status?: string;
  organization_id?: string;
  start_date?: string;
  end_date?: string;
  budget?: number;
  client_name?: string;
}

// Organization integration types
export interface OrganizationInfo {
  id: string;
  name: string;
  type?: string;
  industry?: string;
  location?: string;
  website?: string;
  contact_email?: string;
  contact_phone?: string;
}

// Opportunity pipeline types
export interface OpportunityPipeline {
  stage: OpportunityStage;
  opportunities: JobOpportunity[];
  totalValue: number;
  averageProbability: number;
  count: number;
}

// Opportunity activity types
export interface OpportunityActivity {
  id: string;
  opportunity_id: string;
  activity_type: 'created' | 'updated' | 'stage_changed' | 'note_added' | 'contact_made' | 'proposal_sent' | 'meeting_scheduled';
  description: string;
  performed_by: string;
  performed_at: string;
  metadata?: Record<string, unknown>;
}

// Opportunity contact types
export interface OpportunityContact {
  id: string;
  opportunity_id: string;
  contact_type: 'email' | 'phone' | 'meeting' | 'proposal' | 'follow_up';
  contact_date: string;
  contact_person?: string;
  subject?: string;
  notes?: string;
  outcome?: string;
  next_action?: string;
  next_action_date?: string;
}

// Opportunity proposal types
export interface OpportunityProposal {
  id: string;
  opportunity_id: string;
  proposal_title: string;
  proposal_document_url?: string;
  submitted_at: string;
  submitted_by: string;
  status: 'draft' | 'submitted' | 'under_review' | 'accepted' | 'rejected';
  feedback?: string;
  value: number;
  currency: string;
  validity_period?: string;
}

// Opportunity competitor types
export interface OpportunityCompetitor {
  id: string;
  opportunity_id: string;
  competitor_name: string;
  competitor_type?: string;
  strengths?: string[];
  weaknesses?: string[];
  estimated_bid?: number;
  win_probability?: number;
  notes?: string;
}

// Export all types
export type {
  DataRecord
} from '@ghxstship/ui';

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { useTranslations } from 'next-intl';
import {
  type DataRecord,
  type FieldConfig,
  type FilterConfig,
  type SortConfig,
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
  DataActions,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView
} from '@ghxstship/ui';
import type { DataViewConfig } from '@ghxstship/ui/components/DataViews/types';
import { tryCatch, reportError } from '@ghxstship/ui/utils/error-handling';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface JobsResponse {
  jobs: DataRecord[];
}

export default function JobsClient({ orgId }: { orgId: string }) {
  const t = useTranslations('jobs');
  const [jobs, setJobs] = useState<DataRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState<Record<string, string>>({});
  const [selectedJob, setSelectedJob] = useState<DataRecord | null>(null);

  // Field configuration for jobs data (matching database schema)
  const fieldConfig: FieldConfig[] = [
    {
      key: 'title',
      label: 'Job Title',
      type: 'text' as const,
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select' as const,
      required: true,
      sortable: true,
      filterable: true,
      options: [
        { value: 'open', label: 'Open' },
        { value: 'in_progress', label: 'In Progress' },
        { value: 'blocked', label: 'Blocked' },
        { value: 'done', label: 'Done' },
        { value: 'cancelled', label: 'Cancelled' }
      ]
    },
    {
      key: 'project_id',
      label: 'Project',
      type: 'text' as const,
      required: false,
      sortable: true,
      filterable: true
    },
    {
      key: 'due_at',
      label: 'Due Date',
      type: 'date' as const,
      required: false,
      sortable: true,
      filterable: true
    },
    {
      key: 'created_by',
      label: 'Created By',
      type: 'text' as const,
      required: false,
      sortable: true,
      filterable: true
    },
    {
      key: 'created_at',
      label: 'Created Date',
      type: 'date' as const,
      required: false,
      sortable: true,
      filterable: true
    }
  ];

  // Real Supabase data loading function
  const loadJobsData = useCallback(async (activeFilters?: Record<string, string>): Promise<DataRecord[]> => {
    try {
      const params = new URLSearchParams();
      if (activeFilters?.type) params.append('type', activeFilters.type);
      if (activeFilters?.status) params.append('status', activeFilters.status);
      if (activeFilters?.search) params.append('search', activeFilters.search);

      const responseResult = await tryCatch(async () =>
        fetch(`/api/v1/jobs?${params.toString()}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' }
        })
      );

      if (!responseResult.success) {
        reportError(responseResult.error);
        throw new Error(responseResult.error.message);
      }

      const response = responseResult.data;
      if (!response.ok) {
        throw new Error(`Failed to fetch jobs: ${response.statusText}`);
      }

      const data: JobsResponse = await response.json();
      return data.jobs ?? [];
    } catch (error) {
      console.error('Error loading jobs:', error);
      return [];
    }
  }, []);

  // Load data on mount and when filters change
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const data = await loadJobsData(filters);
      setJobs(data);
      setLoading(false);
    };
    void fetchData();
  }, [filters, loadJobsData]);

  const closeDrawer = useCallback(() => {
    setSelectedJob(null);
  }, []);

  const openViewDrawer = useCallback((record: DataRecord) => {
    setSelectedJob(record);
  }, []);

  const handleSearch = useCallback((query: string) => {
    setFilters(prev => ({ ...prev, search: query }));
  }, []);

  const handleFilter = useCallback((newFilters: FilterConfig[]) => {
    const normalizedFilters = newFilters.reduce<Record<string, string>>((acc, filter) => {
      if (filter.value) {
        acc[filter.field] = String(filter.value);
      }
      return acc;
    }, {});
    setFilters(prev => ({ ...prev, ...normalizedFilters }));
  }, []);

  const handleSort = useCallback((sorts: SortConfig[]) => {
    console.debug('Sort jobs:', sorts);
  }, []);

  const handleRefresh = useCallback(() => {
    setFilters({});
  }, []);

  const handleExport = useCallback((data: DataRecord[], format: string) => {
    console.debug('Export jobs:', format, data.length);
  }, []);

  const handleImport = useCallback((data: DataRecord[]) => {
    console.debug('Import jobs:', data.length);
  }, []);

  const handleDelete = useCallback((ids: string[]) => {
    console.debug('Delete jobs:', ids);
  }, []);

  const dataViewConfig = useMemo<DataViewConfig>(() => ({
    id: 'jobs-view',
    name: 'Jobs Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields: fieldConfig,
    data: jobs,
    loading,
    onSearch: handleSearch,
    onFilter: handleFilter,
    onSort: handleSort,
    onRefresh: handleRefresh,
    onExport: handleExport,
    onImport: handleImport,
    onEdit: openViewDrawer,
    onDelete: handleDelete
  }), [jobs, fieldConfig, handleDelete, handleExport, handleFilter, handleImport, handleRefresh, handleSearch, handleSort, loading, openViewDrawer]);

  return (
    <div className="h-full w-full">
      <StateManagerProvider>
        <DataViewProvider config={dataViewConfig}>
          <div className="flex flex-col h-full stack-md">
            {/* View Switcher and Actions */}
            <div className="flex items-center justify-between">
              <ViewSwitcher />
              <DataActions />
            </div>

            {/* Data Views */}
            <DataGrid />
            <KanbanBoard
              columns={[
                { id: 'open', title: 'Open' },
                { id: 'in_progress', title: 'In Progress' },
                { id: 'blocked', title: 'Blocked' },
                { id: 'done', title: 'Done' },
                { id: 'cancelled', title: 'Cancelled' }
              ]}
              statusField="status"
              titleField="title"
            />
            <CalendarView
              startDateField="due_at"
              titleField="title"
            />
            <ListView
              titleField="title"
              subtitleField="description"
            />

            {/* Drawer for CRUD operations */}
            {selectedJob && (
              <AppDrawer
                open
                onClose={closeDrawer}
                title={selectedJob.title ?? 'Job Details'}
                record={selectedJob}
                mode="view"
              >
                <div className="stack-md">
                  {fieldConfig.map(field => (
                    <div key={field.key} className="stack-2xs">
                      <p className="text-body-xs form-label color-muted">{field.label}</p>
                      <p className="text-body-sm">{String(selectedJob[field.key] ?? 'â€”')}</p>
                    </div>
                  ))}
                </div>
              </AppDrawer>
            )}
          </div>
        </DataViewProvider>
      </StateManagerProvider>
    </div>
  );
}

'use client';

import { Plus, Briefcase, FileText, CheckCircle, AlertTriangle, TrendingUp, Calendar, Users, DollarSign, Clock } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import {
  type DataRecord,
  type FieldConfig,
  DataViewProvider,
  StateManagerProvider,
  DashboardView,
  Button,
  Badge,
  Card
} from '@ghxstship/ui';
import type { DataViewConfig } from '@ghxstship/ui/components/DataViews/types';
import { tryCatch, reportError } from '@ghxstship/ui/utils/error-handling';
import Link from 'next/link';

interface JobsOverviewData {
  totalJobs: number;
  activeJobs: number;
  completedJobs: number;
  draftJobs: number;
  totalOpportunities: number;
  activeBids: number;
  activeContracts: number;
  pendingCompliance: number;
  totalAssignments: number;
  activeRFPs: number;
  recentActivity: RecentActivity[];
}

interface RecentActivity {
  id: string;
  type: 'job' | 'opportunity' | 'bid' | 'contract' | 'assignment' | 'compliance' | 'rfp';
  title: string;
  status: string;
  updatedAt: string;
  priority?: 'high' | 'medium' | 'low';
}

export default function OverviewClient({ orgId }: { orgId: string }) {
  const [overviewData, setOverviewData] = useState<JobsOverviewData | null>(null);
  const [loading, setLoading] = useState(true);

  // Load comprehensive jobs overview data
  const loadOverviewData = useCallback(async (): Promise<JobsOverviewData> => {
    try {
      // Load data from all jobs modules in parallel
      const [
        jobsResponse,
        opportunitiesResponse,
        bidsResponse,
        contractsResponse,
        assignmentsResponse,
        complianceResponse,
        rfpsResponse
      ] = await Promise.all([
        tryCatch(async () => fetch(`/api/v1/jobs?limit=1000`, { headers: { 'Content-Type': 'application/json' } })),
        tryCatch(async () => fetch(`/api/v1/jobs/opportunities?limit=1000`, { headers: { 'Content-Type': 'application/json' } })),
        tryCatch(async () => fetch(`/api/v1/jobs/bids?limit=1000`, { headers: { 'Content-Type': 'application/json' } })),
        tryCatch(async () => fetch(`/api/v1/jobs/contracts?limit=1000`, { headers: { 'Content-Type': 'application/json' } })),
        tryCatch(async () => fetch(`/api/v1/jobs/assignments?limit=1000`, { headers: { 'Content-Type': 'application/json' } })),
        tryCatch(async () => fetch(`/api/v1/jobs/compliance?limit=1000`, { headers: { 'Content-Type': 'application/json' } })),
        tryCatch(async () => fetch(`/api/v1/jobs/rfps?limit=1000`, { headers: { 'Content-Type': 'application/json' } }))
      ]);

      // Process all responses
      const jobs = jobsResponse.success ? await jobsResponse.data.json() : { jobs: [] };
      const opportunities = opportunitiesResponse.success ? await opportunitiesResponse.data.json() : { opportunities: [] };
      const bids = bidsResponse.success ? await bidsResponse.data.json() : { bids: [] };
      const contracts = contractsResponse.success ? await contractsResponse.data.json() : { contracts: [] };
      const assignments = assignmentsResponse.success ? await assignmentsResponse.data.json() : { assignments: [] };
      const compliance = complianceResponse.success ? await complianceResponse.data.json() : { compliance: [] };
      const rfps = rfpsResponse.success ? await rfpsResponse.data.json() : { rfps: [] };

      // Calculate statistics
      const jobsList = jobs.jobs || [];
      const opportunitiesList = opportunities.opportunities || [];
      const bidsList = bids.bids || [];
      const contractsList = contracts.contracts || [];
      const assignmentsList = assignments.assignments || [];
      const complianceList = compliance.compliance || [];
      const rfpsList = rfps.rfps || [];

      // Aggregate recent activity from all modules
      const recentActivity: RecentActivity[] = [
        ...jobsList.slice(0, 3).map((job: unknown) => ({
          id: job.id,
          type: 'job' as const,
          title: job.title || 'Untitled Job',
          status: job.status || 'unknown',
          updatedAt: job.updated_at || job.created_at,
          priority: job.priority
        })),
        ...opportunitiesList.slice(0, 2).map((opp: unknown) => ({
          id: opp.id,
          type: 'opportunity' as const,
          title: opp.title || 'Untitled Opportunity',
          status: opp.status || 'unknown',
          updatedAt: opp.updated_at || opp.created_at,
          priority: opp.priority
        })),
        ...bidsList.slice(0, 2).map((bid: unknown) => ({
          id: bid.id,
          type: 'bid' as const,
          title: bid.opportunity_title || 'Bid',
          status: bid.status || 'unknown',
          updatedAt: bid.submitted_at || bid.created_at,
          priority: 'medium'
        }))
      ].sort((a, b) => new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime()).slice(0, 10);

      return {
        totalJobs: jobsList.length,
        activeJobs: jobsList.filter((j: unknown) => j.status === 'in_progress').length,
        completedJobs: jobsList.filter((j: unknown) => j.status === 'done').length,
        draftJobs: jobsList.filter((j: unknown) => j.status === 'draft').length,
        totalOpportunities: opportunitiesList.length,
        activeBids: bidsList.filter((b: unknown) => ['submitted', 'under_review'].includes(b.status)).length,
        activeContracts: contractsList.filter((c: unknown) => c.status === 'active').length,
        pendingCompliance: complianceList.filter((c: unknown) => c.status === 'pending').length,
        totalAssignments: assignmentsList.length,
        activeRFPs: rfpsList.filter((r: unknown) => r.status === 'open').length,
        recentActivity
      };
    } catch (error) {
      console.error('Error loading overview data:', error);
      return {
        totalJobs: 0,
        activeJobs: 0,
        completedJobs: 0,
        draftJobs: 0,
        totalOpportunities: 0,
        activeBids: 0,
        activeContracts: 0,
        pendingCompliance: 0,
        totalAssignments: 0,
        activeRFPs: 0,
        recentActivity: []
      };
    }
  }, []);

  // Load data on mount
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const data = await loadOverviewData();
      setOverviewData(data);
      setLoading(false);
    };
    void fetchData();
  }, [loadOverviewData]);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
      case 'in_progress':
      case 'open':
        return 'success';
      case 'pending':
      case 'draft':
        return 'warning';
      case 'completed':
      case 'done':
        return 'info';
      case 'cancelled':
      case 'rejected':
        return 'destructive';
      default:
        return 'secondary';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'job':
        return <Briefcase className="h-4 w-4" />;
      case 'opportunity':
        return <TrendingUp className="h-4 w-4" />;
      case 'bid':
        return <DollarSign className="h-4 w-4" />;
      case 'contract':
        return <FileText className="h-4 w-4" />;
      case 'assignment':
        return <Users className="h-4 w-4" />;
      case 'compliance':
        return <CheckCircle className="h-4 w-4" />;
      case 'rfp':
        return <FileText className="h-4 w-4" />;
      default:
        return <Briefcase className="h-4 w-4" />;
    }
  };

  if (loading) {
    return (
      <div className="h-full w-full p-lg">
        <div className="stack-lg">
          <div>
            <h1 className="text-heading-3 color-foreground">Jobs Overview</h1>
            <p className="color-muted">Loading jobs dashboard...</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
            {[...Array(8)].map((_, i) => (
              <Card key={i} className="p-lg">
                <div className="stack-sm">
                  <div className="h-6 w-6 bg-secondary rounded" />
                  <div className="h-4 w-20 bg-secondary rounded" />
                  <div className="h-8 w-16 bg-secondary rounded" />
                </div>
              </Card>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (!overviewData) {
    return (
      <div className="h-full w-full p-lg">
        <div className="text-center stack-lg">
          <AlertTriangle className="h-12 w-12 color-warning mx-auto" />
          <div>
            <h2 className="text-heading-4 color-foreground">Unable to Load Overview</h2>
            <p className="color-muted">There was an error loading the jobs overview data.</p>
          </div>
          <Button onClick={() => window.location.reload()}>
            Retry
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full w-full">
      <StateManagerProvider>
        <div className="flex flex-col h-full stack-lg p-lg">
          {/* Header */}
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-heading-3 color-foreground">Jobs Overview</h1>
              <p className="color-muted">Comprehensive dashboard for all job-related activities</p>
            </div>
            <div className="flex gap-sm">
              <Link href="/jobs">
                <Button variant="outline">View All Jobs</Button>
              </Link>
              <Link href="/jobs/opportunities">
                <Button>New Opportunity</Button>
              </Link>
            </div>
          </div>

          {/* Statistics Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
            <Card className="p-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Total Jobs</p>
                  <p className="text-heading-2 font-semibold color-foreground">{overviewData.totalJobs}</p>
                  <p className="text-body-xs color-muted">{overviewData.activeJobs} active</p>
                </div>
                <Briefcase className="h-8 w-8 color-accent" />
              </div>
            </Card>

            <Card className="p-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Opportunities</p>
                  <p className="text-heading-2 font-semibold color-foreground">{overviewData.totalOpportunities}</p>
                  <p className="text-body-xs color-muted">Pipeline value</p>
                </div>
                <TrendingUp className="h-8 w-8 color-success" />
              </div>
            </Card>

            <Card className="p-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Active Bids</p>
                  <p className="text-heading-2 font-semibold color-foreground">{overviewData.activeBids}</p>
                  <p className="text-body-xs color-muted">In progress</p>
                </div>
                <DollarSign className="h-8 w-8 color-warning" />
              </div>
            </Card>

            <Card className="p-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Active Contracts</p>
                  <p className="text-heading-2 font-semibold color-foreground">{overviewData.activeContracts}</p>
                  <p className="text-body-xs color-muted">Running</p>
                </div>
                <FileText className="h-8 w-8 color-info" />
              </div>
            </Card>

            <Card className="p-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Assignments</p>
                  <p className="text-heading-2 font-semibold color-foreground">{overviewData.totalAssignments}</p>
                  <p className="text-body-xs color-muted">Total assigned</p>
                </div>
                <Users className="h-8 w-8 color-accent" />
              </div>
            </Card>

            <Card className="p-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Pending Compliance</p>
                  <p className="text-heading-2 font-semibold color-foreground">{overviewData.pendingCompliance}</p>
                  <p className="text-body-xs color-muted">Requires attention</p>
                </div>
                <AlertTriangle className="h-8 w-8 color-warning" />
              </div>
            </Card>

            <Card className="p-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Active RFPs</p>
                  <p className="text-heading-2 font-semibold color-foreground">{overviewData.activeRFPs}</p>
                  <p className="text-body-xs color-muted">Open for bids</p>
                </div>
                <FileText className="h-8 w-8 color-info" />
              </div>
            </Card>

            <Card className="p-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Completion Rate</p>
                  <p className="text-heading-2 font-semibold color-foreground">
                    {overviewData.totalJobs > 0 ? Math.round((overviewData.completedJobs / overviewData.totalJobs) * 100) : 0}%
                  </p>
                  <p className="text-body-xs color-muted">Jobs completed</p>
                </div>
                <CheckCircle className="h-8 w-8 color-success" />
              </div>
            </Card>
          </div>

          {/* Recent Activity */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-lg">
            <Card className="p-lg">
              <div className="stack-md">
                <div className="flex items-center justify-between">
                  <h3 className="text-heading-4 color-foreground">Recent Activity</h3>
                  <Link href="/jobs">
                    <Button variant="ghost" size="sm">View All</Button>
                  </Link>
                </div>

                <div className="stack-sm">
                  {overviewData.recentActivity.length === 0 ? (
                    <div className="text-center p-lg">
                      <Clock className="h-8 w-8 color-muted mx-auto mb-sm" />
                      <p className="color-muted">No recent activity</p>
                    </div>
                  ) : (
                    overviewData.recentActivity.map((activity) => (
                      <div key={activity.id} className="flex items-center justify-between p-sm border border-border rounded-md">
                        <div className="flex items-center gap-sm">
                          {getTypeIcon(activity.type)}
                          <div>
                            <p className="text-body-sm font-medium color-foreground">{activity.title}</p>
                            <p className="text-body-xs color-muted">
                              {activity.type} â€¢ {new Date(activity.updatedAt).toLocaleDateString()}
                            </p>
                          </div>
                        </div>
                        <Badge variant={getStatusColor(activity.status)}>
                          {activity.status}
                        </Badge>
                      </div>
                    ))
                  )}
                </div>
              </div>
            </Card>

            {/* Quick Actions */}
            <Card className="p-lg">
              <div className="stack-md">
                <h3 className="text-heading-4 color-foreground">Quick Actions</h3>

                <div className="grid grid-cols-2 gap-sm">
                  <Link href="/jobs/opportunities">
                    <Button variant="outline" className="w-full justify-start">
                      <Plus className="h-4 w-4 mr-xs" />
                      New Opportunity
                    </Button>
                  </Link>

                  <Link href="/jobs/rfps">
                    <Button variant="outline" className="w-full justify-start">
                      <Plus className="h-4 w-4 mr-xs" />
                      Create RFP
                    </Button>
                  </Link>

                  <Link href="/jobs/assignments">
                    <Button variant="outline" className="w-full justify-start">
                      <Users className="h-4 w-4 mr-xs" />
                      Assign Jobs
                    </Button>
                  </Link>

                  <Link href="/jobs/compliance">
                    <Button variant="outline" className="w-full justify-start">
                      <CheckCircle className="h-4 w-4 mr-xs" />
                      Check Compliance
                    </Button>
                  </Link>

                  <Link href="/jobs/contracts">
                    <Button variant="outline" className="w-full justify-start">
                      <FileText className="h-4 w-4 mr-xs" />
                      Manage Contracts
                    </Button>
                  </Link>

                  <Link href="/jobs/bids">
                    <Button variant="outline" className="w-full justify-start">
                      <DollarSign className="h-4 w-4 mr-xs" />
                      Review Bids
                    </Button>
                  </Link>
                </div>
              </div>
            </Card>
          </div>
        </div>
      </StateManagerProvider>
    </div>
  );
}
  totalAssignments: number;
  activeRFPs: number;
}

interface RecentActivity {
  id: string;
  type: 'job' | 'opportunity' | 'bid' | 'contract' | 'assignment' | 'compliance' | 'rfp';
  title: string;
  status: string;
  updatedAt: string;
  priority?: 'high' | 'medium' | 'low';
}

const QUICK_ACTIONS = [
  {
    title: 'Create New Job',
    description: 'Start a new job posting',
    href: '/jobs?action=create',
    icon: BriefcaseIcon,
    color: 'bg-accent',
  },
  {
    title: 'Browse Opportunities',
    description: 'View available opportunities',
    href: '/jobs/opportunities',
    icon: ClipboardDocumentListIcon,
    color: 'bg-success',
  },
  {
    title: 'Submit New Bid',
    description: 'Create a new bid proposal',
    href: '/jobs/bids?action=create',
    icon: DocumentTextIcon,
    color: 'bg-secondary',
  },
  {
    title: 'Review Compliance',
    description: 'Check compliance status',
    href: '/jobs/compliance',
    icon: CheckCircleIcon,
    color: 'bg-warning',
  },
];

export function OverviewClient({ user, orgId, translations }: OverviewClientProps) {
  const [stats, setStats] = useState<JobsStats | null>(null);
  const [recentActivity, setRecentActivity] = useState<RecentActivity[]>([]);
  const [loading, setLoading] = useState(true);
  const supabase = createBrowserClient();

  useEffect(() => {
    loadOverviewData();
  }, [orgId]);

  const loadOverviewData = async () => {
    try {
      setLoading(true);

      // Load jobs stats
      const [
        jobsResult,
        opportunitiesResult,
        bidsResult,
        contractsResult,
        assignmentsResult,
        complianceResult,
        rfpsResult
      ] = await Promise.all([
        supabase.from('jobs').select('status').eq('organization_id', orgId),
        supabase.from('opportunities').select('status').eq('organization_id', orgId),
        supabase.from('job_bids').select('status').eq('organization_id', orgId),
        supabase.from('job_contracts').select('status').eq('organization_id', orgId),
        supabase.from('job_assignments').select('status').eq('organization_id', orgId),
        supabase.from('job_compliance').select('status').eq('organization_id', orgId),
        supabase.from('rfps').select('status').eq('organization_id', orgId)
      ]);

      const jobs = jobsResult.data || [];
      const opportunities = opportunitiesResult.data || [];
      const bids = bidsResult.data || [];
      const contracts = contractsResult.data || [];
      const assignments = assignmentsResult.data || [];
      const compliance = complianceResult.data || [];
      const rfps = rfpsResult.data || [];

      setStats({
        totalJobs: jobs.length,
        activeJobs: jobs.filter((j: unknown) => j.status === 'in_progress').length,
        completedJobs: jobs.filter((j: unknown) => j.status === 'completed').length,
        draftJobs: jobs.filter((j: unknown) => j.status === 'draft').length,
        totalOpportunities: opportunities.length,
        activeBids: bids.filter((b: unknown) => b.status === 'submitted' || b.status === 'under_review').length,
        activeContracts: contracts.filter((c: unknown) => c.status === 'active').length,
        pendingCompliance: compliance.filter((c: unknown) => c.status === 'pending').length,
        totalAssignments: assignments.length,
        activeRFPs: rfps.filter((r: unknown) => r.status === 'open').length,
      });

      // Load recent activity (combining multiple sources)
      const recentJobs = await supabase
        .from('jobs')
        .select('id, title, status, updated_at')
        .eq('organization_id', orgId)
        .order('updated_at', { ascending: false })
        .limit(3);

      const recentOpportunities = await supabase
        .from('opportunities')
        .select('id, title, status, updated_at')
        .eq('organization_id', orgId)
        .order('updated_at', { ascending: false })
        .limit(2);

      const activity: RecentActivity[] = [
        ...(recentJobs.data || []).map((job: unknown) => ({
          id: job.id,
          type: 'job' as const,
          title: job.title,
          status: job.status,
          updatedAt: job.updated_at,
        })),
        ...(recentOpportunities.data || []).map((opp: unknown) => ({
          id: opp.id,
          type: 'opportunity' as const,
          title: opp.title,
          status: opp.status,
          updatedAt: opp.updated_at,
        })),
      ];

      // Sort by updated_at and take top 5
      activity.sort((a, b) => new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime());
      setRecentActivity(activity.slice(0, 5));

    } catch (error) {
      console.error('Error loading overview data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'active': return 'bg-success/10 color-success';
      case 'completed': return 'bg-accent/10 color-accent';
      case 'pending': return 'bg-warning/10 color-warning';
      case 'draft': return 'bg-secondary/10 color-muted';
      case 'cancelled': return 'bg-destructive/10 color-destructive';
      default: return 'bg-secondary/10 color-muted';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'job':
        return BriefcaseIcon;
      case 'opportunity':
        return ClipboardDocumentListIcon;
      case 'bid':
        return DocumentTextIcon;
      case 'contract':
        return CheckCircleIcon;
      case 'assignment':
        return CalendarIcon;
      case 'compliance':
        return ExclamationTriangleIcon;
      default:
        return BriefcaseIcon;
    }
  };

  return (
    <div className="stack-lg">
      {/* Header */}
      <div>
        <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
        <p className="text-body-sm color-foreground/70 mt-xs">{translations.subtitle}</p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
        {loading ? (
          Array.from({ length: 8 }).map((_, i) => (
            <Card key={i} className="p-md">
              <Skeleton className="h-4 w-20 mb-sm" />
              <Skeleton className="h-8 w-12" />
            </Card>
          ))
        ) : (
          <>
            <Card className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-foreground/70">Total Jobs</p>
                  <p className="text-heading-3 text-heading-3 color-foreground">{stats?.totalJobs || 0}</p>
                </div>
                <BriefcaseIcon className="h-8 w-8 color-accent" />
              </div>
            </Card>

            <Card className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-foreground/70">Active Jobs</p>
                  <p className="text-body-sm color-muted">{stats?.activeJobs || 0} active</p>
                </div>
                <ChartBarIcon className="h-8 w-8 color-success" />
              </div>
            </Card>

            <Card className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="color-muted">Monitor job performance and manage opportunities.</p>
                  <p className="text-heading-3 text-heading-3 color-success">{stats?.totalOpportunities || 0}</p>
                </div>
                <ClipboardDocumentListIcon className="h-8 w-8 color-success" />
              </div>
            </Card>

            <Card className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-foreground/70">Active Bids</p>
                  <p className="text-body-sm color-muted">{stats?.activeBids || 0} active bids</p>
                </div>
                <DocumentTextIcon className="h-8 w-8 color-secondary" />
              </div>
            </Card>

            <Card className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <span className="text-body-sm color-muted">tracts</span>
                  <p className="text-heading-3 text-heading-3 color-secondary">{stats?.activeContracts || 0}</p>
                </div>
                <CheckCircleIcon className="h-8 w-8 color-accent" />
              </div>
            </Card>

            <Card className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-foreground/70">Assignments</p>
                  <p className="text-body-sm color-muted">{stats?.totalAssignments || 0} assignments</p>
                </div>
                <DocumentTextIcon className="h-8 w-8 color-warning" />
              </div>
            </Card>

            <Card className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm color-muted">Pending Compliance</p>
                  <h1 className="text-heading-3 text-heading-3 color-foreground">{stats?.pendingCompliance || 0}</h1>
                </div>
                <ExclamationTriangleIcon className="h-8 w-8 color-warning" />
              </div>
            </Card>

            <Card className="p-md">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-body-sm form-label color-foreground/70">Active RFPs</p>
                  <p className="text-body-sm color-muted">{stats?.activeRFPs || 0} active RFPs</p>
                </div>
                <DocumentTextIcon className="h-8 w-8 color-accent" />
              </div>
            </Card>
          </>
        )}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-lg">
        {/* Quick Actions */}
        <Card title="Quick Actions" className="p-lg">
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-md">
            {QUICK_ACTIONS.map((action: unknown) => {
              const Icon = action.icon;
              return (
                <Link key={action.title} href="#">
                  <div className="flex items-center p-md rounded-lg border border-border hover:bg-accent transition-colors cursor-pointer">
                    <div className={`p-sm rounded-lg ${action.color} text-background mr-sm`}>
                      <Icon className="h-5 w-5" />
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-body-sm form-label color-foreground truncate">
                        {action.title}
                      </p>
                      <p className="text-body-sm color-foreground/70 truncate">
                        {action.description}
                      </p>
                    </div>
                    <ArrowRightIcon className="h-4 w-4 color-foreground/40" />
                  </div>
                </Link>
              );
            })}
          </div>
        </Card>

        {/* Recent Activity */}
        <Card title="Recent Activity" className="p-lg">
          {loading ? (
            <div className="stack-sm">
              {Array.from({ length: 5 }).map((_, i) => (
                <div key={i} className="flex items-center cluster-sm">
                  <div className="w-3 h-3 bg-secondary rounded"></div>
                  <Skeleton className="h-5 w-16" />
                </div>
              ))}
            </div>
          ) : recentActivity.length > 0 ? (
            <div className="stack-sm">
              {recentActivity.map((activity: unknown) => {
                const Icon = getTypeIcon(activity.type);
                return (
                  <div key={activity.id} className="flex items-center justify-between p-sm rounded-lg border border-border">
                    <div className="flex items-center cluster-sm">
                      <Icon className="h-5 w-5 color-foreground/60" />
                      <div>
                        <p className="text-body-sm form-label color-foreground truncate">
                          {activity.title}
                        </p>
                        <p className="text-body-sm color-foreground/70 capitalize">
                          {activity.type} â€¢ {new Date(activity.updatedAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                    <Badge className={getStatusColor(activity.status)}>
                      {activity.status.replace('_', ' ')}
                    </Badge>
                  </div>
                );
              })}
            </div>
          ) : (
            <div className="text-center py-xl">
              <BriefcaseIcon className="h-12 w-12 color-muted mx-auto mb-sm" />
              <p className="text-body-sm color-muted">No recent activity</p>
              <p className="text-body-sm color-muted mt-xs">
                Create your first job to get started
              </p>
            </div>
          )}

          {recentActivity.length > 0 && (
            <div className="mt-md pt-md border-t border-border">
              <Link href="/jobs">
                <Button className="w-full">
                  View All Jobs
                  <ArrowRightIcon className="h-4 w-4 ml-sm" />
                </Button>
              </Link>
            </div>
          )}
        </Card>
      </div>
    </div>
  );
}

'use client';

import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Button, Badge, UnifiedInput, Skeleton } from '@ghxstship/ui';
import {
  MagnifyingGlassIcon,
  PlusIcon,
  EyeIcon,
  PencilIcon,
  DocumentTextIcon,
  ClockIcon,
  CheckCircleIcon,
  XCircleIcon,
  ExclamationTriangleIcon,
  CalendarIcon,
  BuildingOfficeIcon,
  CurrencyDollarIcon,
  ArrowDownTrayIcon,
  PaperAirplaneIcon,
  UsersIcon,
} from '@heroicons/react/24/outline';

interface RFPsClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface RFP {
  id: string;
  title: string;
  description: string;
  issuer_organization: string;
  status: 'draft' | 'published' | 'open' | 'closed' | 'awarded' | 'cancelled';
  type: 'services' | 'products' | 'consulting' | 'construction' | 'technology' | 'other';
  budget_min?: number;
  budget_max?: number;
  currency: string;
  submission_deadline: string;
  project_start_date?: string;
  project_duration?: string;
  requirements?: string[];
  evaluation_criteria?: string[];
  contact_email?: string;
  document_url?: string;
  responses_count?: number;
  published_at?: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
}

const STATUS_OPTIONS = [
  { value: 'all', label: 'All Statuses' },
  { value: 'draft', label: 'Draft' },
  { value: 'published', label: 'Published' },
  { value: 'open', label: 'Open' },
  { value: 'closed', label: 'Closed' },
  { value: 'awarded', label: 'Awarded' },
  { value: 'cancelled', label: 'Cancelled' },
];

const TYPE_OPTIONS = [
  { value: 'all', label: 'All Types' },
  { value: 'services', label: 'Services' },
  { value: 'products', label: 'Products' },
  { value: 'consulting', label: 'Consulting' },
  { value: 'construction', label: 'Construction' },
  { value: 'technology', label: 'Technology' },
  { value: 'other', label: 'Other' },
];

export function RFPsClient({ user, orgId, translations }: RFPsClientProps) {
  const [rfps, setRfps] = useState<RFP[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');
  const [showCreateDialog, setShowCreateDialog] = useState(false);

  const supabase = createBrowserClient();

  useEffect(() => {
    loadRFPs();
  }, [orgId]);

  const loadRFPs = async () => {
    try {
      setLoading(true);

      const { data, error } = await supabase
        .from('rfps')
        .select('*')
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setRfps(data || []);
    } catch (error) {
      console.error('Error loading RFPs:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredRFPs = rfps.filter((rfp: unknown) => {
    const matchesSearch = rfp.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         rfp.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         rfp.issuer_organization.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || rfp.status === statusFilter;
    const matchesType = typeFilter === 'all' || rfp.type === typeFilter;

    return matchesSearch && matchesStatus && matchesType;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'draft':
        return 'bg-secondary color-muted';
      case 'published':
        return 'bg-accent/10 color-accent';
      case 'open':
        return 'bg-success/10 color-success';
      case 'closed':
        return 'bg-warning/10 color-warning';
      case 'awarded':
        return 'bg-secondary/10 color-secondary';
      case 'cancelled':
        return 'bg-destructive/10 color-destructive';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'draft':
        return DocumentTextIcon;
      case 'published':
        return PaperAirplaneIcon;
      case 'open':
        return CheckCircleIcon;
      case 'closed':
        return ClockIcon;
      case 'awarded':
        return CheckCircleIcon;
      case 'cancelled':
        return XCircleIcon;
      default:
        return DocumentTextIcon;
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'services':
        return 'bg-accent/10 color-accent';
      case 'products':
        return 'bg-success/10 color-success';
      case 'consulting':
        return 'bg-secondary/10 color-secondary';
      case 'construction':
        return 'bg-warning/10 color-warning';
      case 'technology':
        return 'bg-accent/10 color-accent';
      case 'other':
        return 'bg-secondary color-muted';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const formatBudget = (min?: number, max?: number, currency: string = 'USD') => {
    const formatter = new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    });

    if (!min && !max) return 'Budget not specified';
    if (min && max) return `${formatter.format(min)} - ${formatter.format(max)}`;
    if (min) return `From ${formatter.format(min)}`;
    if (max) return `Up to ${formatter.format(max)}`;
    return 'Budget not specified';
  };

  const isDeadlineApproaching = (deadline: string) => {
    const deadlineDate = new Date(deadline);
    const now = new Date();
    const diffDays = Math.ceil((deadlineDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
    return diffDays <= 7 && diffDays >= 0;
  };

  const isOverdue = (deadline: string) => {
    return new Date(deadline) < new Date();
  };

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
          <p className="text-body-sm color-foreground/70 mt-xs">{translations.subtitle}</p>
        </div>
        <Button onClick={() => setShowCreateDialog(true)}>
          <PlusIcon className="h-4 w-4 mr-sm" />
          Create RFP
        </Button>
      </div>

      {/* Filters */}
      <Card className="p-md">
        <div className="flex flex-col sm:flex-row gap-md">
          <div className="flex-1">
            <UnifiedInput               placeholder="Search RFPs..."
              value={searchTerm}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
              className="w-full"
            />
          </div>
          <div className="flex gap-sm">
            <select
              value={statusFilter}
              onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setStatusFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {STATUS_OPTIONS.map((option: unknown) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <select
              value={typeFilter}
              onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setTypeFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {TYPE_OPTIONS.map((option: unknown) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>
        </div>
      </Card>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-md">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Total RFPs</p>
              <p className="text-heading-3 text-heading-3 color-foreground">{rfps.length}</p>
            </div>
            <DocumentTextIcon className="h-8 w-8 color-accent" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Open</p>
              <p className="text-heading-3 text-heading-3 color-success">
                {rfps.filter(r => r.status === 'open').length}
              </p>
            </div>
            <CheckCircleIcon className="h-8 w-8 color-success" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Published</p>
              <p className="text-heading-3 text-heading-3 color-accent">
                {rfps.filter(r => r.status === 'published').length}
              </p>
            </div>
            <PaperAirplaneIcon className="h-8 w-8 color-accent" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Awarded</p>
              <p className="text-heading-3 text-heading-3 color-secondary">
                {rfps.filter(r => r.status === 'awarded').length}
              </p>
            </div>
            <CheckCircleIcon className="h-8 w-8 color-secondary" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Total Responses</p>
              <p className="text-heading-3 text-heading-3 color-warning">
                {rfps.reduce((sum, r) => sum + (r.responses_count || 0), 0)}
              </p>
            </div>
            <UsersIcon className="h-8 w-8 color-warning" />
          </div>
        </Card>
      </div>

      {/* RFPs List */}
      <div className="stack-md">
        {loading ? (
          Array.from({ length: 5 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <Skeleton className="h-5 w-64 mb-sm" />
                  <Skeleton className="h-4 w-32 mb-sm" />
                  <Skeleton className="h-4 w-full mb-sm" />
                  <Skeleton className="h-4 w-3/4" />
                </div>
                <div className="flex gap-sm">
                  <Skeleton className="h-6 w-16" />
                  <Skeleton className="h-6 w-20" />
                </div>
              </div>
            </Card>
          ))
        ) : filteredRFPs.length > 0 ? (
          filteredRFPs.map((rfp: unknown) => {
            const StatusIcon = getStatusIcon(rfp.status);
            const isUrgent = isDeadlineApproaching(rfp.submission_deadline);
            const isLate = isOverdue(rfp.submission_deadline);

            return (
              <Card key={rfp.id} className={`p-lg hover:shadow-elevated transition-shadow ${isLate ? 'border-destructive/20 bg-destructive/5' : isUrgent ? 'border-warning/20 bg-warning/5' : ''}`}>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-start justify-between mb-sm">
                      <div>
                        <h3 className="text-body text-heading-4 color-foreground mb-xs flex items-center gap-sm">
                          <StatusIcon className="h-5 w-5 color-foreground/60" />
                          {rfp.title}
                          {(isUrgent || isLate) && (
                            <ExclamationTriangleIcon className={`h-4 w-4 ${isLate ? 'color-destructive' : 'color-warning'}`} />
                          )}
                        </h3>
                        <div className="flex items-center gap-sm text-body-sm color-foreground/70">
                          <BuildingOfficeIcon className="h-4 w-4" />
                          <span>{rfp.issuer_organization}</span>
                          {rfp.responses_count !== undefined && (
                            <>
                              <span>â€¢</span>
                              <span>{rfp.responses_count} responses</span>
                            </>
                          )}
                        </div>
                      </div>
                      <div className="flex gap-sm">
                        <Badge className={getStatusColor(rfp.status)}>
                          {rfp.status.replace('_', ' ')}
                        </Badge>
                        <Badge className={getTypeColor(rfp.type)}>
                          {rfp.type}
                        </Badge>
                      </div>
                    </div>

                    <p className="text-body-sm color-foreground/80 mb-sm line-clamp-2">
                      {rfp.description}
                    </p>

                    <div className="flex items-center gap-lg text-body-sm color-foreground/70 mb-sm">
                      <div className="flex items-center gap-xs">
                        <CurrencyDollarIcon className="h-4 w-4" />
                        <span>{formatBudget(rfp.budget_min, rfp.budget_max, rfp.currency)}</span>
                      </div>
                      <div className={`flex items-center gap-xs ${isLate ? 'color-destructive' : isUrgent ? 'color-warning' : ''}`}>
                        <CalendarIcon className="h-4 w-4" />
                        <span>
                          {isLate ? 'Overdue: ' : 'Deadline: '}
                          {new Date(rfp.submission_deadline).toLocaleDateString()}
                        </span>
                      </div>
                      {rfp.project_start_date && (
                        <div className="flex items-center gap-xs">
                          <span>Start: {new Date(rfp.project_start_date).toLocaleDateString()}</span>
                        </div>
                      )}
                      {rfp.project_duration && (
                        <div className="flex items-center gap-xs">
                          <ClockIcon className="h-4 w-4" />
                          <span>{rfp.project_duration}</span>
                        </div>
                      )}
                    </div>

                    {rfp.requirements && rfp.requirements.length > 0 && (
                      <div className="mb-sm">
                        <span className="text-body-sm form-label color-foreground/70 mb-xs block">Key Requirements:</span>
                        <div className="stack-md">
                          {rfp.requirements.slice(0, 3).map((req, index) => (
                            <div key={index} className="text-body-sm color-foreground/70 flex items-start gap-sm">
                              <span className="color-foreground/40">â€¢</span>
                              <span>{req}</span>
                            </div>
                          ))}
                          {rfp.requirements.length > 3 && (
                            <div className="text-body-sm color-foreground/50">
                              +{rfp.requirements.length - 3} more requirements
                            </div>
                          )}
                        </div>
                      </div>
                    )}

                    {rfp.evaluation_criteria && rfp.evaluation_criteria.length > 0 && (
                      <div className="mb-sm">
                        <span className="text-body-sm form-label color-foreground/70 mb-xs block">Evaluation Criteria:</span>
                        <div className="flex flex-wrap gap-xs">
                          {rfp.evaluation_criteria.slice(0, 4).map((criteria, index) => (
                            <Badge key={index} variant="secondary">
                              {criteria}
                            </Badge>
                          ))}
                          {rfp.evaluation_criteria.length > 4 && (
                            <Badge variant="secondary">
                              +{rfp.evaluation_criteria.length - 4} more
                            </Badge>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex items-center justify-between mt-md pt-md border-t border-border">
                  <div className="text-body-sm color-foreground/50">
                    Created: {new Date(rfp.created_at).toLocaleDateString()}
                    {rfp.published_at && (
                      <span> â€¢ Published: {new Date(rfp.published_at).toLocaleDateString()}</span>
                    )}
                    {rfp.contact_email && (
                      <span> â€¢ Contact: {rfp.contact_email}</span>
                    )}
                  </div>
                  <div className="flex gap-sm">
                    <Button>
                      <EyeIcon className="h-4 w-4 mr-xs" />
                      View
                    </Button>
                    <Button>
                      <PencilIcon className="h-4 w-4 mr-xs" />
                      Edit
                    </Button>
                    {rfp.document_url && (
                      <Button>
                        <ArrowDownTrayIcon className="h-4 w-4 mr-xs" />
                        Download
                      </Button>
                    )}
                    {rfp.status === 'open' && (
                      <Button>
                        <PaperAirplaneIcon className="h-4 w-4 mr-xs" />
                        Respond
                      </Button>
                    )}
                  </div>
                </div>
              </Card>
            );
          })
        ) : (
          <Card className="p-2xl text-center">
            <DocumentTextIcon className="h-12 w-12 color-foreground/30 mx-auto mb-md" />
            <h3 className="text-body form-label color-foreground mb-sm">No RFPs found</h3>
            <p className="text-body-sm color-foreground/70 mb-md">
              {searchTerm || statusFilter !== 'all' || typeFilter !== 'all'
                ? 'Try adjusting your filters or search terms.'
                : 'Get started by creating your first RFP.'}
            </p>
            <Button onClick={() => setShowCreateDialog(true)}>
              <PlusIcon className="h-4 w-4 mr-sm" />
              Create RFP
            </Button>
          </Card>
        )}
      </div>
    </div>
  );
}

import { type DataRecord } from '@ghxstship/ui';

// Core RFP types
export type RfpStatus = 'open' | 'closed' | 'awarded' | 'cancelled';
export type RfpCategory = 'construction' | 'consulting' | 'technology' | 'services' | 'supplies' | 'other';
export type RfpPriority = 'low' | 'medium' | 'high' | 'critical';
export type RfpEvaluationCriteria = 'price' | 'quality' | 'experience' | 'timeline' | 'technical' | 'references';

// Main RFP interface
export interface JobRfp extends DataRecord {
  id: string;
  organization_id: string;
  project_id?: string;
  title: string;
  description?: string;
  status: RfpStatus;
  due_at?: string;
  created_at: string;
  // Enhanced fields from joins
  project_title?: string;
  organization_name?: string;
  category?: RfpCategory;
  budget_min?: number;
  budget_max?: number;
  currency?: string;
  requirements?: string[];
  evaluation_criteria?: RfpEvaluationCriteria[];
  submission_guidelines?: string;
  contact_person?: string;
  contact_email?: string;
  location?: string;
  duration?: string;
  start_date?: string;
  end_date?: string;
  award_date?: string;
  winner_id?: string;
  winner_name?: string;
  notes?: string;
}

// API response types
export interface RfpsResponse {
  rfps: JobRfp[];
  total?: number;
  page?: number;
  limit?: number;
}

// Form data types
export interface CreateRfpData {
  organization_id: string;
  project_id?: string;
  title: string;
  description?: string;
  category?: RfpCategory;
  budget_min?: number;
  budget_max?: number;
  currency?: string;
  requirements?: string[];
  evaluation_criteria?: RfpEvaluationCriteria[];
  submission_guidelines?: string;
  contact_person?: string;
  contact_email?: string;
  location?: string;
  duration?: string;
  due_at?: string;
  start_date?: string;
  end_date?: string;
  priority?: RfpPriority;
  notes?: string;
}

export interface UpdateRfpData extends Partial<CreateRfpData> {
  id: string;
  status?: RfpStatus;
  award_date?: string;
  winner_id?: string;
}

// Filter and search types
export interface RfpFilters {
  status?: RfpStatus;
  category?: RfpCategory;
  organization_id?: string;
  project_id?: string;
  priority?: RfpPriority;
  budget_min?: number;
  budget_max?: number;
  search?: string;
  location?: string;
  evaluation_criteria?: RfpEvaluationCriteria[];
  date_from?: string;
  date_to?: string;
  due_soon?: boolean;
  contact_person?: string;
}

// Statistics types
export interface RfpStats {
  total: number;
  byStatus: Record<RfpStatus, number>;
  byCategory: Record<RfpCategory, number>;
  totalValue: number;
  averageValue: number;
  activeRfps: number;
  dueSoonRfps: number;
  recentRfps: number;
  awardRate: number;
  averageResponseTime: number;
  totalSubmissions: number;
}

// View configuration types
export interface RfpViewConfig {
  showBudgets: boolean;
  groupByCategory: boolean;
  showProjectDetails: boolean;
  showDueAlerts: boolean;
  defaultSort: 'created_at' | 'due_at' | 'budget_max' | 'status' | 'title';
  defaultView: 'grid' | 'kanban' | 'calendar' | 'list' | 'timeline' | 'dashboard';
  currencyDisplay: string;
  evaluationDisplay: boolean;
}

// Drawer types
export interface RfpDrawerProps {
  rfp?: JobRfp;
  mode: 'create' | 'edit' | 'view';
  onSave?: (data: CreateRfpData | UpdateRfpData) => Promise<void>;
  onClose: () => void;
  open: boolean;
}

