import { createBrowserClient } from '@ghxstship/auth';
import type { MarketplaceStats } from '../types';

interface OverviewClientProps {
  orgId: string;
  userId: string;
  userRole: 'vendor' | 'client' | 'both';
}

interface DashboardMetric {
  id: string;
  title: string;
  value: string | number;
  change: string;
  trend: 'up' | 'down' | 'neutral';
