    searchable: true,
    visible: true,
    width: 300
  },
  {
    key: 'user',
    label: 'User',
    type: 'text' as const,
    sortable: true,
    filterable: true,
    searchable: true,
    visible: true,
    width: 150
  },
  {
    key: 'status',
