  reviewee_name: string;
  project_title?: string;
  rating: number;
  title: string;
  content: string;
  categories: {
    communication: number;
    quality: number;
    timeliness: number;
    professionalism: number;
  };
  helpful_count: number;
  created_at: string;
  response?: {
    content: string;
    created_at: string;
  };
  verified: boolean;
  type: 'received' | 'given';
}

export default function ReviewsClient({ orgId, userId }: ReviewsClientProps) {
  const [reviews, setReviews] = useState<Review[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<'all' | 'received' | 'given'>('all');
  const [ratingFilter, setRatingFilter] = useState<number | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [showWriteReview, setShowWriteReview] = useState(false);
  const [stats, setStats] = useState({
    averageRating: 0,
    totalReviews: 0,
    fiveStars: 0,
    fourStars: 0,
    threeStars: 0,
    twoStars: 0,
    oneStars: 0
  });

  useEffect(() => {
    loadReviews();
  }, [orgId]);

  const loadReviews = async () => {
    try {
      setLoading(true);

      // Mock review data - would integrate with real reviews API
      const mockReviews: Review[] = [
        {
          id: '1',
          reviewer_id: 'user1',
          reviewer_name: 'TechCorp Solutions',
          reviewer_avatar: '',
