      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-md">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm color-muted">Total Vendors</p>
              <p className="text-heading-3 font-bold">{stats.total}</p>
            </div>
            <Users className="h-8 w-8 color-primary" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm color-muted">Active</p>
              <p className="text-heading-3 font-bold">{stats.active}</p>
            </div>
            <TrendingUp className="h-8 w-8 color-success" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm color-muted">Verified</p>
              <p className="text-heading-3 font-bold">{stats.verified}</p>
            </div>
            <Award className="h-8 w-8 color-warning" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm color-muted">Avg Rating</p>
              <p className="text-heading-3 font-bold">{stats.avgRating}</p>
            </div>
            <Star className="h-8 w-8 color-secondary" />
          </div>
        </Card>
      </div>

      {/* Data View */}
      <StateManagerProvider>
        <DataViewProvider
          data={vendors}
          fields={VENDORS_FIELD_CONFIGS}
          onExport={handleExport}
          onBulkAction={handleBulkAction}
        >
          <Card className="p-md">
            <div className="flex items-center justify-between mb-md">
              <ViewSwitcher
                currentView={selectedView}
                onViewChange={setSelectedView}
                availableViews={['grid', 'list']}
              />
            </div>

            <div className="stack-sm">
              <DataActions
                onSearch={(query) => console.log('Search:', query)}
                onFilter={(filters) => console.log('Filter:', filters)}
                onExport={handleExport}
                showBulkActions={true}
                bulkActions={[
                  { label: 'Delete Selected', value: 'delete', variant: 'destructive' },
                  { label: 'Verify Selected', value: 'verify', variant: 'default' }
                ]}
              />

              {selectedView === 'grid' ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
                  {vendors.map(renderVendorCard)}
                </div>
              ) : (
                <DataGrid
                  viewType={selectedView}
                  onRowClick={handleViewVendor}
                  rowActions={getRowActions}
                  emptyMessage="No vendors found"
                  loading={loading}
                  className="min-h-[400px]"
                />
              )}
            </div>
          </Card>
        </DataViewProvider>
      </StateManagerProvider>

      {/* Create/Edit Drawer */}
      <UniversalDrawer
        isOpen={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={drawerMode === 'create' ? 'Create Vendor Profile' : drawerMode === 'edit' ? 'Edit Vendor Profile' : 'View Vendor Profile'}
        size="lg"
      >
        <CreateVendorClient
          mode={drawerMode}
          vendor={selectedVendor}
          onSuccess={() => {
            setDrawerOpen(false);
            loadVendors();
          }}
          onCancel={() => setDrawerOpen(false)}
        />
      </UniversalDrawer>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useState, useCallback, useMemo } from 'react';
import { type DataRecord } from '@ghxstship/ui';
import { DataViewProvider, StateManagerProvider, ViewSwitcher, DataActions, DataGrid, KanbanBoard, CalendarView, ListView, TimelineView, GalleryView } from '@ghxstship/ui/components/DataViews';
import { type FieldConfig, type DataViewConfig } from '@ghxstship/ui/components/DataViews/types';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface Assignment extends DataRecord {
  id: string;
  project_id: string;
  role: string;
  required_count: number;
  filled_count: number;
  department?: string;
  skills_required?: string[];
  hourly_rate?: number;
  notes?: string;
  organization_id: string;
  created_at: string;
  updated_at: string;
  project?: {
    name: string;
    status: string;
  };
}

interface AssignmentsClientProps {
  orgId: string;
}

export default function AssignmentsClient({ orgId }: AssignmentsClientProps) {
  const t = useTranslations('people.assignments');
  const sb = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [assignmentsData, setAssignmentsData] = useState<Assignment[]>([]);

  useEffect(() => {
    loadAssignmentsData();

    // Set up real-time subscription
    const subscription = sb
      .channel('assignments-changes')
      .on('postgres_changes', 
        { 
          event: '*', 
          schema: 'public', 
          table: 'manning_slots',
          filter: `organization_id=eq.${orgId}`
        }, 
        (payload) => {
          console.log('Real-time update:', payload);
          loadAssignmentsData();
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [orgId]);

  const loadAssignmentsData = useCallback(async () => {
    setLoading(true);
    try {
      const { data, error } = await sb
        .from('manning_slots')
        .select(`
          *,
          project:projects(name, status)
        `)
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) {
        console.error('Supabase error:', error);
        throw error;
      }

      setAssignmentsData(data || []);
    } catch (error) {
      console.error('Error loading assignments data:', error);
      // Could add toast notification here
    } finally {
      setLoading(false);
    }
  }, [orgId, sb]);

  // ATLVS DataViews field configuration - memoized for performance
  const fields: FieldConfig[] = useMemo(() => [
    {
      key: 'role',
      label: t('role'),
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'project.name',
      label: t('project'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'required_count',
      label: t('requiredCount'),
      type: 'number',
      sortable: true,
      filterable: true
    },
    {
      key: 'filled_count',
      label: t('filledCount'),
      type: 'number',
      sortable: true,
      filterable: true
    },
    {
      key: 'department',
      label: t('department'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'hourly_rate',
      label: t('hourlyRate'),
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'created_at',
      label: t('createdAt'),
      type: 'date',
      sortable: true,
      filterable: true
    }
  ], [t]);

  // Advanced search functionality
  const handleSearch = useCallback(async (query: string) => {
    if (!query.trim()) {
      loadAssignmentsData();
      return;
    }

    try {
      const { data } = await sb
        .from('manning_slots')
        .select(`
          *,
          project:projects(name, status)
        `)
        .eq('organization_id', orgId)
        .or(`role.ilike.%${query}%,department.ilike.%${query}%,notes.ilike.%${query}%`)
        .order('created_at', { ascending: false });

      setAssignmentsData(data || []);
    } catch (error) {
      console.error('Search error:', error);
    }
  }, [orgId, sb, loadAssignmentsData]);

  // Advanced filtering
  const handleFilter = useCallback(async (filters: unknown) => {
    try {
      let query = sb
        .from('manning_slots')
        .select(`
          *,
          project:projects(name, status)
        `)
        .eq('organization_id', orgId);

      filters.forEach((filter: unknown) => {
        if (filter.value) {
          query = query.eq(filter.field, filter.value);
        }
      });

      const { data } = await query.order('created_at', { ascending: false });
      setAssignmentsData(data || []);
    } catch (error) {
      console.error('Filter error:', error);
    }
  }, [orgId, sb]);

  // Advanced sorting
  const handleSort = useCallback(async (sorts: unknown) => {
    try {
      let query = sb
        .from('manning_slots')
        .select(`
          *,
          project:projects(name, status)
        `)
        .eq('organization_id', orgId);

      if (sorts.length > 0) {
        const sort = sorts[0];
        query = query.order(sort.field, { ascending: sort.direction === 'asc' });
      }

      const { data } = await query;
      setAssignmentsData(data || []);
    } catch (error) {
      console.error('Sort error:', error);
    }
  }, [orgId, sb]);

  // Export functionality
  const handleExport = useCallback((format: string, data: DataRecord[], config?: unknown) => {
    const exportData = data.map(assignment => ({
      'Role': assignment.role,
      'Project': assignment.project?.name || 'N/A',
      'Required Count': assignment.required_count,
      'Filled Count': assignment.filled_count,
      'Department': assignment.department || 'N/A',
      'Hourly Rate': assignment.hourly_rate || 'N/A',
      'Notes': assignment.notes || 'N/A',
      'Created At': assignment.created_at
    }));

    if (format === 'csv') {
      const csv = [
        Object.keys(exportData[0]).join(','),
        ...exportData.map(row => Object.values(row).join(','))
      ].join('\n');

      const blob = new Blob([csv], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `assignments-${new Date().toISOString().split('T')[0]}.csv`;
      a.click();
    } else if (format === 'json') {
      const json = JSON.stringify(exportData, null, 2);
      const blob = new Blob([json], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `assignments-${new Date().toISOString().split('T')[0]}.json`;
      a.click();
    }
  }, []);

  // Import functionality
  const handleImport = useCallback(async (data: DataRecord[]) => {
    try {
      const importData = data.map(assignment => ({
        ...assignment,
        organization_id: orgId,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      }));

      const { error } = await sb
        .from('manning_slots')
        .insert(importData);

      if (error) throw error;

      loadAssignmentsData();
    } catch (error) {
      console.error('Import error:', error);
    }
  }, [orgId, sb, loadAssignmentsData]);

  // ATLVS DataViews configuration
  const assignmentsConfig: DataViewConfig = useMemo(() => ({
    id: 'assignments-dataviews',
    name: t('title'),
    viewType: 'grid',
    fields,
    data: assignmentsData,
    defaultView: 'grid',
    bulkActions: [
      {
        key: 'delete',
        label: 'Delete Selected',
        variant: 'destructive',
        onClick: async (selectedIds: string[]) => {
          console.log('Delete action:', selectedIds);
          // Implement delete functionality
        },
        requiresSelection: true,
        confirmMessage: 'Are you sure you want to delete the selected assignments?'
      },
      {
        key: 'export',
        label: 'Export Selected',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          const selectedData = assignmentsData.filter(a => selectedIds.includes(a.id));
          handleExport('csv', selectedData);
        },
        requiresSelection: true
      }
    ],
    exportConfig: {
      formats: ['csv', 'json', 'excel'],
      onExport: handleExport
    },
    importConfig: {
      formats: ['csv', 'json'],
      onImport: handleImport
    },
    onSearch: handleSearch,
    onFilter: handleFilter,
    onSort: handleSort
  }), [t, fields, assignmentsData, handleSearch, handleFilter, handleSort, handleExport, handleImport]);

  return (
    <div className="stack-lg">
      <DataViewProvider config={assignmentsConfig}>
        <StateManagerProvider>
          <div className="stack-md">
            <ViewSwitcher />
            <DataActions />

            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'open', title: 'Open' },
                { id: 'filled', title: 'Filled' },
                { id: 'closed', title: 'Closed' }
              ]}
              statusField="status"
              titleField="role"
            />

            <CalendarView 
              startDateField="created_at"
              titleField="role"
            />

            <ListView 
              titleField="role"
            />

            <TimelineView 
              startDateField="created_at"
              titleField="role"
            />

            <GalleryView 
              titleField="role"
            />

            <AppDrawer
              title="Assignment Details"
              open={false}
              onClose={() => {}}
              record={null}
              fields={fields}
              mode="view"
              tabs={[
                {
                  key: 'details',
                  label: 'Details',
                  content: (
                    <div className="p-md stack-md">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Role</label>
                          <div className="text-body color-foreground">Camera Operator</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Project</label>
                          <div className="text-body color-foreground">Blackwater Reverb</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Required Count</label>
                          <div className="text-body color-foreground">3</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Filled Count</label>
                          <div className="text-body color-foreground">2</div>
                        </div>
                      </div>
                    </div>
                  )
                },
                {
                  key: 'requirements',
                  label: 'Requirements',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Skills and requirements will be shown here.</p>
                    </div>
                  )
                },
                {
                  key: 'candidates',
                  label: 'Candidates',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Candidate applications will be shown here.</p>
                    </div>
                  )
                }
              ]}
            />
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { Card, Button, UnifiedInput, Badge } from '@ghxstship/ui';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { AssignmentService } from './lib/assignmentService';
import type { CreateAssignmentData } from './types';

interface CreateAssignmentClientProps {
  orgId: string;
}

export default function CreateAssignmentClient({ orgId }: CreateAssignmentClientProps) {
  const t = useTranslations('people.assignments');
  const router = useRouter();
  const sb = createBrowserClient();
  const assignmentService = new AssignmentService();

  const [loading, setLoading] = useState(false);
  const [projects, setProjects] = useState<any[]>([]);
  const [skills, setSkills] = useState<string[]>([]);
  const [newSkill, setNewSkill] = useState('');

  const [formData, setFormData] = useState<CreateAssignmentData>({
    project_id: '',
    role: '',
    required_count: 1,
    department: '',
    skills_required: [],
    hourly_rate: undefined,
    notes: ''
  });

  useEffect(() => {
    loadProjects();
  }, [orgId]);

  const loadProjects = async () => {
    try {
      const { data, error } = await sb
        .from('projects')
        .select('id, name, status')
        .eq('organization_id', orgId)
        .eq('status', 'active')
        .order('name');

      if (error) throw error;
      setProjects(data || []);
    } catch (error) {
      console.error('Error loading projects:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.project_id || !formData.role) return;

    setLoading(true);
    try {
      await assignmentService.createAssignment(orgId, {
        ...formData,
        skills_required: skills
      });

      router.push('/people/assignments');
    } catch (error) {
      console.error('Error creating assignment:', error);
    } finally {
      setLoading(false);
    }
  };

  const addSkill = () => {
    if (newSkill.trim() && !skills.includes(newSkill.trim())) {
      setSkills([...skills, newSkill.trim()]);
      setNewSkill('');
    }
  };

  const removeSkill = (skillToRemove: string) => {
    setSkills(skills.filter(skill => skill !== skillToRemove));
  };

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 font-anton uppercase">Create Assignment</h1>
          <p className="text-body-sm color-muted">Create a new project assignment</p>
        </div>
        <Button variant="outline" onClick={() => router.back()}>
          Cancel
        </Button>
      </div>

      <Card>
        <div className="p-md">
          <form onSubmit={handleSubmit} className="stack-md">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label className="block text-body-sm form-label mb-xs">Project *</label>
                <select
                  value={formData.project_id}
                  onChange={(e) => setFormData(prev => ({ ...prev, project_id: e.target.value }))}
                  className="w-full px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary bg-background"
                  required
                >
                  <option value="">Select project...</option>
                  {projects.map(project => (
                    <option key={project.id} value={project.id}>
                      {project.name}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-body-sm form-label mb-xs">Role *</label>
                <UnifiedInput
                  type="text"
                  value={formData.role}
                  onChange={(e) => setFormData(prev => ({ ...prev, role: e.target.value }))}
                  placeholder="e.g. Senior Camera Operator"
                  required
                />
              </div>

              <div>
                <label className="block text-body-sm form-label mb-xs">Department</label>
                <UnifiedInput
                  type="text"
                  value={formData.department}
                  onChange={(e) => setFormData(prev => ({ ...prev, department: e.target.value }))}
                  placeholder="e.g. Production"
                />
              </div>

              <div>
                <label className="block text-body-sm form-label mb-xs">Required Count *</label>
                <UnifiedInput
                  type="number"
                  min="1"
                  value={formData.required_count}
                  onChange={(e) => setFormData(prev => ({ ...prev, required_count: parseInt(e.target.value) || 1 }))}
                  required
                />
              </div>

              <div>
                <label className="block text-body-sm form-label mb-xs">Hourly Rate</label>
                <UnifiedInput
                  type="number"
                  step="0.01"
                  value={formData.hourly_rate || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, hourly_rate: parseFloat(e.target.value) || undefined }))}
                  placeholder="0.00"
                />
              </div>
            </div>

            <div>
              <label className="block text-body-sm form-label mb-xs">Skills Required</label>
              <div className="flex gap-sm mb-sm">
                <UnifiedInput
                  type="text"
                  value={newSkill}
                  onChange={(e) => setNewSkill(e.target.value)}
                  placeholder="Add a skill..."
                  onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addSkill())}
                />
                <Button type="button" onClick={addSkill} variant="outline">
                  Add
                </Button>
              </div>
              {skills.length > 0 && (
                <div className="flex flex-wrap gap-xs">
                  {skills.map((skill, index) => (
                    <Badge 
                      key={index} 
                      variant="secondary" 
                      className="cursor-pointer"
                      onClick={() => removeSkill(skill)}
                    >
                      {skill} Ã—
                    </Badge>
                  ))}
                </div>
              )}
            </div>

            <div>
              <label className="block text-body-sm form-label mb-xs">Notes</label>
              <textarea
                value={formData.notes}
                onChange={(e) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
                placeholder="Additional requirements or notes..."
                className="w-full px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary bg-background min-h-[100px]"
              />
            </div>

            <div className="flex gap-sm">
              <Button type="submit" loading={loading}>
                Create Assignment
              </Button>
              <Button type="button" variant="outline" onClick={() => router.back()}>
                Cancel
              </Button>
            </div>
          </form>
        </div>
      </Card>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useState, useCallback, useMemo } from 'react';
import { type DataRecord } from '@ghxstship/ui';
import { DataViewProvider, StateManagerProvider, ViewSwitcher, DataActions, DataGrid, KanbanBoard, CalendarView, ListView, TimelineView, GalleryView } from '@ghxstship/ui/components/DataViews';
import { type FieldConfig, type DataViewConfig } from '@ghxstship/ui/components/DataViews/types';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface Competency extends DataRecord {
  id: string;
  name: string;
  description?: string;
  category: 'technical' | 'creative' | 'management' | 'soft_skills' | 'industry_specific';
  level: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  certification_required: boolean;
  certification_body?: string;
  expiry_period?: number;
  tags?: string[];
  organization_id: string;
  created_at: string;
  updated_at: string;
  assignments_count?: number;
}

interface CompetenciesClientProps {
  orgId: string;
}

export default function CompetenciesClient({ orgId }: CompetenciesClientProps) {
  const t = useTranslations('people.competencies');
  const sb = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [competenciesData, setCompetenciesData] = useState<Competency[]>([]);

  useEffect(() => {
    loadCompetenciesData();

    // Set up real-time subscription
    const subscription = sb
      .channel('competencies-changes')
      .on('postgres_changes', 
        { 
          event: '*', 
          schema: 'public', 
          table: 'competencies',
          filter: `organization_id=eq.${orgId}`
        }, 
        (payload) => {
          console.log('Real-time update:', payload);
          loadCompetenciesData();
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [orgId]);

  const loadCompetenciesData = useCallback(async () => {
    setLoading(true);
    try {
      const { data, error } = await sb
        .from('competencies')
        .select(`
          *,
          assignments:person_competencies(count)
        `)
        .eq('organization_id', orgId)
        .order('name', { ascending: true });

      if (error) {
        console.error('Supabase error:', error);
        throw error;
      }

      setCompetenciesData(data || []);
    } catch (error) {
      console.error('Error loading competencies data:', error);
      // Could add toast notification here
    } finally {
      setLoading(false);
    }
  }, [orgId, sb]);

  // ATLVS DataViews field configuration - memoized for performance
  const fields: FieldConfig[] = useMemo(() => [
