      },
      {
        key: 'export',
        label: 'Export Selected',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          const selectedData = competenciesData.filter(c => selectedIds.includes(c.id));
          handleExport('csv', selectedData);
        },
        requiresSelection: true
      },
      {
        key: 'assign',
        label: 'Assign to People',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          console.log('Assign action:', selectedIds);
          // Implement assignment functionality
        },
        requiresSelection: true
      }
    ],
    exportConfig: {
      formats: ['csv', 'json', 'excel'],
      onExport: handleExport
    },
    importConfig: {
      formats: ['csv', 'json'],
      onImport: handleImport
    },
    onSearch: handleSearch,
    onFilter: handleFilter,
    onSort: handleSort
  }), [t, fields, competenciesData, handleSearch, handleFilter, handleSort, handleExport, handleImport]);

  return (
    <div className="stack-lg">
      <DataViewProvider config={competenciesConfig}>
        <StateManagerProvider>
          <div className="stack-md">
            <ViewSwitcher />
            <DataActions />

            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'beginner', title: 'Beginner' },
                { id: 'intermediate', title: 'Intermediate' },
                { id: 'advanced', title: 'Advanced' },
                { id: 'expert', title: 'Expert' }
              ]}
              statusField="level"
              titleField="name"
            />

            <CalendarView 
              startDateField="created_at"
              titleField="name"
            />

            <ListView 
              titleField="name"
            />

            <TimelineView 
              startDateField="created_at"
              titleField="name"
            />

            <GalleryView 
              titleField="name"
            />

            <AppDrawer
              title="Competency Details"
              open={false}
              onClose={() => {}}
              record={null}
              fields={fields}
              mode="view"
              tabs={[
                {
                  key: 'details',
                  label: 'Details',
                  content: (
                    <div className="p-md stack-md">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Name</label>
                          <div className="text-body color-foreground">Camera Operation</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Category</label>
                          <div className="text-body color-foreground">Technical</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Level</label>
                          <div className="text-body color-foreground">Advanced</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Certification Required</label>
                          <div className="text-body color-foreground">Yes</div>
