}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useState, useCallback, useMemo } from 'react';
import { type DataRecord } from '@ghxstship/ui';
import { DataViewProvider, StateManagerProvider, ViewSwitcher, DataActions, DataGrid, KanbanBoard, CalendarView, ListView, TimelineView, GalleryView } from '@ghxstship/ui/components/DataViews';
import { type FieldConfig, type DataViewConfig } from '@ghxstship/ui/components/DataViews/types';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface Contract extends DataRecord {
  id: string;
  person_id: string;
  project_id: string;
  type: 'employment' | 'freelance' | 'nda' | 'vendor' | 'service';
  status: 'draft' | 'sent' | 'signed' | 'expired' | 'terminated';
  start_date: string;
  end_date?: string;
  value?: number;
  currency: string;
  signed_date?: string;
  document_url?: string;
  notes?: string;
  organization_id: string;
  created_at: string;
  updated_at: string;
  person?: {
    first_name: string;
    last_name: string;
    email?: string;
  };
  project?: {
    name: string;
    status: string;
  };
}

interface ContractsClientProps {
  orgId: string;
}

export default function ContractsClient({ orgId }: ContractsClientProps) {
  const t = useTranslations('people.contracts');
  const sb = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [contractsData, setContractsData] = useState<Contract[]>([]);

  useEffect(() => {
    loadContractsData();

    // Set up real-time subscription
    const subscription = sb
      .channel('files-contracts-changes')
      .on('postgres_changes', 
        { 
          event: '*', 
          schema: 'public', 
          table: 'files_contracts',
          filter: `organization_id=eq.${orgId}`
        }, 
        (payload) => {
          console.log('Real-time update:', payload);
          loadContractsData();
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [orgId]);

  const loadContractsData = useCallback(async () => {
    setLoading(true);
    try {
      const { data, error } = await sb
        .from('files_contracts')
        .select(`
          *,
          person:people(first_name, last_name, email),
          project:projects(name, status)
        `)
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) {
        console.error('Supabase error:', error);
        throw error;
      }

      setContractsData(data || []);
    } catch (error) {
      console.error('Error loading contracts data:', error);
      // Could add toast notification here
    } finally {
      setLoading(false);
    }
  }, [orgId, sb]);

  // ATLVS DataViews field configuration - memoized for performance
  const fields: FieldConfig[] = useMemo(() => [
    {
      key: 'person.first_name',
      label: t('person'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'project.name',
      label: t('project'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'type',
      label: t('type'),
      type: 'select',
      options: [
        { value: 'employment', label: t('employment') },
        { value: 'freelance', label: t('freelance') },
        { value: 'nda', label: t('nda') },
        { value: 'vendor', label: t('vendor') },
        { value: 'service', label: t('service') }
      ],
      sortable: true,
      filterable: true
    },
    {
      key: 'status',
