      type: 'select',
      options: [
        { value: 'draft', label: t('draft') },
        { value: 'sent', label: t('sent') },
        { value: 'signed', label: t('signed') },
        { value: 'expired', label: t('expired') },
        { value: 'terminated', label: t('terminated') }
      ],
      sortable: true,
      filterable: true
    },
    {
      key: 'start_date',
      label: t('startDate'),
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'end_date',
      label: t('endDate'),
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'value',
      label: t('value'),
      type: 'currency',
      sortable: true,
      filterable: true
    },
    {
      key: 'currency',
      label: t('currency'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'signed_date',
      label: t('signedDate'),
      type: 'date',
      sortable: true,
      filterable: true
