                },
                {
                  key: 'documents',
                  label: 'Documents',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Contract documents and attachments will be shown here.</p>
                    </div>
                  )
                }
              ]}
            />
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useState, useCallback, useMemo } from 'react';
import { type DataRecord } from '@ghxstship/ui';
import { DataViewProvider, StateManagerProvider, ViewSwitcher, DataActions, DataGrid, KanbanBoard, CalendarView, ListView, TimelineView, GalleryView } from '@ghxstship/ui/components/DataViews';
import { type FieldConfig, type DataViewConfig } from '@ghxstship/ui/components/DataViews/types';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface Person extends DataRecord {
  id: string;
  first_name: string;
  last_name: string;
  email?: string;
  phone?: string;
  role?: string;
  department?: string;
  location?: string;
  status: 'active' | 'inactive' | 'terminated';
  avatar_url?: string;
  hire_date?: string;
  birth_date?: string;
  emergency_contact?: string;
  emergency_phone?: string;
  skills?: string[];
  notes?: string;
  organization_id: string;
  created_at: string;
  updated_at: string;
}

interface DirectoryClientProps {
  orgId: string;
}

export default function DirectoryClient({ orgId }: DirectoryClientProps) {
  const t = useTranslations('people.directory');
  const sb = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [peopleData, setPeopleData] = useState<Person[]>([]);

  useEffect(() => {
    loadPeopleData();

    // Set up real-time subscription
    const subscription = sb
      .channel('people-changes')
      .on('postgres_changes', 
        { 
          event: '*', 
          schema: 'public', 
          table: 'people',
          filter: `organization_id=eq.${orgId}`
        }, 
        (payload) => {
          console.log('Real-time update:', payload);
          loadPeopleData();
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [orgId]);

  const loadPeopleData = useCallback(async () => {
    setLoading(true);
    try {
      const { data, error } = await sb
        .from('people')
        .select('*')
        .eq('organization_id', orgId)
        .order('first_name', { ascending: true });

      if (error) {
        console.error('Supabase error:', error);
        throw error;
      }

      setPeopleData(data || []);
    } catch (error) {
      console.error('Error loading people data:', error);
      // Could add toast notification here
    } finally {
      setLoading(false);
    }
  }, [orgId, sb]);

  // ATLVS DataViews field configuration - memoized for performance
  const fields: FieldConfig[] = useMemo(() => [
    {
      key: 'first_name',
      label: t('firstName'),
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'last_name',
      label: t('lastName'),
      type: 'text',
      required: true,
      sortable: true,
      filterable: true
    },
    {
      key: 'email',
      label: t('email'),
      type: 'email',
      sortable: true,
      filterable: true
    },
    {
      key: 'phone',
      label: t('phone'),
      type: 'tel',
      sortable: true,
      filterable: true
    },
    {
      key: 'role',
      label: t('role'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'department',
      label: t('department'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'location',
      label: t('location'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'status',
      label: t('status'),
      type: 'select',
      options: [
        { value: 'active', label: t('active') },
        { value: 'inactive', label: t('inactive') },
        { value: 'terminated', label: t('terminated') }
      ],
      sortable: true,
      filterable: true,
      defaultValue: 'active'
    },
    {
      key: 'hire_date',
      label: t('hireDate'),
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'created_at',
      label: t('createdAt'),
      type: 'date',
      sortable: true,
      filterable: true
    }
  ], [t]);

  // Advanced search functionality
  const handleSearch = useCallback(async (query: string) => {
    if (!query.trim()) {
      loadPeopleData();
      return;
    }

    try {
      const { data, error } = await sb
        .from('people')
        .select('*')
        .eq('organization_id', orgId)
        .or(`first_name.ilike.%${query}%,last_name.ilike.%${query}%,email.ilike.%${query}%,role.ilike.%${query}%,department.ilike.%${query}%`)
        .order('first_name', { ascending: true });

      if (error) throw error;
      setPeopleData(data || []);
    } catch (error) {
      console.error('Search error:', error);
    }
  }, [orgId, sb, loadPeopleData]);

  // Advanced filtering
  const handleFilter = useCallback(async (filters: unknown) => {
    try {
      let query = sb
        .from('people')
        .select('*')
        .eq('organization_id', orgId);

      filters.forEach((filter: unknown) => {
        if (filter.value) {
          query = query.eq(filter.field, filter.value);
        }
      });

      const { data, error } = await query.order('first_name', { ascending: true });
      if (error) throw error;
      setPeopleData(data || []);
    } catch (error) {
      console.error('Filter error:', error);
    }
  }, [orgId, sb]);

  // Advanced sorting
  const handleSort = useCallback(async (sorts: unknown) => {
    try {
      let query = sb
        .from('people')
        .select('*')
        .eq('organization_id', orgId);

      if (sorts.length > 0) {
        const sort = sorts[0];
        query = query.order(sort.field, { ascending: sort.direction === 'asc' });
      }

      const { data, error } = await query;
      if (error) throw error;
      setPeopleData(data || []);
    } catch (error) {
      console.error('Sort error:', error);
    }
  }, [orgId, sb]);

  // Export functionality
  const handleExport = useCallback((format: string, data: DataRecord[], config?: unknown) => {
    const exportData = data.map(person => ({
      'First Name': person.first_name,
      'Last Name': person.last_name,
      'Email': person.email || 'N/A',
      'Phone': person.phone || 'N/A',
      'Role': person.role || 'N/A',
      'Department': person.department || 'N/A',
      'Location': person.location || 'N/A',
      'Status': person.status,
      'Hire Date': person.hire_date || 'N/A',
      'Created At': person.created_at
    }));

    if (format === 'csv') {
      const csv = [
        Object.keys(exportData[0]).join(','),
        ...exportData.map(row => Object.values(row).join(','))
      ].join('\n');

      const blob = new Blob([csv], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `people-directory-${new Date().toISOString().split('T')[0]}.csv`;
      a.click();
    } else if (format === 'json') {
      const json = JSON.stringify(exportData, null, 2);
      const blob = new Blob([json], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `people-directory-${new Date().toISOString().split('T')[0]}.json`;
      a.click();
    }
  }, []);

  // Import functionality
  const handleImport = useCallback(async (data: DataRecord[]) => {
    try {
      const importData = data.map(person => ({
        ...person,
        organization_id: orgId,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      }));

      const { error } = await sb
        .from('people')
        .insert(importData);

      if (error) throw error;

      loadPeopleData();
    } catch (error) {
      console.error('Import error:', error);
    }
  }, [orgId, sb, loadPeopleData]);

  // ATLVS DataViews configuration
  const directoryConfig: DataViewConfig = useMemo(() => ({
    id: 'directory-dataviews',
    name: t('title'),
    viewType: 'grid',
    fields,
    data: peopleData,
    defaultView: 'grid',
    bulkActions: [
      {
        key: 'delete',
        label: 'Delete Selected',
        variant: 'destructive',
        onClick: async (selectedIds: string[]) => {
          console.log('Delete action:', selectedIds);
          // Implement delete functionality
        },
        requiresSelection: true,
        confirmMessage: 'Are you sure you want to delete the selected people?'
      },
      {
        key: 'export',
        label: 'Export Selected',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          const selectedData = peopleData.filter(p => selectedIds.includes(p.id));
          handleExport('csv', selectedData);
        },
        requiresSelection: true
      },
      {
        key: 'activate',
        label: 'Activate Selected',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          console.log('Activate action:', selectedIds);
          // Implement activate functionality
        },
        requiresSelection: true
      },
      {
        key: 'deactivate',
        label: 'Deactivate Selected',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          console.log('Deactivate action:', selectedIds);
          // Implement deactivate functionality
        },
        requiresSelection: true
      }
    ],
    exportConfig: {
      formats: ['csv', 'json', 'excel'],
      onExport: handleExport
    },
    importConfig: {
      formats: ['csv', 'json'],
      onImport: handleImport
    },
    onSearch: handleSearch,
    onFilter: handleFilter,
    onSort: handleSort
  }), [t, fields, peopleData, handleSearch, handleFilter, handleSort, handleExport, handleImport]);

  return (
    <div className="stack-lg">
      <DataViewProvider config={directoryConfig}>
        <StateManagerProvider>
          <div className="stack-md">
            <ViewSwitcher />
            <DataActions />

            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'active', title: 'Active' },
                { id: 'inactive', title: 'Inactive' },
                { id: 'terminated', title: 'Terminated' }
              ]}
              statusField="status"
              titleField="first_name"
            />

            <CalendarView 
              startDateField="hire_date"
              titleField="first_name"
            />

            <ListView 
              titleField="first_name"
            />

            <TimelineView 
              startDateField="hire_date"
              titleField="first_name"
            />

            <GalleryView 
              titleField="first_name"
              imageField="avatar_url"
            />

            <AppDrawer
              title="Person Details"
              open={false}
              onClose={() => {}}
              record={null}
              fields={fields}
              mode="view"
              tabs={[
                {
                  key: 'profile',
                  label: 'Profile',
                  content: (
                    <div className="p-md stack-md">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
                        <div>
                          <label className="block text-body-sm form-label mb-xs">First Name</label>
                          <div className="text-body color-foreground">Jack</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Last Name</label>
                          <div className="text-body color-foreground">Sparrow</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Email</label>
                          <div className="text-body color-foreground">jack.sparrow@blackwater.fleet</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Role</label>
                          <div className="text-body color-foreground">Captain</div>
                        </div>
                      </div>
                    </div>
                  )
                },
                {
                  key: 'contact',
                  label: 'Contact',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Contact information and emergency contacts will be shown here.</p>
                    </div>
                  )
                },
                {
                  key: 'employment',
                  label: 'Employment',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Employment history and contract information will be shown here.</p>
                    </div>
                  )
                }
              ]}
            />
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { Card } from '@ghxstship/ui';
import { cookies } from 'next/headers';
import { createServerClient } from '@ghxstship/auth';
import { getTranslations } from 'next-intl/server';
import DirectoryClient from './DirectoryClient';
import CreatePersonClient from './CreatePersonClient';

export const metadata = { title: 'People Â· Directory' };

export default async function PeopleDirectoryPage() {
  const t = await getTranslations('people.directory');
  const cookieStore = cookies();
  const supabase = createServerClient(cookieStore);

  const { data: { user } } = await supabase.auth.getUser();
  let orgId: string | null = null;
  if (user) {
    const { data: membership } = await supabase
      .from('memberships')
      .select('organization_id')
      .eq('user_id', user.id)
      .eq('status', 'active')
      .order('created_at', { ascending: true })
      .maybeSingle();
    orgId = membership?.organization_id ?? null;
  }

  if (!orgId) {
    return <div>Access denied</div>;
  }

  return (
    <div className="stack-md">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 font-anton uppercase">People Directory</h1>
          <p className="text-body-sm color-muted">Manage your organization's team members</p>
        </div>
        <CreatePersonClient orgId={orgId} />
      </div>
      <DirectoryClient orgId={orgId} />
    </div>
  );
}

'use client';


import { Star, Plus, Save, X } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import { usePostHog } from 'posthog-js/react';
import {
  Button,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
  UnifiedInput,
} from '@ghxstship/ui';
import { tryCatch, reportError } from '@ghxstship/ui/utils/error-handling';
import AppDrawer from '@/app/_components/shared/AppDrawer';

type EndorsementType = 'skill' | 'performance' | 'leadership' | 'collaboration' | 'general';

interface EndorsementFormData {
  personId: string;
  competencyId: string;
  rating: string;
  comment: string;
  projectId: string;
  endorsementType: EndorsementType;
}

interface Person {
  id: string;
  first_name: string;
  last_name: string;
  role?: string;
}

interface Competency {
  id: string;
  name: string;
  category?: string;
}

interface CreateEndorsementClientProps {
  orgId: string;
  onEndorsementCreated?: () => void;
}

const endorsementTypeOptions: ReadonlyArray<{ value: EndorsementType; label: string }> = [
  { value: 'skill', label: 'Skill' },
  { value: 'performance', label: 'Performance' },
  { value: 'leadership', label: 'Leadership' },
  { value: 'collaboration', label: 'Collaboration' },
  { value: 'general', label: 'General' },
];

const ratingOptions: ReadonlyArray<{ value: string; label: string }> = [
  { value: '1', label: '1 - Needs Improvement' },
  { value: '2', label: '2 - Below Expectations' },
  { value: '3', label: '3 - Meets Expectations' },
  { value: '4', label: '4 - Exceeds Expectations' },
  { value: '5', label: '5 - Outstanding' },
];

const createInitialFormData = (): EndorsementFormData => ({
  personId: '',
  competencyId: '',
  rating: '5',
  comment: '',
  projectId: '',
  endorsementType: 'skill',
});

export default function CreateEndorsementClient({ orgId, onEndorsementCreated }: CreateEndorsementClientProps) {
  const posthog = usePostHog();
  const sb = createBrowserClient();
  const [isOpen, setIsOpen] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [people, setPeople] = useState<Person[]>([]);
  const [competencies, setCompetencies] = useState<Competency[]>([]);
  const [formData, setFormData] = useState<EndorsementFormData>(() => createInitialFormData());
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (isOpen) {
      loadPeople();
      loadCompetencies();
    }
  }, [isOpen, orgId]);

  const loadPeople = async () => {
    try {
      const { data: people } = await sb
        .from('people')
        .select('id, first_name, last_name, role')
        .eq('organization_id', orgId)
        .eq('status', 'active')
        .order('first_name');

      setPeople(people || []);
    } catch (error) {
      console.error('Error loading people:', error);
    }
  };

  const loadCompetencies = async () => {
    try {
      const { data: competencies } = await sb
        .from('people_competencies')
        .select('id, name, category')
        .eq('organization_id', orgId)
        .order('name');

      setCompetencies(competencies || []);
    } catch (error) {
      console.error('Error loading competencies:', error);
    }
  };

  const handleInputChange = useCallback((event: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  }, []);

  const handleTextareaChange = useCallback((event: ChangeEvent<HTMLTextAreaElement>) => {
    const { name, value } = event.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  }, []);

  const handleSelectChange = useCallback(<Key extends keyof EndorsementFormData>(field: Key) => (value: string) => {
    setFormData(prev => ({ ...prev, [field]: value as EndorsementFormData[Key] }));
  }, []);

  const fetchPeopleAndCompetencies = useCallback(async () => {
    try {
      const [{ data: peopleData }, { data: competenciesData }] = await Promise.all([
        sb
          .from('people')
          .select('id, first_name, last_name, role')
          .eq('organization_id', orgId)
          .eq('status', 'active')
          .order('first_name'),
        sb
          .from('people_competencies')
          .select('id, name, category')
          .eq('organization_id', orgId)
          .order('name'),
      ]);

      setPeople(peopleData || []);
      setCompetencies(competenciesData || []);
    } catch (loadError) {
      console.error('Error loading endorsements metadata:', loadError);
    }
  }, [orgId, sb]);

  const handleOpenDrawer = useCallback(() => {
    setError(null);
    setIsOpen(true);
  }, []);

  useEffect(() => {
    if (isOpen) {
      fetchPeopleAndCompetencies();
    }
  }, [fetchPeopleAndCompetencies, isOpen]);

  const handleSubmit = useCallback(async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!formData.personId) {
      setError('Person selection is required.');
      return;
    }

    const ratingValue = Number(formData.rating);

    if (Number.isNaN(ratingValue) || ratingValue < 1 || ratingValue > 5) {
      setError('Rating must be between 1 and 5.');
      return;
    }

    setError(null);
    try {
      setIsSubmitting(true);

      // Get current user
      const { data: { user } } = await sb.auth.getUser();
      if (!user) throw new Error('User not authenticated');

      const responseResult = await tryCatch(async () =>
        fetch('/api/v1/people/endorsements', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'x-org-id': orgId,
          },
          body: JSON.stringify({
            personId: formData.personId,
            competencyId: formData.competencyId || undefined,
            rating: ratingValue,
            comment: formData.comment.trim() || undefined,
            projectId: formData.projectId.trim() || undefined,
            endorsementType: formData.endorsementType,
            endorserId: user.id,
          }),
        })
      );
      if (!responseResult.success) {
        reportError(responseResult.error);
        throw new Error(responseResult.error.message);
      }
      const response = responseResult.data;

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to create endorsement');
      }

      const result = await response.json();

      // Track endorsement creation
      posthog?.capture('people_endorsement_created', {
        endorsement_id: result.data.id,
        person_id: formData.personId,
        competency_id: formData.competencyId || undefined,
        rating: ratingValue,
        type: formData.endorsementType,
        organization_id: orgId,
      });

      // Log activity
      await sb.from('activities').insert({
        organization_id: orgId,
        user_id: user.id,
        action: 'create',
        resource_type: 'endorsement',
        resource_id: result.data.id,
        details: {
          person_id: formData.personId,
          competency_id: formData.competencyId || undefined,
          rating: ratingValue,
          type: formData.endorsementType,
        },
      });

      // Reset form and close drawer
      setFormData(createInitialFormData());
      setIsOpen(false);
      onEndorsementCreated?.();

    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unknown error';
      console.error('Error creating endorsement:', error);
      setError(message);
      posthog?.capture('people_endorsement_creation_failed', {
        error: message,
        organization_id: orgId,
      });
    } finally {
      setIsSubmitting(false);
    }
  }, [formData, onEndorsementCreated, orgId, posthog, sb]);

  const handleClose = () => {
    if (!isSubmitting) {
      setFormData(createInitialFormData());
      setError(null);
      setIsOpen(false);
    }
  };

  const isSubmitDisabled = useMemo(() => {
    if (isSubmitting) {
      return true;
    }

    if (!formData.personId) {
      return true;
    }

    const ratingValue = Number(formData.rating);
    return Number.isNaN(ratingValue) || ratingValue < 1 || ratingValue > 5;
  }, [formData.personId, formData.rating, isSubmitting]);

  return (
    <>
      <Button
        onClick={handleOpenDrawer}
        className="flex items-center gap-sm"

      >
        <Plus className="h-4 w-4" />
        Add Endorsement
      </Button>

      <AppDrawer
        open={isOpen}
        onClose={handleClose}
        record={null}
        fields={[]}
        mode="create"
        title="Add Endorsement"
        loading={isSubmitting}
        tabs={[{
          key: 'content',
          label: 'Details',
          content: (
        <form id="endorsement-form" onSubmit={handleSubmit} className="stack-lg">
          <div className="flex items-center gap-sm mb-lg">
            <div className="p-sm bg-warning/10 rounded-lg">
              <Star className="h-5 w-5 color-warning" />
            </div>
            <div>
              <h3 className="form-label">Endorsement Information</h3>
              <p className="text-body-sm color-foreground/70">
                Provide feedback and recognition for team members
              </p>
            </div>
          </div>

          <div className="stack-md">
            <div>
              <label className="block text-body-sm form-label mb-sm">
                Person *
              </label>
              <Select value={formData.personId} onValueChange={handleSelectChange('personId')}>
                <SelectTrigger>
                  <SelectValue placeholder="Select person..." />
                </SelectTrigger>
                <SelectContent>
                  {people.map(person => (
                    <SelectItem key={person.id} value={person.id}>
                      {person.first_name} {person.last_name}
                      {person.role ? ` (${person.role})` : ''}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="grid grid-cols-2 gap-md">
              <div>
                <label className="block text-body-sm form-label mb-sm">
                  Endorsement Type
                </label>
                <Select value={formData.endorsementType} onValueChange={handleSelectChange('endorsementType')}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {endorsementTypeOptions.map(option => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="block text-body-sm form-label mb-sm">
                  Rating *
                </label>
                <Select value={formData.rating} onValueChange={handleSelectChange('rating')}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {ratingOptions.map(option => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <label className="block text-body-sm form-label mb-sm">
                Specific Competency (Optional)
              </label>
              <Select value={formData.competencyId} onValueChange={handleSelectChange('competencyId')}>
                <SelectTrigger>
                  <SelectValue placeholder="Select competency (optional)..." />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">None</SelectItem>
                  {competencies.map(competency => (
                    <SelectItem key={competency.id} value={competency.id}>
                      {competency.name}
                      {competency.category ? ` (${competency.category})` : ''}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <label className="block text-body-sm form-label mb-sm">
                Project (Optional)
              </label>
              <UnifiedInput
                id="projectId"
                name="projectId"
                value={formData.projectId}
                onChange={handleInputChange}
                placeholder="Project ID or name where this was observed"
              />
            </div>

            <div>
              <label className="block text-body-sm form-label mb-sm">
                Comments
              </label>
              <Textarea
                id="comment"
                name="comment"
                value={formData.comment}
                onChange={handleTextareaChange}
                placeholder="Specific feedback, examples, and observations"
                rows={4}
              />
            </div>
          </div>

          <div className="bg-warning/5 p-md rounded-lg">
            <h4 className="form-label color-warning mb-sm">Endorsement Guidelines</h4>
            <ul className="text-body-sm color-warning/80 stack-xs">
              <li>â€¢ Provide specific, constructive feedback with examples</li>
              <li>â€¢ Focus on observable behaviors and outcomes</li>
              <li>â€¢ Be fair and objective in your ratings</li>
              <li>â€¢ Consider the person's role and experience level</li>
              <li>â€¢ Use endorsements to recognize good work and guide development</li>
            </ul>
          </div>
          {error && (
            <div className="flex items-center gap-sm text-body-sm color-destructive">
              {error}
            </div>
          )}
          <div className="flex justify-end gap-sm mt-lg">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              disabled={isSubmitting}
            >
              <X className="h-4 w-4 mr-sm" />
              Cancel
            </Button>
            <Button
              type="submit"
              form="endorsement-form"
              disabled={isSubmitDisabled}
            >
              <Save className="h-4 w-4 mr-sm" />
              {isSubmitting ? 'Creating...' : 'Create Endorsement'}
            </Button>
          </div>
        </form>
          ),
        }]} />
    </>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
