import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface Endorsement extends DataRecord {
  id: string;
  endorsed_person_id: string;
  endorser_person_id: string;
  competency_id?: string;
  rating: number;
  message?: string;
  context?: string;
  organization_id: string;
  created_at: string;
  updated_at: string;
  endorsed_person?: {
    first_name: string;
    last_name: string;
    email?: string;
    role?: string;
    department?: string;
  };
  endorser_person?: {
    first_name: string;
    last_name: string;
    email?: string;
    role?: string;
    department?: string;
  };
  competency?: {
    name: string;
    category: string;
  };
}

interface EndorsementsClientProps {
  orgId: string;
}

export default function EndorsementsClient({ orgId }: EndorsementsClientProps) {
  const t = useTranslations('people.endorsements');
  const sb = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [endorsementsData, setEndorsementsData] = useState<Endorsement[]>([]);

  useEffect(() => {
    loadEndorsementsData();

    // Set up real-time subscription
    const subscription = sb
      .channel('endorsements-changes')
      .on('postgres_changes', 
        { 
          event: '*', 
          schema: 'public', 
          table: 'people_endorsements',
          filter: `organization_id=eq.${orgId}`
        }, 
        (payload) => {
          console.log('Real-time update:', payload);
          loadEndorsementsData();
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [orgId]);

  const loadEndorsementsData = useCallback(async () => {
    setLoading(true);
    try {
      const { data, error } = await sb
        .from('people_endorsements')
        .select(`
          *,
          endorsed_person:people!endorsed_id(first_name, last_name, email, role, department),
          endorser_person:people!endorser_id(first_name, last_name, email, role, department),
          competency:people_competencies(name, category)
        `)
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) {
        console.error('Supabase error:', error);
        throw error;
      }

      setEndorsementsData(data || []);
    } catch (error) {
      console.error('Error loading endorsements data:', error);
    } finally {
      setLoading(false);
    }
  }, [orgId, sb]);

  // ATLVS DataViews field configuration - memoized for performance
  const fields: FieldConfig[] = useMemo(() => [
    {
      key: 'endorsed_person.first_name',
      label: t('endorsedPerson'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'endorser_person.first_name',
      label: t('endorser'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'competency.name',
      label: t('competency'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'rating',
      label: t('rating'),
      type: 'number',
      sortable: true,
      filterable: true
    },
    {
      key: 'message',
      label: t('message'),
      type: 'textarea',
      sortable: false,
      filterable: true
    },
    {
      key: 'context',
      label: t('context'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'created_at',
      label: t('createdAt'),
      type: 'date',
      sortable: true,
      filterable: true
    }
  ], [t]);

  // Advanced search functionality
  const handleSearch = useCallback(async (query: string) => {
    if (!query.trim()) {
      loadEndorsementsData();
      return;
    }

    try {
      const { data, error } = await sb
        .from('people_endorsements')
        .select(`
          *,
          endorsed_person:people!endorsed_id(first_name, last_name, email, role, department),
          endorser_person:people!endorser_id(first_name, last_name, email, role, department),
          competency:people_competencies(name, category)
        `)
        .eq('organization_id', orgId)
        .or(`message.ilike.%${query}%,context.ilike.%${query}%`)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setEndorsementsData(data || []);
    } catch (error) {
      console.error('Search error:', error);
    }
  }, [orgId, sb, loadEndorsementsData]);

  // Advanced filtering
  const handleFilter = useCallback(async (filters: unknown) => {
    try {
      let query = sb
        .from('people_endorsements')
        .select(`
          *,
          endorsed_person:people!endorsed_id(first_name, last_name, email, role, department),
          endorser_person:people!endorser_id(first_name, last_name, email, role, department),
          competency:people_competencies(name, category)
        `)
        .eq('organization_id', orgId);

      filters.forEach((filter: unknown) => {
        if (filter.value) {
          query = query.eq(filter.field, filter.value);
        }
      });

      const { data, error } = await query.order('created_at', { ascending: false });
      if (error) throw error;
      setEndorsementsData(data || []);
    } catch (error) {
      console.error('Filter error:', error);
    }
  }, [orgId, sb]);

  // Advanced sorting
  const handleSort = useCallback(async (sorts: unknown) => {
    try {
      let query = sb
        .from('people_endorsements')
        .select(`
          *,
          endorsed_person:people!endorsed_id(first_name, last_name, email, role, department),
          endorser_person:people!endorser_id(first_name, last_name, email, role, department),
          competency:people_competencies(name, category)
        `)
        .eq('organization_id', orgId);

      if (sorts.length > 0) {
        const sort = sorts[0];
        query = query.order(sort.field, { ascending: sort.direction === 'asc' });
      }

      const { data, error } = await query;
      if (error) throw error;
      setEndorsementsData(data || []);
    } catch (error) {
      console.error('Sort error:', error);
    }
  }, [orgId, sb]);

  // Export functionality
  const handleExport = useCallback((format: string, data: DataRecord[], config?: unknown) => {
    const exportData = data.map(endorsement => ({
      'Endorsed Person': endorsement.endorsed_person ? `${endorsement.endorsed_person.first_name} ${endorsement.endorsed_person.last_name}` : 'N/A',
      'Endorser': endorsement.endorser_person ? `${endorsement.endorser_person.first_name} ${endorsement.endorser_person.last_name}` : 'N/A',
      'Competency': endorsement.competency?.name || 'N/A',
      'Rating': endorsement.rating,
      'Message': endorsement.message || 'N/A',
      'Context': endorsement.context || 'N/A',
      'Created At': endorsement.created_at
    }));

    if (format === 'csv') {
      const csv = [
        Object.keys(exportData[0]).join(','),
        ...exportData.map(row => Object.values(row).join(','))
      ].join('\n');

      const blob = new Blob([csv], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `endorsements-${new Date().toISOString().split('T')[0]}.csv`;
      a.click();
    } else if (format === 'json') {
      const json = JSON.stringify(exportData, null, 2);
      const blob = new Blob([json], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `endorsements-${new Date().toISOString().split('T')[0]}.json`;
      a.click();
    }
  }, []);

  // Import functionality
  const handleImport = useCallback(async (data: DataRecord[]) => {
    try {
      const importData = data.map(endorsement => ({
        ...endorsement,
        organization_id: orgId,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      }));

      const { error } = await sb
        .from('people_endorsements')
        .insert(importData);

      if (error) throw error;

      loadEndorsementsData();
    } catch (error) {
      console.error('Import error:', error);
    }
  }, [orgId, sb, loadEndorsementsData]);

  // ATLVS DataViews configuration
  const endorsementsConfig: DataViewConfig = useMemo(() => ({
    id: 'endorsements-dataviews',
    name: t('title'),
    viewType: 'grid',
    fields,
    data: endorsementsData,
    defaultView: 'grid',
    bulkActions: [
      {
        key: 'delete',
        label: 'Delete Selected',
        variant: 'destructive',
        onClick: async (selectedIds: string[]) => {
          console.log('Delete action:', selectedIds);
          // Implement delete functionality
        },
        requiresSelection: true,
        confirmMessage: 'Are you sure you want to delete the selected endorsements?'
      },
      {
        key: 'export',
        label: 'Export Selected',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          const selectedData = endorsementsData.filter(e => selectedIds.includes(e.id));
          handleExport('csv', selectedData);
        },
        requiresSelection: true
      }
    ],
    exportConfig: {
      formats: ['csv', 'json', 'excel'],
      onExport: handleExport
    },
    importConfig: {
      formats: ['csv', 'json'],
      onImport: handleImport
    },
    onSearch: handleSearch,
    onFilter: handleFilter,
    onSort: handleSort
  }), [t, fields, endorsementsData, handleSearch, handleFilter, handleSort, handleExport, handleImport]);

  return (
    <div className="stack-lg">
      <DataViewProvider config={endorsementsConfig}>
        <StateManagerProvider>
          <div className="stack-md">
            <ViewSwitcher />
            <DataActions />

            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: '1', title: '1 Star' },
                { id: '2', title: '2 Stars' },
                { id: '3', title: '3 Stars' },
                { id: '4', title: '4 Stars' },
                { id: '5', title: '5 Stars' }
              ]}
              statusField="rating"
              titleField="endorsed_person.first_name"
            />

            <CalendarView 
              startDateField="created_at"
              titleField="endorsed_person.first_name"
            />

            <ListView 
              titleField="endorsed_person.first_name"
            />

            <TimelineView 
              startDateField="created_at"
              titleField="endorsed_person.first_name"
            />

            <GalleryView 
              titleField="endorsed_person.first_name"
            />

            <AppDrawer
              title="Endorsement Details"
              open={false}
              onClose={() => {}}
              record={null}
              fields={fields}
              mode="view"
              tabs={[
                {
                  key: 'details',
                  label: 'Details',
                  content: (
                    <div className="p-md stack-md">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Endorsed Person</label>
                          <div className="text-body color-foreground">Jack Sparrow</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Endorser</label>
                          <div className="text-body color-foreground">Elizabeth Swann</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Rating</label>
                          <div className="text-body color-foreground">5 Stars</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Competency</label>
                          <div className="text-body color-foreground">Leadership</div>
                        </div>
                      </div>
                    </div>
                  )
                },
                {
                  key: 'message',
                  label: 'Message',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Endorsement message and feedback will be shown here.</p>
                    </div>
                  )
                },
                {
                  key: 'context',
                  label: 'Context',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Project context and collaboration details will be shown here.</p>
                    </div>
                  )
                }
              ]}
            />
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

