        <StateManagerProvider>
          <div className="stack-md">
            <ViewSwitcher />
            <DataActions />

            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'weak', title: 'Weak' },
                { id: 'moderate', title: 'Moderate' },
                { id: 'strong', title: 'Strong' }
              ]}
              statusField="strength"
              titleField="person.first_name"
            />

            <CalendarView 
              startDateField="created_at"
              titleField="person.first_name"
            />

            <ListView 
              titleField="person.first_name"
            />

            <TimelineView 
              startDateField="created_at"
              titleField="person.first_name"
            />

            <GalleryView 
              titleField="person.first_name"
            />

            <AppDrawer
              title="Network Connection Details"
              open={false}
              onClose={() => {}}
              record={null}
              fields={fields}
              mode="view"
              tabs={[
                {
                  key: 'details',
                  label: 'Details',
                  content: (
                    <div className="p-md stack-md">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Person</label>
                          <div className="text-body color-foreground">Jack Sparrow</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Connected Person</label>
                          <div className="text-body color-foreground">Elizabeth Swann</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Relationship Type</label>
                          <div className="text-body color-foreground">Colleague</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Strength</label>
                          <div className="text-body color-foreground">Strong</div>
                        </div>
                      </div>
                    </div>
                  )
                },
                {
                  key: 'history',
                  label: 'History',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Connection history and interaction timeline will be shown here.</p>
                    </div>
