'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { Card, Button, UnifiedInput } from '@ghxstship/ui';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import type { CreateTrainingRecordData } from './types';

interface CreateTrainingRecordClientProps {
  orgId: string;
}

export default function CreateTrainingRecordClient({ orgId }: CreateTrainingRecordClientProps) {
  const t = useTranslations('people.training');
  const router = useRouter();
  const sb = createBrowserClient();

  const [loading, setLoading] = useState(false);
  const [people, setPeople] = useState<any[]>([]);
  const [programs, setPrograms] = useState<any[]>([]);

  const [formData, setFormData] = useState<CreateTrainingRecordData>({
    person_id: '',
    program_id: '',
    notes: ''
  });

  useEffect(() => {
    loadData();
  }, [orgId]);

  const loadData = async () => {
    try {
      // Load people
      const { data: peopleData } = await sb
        .from('people')
        .select('id, first_name, last_name, department')
        .eq('organization_id', orgId)
        .eq('status', 'active')
        .order('first_name');

      setPeople(peopleData || []);

      // Load training programs
      const { data: programsData } = await sb
        .from('training_programs')
        .select('id, name, category, duration, required')
        .eq('organization_id', orgId)
        .order('name');

      setPrograms(programsData || []);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.person_id || !formData.program_id) return;

    setLoading(true);
    try {
      const { error } = await sb
        .from('training_records')
        .insert({
          ...formData,
          organization_id: orgId,
          status: 'enrolled',
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString()
        });

      if (error) throw error;

      router.push('/people/training');
    } catch (error) {
      console.error('Error creating training record:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 font-anton uppercase">Enroll in Training</h1>
          <p className="text-body-sm color-muted">Enroll a person in a training program</p>
        </div>
        <Button variant="outline" onClick={() => router.back()}>
          Cancel
        </Button>
      </div>

      <Card>
        <div className="p-md">
          <form onSubmit={handleSubmit} className="stack-md">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label className="block text-body-sm form-label mb-xs">Person *</label>
                <select
                  value={formData.person_id}
                  onChange={(e) => setFormData(prev => ({ ...prev, person_id: e.target.value }))}
                  className="w-full px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary bg-background"
                  required
                >
                  <option value="">Select person...</option>
                  {people.map(person => (
                    <option key={person.id} value={person.id}>
                      {person.first_name} {person.last_name} - {person.department}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-body-sm form-label mb-xs">Training Program *</label>
                <select
                  value={formData.program_id}
                  onChange={(e) => setFormData(prev => ({ ...prev, program_id: e.target.value }))}
                  className="w-full px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary bg-background"
                  required
                >
                  <option value="">Select program...</option>
                  {programs.map(program => (
                    <option key={program.id} value={program.id}>
                      {program.name} ({program.duration}h) - {program.category}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div>
              <label className="block text-body-sm form-label mb-xs">Notes</label>
              <textarea
                value={formData.notes}
                onChange={(e) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
                placeholder="Additional notes about this enrollment..."
                className="w-full px-md py-sm border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-primary bg-background min-h-[100px]"
              />
            </div>

            <div className="flex gap-sm">
              <Button type="submit" loading={loading}>
                Enroll in Training
              </Button>
              <Button type="button" variant="outline" onClick={() => router.back()}>
                Cancel
              </Button>
            </div>
          </form>
        </div>
      </Card>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useState, useCallback, useMemo } from 'react';
import { type DataRecord } from '@ghxstship/ui';
import { DataViewProvider, StateManagerProvider, ViewSwitcher, DataActions, DataGrid, KanbanBoard, CalendarView, ListView, TimelineView, GalleryView } from '@ghxstship/ui/components/DataViews';
import { type FieldConfig, type DataViewConfig } from '@ghxstship/ui/components/DataViews/types';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface TrainingRecord extends DataRecord {
  id: string;
  person_id: string;
  program_id: string;
  status: 'enrolled' | 'in_progress' | 'completed' | 'expired' | 'failed';
  completion_date?: string;
  expiry_date?: string;
  score?: number;
  notes?: string;
  organization_id: string;
  created_at: string;
  updated_at: string;
  person?: {
    first_name: string;
    last_name: string;
    email?: string;
    department?: string;
  };
  program?: {
    name: string;
    category: string;
    duration: number;
    required: boolean;
  };
}

interface TrainingClientProps {
  orgId: string;
}

export default function TrainingClient({ orgId }: TrainingClientProps) {
  const t = useTranslations('people.training');
  const sb = createBrowserClient();
  const [loading, setLoading] = useState(false);
  const [trainingData, setTrainingData] = useState<TrainingRecord[]>([]);

  useEffect(() => {
    loadTrainingData();

    // Set up real-time subscription
    const subscription = sb
      .channel('training-changes')
      .on('postgres_changes', 
        { 
          event: '*', 
          schema: 'public', 
          table: 'training_records',
          filter: `organization_id=eq.${orgId}`
        }, 
        (payload) => {
          console.log('Real-time update:', payload);
          loadTrainingData();
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [orgId]);

  const loadTrainingData = useCallback(async () => {
    setLoading(true);
    try {
      const { data, error } = await sb
        .from('training_records')
        .select(`
          *,
          person:people(first_name, last_name, email, department),
          program:training_programs(name, category, duration, required)
        `)
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) {
        console.error('Supabase error:', error);
        throw error;
      }

      setTrainingData(data || []);
    } catch (error) {
      console.error('Error loading training data:', error);
    } finally {
      setLoading(false);
    }
  }, [orgId, sb]);

  // ATLVS DataViews field configuration - memoized for performance
  const fields: FieldConfig[] = useMemo(() => [
    {
      key: 'person.first_name',
      label: t('person'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'program.name',
      label: t('program'),
      type: 'text',
      sortable: true,
      filterable: true
    },
    {
      key: 'program.category',
      label: t('category'),
      type: 'select',
      options: [
        { value: 'safety', label: t('safety') },
        { value: 'technical', label: t('technical') },
        { value: 'compliance', label: t('compliance') },
        { value: 'leadership', label: t('leadership') },
        { value: 'certification', label: t('certification') }
      ],
      sortable: true,
      filterable: true
    },
    {
      key: 'status',
      label: t('status'),
      type: 'select',
      options: [
        { value: 'enrolled', label: t('enrolled') },
        { value: 'in_progress', label: t('inProgress') },
        { value: 'completed', label: t('completed') },
        { value: 'expired', label: t('expired') },
        { value: 'failed', label: t('failed') }
      ],
      sortable: true,
      filterable: true
    },
    {
      key: 'completion_date',
      label: t('completionDate'),
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'expiry_date',
      label: t('expiryDate'),
      type: 'date',
      sortable: true,
      filterable: true
    },
    {
      key: 'score',
      label: t('score'),
      type: 'number',
      sortable: true,
      filterable: true
    },
    {
      key: 'program.duration',
      label: t('duration'),
      type: 'number',
      sortable: true,
      filterable: true
    },
    {
      key: 'created_at',
      label: t('enrolledAt'),
      type: 'date',
      sortable: true,
      filterable: true
    }
  ], [t]);

  // Advanced search functionality
  const handleSearch = useCallback(async (query: string) => {
    if (!query.trim()) {
      loadTrainingData();
      return;
    }

    try {
      const { data, error } = await sb
        .from('training_records')
        .select(`
          *,
          person:people(first_name, last_name, email, department),
          program:training_programs(name, category, duration, required)
        `)
        .eq('organization_id', orgId)
        .or(`notes.ilike.%${query}%`)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTrainingData(data || []);
    } catch (error) {
      console.error('Search error:', error);
    }
  }, [orgId, sb, loadTrainingData]);

  // Advanced filtering
  const handleFilter = useCallback(async (filters: unknown) => {
    try {
      let query = sb
        .from('training_records')
        .select(`
          *,
          person:people(first_name, last_name, email, department),
          program:training_programs(name, category, duration, required)
        `)
        .eq('organization_id', orgId);

      filters.forEach((filter: unknown) => {
        if (filter.value) {
          query = query.eq(filter.field, filter.value);
        }
      });

      const { data, error } = await query.order('created_at', { ascending: false });
      if (error) throw error;
      setTrainingData(data || []);
    } catch (error) {
      console.error('Filter error:', error);
    }
  }, [orgId, sb]);

  // Advanced sorting
  const handleSort = useCallback(async (sorts: unknown) => {
    try {
      let query = sb
        .from('training_records')
        .select(`
          *,
          person:people(first_name, last_name, email, department),
          program:training_programs(name, category, duration, required)
        `)
        .eq('organization_id', orgId);

      if (sorts.length > 0) {
        const sort = sorts[0];
        query = query.order(sort.field, { ascending: sort.direction === 'asc' });
      }

      const { data, error } = await query;
      if (error) throw error;
      setTrainingData(data || []);
    } catch (error) {
      console.error('Sort error:', error);
    }
  }, [orgId, sb]);

  // Export functionality
  const handleExport = useCallback((format: string, data: DataRecord[], config?: unknown) => {
    const exportData = data.map(record => ({
      'Person': record.person ? `${record.person.first_name} ${record.person.last_name}` : 'N/A',
      'Program': record.program?.name || 'N/A',
      'Category': record.program?.category || 'N/A',
      'Status': record.status,
      'Completion Date': record.completion_date || 'N/A',
      'Expiry Date': record.expiry_date || 'N/A',
      'Score': record.score || 'N/A',
      'Duration (hours)': record.program?.duration || 'N/A',
      'Enrolled At': record.created_at
    }));

    if (format === 'csv') {
      const csv = [
        Object.keys(exportData[0]).join(','),
        ...exportData.map(row => Object.values(row).join(','))
      ].join('\n');

      const blob = new Blob([csv], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `training-records-${new Date().toISOString().split('T')[0]}.csv`;
      a.click();
    } else if (format === 'json') {
      const json = JSON.stringify(exportData, null, 2);
      const blob = new Blob([json], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `training-records-${new Date().toISOString().split('T')[0]}.json`;
      a.click();
    }
  }, []);

  // Import functionality
  const handleImport = useCallback(async (data: DataRecord[]) => {
    try {
      const importData = data.map(record => ({
        ...record,
        organization_id: orgId,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      }));

      const { error } = await sb
        .from('training_records')
        .insert(importData);

      if (error) throw error;

      loadTrainingData();
    } catch (error) {
      console.error('Import error:', error);
    }
  }, [orgId, sb, loadTrainingData]);

  // ATLVS DataViews configuration
  const trainingConfig: DataViewConfig = useMemo(() => ({
    id: 'training-dataviews',
    name: t('title'),
    viewType: 'grid',
    fields,
    data: trainingData,
    defaultView: 'grid',
    bulkActions: [
      {
        key: 'delete',
        label: 'Delete Selected',
        variant: 'destructive',
        onClick: async (selectedIds: string[]) => {
          console.log('Delete action:', selectedIds);
          // Implement delete functionality
        },
        requiresSelection: true,
        confirmMessage: 'Are you sure you want to delete the selected training records?'
      },
      {
        key: 'export',
        label: 'Export Selected',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          const selectedData = trainingData.filter(t => selectedIds.includes(t.id));
          handleExport('csv', selectedData);
        },
        requiresSelection: true
      },
      {
        key: 'complete',
        label: 'Mark as Completed',
        variant: 'secondary',
        onClick: async (selectedIds: string[]) => {
          console.log('Complete action:', selectedIds);
          // Implement completion functionality
        },
        requiresSelection: true
      }
    ],
    exportConfig: {
      formats: ['csv', 'json', 'excel'],
      onExport: handleExport
    },
    importConfig: {
      formats: ['csv', 'json'],
      onImport: handleImport
    },
    onSearch: handleSearch,
    onFilter: handleFilter,
    onSort: handleSort
  }), [t, fields, trainingData, handleSearch, handleFilter, handleSort, handleExport, handleImport]);

  return (
    <div className="stack-lg">
      <DataViewProvider config={trainingConfig}>
        <StateManagerProvider>
          <div className="stack-md">
            <ViewSwitcher />
            <DataActions />

            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'enrolled', title: 'Enrolled' },
                { id: 'in_progress', title: 'In Progress' },
                { id: 'completed', title: 'Completed' },
                { id: 'expired', title: 'Expired' }
              ]}
              statusField="status"
              titleField="person.first_name"
            />

            <CalendarView 
              startDateField="created_at"
              endDateField="expiry_date"
              titleField="person.first_name"
            />

            <ListView 
              titleField="person.first_name"
            />

            <TimelineView 
              startDateField="created_at"
              titleField="person.first_name"
            />

            <GalleryView 
              titleField="person.first_name"
            />

            <AppDrawer
              title="Training Record Details"
              open={false}
              onClose={() => {}}
              record={null}
              fields={fields}
              mode="view"
              tabs={[
                {
                  key: 'details',
                  label: 'Details',
                  content: (
                    <div className="p-md stack-md">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Person</label>
                          <div className="text-body color-foreground">Jack Sparrow</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Program</label>
                          <div className="text-body color-foreground">Safety Training</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Status</label>
                          <div className="text-body color-foreground">Completed</div>
                        </div>
                        <div>
                          <label className="block text-body-sm form-label mb-xs">Score</label>
                          <div className="text-body color-foreground">95%</div>
                        </div>
                      </div>
                    </div>
                  )
                },
                {
                  key: 'progress',
                  label: 'Progress',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Training progress and milestones will be shown here.</p>
                    </div>
                  )
                },
                {
                  key: 'certificates',
                  label: 'Certificates',
                  content: (
                    <div className="p-md">
                      <p className="color-muted">Training certificates and credentials will be shown here.</p>
                    </div>
                  )
                }
              ]}
            />
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

'use client';

import { CheckCircle, XCircle, Clock, AlertTriangle, Users, TrendingUp, Filter, Settings, Plus, Search, Download, Upload, RefreshCw, Eye, Edit, Trash2, MoreHorizontal, Grid3X3, List, LayoutDashboard, Calendar, BarChart3 } from "lucide-react";
import { useState, useEffect, useCallback } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Button, 
  Badge, 
  Card, 
  Input,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  Checkbox
} from '@ghxstship/ui';
import AppDrawer from '@/app/_components/shared/AppDrawer';
import { ApprovalsService } from './lib/approvalsService';
import { CreatePolicyClient } from './CreatePolicyClient';
import type { 
  ApprovalStep, 
  ApprovalSortOptions,
  ApprovalDashboardData
} from './types';

// View Types
type ApprovalViewMode = 'dashboard' | 'grid' | 'list' | 'kanban' | 'calendar';

// Filters Interface
interface ApprovalFilters {
  search: string;
  status: string;
  priority: string;
  approver_id?: string;
}

interface ApprovalsClientProps {
  className?: string;
  organizationId?: string;
  userId?: string;
}

export default function ApprovalsClient({ 
  className, 
  organizationId = 'default-org',
  userId = 'current-user-id'
}: ApprovalsClientProps) {
  const t = useTranslations();
  const supabase = createBrowserClient();

  // State management
  const [loading, setLoading] = useState(false);
  const [approvals, setApprovals] = useState<ApprovalStep[]>([]);
  const [dashboardData, setDashboardData] = useState<ApprovalDashboardData | null>(null);
  const [selectedApprovals, setSelectedApprovals] = useState<string[]>([]);
  const [viewMode, setViewMode] = useState<ApprovalViewMode>('dashboard');
  const [showCreatePolicy, setShowCreatePolicy] = useState(false);
  const [showApprovalDrawer, setShowApprovalDrawer] = useState(false);
  const [selectedApproval, setSelectedApproval] = useState<ApprovalStep | null>(null);

  // Filters and sorting
  const [filters, setFilters] = useState<ApprovalFilters>({
    search: '',
    status: 'all',
    priority: 'all'
  });
  const [sort, setSort] = useState<ApprovalSortOptions>({
    field: 'created_at',
    direction: 'desc'
  });

  const approvalsService = new ApprovalsService();

  // Load approvals data
  const loadApprovals = useCallback(async () => {
    try {
      setLoading(true);

      // Load approval steps
      const { data: approvalSteps, error } = await approvalsService.getApprovalSteps(
        organizationId,
        filters,
        sort
      );

      if (error) {
        console.error('Error loading approvals:', error);
        // Fallback to demo data
        const demoApprovals: ApprovalStep[] = [
          {
            id: 'demo-approval-1',
            request_id: 'demo-request-1',
            approver_id: userId,
            step_order: 1,
            status: 'pending',
            created_at: new Date().toISOString(),
            updated_at: new Date().toISOString(),
            request: {
              id: 'demo-request-1',
              title: 'Camera Equipment Purchase',
              status: 'submitted',
              estimated_total: 25000
            }
          },
          {
            id: 'demo-approval-2',
            request_id: 'demo-request-2',
            approver_id: userId,
            step_order: 1,
            status: 'pending',
            created_at: new Date().toISOString(),
            updated_at: new Date().toISOString(),
            request: {
              id: 'demo-request-2',
              title: 'Catering Services Contract',
              status: 'submitted',
              estimated_total: 8500
            }
          }
        ];
        setApprovals(demoApprovals);
      } else {
        setApprovals(approvalSteps || []);
      }

      // Load dashboard data
      const { data: dashboard } = await approvalsService.getDashboardData(organizationId, userId);
      setDashboardData(dashboard);

    } catch (error) {
      console.error('Error loading approvals:', error);
      // Set demo data on error
      setApprovals([]);
    } finally {
      setLoading(false);
    }
  }, [organizationId, userId, filters, sort]);

  // Load data on mount and when dependencies change
  useEffect(() => {
    loadApprovals();
  }, [loadApprovals]);

  // Handle filter changes
  const handleFilterChange = useCallback((newFilters: Partial<ApprovalFilters>) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
  }, []);

  // Handle sort changes
  const handleSortChange = useCallback((newSort: ApprovalSortOptions) => {
    setSort(newSort);
  }, []);

  // Handle approval actions
  const handleApprovalAction = useCallback(async (
    approvalId: string, 
    action: 'approve' | 'reject' | 'skip',
    notes?: string
  ) => {
    try {
      setLoading(true);

      // Mock approval action for demo
      console.log(`Processing approval ${approvalId} with action ${action}`, notes);

      // Update local state
      setApprovals(prev => prev.map(approval => 
        approval.id === approvalId 
          ? { ...approval, status: action === 'approve' ? 'approved' : action === 'reject' ? 'rejected' : 'skipped' }
          : approval
      ));

    } catch (error) {
      console.error('Error processing approval:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  // Handle record selection
  const handleRecordSelect = useCallback((recordIds: string[]) => {
    setSelectedApprovals(recordIds);
  }, []);

  // Handle record actions
  const handleRecordAction = useCallback((action: string, approval: ApprovalStep) => {
    switch (action) {
      case 'view':
        setSelectedApproval(approval);
        setShowApprovalDrawer(true);
        break;
      case 'approve':
        handleApprovalAction(approval.id!, 'approve');
        break;
      case 'reject':
        handleApprovalAction(approval.id!, 'reject');
        break;
      case 'skip':
        handleApprovalAction(approval.id!, 'skip');
        break;
    }
  }, [handleApprovalAction]);

  // Handle bulk actions
  const handleBulkAction = useCallback(async (action: string) => {
    try {
      setLoading(true);

      for (const approvalId of selectedApprovals) {
        await handleApprovalAction(approvalId, action as unknown);
      }

      setSelectedApprovals([]);

    } catch (error) {
      console.error('Error processing bulk action:', error);
    } finally {
      setLoading(false);
    }
  }, [selectedApprovals, handleApprovalAction]);

  // Render dashboard cards
  const renderDashboardCards = () => {
    const pendingCount = approvals.filter(a => a.status === 'pending').length;
    const approvedToday = approvals.filter(a => 
      a.status === 'approved' && 
      a.approved_at && 
      new Date(a.approved_at).toDateString() === new Date().toDateString()
    ).length;
    const totalValue = approvals.reduce((sum, a) => sum + (a.request?.estimated_total || 0), 0);

    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md mb-lg">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Pending Approvals</p>
              <p className="text-2xl font-bold">{pendingCount}</p>
            </div>
            <Clock className="h-8 w-8 text-warning" />
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Approved Today</p>
              <p className="text-2xl font-bold">{approvedToday}</p>
            </div>
            <CheckCircle className="h-8 w-8 text-success" />
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Overdue</p>
              <p className="text-2xl font-bold">0</p>
            </div>
            <AlertTriangle className="h-8 w-8 text-destructive" />
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Total Value</p>
              <p className="text-2xl font-bold">
                {new Intl.NumberFormat('en-US', {
                  style: 'currency',
                  currency: 'USD'
                }).format(totalValue)}
              </p>
            </div>
            <TrendingUp className="h-8 w-8 text-info" />
          </div>
        </Card>
      </div>
    );
  };

  // Render approval list
  const renderApprovalList = () => {
    const filteredApprovals = approvals.filter(approval => {
      if (filters.search && !approval.request?.title?.toLowerCase().includes(filters.search.toLowerCase())) {
        return false;
      }
      if (filters.status !== 'all' && approval.status !== filters.status) {
        return false;
      }
      return true;
    });

    return (
      <div className="space-y-sm">
        {filteredApprovals.map((approval) => (
          <Card key={approval.id} className="p-md">
            <div className="flex items-center justify-between">
              <div className="flex-1">
                <div className="flex items-center gap-sm mb-sm">
                  <h4 className="font-medium">{approval.request?.title || 'Unknown Request'}</h4>
                  <Badge variant={
                    approval.status === 'approved' ? 'success' :
                    approval.status === 'rejected' ? 'destructive' :
                    approval.status === 'pending' ? 'warning' : 'secondary'
                  }>
                    {approval.status}
                  </Badge>
                </div>
                <p className="text-sm text-muted-foreground">
                  Amount: {new Intl.NumberFormat('en-US', {
                    style: 'currency',
                    currency: 'USD'
                  }).format(approval.request?.estimated_total || 0)}
                </p>
              </div>
              <div className="flex gap-sm">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => handleRecordAction('view', approval)}
                >
                  <Eye className="h-4 w-4 mr-sm" />
                  View
                </Button>
                {approval.status === 'pending' && (
                  <>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleRecordAction('approve', approval)}
                    >
                      <CheckCircle className="h-4 w-4 mr-sm" />
                      Approve
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleRecordAction('reject', approval)}
                    >
                      <XCircle className="h-4 w-4 mr-sm" />
                      Reject
                    </Button>
                  </>
                )}
              </div>
            </div>
          </Card>
        ))}
        {filteredApprovals.length === 0 && (
          <Card className="p-xl text-center">
            <Users className="h-12 w-12 mx-auto mb-md text-muted-foreground" />
            <h3 className="text-lg font-medium mb-sm">No approvals found</h3>
            <p className="text-sm text-muted-foreground">
              {filters.search || filters.status !== 'all' 
                ? 'Try adjusting your filters to see more results.'
                : 'There are no pending approvals at this time.'
              }
            </p>
          </Card>
        )}
      </div>
    );
  };

  // Render view content based on current view mode
  const renderViewContent = () => {
    switch (viewMode) {
      case 'dashboard':
        return (
          <div className="space-y-lg">
            {renderDashboardCards()}
            <Card className="p-md">
              <h3 className="text-lg font-semibold mb-md">Recent Approvals</h3>
              {renderApprovalList()}
            </Card>
          </div>
        );
      case 'list':
        return renderApprovalList();
      case 'grid':
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
            {approvals.map((approval) => (
              <Card key={approval.id} className="p-md">
                <div className="space-y-sm">
                  <div className="flex items-center justify-between">
                    <h4 className="font-medium">{approval.request?.title || 'Unknown Request'}</h4>
                    <Badge variant={
                      approval.status === 'approved' ? 'success' :
                      approval.status === 'rejected' ? 'destructive' :
                      approval.status === 'pending' ? 'warning' : 'secondary'
                    }>
                      {approval.status}
                    </Badge>
                  </div>
                  <p className="text-sm text-muted-foreground">
                    {new Intl.NumberFormat('en-US', {
                      style: 'currency',
                      currency: 'USD'
                    }).format(approval.request?.estimated_total || 0)}
                  </p>
                  <div className="flex gap-sm pt-sm">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleRecordAction('view', approval)}
                    >
                      <Eye className="h-4 w-4" />
                    </Button>
                    {approval.status === 'pending' && (
                      <>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleRecordAction('approve', approval)}
                        >
                          <CheckCircle className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleRecordAction('reject', approval)}
                        >
                          <XCircle className="h-4 w-4" />
                        </Button>
                      </>
                    )}
                  </div>
                </div>
              </Card>
            ))}
          </div>
        );
      default:
        return renderApprovalList();
    }
  };

  return (
    <div className={`space-y-lg ${className || ''}`}>
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-sm">
          <Users className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Procurement Approvals</h3>
          <Badge variant="secondary">
            {approvals.length} approval{approvals.length !== 1 ? 's' : ''}
          </Badge>
        </div>
        <div className="flex items-center gap-sm">
          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowCreatePolicy(true)}
          >
            <Settings className="h-4 w-4 mr-sm" />
            Policies
          </Button>
          <Button variant="outline" size="sm">
            <Upload className="h-4 w-4 mr-sm" />
            Import
          </Button>
          <Button variant="outline" size="sm">
            <Download className="h-4 w-4 mr-sm" />
            Export
          </Button>
          <Button size="sm" onClick={loadApprovals}>
            <RefreshCw className="h-4 w-4 mr-sm" />
            Refresh
          </Button>
        </div>
      </div>

      {/* Filters */}
      <Card className="p-md">
        <div className="flex flex-col lg:flex-row gap-md">
          {/* Search */}
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search approvals..."
              value={filters.search}
              onChange={(e) => handleFilterChange({ search: e.target.value })}
              className="pl-10"
            />
          </div>

          {/* Status Filter */}
          <Select
            value={filters.status}
            onValueChange={(value) => handleFilterChange({ status: value })}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="All Status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="pending">Pending</SelectItem>
              <SelectItem value="approved">Approved</SelectItem>
              <SelectItem value="rejected">Rejected</SelectItem>
              <SelectItem value="skipped">Skipped</SelectItem>
            </SelectContent>
          </Select>

          {/* Priority Filter */}
          <Select
            value={filters.priority}
            onValueChange={(value) => handleFilterChange({ priority: value })}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="All Priorities" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Priorities</SelectItem>
              <SelectItem value="low">Low</SelectItem>
              <SelectItem value="medium">Medium</SelectItem>
              <SelectItem value="high">High</SelectItem>
              <SelectItem value="urgent">Urgent</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </Card>

      {/* View Mode Tabs */}
      <Tabs value={viewMode} onValueChange={(value) => setViewMode(value as ApprovalViewMode)}>
        <div className="flex items-center justify-between">
          <TabsList>
            <TabsTrigger value="dashboard">
              <LayoutDashboard className="h-4 w-4 mr-sm" />
              Dashboard
            </TabsTrigger>
            <TabsTrigger value="grid">
              <Grid3X3 className="h-4 w-4 mr-sm" />
              Grid
            </TabsTrigger>
            <TabsTrigger value="list">
              <List className="h-4 w-4 mr-sm" />
              List
            </TabsTrigger>
            <TabsTrigger value="kanban">
              <BarChart3 className="h-4 w-4 mr-sm" />
              Kanban
            </TabsTrigger>
            <TabsTrigger value="calendar">
              <Calendar className="h-4 w-4 mr-sm" />
              Calendar
            </TabsTrigger>
          </TabsList>

          {/* Bulk Actions */}
          {selectedApprovals.length > 0 && (
            <div className="flex items-center gap-sm">
              <span className="text-sm text-muted-foreground">
                {selectedApprovals.length} selected
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleBulkAction('approve')}
              >
                <CheckCircle className="h-4 w-4 mr-sm" />
                Approve All
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleBulkAction('reject')}
              >
                <XCircle className="h-4 w-4 mr-sm" />
                Reject All
              </Button>
            </div>
          )}
        </div>

        <TabsContent value={viewMode} className="mt-md">
          {loading ? (
            <Card className="p-xl text-center">
              <RefreshCw className="h-8 w-8 mx-auto mb-md animate-spin" />
              <p className="text-muted-foreground">Loading approvals...</p>
            </Card>
          ) : (
            renderViewContent()
          )}
        </TabsContent>
      </Tabs>

      {/* Approval Details Drawer */}
      <AppDrawer
        open={showApprovalDrawer}
        onClose={() => setShowApprovalDrawer(false)}
        title="Approval Details"
      >
        {selectedApproval && (
          <div className="space-y-md">
            <div>
              <h4 className="font-medium mb-sm">Request Information</h4>
              <p className="text-sm text-muted-foreground">
                {selectedApproval.request?.title || 'No title'}
              </p>
            </div>

            <div>
              <h4 className="font-medium mb-sm">Status</h4>
              <Badge variant={
                selectedApproval.status === 'approved' ? 'success' :
                selectedApproval.status === 'rejected' ? 'destructive' :
                selectedApproval.status === 'pending' ? 'warning' : 'secondary'
              }>
                {selectedApproval.status}
              </Badge>
            </div>

            <div>
              <h4 className="font-medium mb-sm">Amount</h4>
              <p className="text-lg font-semibold">
                {new Intl.NumberFormat('en-US', {
                  style: 'currency',
                  currency: 'USD'
                }).format(selectedApproval.request?.estimated_total || 0)}
              </p>
            </div>

            {selectedApproval.status === 'pending' && (
              <div className="flex gap-sm pt-md border-t">
                <Button
                  onClick={() => {
                    handleApprovalAction(selectedApproval.id!, 'approve');
                    setShowApprovalDrawer(false);
                  }}
                >
                  <CheckCircle className="h-4 w-4 mr-sm" />
                  Approve
                </Button>
                <Button
                  variant="outline"
                  onClick={() => {
                    handleApprovalAction(selectedApproval.id!, 'reject');
                    setShowApprovalDrawer(false);
                  }}
                >
                  <XCircle className="h-4 w-4 mr-sm" />
                  Reject
                </Button>
              </div>
            )}
          </div>
        )}
      </AppDrawer>

      {/* Create Policy Drawer */}
      <CreatePolicyClient
        open={showCreatePolicy}
        onClose={() => setShowCreatePolicy(false)}
        onSuccess={() => {
          setShowCreatePolicy(false);
          loadApprovals();
        }}
        organizationId={organizationId}
      />
    </div>
  );
}

'use client';

import { CheckCircle, XCircle, Clock, AlertTriangle, Users, TrendingUp, Filter, Settings, Plus, Search, Download, Upload, RefreshCw, Eye, Edit, Trash2, MoreHorizontal, Grid3X3, List, LayoutDashboard, Calendar, BarChart3 } from "lucide-react";
import { useState, useEffect, useCallback } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Button, 
  Badge, 
  Card, 
  Input,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger
} from '@ghxstship/ui';
import AppDrawer from '@/app/_components/shared/AppDrawer';
import { approvalsService } from './lib/approvalsService';
import { CreatePolicyClient } from './CreatePolicyClient';
import type { 
  ApprovalStep, 
  ApprovalSortOptions,
  ApprovalDashboardData
} from './types';

interface ApprovalsClientProps {
  className?: string;
  organizationId?: string;
  userId?: string;
}

export default function ApprovalsClient({ 
  className, 
  organizationId = 'default-org',
  userId = 'current-user-id'
}: ApprovalsClientProps) {
  const t = useTranslations();
  const supabase = createBrowserClient();

  // State management
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState<DataRecord[]>([]);
  const [dashboardData, setDashboardData] = useState<ApprovalDashboardData | null>(null);
  const [selectedApprovals, setSelectedApprovals] = useState<string[]>([]);
  const [showCreatePolicy, setShowCreatePolicy] = useState(false);
  const [showApprovalDrawer, setShowApprovalDrawer] = useState(false);
  const [selectedApproval, setSelectedApproval] = useState<ApprovalStep | null>(null);

  // Filters and sorting
  const [filters, setFilters] = useState({
    status: 'all' as string,
    priority: 'all' as string,
    search: ''
  });
  const [sort, setSort] = useState<ApprovalSortOptions>({
    field: 'created_at',
    direction: 'desc'
  });

  const approvalsService = new ApprovalsService();

  // Field configuration for ATLVS DataViews
  const fieldConfig: FieldConfig[] = [
    {
      key: 'request_title',
      label: 'Request',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 200
    },
    {
      key: 'requester_name',
      label: 'Requester',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 150
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      sortable: true,
      filterable: true,
      width: 120
    },
    {
      key: 'priority',
      label: 'Priority',
      type: 'select',
      sortable: true,
      filterable: true,
      width: 100
    },
    {
      key: 'amount',
      label: 'Amount',
      type: 'currency',
      sortable: true,
      filterable: true,
      width: 120
    },
    {
      key: 'due_date',
      label: 'Due Date',
      type: 'date',
      sortable: true,
      filterable: true,
      width: 120
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'date',
      sortable: true,
      filterable: true,
      width: 120
    }
  ];

  // Filter configuration for ATLVS
  const filterConfig: FilterConfig[] = [
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'all', label: 'All Status' },
        { value: 'pending', label: 'Pending' },
        { value: 'approved', label: 'Approved' },
        { value: 'rejected', label: 'Rejected' },
        { value: 'skipped', label: 'Skipped' }
      ]
    },
    {
      key: 'priority',
      label: 'Priority',
      type: 'select',
      options: [
        { value: 'all', label: 'All Priorities' },
        { value: 'low', label: 'Low' },
        { value: 'medium', label: 'Medium' },
        { value: 'high', label: 'High' },
        { value: 'urgent', label: 'Urgent' }
      ]
    },
    {
      key: 'search',
      label: 'Search',
      type: 'text',
      placeholder: 'Search approvals...'
    }
  ];

  // Sort configuration for ATLVS
  const sortConfig: SortConfig[] = [
    { key: 'created_at', label: 'Created Date' },
    { key: 'due_date', label: 'Due Date' },
    { key: 'amount', label: 'Amount' },
    { key: 'status', label: 'Status' },
    { key: 'priority', label: 'Priority' }
  ];

  // Field visibility state
  const [visibleFields, setVisibleFields] = useState<string[]>(
    fieldConfig.map(field => field.key)
  );
  const [fieldOrder, setFieldOrder] = useState<string[]>(
    fieldConfig.map(field => field.key)
  );

  // DataView configuration for ATLVS
  const dataViewConfig: DataViewConfig = {
    id: 'procurement-approvals',
    name: 'Procurement Approvals',
    viewType: 'grid',
    defaultView: 'grid',
    fields: fieldConfig.filter(field => visibleFields.includes(field.key)),
    filters: filterConfig,
    sorts: sortConfig,
    actions: {
      create: true,
      edit: true,
      delete: true,
      export: true,
      import: true,
      bulkActions: true,
      fieldVisibility: true,
      fieldReordering: true
    },
    views: {
      grid: { enabled: true, default: true },
      list: { enabled: true },
      kanban: { enabled: true, groupBy: 'status' },
      calendar: { enabled: true, dateField: 'due_date' },
      dashboard: { enabled: true }
    },
    preferences: {
      visibleFields,
      fieldOrder,
      pageSize: 25,
      defaultSort: { field: 'created_at', direction: 'desc' }
    }
  };

  // Load approvals data
  const loadApprovals = useCallback(async () => {
    try {
      setLoading(true);

      // Load approval steps
      const { data: approvals, error } = await approvalsService.getApprovalSteps(
        organizationId,
        filters,
        sort
      );

      if (error) {
        console.error('Error loading approvals:', error);
        return;
      }

      // Transform to DataRecord format for ATLVS
      const transformedData: DataRecord[] = (approvals || []).map((approval: ApprovalStep) => ({
        id: approval.id,
        request_title: approval.request?.title || 'Unknown Request',
        requester_name: (approval.request as unknown)?.requester?.name || 'Unknown',
        status: approval.status,
        priority: approval.priority || 'medium',
        amount: approval.request?.estimated_total || 0,
        due_date: approval.due_date,
        created_at: approval.created_at,
        updated_at: approval.updated_at,
        record_type: 'approval'
      }));

      setData(transformedData);

      // Load dashboard data
      const { data: dashboard } = await approvalsService.getDashboardData(organizationId, userId);
      setDashboardData(dashboard);

    } catch (error) {
      console.error('Error loading approvals:', error);
    } finally {
      setLoading(false);
    }
  }, [organizationId, userId, filters, sort]);

  // Load data on mount and when dependencies change
  useEffect(() => {
    loadApprovals();
  }, [loadApprovals]);

  // Handle filter changes
  const handleFilterChange = useCallback((newFilters: Partial<ApprovalFilters>) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
  }, []);

  // Handle sort changes
  const handleSortChange = useCallback((newSort: ApprovalSortOptions) => {
    setSort(newSort);
  }, []);

  // Handle approval actions
  const handleApprovalAction = useCallback(async (
    approvalId: string, 
    action: 'approve' | 'reject' | 'skip',
    notes?: string
  ) => {
    try {
      setLoading(true);

      const { error } = await approvalsService.processApproval(
        organizationId,
        approvalId,
        action,
        userId,
        notes
      );

      if (error) {
        console.error('Error processing approval:', error);
        return;
      }

      // Reload data
      await loadApprovals();

    } catch (error) {
      console.error('Error processing approval:', error);
    } finally {
      setLoading(false);
    }
  }, [organizationId, userId, loadApprovals]);

  // Handle record selection
  const handleRecordSelect = useCallback((recordIds: string[]) => {
    setSelectedApprovals(recordIds);
  }, []);

  // Handle record actions
  const handleRecordAction = useCallback((action: string, record: DataRecord) => {
    switch (action) {
      case 'view':
        setSelectedApproval(record as unknown);
        setShowApprovalDrawer(true);
        break;
      case 'approve':
        handleApprovalAction(record.id, 'approve');
        break;
      case 'reject':
        handleApprovalAction(record.id, 'reject');
        break;
      case 'skip':
        handleApprovalAction(record.id, 'skip');
        break;
    }
  }, [handleApprovalAction]);

  // Handle bulk actions
  const handleBulkAction = useCallback(async (action: string, recordIds: string[]) => {
    try {
      setLoading(true);

      for (const recordId of recordIds) {
        await handleApprovalAction(recordId, action as unknown);
      }

      setSelectedApprovals([]);

    } catch (error) {
      console.error('Error processing bulk action:', error);
    } finally {
      setLoading(false);
    }
  }, [handleApprovalAction]);

  // Render dashboard cards
  const renderDashboardCards = () => {
    if (!dashboardData) return null;

    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md mb-lg">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Pending Approvals</p>
              <p className="text-2xl font-bold">{dashboardData.pending_count}</p>
            </div>
            <Clock className="h-8 w-8 text-warning" />
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Approved Today</p>
              <p className="text-2xl font-bold">{dashboardData.approved_today}</p>
            </div>
            <CheckCircle className="h-8 w-8 text-success" />
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Overdue</p>
              <p className="text-2xl font-bold">{dashboardData.overdue_count}</p>
            </div>
            <AlertTriangle className="h-8 w-8 text-destructive" />
          </div>
        </Card>

        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Total Value</p>
              <p className="text-2xl font-bold">
                {new Intl.NumberFormat('en-US', {
                  style: 'currency',
                  currency: 'USD'
                }).format(dashboardData.total_value || 0)}
              </p>
            </div>
            <TrendingUp className="h-8 w-8 text-info" />
          </div>
        </Card>
      </div>
    );
  };

  return (
    <div className={`space-y-lg ${className || ''}`}>
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-sm">
          <Users className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Procurement Approvals</h3>
          <Badge variant="secondary">
            {data.length} approval{data.length !== 1 ? 's' : ''}
          </Badge>
        </div>
        <div className="flex items-center gap-sm">
          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowCreatePolicy(true)}
          >
            <Settings className="h-4 w-4 mr-sm" />
            Policies
          </Button>
          <Button variant="outline" size="sm">
            <Upload className="h-4 w-4 mr-sm" />
            Import
          </Button>
          <Button variant="outline" size="sm">
            <Download className="h-4 w-4 mr-sm" />
            Export
          </Button>
          <Button size="sm" onClick={loadApprovals}>
            <RefreshCw className="h-4 w-4 mr-sm" />
            Refresh
          </Button>
        </div>
      </div>

      {/* Dashboard Cards */}
      {renderDashboardCards()}

      {/* ATLVS DataViews */}
      <DataViewProvider config={dataViewConfig}>
        <div className="space-y-md">
          {/* Data Actions */}
          <DataActions
            selectedCount={selectedApprovals.length}
            onBulkAction={handleBulkAction}
            onFilterChange={handleFilterChange}
            onSortChange={handleSortChange}
            filters={filters}
            sort={sort}
          />

          {/* View Switcher and Data Display */}
          <ViewSwitcher
            data={data}
            loading={loading}
            selectedRecords={selectedApprovals}
            onRecordSelect={handleRecordSelect}
            onRecordAction={handleRecordAction}
            fieldConfig={fieldConfig}
          />
        </div>
      </DataViewProvider>

      {/* Approval Details Drawer */}
      <AppDrawer
        open={showApprovalDrawer}
        onClose={() => setShowApprovalDrawer(false)}
        title="Approval Details"
        size="md"
      >
        {selectedApproval && (
          <div className="space-y-md">
            <div>
              <h4 className="font-medium mb-sm">Request Information</h4>
              <p className="text-sm text-muted-foreground">
                {selectedApproval.request?.title || 'No title'}
              </p>
            </div>

            <div>
              <h4 className="font-medium mb-sm">Status</h4>
              <Badge variant={
                selectedApproval.status === 'approved' ? 'success' :
                selectedApproval.status === 'rejected' ? 'destructive' :
                selectedApproval.status === 'pending' ? 'warning' : 'secondary'
              }>
                {selectedApproval.status}
              </Badge>
            </div>

            <div className="flex gap-sm pt-md border-t">
              <Button
                variant="outline"
                onClick={() => handleApprovalAction(selectedApproval.id, 'approve')}
                disabled={selectedApproval.status !== 'pending'}
              >
                <CheckCircle className="h-4 w-4 mr-sm" />
                Approve
              </Button>
              <Button
                variant="outline"
                onClick={() => handleApprovalAction(selectedApproval.id, 'reject')}
                disabled={selectedApproval.status !== 'pending'}
              >
                <XCircle className="h-4 w-4 mr-sm" />
                Reject
              </Button>
            </div>
          </div>
        )}
      </AppDrawer>

      {/* Create Policy Drawer */}
      <CreatePolicyClient
        open={showCreatePolicy}
        onClose={() => setShowCreatePolicy(false)}
        onSuccess={() => {
          setShowCreatePolicy(false);
          loadApprovals();
        }}
        organizationId={organizationId}
      />
    </div>
  );
}

